-- ----------------------------
-- Table structure for articles
-- ----------------------------
DROP TABLE IF EXISTS `articles`;
CREATE TABLE `articles`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `newsId` int NOT NULL COMMENT '区分内容类型',
  `newsName` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '内容类型名称',
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '文章标题',
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '文章内容',
  `author` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '文章作者',
  `titleImg` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '文章图片',
  `time` datetime(0) NOT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 59 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of articles
-- ----------------------------
INSERT INTO `articles` VALUES (13, 1, '网站公告', '关于环保主题征文活动投稿截止的通知', '<p style=\"text-align: center;\"><strong>关于“我身边的环保故事·法治相伴”中小学生主题征文活动投稿截止的通知</strong></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">由生态环境部法规与标准司主办，全国青少年法治教育中心（以下简称“中心”）、外语教学与研究出版社承办的“我身边的环保故事·法治相伴”中小学生主题征文活动于2022年12月正式启动。自开展以来，活动得到了全国各地中小学生的积极响应与热情参与。</p><p style=\"text-align: left;\">现就征文活动的后续安排通知如下：</p><p style=\"text-align: left;\">1.征文活动作品上传通道将于2023年3月1日10:00关闭。</p><p style=\"text-align: left;\">2.通过普法网上传的作品，将由活动组委会统一进行评审，优秀名单请关注网站后续通知。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: right;\">全国青少年法治教育中心</p><p style=\"text-align: right;\">2023年2月28日</p>', '教育部全国青少年普法网', '', '2023-04-11 19:57:24');
INSERT INTO `articles` VALUES (14, 1, '网站公告', '教育部全国青少年普法网政府网站工作年度报表（2022 年度）', '<p style=\"text-align: center;\"><strong>政府网站工作年度报表</strong></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0);\">（2022年度）</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\">填报单位：教育部全国青少年普法网</span></p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202301/17155633f43m.png\" alt=\"\" data-href=\"\" style=\"width: 800px;height: 1500px;\"></p>', '教育部全国青少年普法网', '', '2023-04-11 19:58:55');
INSERT INTO `articles` VALUES (15, 1, '网站公告', '关于公布第七届全国学生“学宪法 讲宪法”活动获奖名单的通知', '<p style=\"text-align: center;\">教育部全国教育普法领导小组办公室关于公布第七届</p><p style=\"text-align: center;\">全国学生“学宪法 讲宪法”活动获奖名单的通知</p><p style=\"text-align: justify;\"><br></p><p style=\"text-align: justify;\">各省、自治区、直辖市教育厅（教委），新疆生产建设兵团教育局：</p><p style=\"text-indent: 43px; text-align: justify;\">为深入学习贯彻党的二十大精神，纪念现行宪法公布施行40周年，根据党中央相关决策部署，2022年我部组织开展了第七届全国学生“学宪法 讲宪法”系列活动。活动中，各地各校精心组织部署，广大干部师生积极参与，取得了良好成效。现将第七届全国学生“学宪法 讲宪法”活动组织奖获奖名单（附件1）及全国总决赛获奖名单予以公布（附件2）。</p><p style=\"text-indent: 43px; text-align: justify;\">各地各校要深入学习贯彻党的二十大精神，坚持以习近平法治思想为引领，持续开展好“尊崇宪法、学习宪法、遵守宪法、维护宪法、运用宪法”学习宣传教育活动，推动青少年宪法教育不断取得新进展。</p><p style=\"text-indent: 43px; text-align: justify;\">特此通知。</p><p style=\"text-indent: 43px; text-align: justify;\"><br></p><p style=\"text-indent: 43px; text-align: justify;\">附件：</p><p style=\"text-indent: 41px; text-align: justify;\">1.第七届全国学生“学宪法 讲宪法”活动组织奖获奖名单</p><p style=\"text-indent: 43px; text-align: justify;\">2.第七届全国学生“学宪法 讲宪法”活动全国总决赛获奖名单</p><p style=\"text-indent: 43px; text-align: justify;\"><br></p><p style=\"text-indent: 171px; text-align: justify;\"> </p><p style=\"text-indent: 43px; text-align: right;\">教育部全国教育普法领导小组办公室</p><p style=\"text-align: right;\">2023年1月13日</p><p style=\"text-align: justify;\"><br></p><p style=\"text-align: justify;\"><br></p><p style=\"text-align: justify;\"><br></p><p style=\"text-align: justify;\"><br></p><p style=\"text-align: left;\">附件1</p><p style=\"text-align: center;\">第七届全国学生“学宪法 讲宪法”活动</p><p style=\"text-align: center;\">组织奖获奖名单</p><p style=\"text-align: left;\"><span style=\"font-family: 黑体;\"><br></span></p><p style=\"text-align: left;\"><span style=\"font-family: 黑体;\">一、 最佳组织奖</span></p><p style=\"text-indent: 43px; text-align: justify;\">新疆生产建设兵团教育局</p><p style=\"text-indent: 43px; text-align: justify;\">黑龙江省教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">广西壮族自治区教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">天津市教育委员会</p><p style=\"text-indent: 43px; text-align: justify;\">福建省教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">山东省教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">重庆市教育委员会</p><p style=\"text-indent: 43px; text-align: justify;\">上海市教育委员会</p><p style=\"text-indent: 43px; text-align: justify;\">四川省教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">江西省教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">浙江省教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">广东省教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">海南省教育厅</p><p style=\"text-indent: 43px; text-align: left;\"><span style=\"font-family: 黑体;\">二、优秀组织奖</span></p><p style=\"text-indent: 43px; text-align: justify;\">湖南省教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">辽宁省教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">吉林省教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">安徽省教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">江苏省教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">宁夏回族自治区教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">陕西省教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">北京市教育委员会</p><p style=\"text-indent: 43px; text-align: justify;\">内蒙古自治区教育厅</p><p style=\"text-indent: 43px; text-align: justify;\">河北省教育厅</p><p style=\"text-indent: 43px; text-align: left;\"><span style=\"font-family: 黑体;\">三、特别组织奖</span></p><p style=\"text-indent: 43px; text-align: left;\">澳门特别行政区政府教育及青年发展局</p><p style=\"text-align: justify;\"><br></p><p style=\"text-align: justify;\"><br></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">附件2</p><p style=\"text-align: left;\"> </p><p style=\"text-align: center;\">第七届全国学生“学宪法 讲宪法”活动</p><p style=\"text-align: center;\">全国总决赛获奖名单</p><p style=\"text-align: left;\"> </p><p style=\"text-indent: 43px; text-align: left;\"><span style=\"font-family: 黑体;\">一、演讲比赛小学组获奖名单</span></p><p style=\"text-indent: 43px; text-align: justify;\"><strong>冠军：</strong>李沛元（北京市）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>亚军：</strong>王禹晴（北京市 网络风采）、李沛恩（四川省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>季军：</strong>谢宇宸（广西壮族自治区）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>最佳表达奖：</strong>李沛元（北京市）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>最佳风采奖：</strong>谢宇宸（广西壮族自治区）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>一等奖：</strong>颜昱菲（湖北省）、邓雨菲（澳门特别行政区）、涂筱茉（重庆市 网络风采）、郑雯洁（福建省 网络风采）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>二等奖：</strong>景伊明（北京市 网络风采）、郗湖（黑龙江省）、余盺嫒（重庆市）、张云杰（新疆生产建设兵团）、吴思桓（天津市）、林靖博（广东省）、陈语竹（湖南省）、陆好（广西壮族自治区 网络风采）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>三等奖：</strong><a href=\"https://static.qspfw.moe.gov.cn/xf2022/getprize.html\" target=\"_blank\">详见教育部全国青少年普法网</a></p><p style=\"text-indent: 43px; text-align: left;\"><span style=\"font-family: 黑体;\">二、演讲比赛初中组获奖名单</span></p><p style=\"text-indent: 43px; text-align: justify;\"><strong>冠军：</strong>夏馨逸（浙江省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>亚军：</strong>韦雅凡（广东省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>季军：</strong>侯沈奇（黑龙江省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>最佳表达奖：</strong>韦雅凡（广东省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>最佳风采奖：</strong>夏馨逸（浙江省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>一等奖：</strong>龚庆杰（陕西省）、黄馨谊（山东省）、龚金巧（四川省）、龚依祎（重庆市）、周桐菲（广西壮族自治区）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>二等奖：</strong>罗欣雨（新疆维吾尔自治区）、年卿仁（安徽省）、冯佳月（北京市）、牛子壬（福建省）、万洺泽（河南省）、郭源清（新疆生产建设兵团）、家心怡（山西省）、薛怡然（北京市 网络风采）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>三等奖：</strong><a href=\"https://static.qspfw.moe.gov.cn/xf2022/getprize.html\" target=\"_blank\">详见教育部全国青少年普法网</a></p><p style=\"text-indent: 43px; text-align: left;\"><span style=\"font-family: 黑体;\">三、演讲比赛高中组获奖名单</span></p><p style=\"text-indent: 43px; text-align: justify;\"><strong>冠军：</strong>乔蓝棋（黑龙江省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>亚军：</strong>胡艺馨（安徽省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>季军：</strong>刘睿萱（山西省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>最佳表达奖：</strong>乔蓝棋（黑龙江省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>最佳风采奖：</strong>孟平渰（浙江省 网络风采）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>一等奖：</strong>李元元（广西壮族自治区）、冯雨燃（云南省）、孟平渰（浙江省 网络风采）、陈若艺（浙江省）、吴卓谦（广东省）、彭红稀（四川省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>二等奖：</strong>毕俊琪（山东省 网络风采）、杨钧涵（上海市）、乔龙巴图·布塔（新疆生产建设兵团）、何俊豪（重庆市）、杨康意（重庆市 网络风采）、商云溪（陕西省）、李梦翔（宁夏回族自治区）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>三等奖：</strong><a href=\"https://static.qspfw.moe.gov.cn/xf2022/getprize.html\" target=\"_blank\">详见教育部全国青少年普法网</a></p><p style=\"text-indent: 43px; text-align: left;\"><span style=\"font-family: 黑体;\">四、演讲比赛高校组获奖名单</span></p><p style=\"text-indent: 43px; text-align: justify;\"><strong>冠军：</strong>徐文涛（天津市）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>亚军：</strong>卢柏文（北京市 网络风采）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>季军：</strong>李乐豪（四川省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>最佳表达奖：</strong>徐文涛（天津市）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>最佳风采奖：</strong>李乐豪（四川省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>一等奖：</strong>赵逸雯（重庆市）、常瀚文（天津市 网络风采）、赵阳（山东省）、方媛（江西省）、阿明奇（黑龙江省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>二等奖：</strong>王彩萌（浙江省）、陈泽原（海南省）、向锦睿（宁夏回族自治区）、司雨诺（福建省 网络风采）、高健豪（上海市）、刘惠华（北京市）、谢田靖（广西壮族自治区）、吴钊泽（山西省）</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>三等奖：</strong><a href=\"https://static.qspfw.moe.gov.cn/xf2022/getprize.html\" target=\"_blank\">详见教育部全国青少年普法网</a></p><p style=\"text-indent: 43px; text-align: justify;\"><span style=\"font-family: 黑体;\">五、知识竞赛团体获奖名单</span></p><p style=\"text-indent: 43px; text-align: justify;\"><strong>冠军：</strong>广东省代表队</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>亚军：</strong>云南省代表队</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>季军：</strong>四川省代表队</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>一等奖：</strong>天津市代表队、宁夏回族自治区代表队、福建省代表队、重庆市代表队、山西省代表队、江西省代表队、浙江省代表队</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>二等奖：</strong>湖北省代表队、河北省代表队、辽宁省代表队、广西壮族自治区代表队、河南省代表队、新疆生产建设兵团代表队、江苏省代表队、北京市代表队、黑龙江省代表队、湖南省代表队</p><p style=\"text-indent: 43px; text-align: justify;\"><strong>三等奖：</strong><a href=\"https://static.qspfw.moe.gov.cn/xf2022/getprize.html\" target=\"_blank\">详见教育部全国青少年普法网</a></p><p style=\"text-align: justify;\"><br></p><p style=\"text-indent: 43px; text-align: justify;\"><br></p><p style=\"text-indent: 43px; text-align: justify;\"><br></p><p style=\"text-indent: 43px; text-align: justify;\"><br></p><p style=\"text-indent: 43px; text-align: justify;\"><br></p><p style=\"text-indent: 43px; text-align: justify;\"><br></p>', '教育部全国青少年普法网', '', '2023-04-11 20:00:19');
INSERT INTO `articles` VALUES (16, 1, '网站公告', '关于关闭2022年“宪法卫士”行动计划参与通道的通知', '<p style=\"text-align: left;\">为深入学习宣传贯彻党的二十大精神，认真学习贯彻习近平法治思想和习近平总书记关于教育的重要论述，推动青少年宪法学习宣传教育走深走实，根据中央依法治国办部署和教育系统“八五”普法规划，教育部自2022年5月起组织开展了第七届全国学生“学宪法 讲宪法”系列活动。活动结合纪念现行宪法公布施行四十周年等主题内容，深入学习宣传习近平法治思想，用社会主义核心价值观铸魂育人，引导青少年学生诵读宪法原文，参与宪法网络学习和相关法治实践，进一步增强宪法意识，弘扬宪法精神。</p><p style=\"text-align: left;\">截至2022年12月13日，全国学生通过普法网“宪法卫士”专栏在线学习人次达75.64亿，通过学习、练习、综合评价产生的“宪法卫士”达1.61亿名。</p><p style=\"text-align: left;\"><strong>“宪法卫士”行动计划参与通道拟于2022年12月14日17:00关闭。</strong>通道关闭后，学生将不能再通过网页、微信小程序与PC客户端访问“宪法卫士”专栏中的“学习练习”“综合评价”及“法治实践”三个模块。通道关闭后，本届活动的“宪法卫士”行动计划相关统计数据将停止更新，教育行政部门信息管理员与学校信息管理员仍可通过管理平台对本单位活动参与数据进行查询，机构（学校）基础数据维护功能暂时关闭。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: right;\">教育部全国青少年普法网</p><p style=\"text-align: right;\">2022年12月13日</p>', '教育部全国青少年普法网', '', '2023-04-11 20:01:10');
INSERT INTO `articles` VALUES (17, 1, '网站公告', '关于教育部全国青少年普法网暂停办公电话咨询服务的通知', '<p style=\"text-align: left;\">受北京市新冠疫情影响，教育部全国青少年普法网（以下简称普法网）自2022年11月21日起暂停办公电话咨询服务。</p><p style=\"text-align: left;\">普法网问题反馈功能正常运营，各级各类管理员登录普法网管理服务云平台后，可通过左侧导航栏底部的“问题反馈”功能进行咨询。学生账号问题请联系学生所在学校处理。</p><p style=\"text-align: left;\">特此通知。</p><p style=\"text-align: right;\">教育部全国青少年普法网</p><p style=\"text-align: right;\">2022年11月22日</p>', '教育部全国青少年普法网', '', '2023-04-11 20:01:50');
INSERT INTO `articles` VALUES (18, 2, '热点资讯', '习近平在二十届中共中央政治局常委同中外记者见面时强调 始终坚持一切为了人民一切依靠人民 以中国式现代化全面推进中华民族伟大复兴', '<p style=\"text-align: left;\">新华社北京10月23日电 &nbsp;刚刚在中国共产党第二十届中央委员会第一次全体会议上当选的中共中央总书记习近平和中共中央政治局常委李强、赵乐际、王沪宁、蔡奇、丁薛祥、李希，23日中午在人民大会堂同采访中共二十大的中外记者亲切见面。</p><p style=\"text-align: left;\">人民大会堂金色大厅华灯璀璨、气氛热烈庄重。600多名中外记者来到这里，向全球报道新一届中共中央政治局常委的首次集体亮相。</p><p style=\"text-align: left;\">12时5分，习近平等在热烈的掌声中步入大厅，向在场的中外记者挥手致意。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202210/24085725cdoz.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">10月23日，刚刚在中国共产党第二十届中央委员会第一次全体会议上当选的中共中央总书记习近平和中共中央政治局常委李强、赵乐际、王沪宁、蔡奇、丁薛祥、李希在北京人民大会堂同中外记者亲切见面。这是习近平发表重要讲话。（新华社记者 李学仁/摄）</span></p><p style=\"text-align: left;\">在中共二十大新闻发言人孙业礼作简要介绍后，习近平发表了重要讲话。习近平首先表示，很高兴在这里同各位记者朋友见面。昨天，中国共产党第二十次全国代表大会已经胜利闭幕了。这是一次高举旗帜、凝聚力量、团结奋进的大会。国际社会对中共二十大高度关注。许多国家的政党发来了贺电贺信，其中很多是国家元首、政府首脑、政党和重要组织机构领导人发来的。在此，我谨代表中共中央，致以诚挚谢意。</p><p style=\"text-align: left;\">习近平向大家介绍了中共中央政治局常务委员会的其他成员后，代表新一届中共中央领导成员衷心感谢全党同志的信任，表示一定牢记党的性质和宗旨，牢记自己的使命和责任，恪尽职守、勤勉工作，决不辜负党和人民重托。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202210/24085803ztrk.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">10月23日，刚刚在中国共产党第二十届中央委员会第一次全体会议上当选的中共中央总书记习近平和中共中央政治局常委李强、赵乐际、王沪宁、蔡奇、丁薛祥、李希在北京人民大会堂同中外记者亲切见面。（新华社记者 申宏/摄）</span></p><p style=\"text-align: left;\">习近平指出，经过全党全国各族人民共同努力，我们如期全面建成小康社会、实现了第一个百年奋斗目标。现在，我们正意气风发迈上全面建设社会主义现代化国家新征程，向第二个百年奋斗目标进军，以中国式现代化全面推进中华民族伟大复兴。</p><p style=\"text-align: left;\">习近平强调，新征程上，我们要始终保持昂扬奋进的精神状态。全面建设社会主义现代化国家寄托着中华民族的夙愿和期盼，凝结着中国人民的奋斗和汗水。中国式现代化是中国共产党和中国人民长期实践探索的成果，是一项伟大而艰巨的事业。惟其艰巨，所以伟大；惟其艰巨，更显荣光。为了这一事业，无数先辈筚路蓝缕、披荆斩棘，进行了艰苦卓绝的奋斗，我们心中永远铭记着他们的奉献和牺牲。我们要埋头苦干、担当作为，以更加强烈的历史主动精神推进马克思主义中国化时代化，不断谱写新时代中国特色社会主义新篇章，奋力实现中华民族伟大复兴的中国梦。</p><p style=\"text-align: left;\">习近平指出，新征程上，我们要始终坚持一切为了人民、一切依靠人民。一路走来，我们紧紧依靠人民交出了一份又一份载入史册的答卷。面向未来，我们仍然要依靠人民创造新的历史伟业。道阻且长，行则将至。前进道路上，无论是风高浪急还是惊涛骇浪，人民永远是我们最坚实的依托、最强大的底气。我们要始终与人民风雨同舟、与人民心心相印，想人民之所想，行人民之所嘱，不断把人民对美好生活的向往变为现实。</p><p style=\"text-align: left;\">习近平强调，新征程上，我们要始终推进党的自我革命。一个饱经沧桑而初心不改的党，才能基业常青；一个铸就辉煌仍勇于自我革命的党，才能无坚不摧。百年栉风沐雨、淬火成钢，特别是新时代10年革命性锻造，中国共产党更加坚强有力、更加充满活力。面对新征程上的新挑战新考验，我们必须高度警省，永远保持赶考的清醒和谨慎，驰而不息推进全面从严治党，使百年大党在自我革命中不断焕发蓬勃生机，始终成为中国人民最可靠、最坚强的主心骨。</p><p style=\"text-align: left;\">习近平指出，新征程上，我们要始终弘扬全人类共同价值。当今世界面临前所未有的挑战。我们历来主张，人类的前途命运应该由世界各国人民来把握和决定。只要共行天下大道，各国就能够和睦相处、合作共赢，携手创造世界的美好未来。我们将同各国人民一道，弘扬和平、发展、公平、正义、民主、自由的全人类共同价值，维护世界和平、促进世界发展，持续推动构建人类命运共同体。</p><p style=\"text-align: left;\">习近平强调，中国发展离不开世界，世界发展也需要中国。经过改革开放40多年不懈努力，我们创造了经济快速发展和社会长期稳定两大奇迹。现在，中国经济韧性强、潜力足、回旋余地广，长期向好的基本面不会改变。中国开放的大门只会越来越大。我们将坚定不移全面深化改革开放，坚定不移推动高质量发展，以自身发展为世界创造更多机遇。</p><p style=\"text-align: left;\">习近平指出，新征程是充满光荣和梦想的远征。蓝图已经绘就，号角已经吹响。我们要踔厉奋发、勇毅前行，努力创造更加灿烂的明天。</p><p style=\"text-align: left;\">习近平最后说，几天来，记者朋友们对大会进行了充分报道，向全世界传递中国声音、传播中国共产党的主张，使世界的目光注视中国。感谢你们的辛勤工作，大家辛苦了。欢迎大家多到中国各地走一走、看一看，客观真实向世界讲好中国故事，讲好中国共产党故事，讲好我们正在经历的新时代故事。</p><p style=\"text-align: left;\">12时30分，见面会结束。习近平等向中外记者挥手道别。全场再次响起热烈的掌声。</p><p style=\"text-align: left;\">中央广播电视总台和新华网进行了现场直播。</p>', '本站编辑', '', '2023-04-11 20:03:38');
INSERT INTO `articles` VALUES (19, 2, '热点资讯', '教育部召开党组扩大会传达学习2023年全国“两会”精神', '<p style=\"text-align: left;\">3月14日，教育部党组书记、部长怀进鹏主持召开党组扩大会，传达学习十四届全国人大一次会议和全国政协十四届一次会议精神。会议重点传达了“两会”期间习近平总书记重要讲话精神，政府工作报告的主要内容特别是关于教育的工作部署，全国人大常委会、全国政协常委会工作报告和“两高”报告关于教育的内容。会议梳理了“两会”期间人大代表、政协委员对教育的意见建议及媒体关注情况，对深入贯彻落实“两会”精神提出了明确要求。</p><p style=\"text-align: left;\">会议指出，今年是全面贯彻落实党的二十大精神的开局之年。全国“两会”胜利召开，把党的主张和人民意愿凝聚为国家意志，通过法定程序把党的二十大作出的决策部署转化为全国人民的实际行动，这对贯彻落实习近平新时代中国特色社会主义思想和党的二十大精神，动员全党全国各族人民为全面建设社会主义现代化国家、全面推进中华民族伟大复兴而团结奋斗，具有十分重要的意义。十四届全国人大一次会议选举产生了新一届国家领导人，习近平总书记以全票当选国家主席、中央军委主席，充分体现了人民对习近平总书记的衷心拥护和爱戴，充分展现了我们党和国家强大的凝聚力、向心力，充分激发了万众一心、团结奋斗的豪情壮志。“两会”期间，习近平总书记发表重要讲话，强调强国建设、民族复兴的接力棒历史地落在我们这一代人身上，强调牢牢把握高质量发展这个首要任务，强调正确引导民营经济健康发展高质量发展，强调巩固提高一体化国家战略体系和能力，强调有关教育工作的内容。今年的政府工作报告系统回顾总结了过去一年和五年我国经济社会发展取得的重大成就，提出了2023年工作总体要求、主要预期目标和八项重点工作，充分肯定过去一年和五年教育工作，并对今年教育工作提出了建议。</p><p style=\"text-align: left;\">会议强调，学习贯彻全国“两会”精神是当前一项重大政治任务。我们要坚持以习近平新时代中国特色社会主义思想为指导，把学习贯彻“两会”精神与学习贯彻习近平总书记关于教育的重要论述和党的二十大精神结合起来，深刻领悟“两个确立”的决定性意义，增强“四个意识”、坚定“四个自信”、做到“两个维护”，牢牢把握高质量发展这一首要任务，完整、准确、全面贯彻新发展理念，服务构建新发展格局，以强烈的政治责任感和历史使命感履行职责，切实把“两会”精神落到实处。</p><p style=\"text-align: left;\">会议要求，要坚持育人的根本在于立德，善用“大思政课”铸魂育人，以中华优秀传统文化教育涵养历史自信、文化自信，加快发展素质教育，培养德智体美劳全面发展的社会主义建设者和接班人。要围绕加快实现高水平科技自立自强，强化国家战略科技力量建设，全面提高人才自主培养质量，深化高等教育综合改革，统筹职业教育、高等教育、继续教育协同创新，着力建设世界重要人才中心和创新高地。要围绕人民幸福安康，加快义务教育优质均衡和城乡一体化，着力补齐教育发展短板，大力实施教育数字化战略行动，切实加强高校毕业生就业工作，不断促进教育发展成果更多更公平惠及全体人民。要以时不我待的精神状态砥砺奋进，增强政治能力，维护安全稳定，做到真抓实干，不断开创新时代教育工作新局面。要以高度的责任感对待建议提案办理工作，主动沟通联系、争取理解支持，抓好工作结合、提高办理实效, 把建议提案办理和年度重点工作相结合，和教育强国建设的目标和路径相结合, 注重解决实质性问题，推动工作取得实效。</p><p style=\"text-align: left;\">会议以视频形式召开。部党组成员，老部长，秘书局、机关各司局和驻部纪检监察组副司级以上干部、直属单位主要负责同志在主会场参会。直属单位其他领导班子成员在分会场参会。</p>', '教育部', '', '2023-04-11 20:04:42');
INSERT INTO `articles` VALUES (20, 2, '热点资讯', '李强总理出席记者会并回答中外记者提问', '<p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/14100151trci.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: center;\"><strong>3月13日，十四届全国人大一次会议在北京人民大会堂举行记者会，国务院总理李强应大会发言人王超的邀请出席记者会，并回答中外记者提问。国务院副总理丁薛祥、何立峰、张国清、刘国中参加。这是李强回答记者提问。新华社记者 刘彬 摄</strong></p><p style=\"text-align: left;\">新华社北京3月13日电 十四届全国人大一次会议13日上午在人民大会堂举行记者会，国务院总理李强应大会发言人王超的邀请出席记者会，并回答中外记者提问。国务院副总理丁薛祥、何立峰、张国清、刘国中参加。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/14100242z6cv.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: center;\"><strong>3月13日，十四届全国人大一次会议在北京人民大会堂举行记者会，国务院总理李强应大会发言人王超的邀请出席记者会，并回答中外记者提问。国务院副总理丁薛祥、何立峰、张国清、刘国中参加。新华社记者 李鑫 摄</strong></p><p style=\"text-align: left;\">记者会开始时，李强说，很高兴今天能够与各位媒体朋友在这里见面交流，首先要感谢大家为报道这次中国两会付出的辛劳。李强表示，我们衷心感谢党和人民的信任，也深知肩负的责任重大、使命光荣。我们一定在以习近平同志为核心的党中央坚强领导下，紧紧依靠广大人民，以奋发有为的精神状态和“时时放心不下”的责任意识，忠实履行宪法和法律赋予的职责，勇毅前行、廉洁奉公、鞠躬尽瘁、不辱使命。</p><p style=\"text-align: left;\">中央广播电视总台记者：首先向您和四位副总理履新表示祝贺。新一届政府任期的未来五年对于全面建设社会主义现代化国家来说是开局起步的关键时期，国内外非常关注新一届政府的工作。新一届政府的施政目标是什么？有哪些工作重点？将会怎样开展工作？</p><p style=\"text-align: left;\">李强：谢谢你的祝贺，谢谢大家对新一届政府工作的关心。</p><p style=\"text-align: left;\">去年十月份召开的党的二十大，对我们国家今后五年和更长时间的发展作了全面的战略部署，大家比较关心的重大问题都能从党的二十大报告中找到清晰的答案。</p><p style=\"text-align: left;\">新一届政府的工作，就是要把党中央的决策部署贯彻好、落实好，把党的二十大擘画的宏伟蓝图变成施工图，与全国人民一道，一步一个脚印把宏伟蓝图变成美好现实。</p><p style=\"text-align: left;\">今后工作中，有这么几个方面是我们要重点把握的：</p><p style=\"text-align: left;\">第一是牢固树立以人民为中心的发展思想。说到底，党和政府一切工作的宗旨就是为民造福。习近平总书记讲：“人民对美好生活的向往，就是我们的奋斗目标。”我们任何时候都必须始终牢记人民政府前面的“人民”这两个字，扎扎实实办好每一件民生实事。客观地讲，绝大部分老百姓不会天天盯着看GDP增长了多少，大家更在乎的是住房、就业、收入、教育、就医、生态环境等身边具体事。政府工作就是要贴近老百姓的实际感受去谋划、推进，真正做到民有所盼、政有所为。</p><p style=\"text-align: left;\">第二是集中力量推动高质量发展。这次两会期间，习近平总书记在参加江苏代表团审议时特别强调，要牢牢把握高质量发展这个首要任务。我国经济社会发展已经取得了巨大成就，经济总量稳居世界第二，但发展还不平衡、不充分。任何一个总量指标，分摊到14亿多的人口基数上，人均水平都比较有限。现在，我们的发展更多地只是解决“有没有”的问题，下一步需要更加重视解决“好不好”的问题，特别是提高科技创新能力、建设现代化产业体系、推动发展方式绿色转型等。总的来说，我们要完整、准确、全面贯彻新发展理念，加快构建新发展格局，着力推动高质量发展。</p><p style=\"text-align: left;\">第三是坚定不移深化改革开放。改革开放是决定当代中国命运的关键一招，我们在推进中国式现代化、实现第二个百年奋斗目标的历史进程中，还是要“吃改革饭、走开放路”。要坚持社会主义市场经济改革方向，坚持高水平对外开放，在深化改革开放中不断增强发展的动力和活力。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/141004367f1k.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: center;\"><strong>3月13日，十四届全国人大一次会议在北京人民大会堂举行记者会，国务院总理李强应大会发言人王超的邀请出席记者会，并回答中外记者提问。国务院副总理丁薛祥、何立峰、张国清、刘国中参加。新华社记者 刘彬 摄</strong></p><p style=\"text-align: left;\">美国消费者新闻与商业频道记者：今年是中国疫情过峰后的第一年，很多人预期经济将在短期内反弹，中国准备采取怎样的措施实现全年经济增长目标？中国经济在今年及接下来的五年将面临哪些有利和不利的因素？未来五年中国计划采取什么样的政策来支持可持续、高质量增长？近几年中国在应对债务、资本无序扩张等系统性风险方面的政策取得了哪些成效？</p><p style=\"text-align: left;\">李强：这位记者朋友很会抓机会，我记了一下，你一口气提了4个问题、涉及6方面内容。时间关系，我只能择要作些回答。</p><p style=\"text-align: left;\">今年世界经济形势总体不容乐观，不稳定、不确定、难预料因素比较多，如何稳增长对世界各国都是一个考验。今年，中国经济预期增长目标定为5%左右，这是我们综合考虑各方面因素后确定的。当然，中国的经济总量已经突破120万亿元，基数很高，加上今年的新挑战不少，要实现5%左右的增长，并不轻松，需要倍加努力。</p><p style=\"text-align: left;\">有关具体政策措施，去年中央经济工作会议已经作了全面部署，基本取向是坚持稳字当头、稳中求进，推动经济运行整体好转。稳，重点是稳增长、稳就业、稳物价；进，关键是在高质量发展上取得新进步。具体来讲，我想要特别做好几件事，或者说要打好这么几套组合拳：一是宏观政策的组合拳，二是扩大需求的组合拳，三是改革创新的组合拳，四是防范化解风险的组合拳。这些组合拳，都是有其具体内涵的，有的还会根据实践的需要不断充实、调整和完善。</p><p style=\"text-align: left;\">刚才，这位记者朋友问到了有利和不利因素问题。中国发展确实有很多优势条件，比如，市场规模巨大，产业体系完备，人力资源丰富，发展基础厚实等，更重要的是制度优势显著。说到困难，大家都有困难，今年我们的困难也不会少，但是，大家想一想，哪个时候、哪一年没有困难呢？我们从来都是在克服困难中不断实现新发展的。我们这一代中国人从小听得最多的故事就是大禹治水、愚公移山、精卫填海、夸父逐日等等，都很励志，讲的都是不怕困难、不畏艰险、勇于斗争、自强不息的精神，我们中国人不会被任何困难所压倒。</p><p style=\"text-align: left;\">从最近两个多月情况看，我国经济运行出现了企稳回升态势，一些国际组织也调高了今年中国经济增速的预期。对中国经济的前景，我想用8个字来概括，就是“长风破浪，未来可期”。对此，我充满信心。</p><p style=\"text-align: left;\">香港凤凰卫视、凤凰网记者：这几年，香港实现了由乱到治的根本性转折。香港稳定下来了，发展问题又摆在了眼前。随着世界政经局势和环境的变化，有评论认为现在港澳的竞争力弱化了。请问，您如何看待港澳未来的发展前景？中央政府下一步还会有哪些措施支持港澳发挥自己的优势和特点，实现更好的发展？</p><p style=\"text-align: left;\">李强：中央政府始终高度重视发挥香港、澳门的优势和特点。回归以来，在国家的支持下，香港国际金融、航运、贸易三大中心地位得到了巩固和提高，澳门作为旅游休闲中心享誉世界。</p><p style=\"text-align: left;\">近年来，受各种因素影响，香港、澳门经济发展遇到一些困难，但这是暂时的困难、发展中的困难。中央政府将全面准确、坚定不移贯彻“一国两制”方针，全力支持香港、澳门融入国家发展大局，全力支持香港、澳门发展经济、改善民生，全力支持香港、澳门提高国际竞争力。有祖国作坚强后盾、有“一国两制”的制度保障，香港、澳门的地位和作用只会加强、不会削弱。香港、澳门的明天一定会更加美好！</p><p style=\"text-align: left;\">新加坡联合早报记者：您早年当过温州市委书记，温州市以及您后来任职的浙江、江苏以及上海都是中国民营经济非常发达的地方，您对民营经济有很深的了解。请问您认为中国还需要采取哪些措施提振民企的信心、支持民企的发展？还有哪些地方做得不够、需要补齐？</p><p style=\"text-align: left;\">李强：大家一定都已关注到，这次两会期间，习近平总书记在看望全国政协委员时，就民营经济健康发展、高质量发展作了深刻阐述，引起热烈反响。广大民营企业家深受鼓舞、深受激励，都很振奋。</p><p style=\"text-align: left;\">我长期在民营经济比较发达的地方工作，经常有机会与民营企业家交流，对他们发展中的期盼和顾虑还是比较了解的。在这里，我主要想表达这么几层意思：</p><p style=\"text-align: left;\">第一，“两个毫不动摇”是我国基本经济制度的重要内容，是长久之策，过去没有变，以后更不会变。确实，去年有段时间，社会上有一些不正确的议论，使一些民营企业家内心感到忧虑。其实在发展民营经济这个问题上，党中央的方针政策一直是非常明确的。党的十九大、二十大和去年的中央经济工作会议，都作了强调。对此，我们是旗帜鲜明、坚定不移的。</p><p style=\"text-align: left;\">第二，民营经济的发展环境会越来越好，发展空间会越来越大。我们将在新起点上大力营造市场化、法治化、国际化营商环境，平等对待各类所有制企业，依法保护企业产权和企业家权益，促进各类经营主体公平竞争，支持民营企业发展壮大。从发展空间看，中国具有超大规模的市场需求，还有很多新领域新赛道有待开拓，都蕴藏着巨大的发展机遇。民营经济一定是大有可为的。</p><p style=\"text-align: left;\">第三，时代呼唤广大民营企业家谱写新的创业史。这也是我特别想说的一点。经济发展有其客观规律，也依赖客观条件，但更需要很强的主观能动性。希望民营企业家大力弘扬优秀企业家精神，坚定信心再出发。说到这里，我不由想起当年江浙等地发展个体私营经济、发展乡镇企业时创造的“四千”精神：走遍千山万水、说尽千言万语、想尽千方百计、吃尽千辛万苦。虽然现在创业的模式、形态发生了很大的变化，但是当时那样一种筚路蓝缕、披荆斩棘的创业精神，是永远需要的。我们各级领导干部要真诚关心、服务民营企业，构建亲清政商关系，带动全社会形成尊重创业者、尊重企业家的良好氛围。我相信，在新时代新征程上，广大民营企业家一定能谱写出更加辉煌的创业史。</p><p style=\"text-align: left;\">澎湃新闻记者：网民对于一些民生热点问题高度关注。今年我国就业形势依然严峻，请问政府将采取哪些措施稳就业呢？去年中国出现了多年未有的人口负增长，这是否意味着人口红利即将消失？今年会不会出台延迟退休政策？</p><p style=\"text-align: left;\">李强：我很愿意回应网民的关切，一有时间我也会上网，看看网民关注什么，有什么好的意见建议。</p><p style=\"text-align: left;\">刚才，你实际上提了三个问题。第一个问题，就业问题。就业是民生之本，解决就业问题，最根本的一条，还是要靠发展经济。具体工作中，我们将全面落实就业优先战略，进一步加大就业服务、技能培训等方面的政策支持力度，多措并举稳定和扩大就业岗位，支持和规范发展新就业形态。今年高校毕业生预计1158万，从就业看，有一定压力；但从发展看，注入的是蓬勃的活力。我们将进一步拓宽就业渠道，帮助年轻人通过劳动和奋斗，更好地实现自己的人生价值。</p><p style=\"text-align: left;\">第二个问题，人口负增长问题。我国人口增长由正转负，有人担心人口红利会不会就此消失。我看没那么简单。人口红利既要看总量、更要看质量，既要看人口、更要看人才。我国有近9亿劳动力，每年新增劳动力都超过1500万，人力资源丰富仍然是中国的突出优势。更重要的是，我国接受高等教育的人口已超过2.4亿，新增劳动力平均受教育年限达到14年。可以说，我们的“人口红利”没有消失，“人才红利”正在形成，发展动力依旧强劲。当然，我们对人口增减可能带来的问题还要作深入的分析研判，积极应对。</p><p style=\"text-align: left;\">至于延迟退休政策问题，我们将认真研究、充分论证，在适当时候稳妥推出。</p><p style=\"text-align: left;\">乌兹别克斯坦人民言论报记者：国际上有些人质疑，相比于其他国家，中国在防疫上实施了两年多的严控，是否确有必要？应对未来可能出现的新一波疫情，中国作了哪些准备？</p><p style=\"text-align: left;\">李强：疫情三年，中国人民在中国共产党的坚强领导下，同心抗疫，现在已经取得重大决定性胜利。三年多来，我们始终坚持人民至上、生命至上，坚持科学精准防控，因时因势优化调整防控措施。在病毒致病力较强的阶段，我们果断实施“乙类甲管”，有效保护了人民生命安全和身体健康，也为疫苗和药物研制、疫苗接种普及等争取了宝贵时间。随着病毒致病力减弱和我国防治能力提高，我们对防控措施作了一系列优化调整，适时进行了转段，实施了“乙类乙管”。中国这样一个人口众多、发展不平衡的大国，用不到两个月的时间实现了疫情防控平稳转段，较快恢复了经济社会正常秩序，是很了不起的。实践证明，我国疫情防控的各项策略措施是完全正确的，防控成效是巨大的。</p><p style=\"text-align: left;\">人类与病毒的斗争是一个长期历史过程。当前新冠疫情传播的风险仍然存在，我们将及时研判，做好预测预警，并制定完善不同情景下的疫情应对预案，不断加强医疗卫生服务体系建设，加快疫苗和药物的研发，加强与国际社会的合作和协调，共同维护人类健康福祉。</p><p style=\"text-align: left;\">台湾联合报记者：大陆方面表示，将始终关爱、尊重、造福台湾同胞。随着大陆疫情防控措施的调整和优化，两岸民众都希望能够尽快全面恢复两岸的正常交流和往来。请问大陆方面在推动两岸经济文化合作交流以及人员往来方面有何规划？</p><p style=\"text-align: left;\">李强：两岸同胞是一家人，血浓于水，打断骨头连着筋。我们将在一个中国原则和“九二共识”的基础上，继续推动两岸经济文化交流合作，要让更多台胞、台企来大陆，不仅愿意来，而且能融得进，有好的发展。</p><p style=\"text-align: left;\">当前，早日实现两岸同胞正常往来，恢复各领域常态合作，是大家的共同期望，需要共同努力。</p><p style=\"text-align: left;\">人民日报记者：全面建设社会主义现代化国家，最艰巨最繁重的任务仍然在农村。请问本届政府在推进乡村振兴上有哪些考虑？对保障中国粮食安全有哪些举措？</p><p style=\"text-align: left;\">李强：中国是一个农业大国，现在还有近5亿人常住在农村。没有农业农村现代化，社会主义现代化是不全面的。在去年底召开的中央农村工作会议上，习近平总书记对建设农业强国、做好“三农”工作、推进乡村振兴作出了全面部署。当前我们就是要按照习近平总书记的要求，抓好各项任务的落实。</p><p style=\"text-align: left;\">就乡村振兴来讲，下一步在具体工作中，我感到要突出三个关键词：一是全面。不光是发展经济，而是要全面彰显乡村的经济价值、生态价值、社会价值、文化价值。二是特色。中国地域辽阔，“十里不同风、百里不同俗”，要因地制宜，打造各具特色的乡村风貌，保护和传承好地域文化、乡土文化，不能千村一面。三是改革。要通过深化农村改革来促进乡村振兴，广大农民是乡村振兴的主体，必须充分调动他们的积极性，要让他们积极参与改革，并更好分享改革发展成果。</p><p style=\"text-align: left;\">刚才你还提到粮食安全问题。我国粮食产量连续8年超过1.3万亿斤，总体上粮食安全是有保障的，我们将抓住耕地和种子“两个要害”，不断提高粮食生产能力。我这里特别想跟农民朋友们说句话：政府所有支持粮食生产的政策只增不减，鼓励大家多种粮，确保14亿多中国人的饭碗牢牢端在自己手中。</p><p style=\"text-align: left;\">中阿卫视记者：当前，地缘政治摩擦和去全球化加剧，中美关系紧张对立，虽然中国一直强调坚定不移推进改革开放，但外资外企依然难以安心经营，有的开始考虑撤离。请问，中国的对外开放政策会有变化吗？您如何看待当前的中美关系及其改善前景？</p><p style=\"text-align: left;\">李强：今年是中国改革开放45周年，改革开放发展了中国，也影响了世界。从我掌握的情况看，绝大多数外资企业依然看好在华发展前景，去年，中国实际使用外资1890多亿美元，创历史新高，比三年前增加了近500亿美元。这说明，中国依然是全球投资高地。</p><p style=\"text-align: left;\">对外开放是我们的基本国策，无论外部形势怎么变，我们都将坚定不移向前推进。我这里特别想说一说中国国际进口博览会，这是中国主动向世界开放市场、共享发展机遇的重大举措，已连续举办5届，即使在疫情之下也没有中断，去年有127个国家和地区的2800多家企业参展。进博会的举办充分说明，开放的中国大市场是各国企业发展的大机遇。</p><p style=\"text-align: left;\">今年，我们还将对接高标准国际经贸规则，进一步扩大开放。中国开放的大门会越开越大、环境会越来越好、服务会越来越优。开放的中国欢迎大家来投资兴业。</p><p style=\"text-align: left;\">有关中美关系的具体问题，秦刚外长在前几天的记者会上已经作了阐述，我不再多作重复。当前重要的是，把习近平主席同拜登总统去年11月份会晤时达成的一系列重要共识，转化为实际政策和具体行动。</p><p style=\"text-align: left;\">我知道，这几年在美国国内有些人在炒作两国“脱钩”的论调，有时还很热，但我不知道到底有多少人能真正从这种炒作中受益。据中方统计，去年，中美贸易额近7600亿美元，创下历史新高。中美两国经济你中有我、我中有你，彼此都从对方的发展中受益。我去年大部分时间是在上海工作，接触了不少包括美资在内的外企高管，他们都告诉我看好上海、看好中国，希望从合作中实现共赢。这些都表明，中美可以合作、也应该合作。中美合作大有可为。围堵、打压对谁都没有好处。</p><p style=\"text-align: left;\">新华社记者：新一届政府面临着艰巨繁重的任务，各方面也寄予很高的期许，这对于政府的履职能力、工作作风都提出了新的要求。请问新一届政府在加强自身建设方面有哪些考虑？</p><p style=\"text-align: left;\">李强：确实，如你所说，新的使命任务对我们提出了新的更高要求。我们打算，以新一轮政府机构改革为契机，切实加强政府自身建设，进一步转变职能、提升效能、改进作风。突出抓四件事：</p><p style=\"text-align: left;\">一是大兴调查研究之风。最近党中央已提出明确要求，国务院要带头认真抓好落实。我长期在地方工作，有一个很深的感受：坐在办公室碰到的都是问题，深入基层看到的全是办法。高手在民间。我们要推动各级干部多到一线去，问需于民、问计于民，向人民群众学习，真正帮助基层解决实际问题。特别是长期在大机关工作的年轻同志，要深入基层、心入基层，更多地接地气。</p><p style=\"text-align: left;\">二是扎实推进依法行政。政府工作必须在法律框架内进行，所有行政行为都要于法有据。要进一步加强法治政府建设，不断提高运用法治思维、法治方式解决问题的能力。</p><p style=\"text-align: left;\">三是提高创造性执行能力。各级政府部门和公务人员，都要有服务意识、发展意识，特别是在履行审批、管理职能时，不能光踩刹车、不踩油门；不能尽设路障、不设路标；凡事要多作“应不应该办”的价值判断，不能简单地只作“可不可以办”的技术判断。要坚决反对一切形式主义、官僚主义，真正做有创造力的执行者。</p><p style=\"text-align: left;\">四是坚决守住廉洁底线。我们将始终以严的标准、严的措施抓好廉洁政府建设，对一切腐败行为一定要零容忍。每一位政府工作人员都要自觉接受监督，真正做到忠诚、干净、担当。</p><p style=\"text-align: left;\">记者会在人民大会堂三楼金色大厅举行，历时约80分钟，参加采访的中外记者约500人。</p>', '新华社', '', '2023-04-11 20:05:43');
INSERT INTO `articles` VALUES (21, 2, '热点资讯', '李克强作的政府工作报告（摘登）', '<p style=\"text-align: left;\">啊啊</p><p style=\"text-align: left;\">过去一年是党和国家历史上极为重要的一年</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，2022年是党和国家历史上极为重要的一年。党的二十大胜利召开，描绘了全面建设社会主义现代化国家的宏伟蓝图。面对风高浪急的国际环境和艰巨繁重的国内改革发展稳定任务，以习近平同志为核心的党中央团结带领全国各族人民迎难而上，全面落实疫情要防住、经济要稳住、发展要安全的要求，加大宏观调控力度，实现了经济平稳运行、发展质量稳步提升、社会大局保持稳定，我国发展取得来之极为不易的新成就。</p><p style=\"text-align: left;\">过去一年，我国经济发展遇到疫情等国内外多重超预期因素冲击。在党中央坚强领导下，我们高效统筹疫情防控和经济社会发展，根据病毒变化和防疫形势，优化调整疫情防控措施。面对经济新的下行压力，果断应对、及时调控，动用近年储备的政策工具，靠前实施既定政策举措，坚定不移推进供给侧结构性改革，出台实施稳经济一揽子政策和接续措施，部署稳住经济大盘工作，加强对地方落实政策的督导服务，支持各地挖掘政策潜力，支持经济大省勇挑大梁，突出稳增长稳就业稳物价，推动经济企稳回升。全年国内生产总值增长3%，城镇新增就业1206万人，年末城镇调查失业率降到5.5%，居民消费价格上涨2%。货物进出口总额增长7.7%。财政赤字率控制在2.8%，中央财政收支符合预算、支出略有结余。国际收支保持平衡，人民币汇率在全球主要货币中表现相对稳健。粮食产量1.37万亿斤，增产74亿斤。生态环境质量持续改善。</p><p style=\"text-align: left;\">针对企业生产经营困难加剧，加大纾困支持力度。受疫情等因素冲击，不少企业和个体工商户遇到特殊困难。全年增值税留抵退税超过2.4万亿元，新增减税降费超过1万亿元，缓税缓费7500多亿元。为有力支持减税降费政策落实，中央对地方转移支付大幅增加。引导金融机构增加信贷投放，降低融资成本，新发放企业贷款平均利率降至有统计以来最低水平，对普惠小微贷款阶段性减息。用改革办法激发市场活力。量大面广的中小微企业和个体工商户普遍受益。</p><p style=\"text-align: left;\">针对有效需求不足的突出矛盾，多措并举扩投资促消费稳外贸。去年终端消费直接受到冲击，投资也受到影响。提前实施部分“十四五”规划重大工程项目，加快地方政府专项债券发行使用，依法盘活用好专项债务结存限额，分两期投放政策性开发性金融工具7400亿元，为重大项目建设补充资本金。运用专项再贷款、财政贴息等政策，支持重点领域设备更新改造。采取联合办公、地方承诺等办法，提高项目审批效率。全年基础设施、制造业投资分别增长9.4%、9.1%，带动固定资产投资增长5.1%，一定程度弥补了消费收缩缺口。发展消费新业态新模式，采取减免车辆购置税等措施促进汽车消费，新能源汽车销量增长93.4%，开展绿色智能家电、绿色建材下乡，社会消费品零售总额保持基本稳定。出台金融支持措施，支持刚性和改善性住房需求，扎实推进保交楼稳民生工作。帮助外贸企业解决原材料、用工、物流等难题，提升港口集疏运效率，及时回应和解决外资企业关切，货物进出口好于预期，实际使用外资稳定增长。</p><p style=\"text-align: left;\">针对就业压力凸显，强化稳岗扩就业政策支持。去年城镇调查失业率一度明显攀升。财税、金融、投资等政策更加注重稳就业。对困难行业企业社保费实施缓缴，大幅提高失业保险基金稳岗返还比例，增加稳岗扩岗补助。落实担保贷款、租金减免等创业支持政策。突出做好高校毕业生就业工作，开展就业困难人员专项帮扶。在重点工程建设中推广以工代赈。脱贫人口务工规模超过3200万人、实现稳中有增。就业形势总体保持稳定。</p><p style=\"text-align: left;\">针对全球通胀高企带来的影响，以粮食和能源为重点做好保供稳价。去年全球通胀达到40多年来新高，国内价格稳定面临较大压力。有效应对洪涝、干旱等严重自然灾害，不误农时抢抓粮食播种和收获，督促和协调农机通行，保障农事活动有序开展，分三批向种粮农民发放农资补贴，保障粮食丰收和重要农产品稳定供给。发挥煤炭主体能源作用，增加煤炭先进产能，加大对发电供热企业支持力度，保障能源正常供应。在全球高通胀的背景下，我国物价保持较低水平，尤为难得。</p><p style=\"text-align: left;\">针对部分群众生活困难增多，强化基本民生保障。阶段性扩大低保等社会保障政策覆盖面，将更多困难群体纳入保障范围。延续实施失业保险保障扩围政策，共向1000多万失业人员发放失业保险待遇。向更多低收入群众发放价格补贴，约6700万人受益。免除经济困难高校毕业生2022年国家助学贷款利息并允许延期还本。做好因疫因灾遇困群众临时救助工作，切实兜住民生底线。</p><p style=\"text-align: left;\">经过艰苦努力，当前消费需求、市场流通、工业生产、企业预期等明显向好，经济增长正在企稳向上，我国经济有巨大潜力和发展动力。</p><p style=\"text-align: left;\">过去五年极不寻常、极不平凡</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，过去五年极不寻常、极不平凡。在以习近平同志为核心的党中央坚强领导下，我们经受了世界变局加快演变、新冠疫情冲击、国内经济下行等多重考验，如期打赢脱贫攻坚战，如期全面建成小康社会，实现第一个百年奋斗目标，开启向第二个百年奋斗目标进军新征程。各地区各部门坚持以习近平新时代中国特色社会主义思想为指导，深刻领悟“两个确立”的决定性意义，增强“四个意识”、坚定“四个自信”、做到“两个维护”，全面贯彻党的十九大和十九届历次全会精神，深入贯彻党的二十大精神，坚持稳中求进工作总基调，完整、准确、全面贯彻新发展理念，构建新发展格局，推动高质量发展，统筹发展和安全，我国经济社会发展取得举世瞩目的重大成就。</p><p style=\"text-align: left;\">——经济发展再上新台阶。国内生产总值增加到121万亿元，五年年均增长5.2%，十年增加近70万亿元、年均增长6.2%，在高基数基础上实现了中高速增长、迈向高质量发展。财政收入增加到20.4万亿元。粮食产量连年稳定在1.3万亿斤以上。工业增加值突破40万亿元。城镇新增就业年均1270多万人。外汇储备稳定在3万亿美元以上。我国经济实力明显提升。</p><p style=\"text-align: left;\">——脱贫攻坚任务胜利完成。经过八年持续努力，近1亿农村贫困人口实现脱贫，全国832个贫困县全部摘帽，960多万贫困人口实现易地搬迁，历史性地解决了绝对贫困问题。</p><p style=\"text-align: left;\">——科技创新成果丰硕。构建新型举国体制，组建国家实验室，分批推进全国重点实验室重组。一些关键核心技术攻关取得新突破，载人航天、探月探火、深海深地探测、超级计算机、卫星导航、量子信息、核电技术、大飞机制造、人工智能等领域创新成果不断涌现。全社会研发经费投入强度从2.1%提高到2.5%以上，科技进步贡献率提高到60%以上，创新支撑发展能力不断增强。</p><p style=\"text-align: left;\">——经济结构进一步优化。高技术制造业、装备制造业增加值年均分别增长10.6%、7.9%，数字经济不断壮大，新产业新业态新模式增加值占国内生产总值的比重达到17%以上。区域协调发展战略、区域重大战略深入实施。常住人口城镇化率从60.2%提高到65.2%，乡村振兴战略全面实施。经济发展新动能加快成长。</p><p style=\"text-align: left;\">——基础设施更加完善。一批防汛抗旱、引水调水等重大水利工程开工建设。高速铁路运营里程从2.5万公里增加到4.2万公里，高速公路里程从13.6万公里增加到17.7万公里。新建改建农村公路125万公里。新增机场容量4亿人次。发电装机容量增长40%以上。所有地级市实现千兆光网覆盖，所有行政村实现通宽带。</p><p style=\"text-align: left;\">——改革开放持续深化。全面深化改革开放推动构建新发展格局，供给侧结构性改革深入实施，简政放权、放管结合、优化服务改革不断深化，营商环境明显改善。共建“一带一路”扎实推进。推动区域全面经济伙伴关系协定（RCEP）生效实施，建成全球最大自由贸易区。货物进出口总额年均增长8.6%，突破40万亿元、连续多年居世界首位，吸引外资和对外投资居世界前列。</p><p style=\"text-align: left;\">——生态环境明显改善。单位国内生产总值能耗下降8.1%、二氧化碳排放下降14.1%。地级及以上城市细颗粒物（PM2.5）平均浓度下降27.5%，重污染天数下降超过五成，全国地表水优良水体比例由67.9%上升到87.9%。设立首批国家公园，建立各级各类自然保护地9000多处。美丽中国建设迈出重大步伐。</p><p style=\"text-align: left;\">——人民生活水平不断提高。居民收入增长与经济增长基本同步。居民消费价格年均上涨2.1%。新增劳动力平均受教育年限从13.5年提高到14年。基本养老保险参保人数增加1.4亿、覆盖10.5亿人，基本医保水平稳步提高。多年累计改造棚户区住房4200多万套，上亿人出棚进楼、实现安居。</p><p style=\"text-align: left;\">经过多年精心筹办，成功举办了简约、安全、精彩的北京冬奥会、冬残奥会，为促进奥林匹克运动发展、促进世界人民团结友谊作出重要贡献。</p><p style=\"text-align: left;\">全国人民坚忍不拔，共同抵御疫情重大挑战</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，新冠疫情发生三年多来，以习近平同志为核心的党中央始终坚持人民至上、生命至上，强化医疗资源和物资保障，全力救治新冠患者，有效保护人民群众生命安全和身体健康，因时因势优化调整防控政策措施，全国人民坚忍不拔，取得重大决定性胜利。在极不平凡的抗疫历程中，各地区各部门各单位做了大量工作，各行各业共克时艰，广大医务人员不畏艰辛，特别是亿万人民克服多重困难、付出和奉献、都十分不易，大家共同抵御疫情重大挑战，面对尚未结束的疫情，仍在不断巩固统筹疫情防控和经济社会发展成果。</p><p style=\"text-align: left;\">五年来创新宏观调控，保持经济运行在合理区间</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，五年来，我们深入贯彻以习近平同志为核心的党中央决策部署，创新宏观调控，保持经济运行在合理区间。面对贸易保护主义抬头、疫情冲击等接踵而来的严峻挑战，创新宏观调控方式，不过度依赖投资，统筹运用财政货币等政策，重点支持市场主体纾困发展。把年度主要预期目标作为一个有机整体来把握，加强区间调控、定向调控、相机调控、精准调控，既果断加大力度，又不搞“大水漫灌”、透支未来，持续做好“六稳”、“六保”工作，强化保居民就业、保基本民生、保市场主体、保粮食能源安全、保产业链供应链稳定、保基层运转，推动经济爬坡过坎、持续前行。</p><p style=\"text-align: left;\">坚持实施积极的财政政策。合理把握赤字规模，五年总体赤字率控制在3%以内，政府负债率控制在50%左右。不断优化支出结构，教育科技、生态环保、基本民生等重点领域得到有力保障。实施大规模减税降费政策，制度性安排与阶段性措施相结合，疫情发生后减税降费力度进一步加大，成为应对冲击的关键举措。减税降费公平普惠、高效直达，五年累计减税5.4万亿元、降费2.8万亿元，既帮助企业渡过难关、留得青山，也放水养鱼、涵养税源，年均新增涉税企业和个体工商户等超过1100万户，各年度中央财政收入预算都顺利完成，考虑留抵退税因素，全国财政收入十年接近翻一番。各级政府坚持过紧日子，严控一般性支出，中央部门带头压减支出，盘活存量资金和闲置资产，腾出的资金千方百计惠企裕民，全国财政支出70%以上用于民生。</p><p style=\"text-align: left;\">坚持实施稳健的货币政策。根据形势变化灵活把握政策力度，保持流动性合理充裕，用好降准、再贷款等政策工具，加大对实体经济的有效支持，缓解中小微企业融资难融资贵等问题。人民币汇率在合理均衡水平上弹性增强、保持基本稳定。完全化解了本世纪初形成的14486亿元金融改革历史成本。运用市场化法治化方式，精准处置一批大型企业集团风险，平稳化解高风险中小金融机构风险，大型金融机构健康发展，金融体系稳健运行，守住了不发生系统性风险的底线。</p><p style=\"text-align: left;\">强化就业优先政策导向。把稳就业作为经济运行在合理区间的关键指标。着力促进市场化社会化就业，加大对企业稳岗扩岗支持力度。将养老保险单位缴费比例从20%降至16%，同时充实全国社保基金，储备规模从1.8万亿元增加到2.5万亿元以上。实施失业保险基金稳岗返还、留工培训补助等政策。持续推进大众创业万众创新，连续举办8届全国双创活动周、超过5.2亿人次参与，鼓励以创业带动就业，新就业形态和灵活就业成为就业增收的重要渠道。做好高校毕业生、退役军人、农民工等群体就业工作。</p><p style=\"text-align: left;\">保持物价总体平稳。在应对冲击中没有持续大幅增加赤字规模，也没有超发货币，为物价稳定创造了宏观条件。下大气力抓农业生产，强化产销衔接和储备调节，确保粮食和生猪、蔬菜等稳定供应，及时解决煤炭电力供应紧张问题，满足民生和生产用能需求，保障交通物流畅通。加强市场监管，维护正常价格秩序。十年来我国居民消费价格涨幅稳定在2%左右的较低水平。</p><p style=\"text-align: left;\">五年来如期打赢脱贫攻坚战，巩固拓展脱贫攻坚成果</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，五年来，我们如期打赢脱贫攻坚战，巩固拓展脱贫攻坚成果。坚持精准扶贫，聚焦“三区三州”等深度贫困地区，强化政策倾斜支持，优先保障脱贫攻坚资金投入，对脱贫难度大的县和村挂牌督战。深入实施产业、就业、生态、教育、健康、社会保障等帮扶，加强易地搬迁后续扶持，重点解决“两不愁三保障”问题，脱贫群众不愁吃、不愁穿，义务教育、基本医疗、住房安全有保障，饮水安全也有了保障。贫困地区农村居民收入明显增加，生产生活条件显著改善。</p><p style=\"text-align: left;\">推动巩固拓展脱贫攻坚成果同乡村振兴有效衔接。保持过渡期内主要帮扶政策总体稳定，严格落实“四个不摘”要求，建立健全防止返贫动态监测和帮扶机制，有力应对疫情、灾情等不利影响，确保不发生规模性返贫。确定并集中支持160个国家乡村振兴重点帮扶县，加大对易地搬迁集中安置区等重点区域支持力度，坚持并完善东西部协作、对口支援、定点帮扶等机制，选派用好医疗、教育“组团式”帮扶干部人才和科技特派员，推动脱贫地区加快发展和群众稳定增收。</p><p style=\"text-align: left;\">五年来聚焦重点领域和关键环节深化改革，更大激发市场活力和社会创造力</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，五年来，我们聚焦重点领域和关键环节深化改革，更大激发市场活力和社会创造力。坚持社会主义市场经济改革方向，处理好政府和市场的关系，使市场在资源配置中起决定性作用，更好发挥政府作用，推动有效市场和有为政府更好结合。</p><p style=\"text-align: left;\">持续推进政府职能转变。完成国务院及地方政府机构改革。加快建设全国统一大市场，建设高标准市场体系，营造市场化法治化国际化营商环境。持之以恒推进触动政府自身利益的改革。进一步简政放权，放宽市场准入，全面实施市场准入负面清单制度，清单管理措施比制度建立之初压减64%，将行政许可事项全部纳入清单管理。多年来取消和下放行政许可事项1000多项，中央政府层面核准投资项目压减90%以上，工业产品生产许可证从60类减少到10类，工程建设项目全流程审批时间压缩到不超过120个工作日。改革商事制度，推行“证照分离”改革，企业开办时间从一个月以上压缩到目前的平均4个工作日以内，实行中小微企业简易注销制度。坚持放管结合，加强事中事后监管，严格落实监管责任，推行“双随机、一公开”等方式加强公正监管，规范行使行政裁量权。加强反垄断和反不正当竞争，全面落实公平竞争审查制度，改革反垄断执法体制。依法规范和引导资本健康发展，依法坚决管控资本无序扩张。不断优化服务，推进政务服务集成办理，压减各类证明事项，加快数字政府建设，90%以上的政务服务实现网上可办，户籍证明、社保转接等200多项群众经常办理事项实现跨省通办。改革给人们经商办企业更多便利和空间，去年底企业数量超过5200万户、个体工商户超过1.1亿户，市场主体总量超过1.6亿户、是十年前的3倍，发展内生动力明显增强。</p><p style=\"text-align: left;\">促进多种所有制经济共同发展。坚持和完善社会主义基本经济制度，坚持“两个毫不动摇”。完成国企改革三年行动任务，健全现代企业制度，推动国企聚焦主责主业优化重组、提质增效。促进民营企业健康发展，破除各种隐性壁垒，一视同仁给予政策支持，提振民间投资信心。完善产权保护制度，保护企业家合法权益，弘扬企业家精神。</p><p style=\"text-align: left;\">推进财税金融体制改革。深化预算管理体制改革，加大预算公开力度，推进中央与地方财政事权和支出责任划分改革，完善地方政府债务管理体系，构建综合与分类相结合的个人所得税制。推动金融监管体制改革，统筹推进中小银行补充资本和改革化险，推进股票发行注册制改革，完善资本市场基础制度，加强金融稳定法治建设。</p><p style=\"text-align: left;\">五年来深入实施创新驱动发展战略，推动产业结构优化升级</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，五年来，我们深入实施创新驱动发展战略，推动产业结构优化升级。深化供给侧结构性改革，完善国家创新体系，推进科技自立自强，紧紧依靠创新提升实体经济发展水平，不断培育壮大发展新动能，有效应对外部打压遏制。</p><p style=\"text-align: left;\">增强科技创新引领作用。强化国家战略科技力量，实施一批科技创新重大项目，加强关键核心技术攻关。发挥好高校、科研院所作用，支持新型研发机构发展。推进国际和区域科技创新中心建设，布局建设综合性国家科学中心。支持基础研究和应用基础研究，全国基础研究经费五年增长1倍。改革科研项目和经费管理制度，赋予科研单位和科研人员更大自主权。加强知识产权保护，激发创新动力。促进国际科技交流合作。通过市场化机制激励企业创新，不断提高企业研发费用加计扣除比例，将制造业企业、科技型中小企业分别从50%、75%提高至100%，并阶段性扩大到所有适用行业，各类支持创新的税收优惠政策年度规模已超过万亿元。创设支持创新的金融政策工具，引导创业投资等发展。企业研发投入保持两位数增长，一大批创新企业脱颖而出。</p><p style=\"text-align: left;\">推动产业向中高端迈进。严格执行环保、质量、安全等法规标准，淘汰落后产能。开展重点产业强链补链行动。启动一批产业基础再造工程。鼓励企业加快设备更新和技术改造，将固定资产加速折旧优惠政策扩大至全部制造业。推动高端装备、生物医药、新能源汽车、光伏、风电等新兴产业加快发展。持续推进网络提速降费，发展“互联网+”。移动互联网用户数增加到14.5亿户。支持工业互联网发展，有力促进了制造业数字化智能化。专精特新中小企业达7万多家。促进平台经济健康持续发展，发挥其带动就业创业、拓展消费市场、创新生产模式等作用。发展研发设计、现代物流、检验检测认证等生产性服务业。加强全面质量管理和质量基础设施建设。中国制造的品质和竞争力不断提升。</p><p style=\"text-align: left;\">五年来扩大国内有效需求，推进区域协调发展和新型城镇化</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，五年来，我们扩大国内有效需求，推进区域协调发展和新型城镇化。围绕构建新发展格局，立足超大规模市场优势，坚持实施扩大内需战略，培育更多经济增长动力源。</p><p style=\"text-align: left;\">着力扩大消费和有效投资。面对需求不足甚至出现收缩，推动消费尽快恢复。多渠道促进居民增收，提高中低收入群体收入。支持汽车、家电等大宗消费，汽车保有量突破3亿辆、增长46.7%。推动线上线下消费深度融合，实物商品网上零售额占社会消费品零售总额的比重从15.8%提高到27.2%。发展城市社区便民商业，完善农村快递物流配送体系。帮扶旅游业发展。围绕补短板、调结构、增后劲扩大有效投资。创新投融资体制机制，预算内投资引导和撬动社会投资成倍增加，增加地方政府专项债券额度，重点支持交通、水利、能源、信息等基础设施和民生工程建设，鼓励社会资本参与建设运营，调动民间投资积极性。</p><p style=\"text-align: left;\">增强区域发展平衡性协调性。统筹推进西部大开发、东北全面振兴、中部地区崛起、东部率先发展，中西部地区经济增速总体高于东部地区。加大对革命老区、民族地区、边疆地区的支持力度，中央财政对相关地区转移支付资金比五年前增长66.8%。推进京津冀协同发展、长江经济带发展、长三角一体化发展，推动黄河流域生态保护和高质量发展。高标准高质量建设雄安新区。发展海洋经济。支持经济困难地区发展，促进资源型地区转型发展，鼓励有条件地区更大发挥带动作用，推动形成更多新的增长极增长带。</p><p style=\"text-align: left;\">持续推进以人为核心的新型城镇化。完善城市特别是县城功能，增强综合承载能力。分类放宽或取消城镇落户限制，十年1.4亿农村人口在城镇落户。有序发展城市群和都市圈，促进大中小城市协调发展。推动成渝地区双城经济圈建设。坚持房子是用来住的、不是用来炒的定位，建立实施房地产长效机制，扩大保障性住房供给，推进长租房市场建设，稳地价、稳房价、稳预期，因城施策促进房地产市场健康发展。加强城市基础设施建设，轨道交通运营里程从4500多公里增加到近1万公里，排水管道从63万公里增加到89万公里。改造城镇老旧小区16.7万个，惠及2900多万家庭。</p><p style=\"text-align: left;\">五年来保障国家粮食安全，大力实施乡村振兴战略</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，五年来，我们保障国家粮食安全，大力实施乡村振兴战略。完善强农惠农政策，持续抓紧抓好农业生产，加快推进农业农村现代化。</p><p style=\"text-align: left;\">提升农业综合生产能力。稳定和扩大粮食播种面积，扩种大豆油料，优化生产结构布局，提高单产和品质。完善粮食生产支持政策，稳定种粮农民补贴，合理确定稻谷、小麦最低收购价，加大对产粮大县奖励力度，健全政策性农业保险制度。加强耕地保护，实施黑土地保护工程，完善水利设施，新建高标准农田4.56亿亩。推进国家粮食安全产业带建设。加快种业、农机等科技创新和推广应用，农作物耕种收综合机械化率从67%提高到73%。全面落实粮食安全党政同责，强化粮食和重要农产品稳产保供，始终不懈地把14亿多中国人的饭碗牢牢端在自己手中。</p><p style=\"text-align: left;\">扎实推进农村改革发展。巩固和完善农村基本经营制度，完成承包地确权登记颁证和农村集体产权制度改革阶段性任务，稳步推进多种形式适度规模经营，抓好家庭农场和农民合作社发展，加快发展农业社会化服务。启动乡村建设行动，持续整治提升农村人居环境，加强水电路气信邮等基础设施建设，实现符合条件的乡镇和建制村通硬化路、通客车，农村自来水普及率从80%提高到87%，多年累计改造农村危房2400多万户。深化供销合作社、集体林权、农垦等改革。立足特色资源发展乡村产业，促进农民就业创业增收。持续强化农民工工资拖欠治理，出台实施保障农民工工资支付条例，严厉打击恶意拖欠行为。</p><p style=\"text-align: left;\">五年来坚定扩大对外开放，深化互利共赢的国际经贸合作</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，五年来，我们坚定扩大对外开放，深化互利共赢的国际经贸合作。面对外部环境变化，实行更加积极主动的开放战略，以高水平开放更有力促改革促发展。</p><p style=\"text-align: left;\">推动进出口稳中提质。加大出口退税、信保、信贷等政策支持力度，企业出口退税办理时间压缩至6个工作日以内。优化外汇服务。发展外贸新业态，新设152个跨境电商综试区，支持建设一批海外仓。发挥进博会、广交会、服贸会、消博会等重大展会作用。推进通关便利化，进口、出口通关时间分别压减67%和92%，进出口环节合规成本明显下降。关税总水平从9.8%降至7.4%。全面深化服务贸易创新发展试点，推出跨境服务贸易负面清单。</p><p style=\"text-align: left;\">积极有效利用外资。出台外商投资法实施条例，不断优化外商投资环境。持续放宽外资市场准入，全国和自由贸易试验区负面清单条数分别压减51%、72%，制造业领域基本全面放开，金融等服务业开放水平不断提升。已设21个自由贸易试验区，海南自由贸易港建设稳步推进。各地创新方式加强外资促进服务，加大招商引资和项目对接力度。</p><p style=\"text-align: left;\">推动高质量共建“一带一路”。坚持共商共建共享，遵循市场原则和国际通行规则，实施一批互联互通和产能合作项目，对沿线国家货物进出口额年均增长13.4%，各领域交流合作不断深化。引导对外投资健康有序发展，加强境外风险防控。新签和升级6个自贸协定，与自贸伙伴货物进出口额占比从26%提升至35%左右。坚定维护多边贸易体制，反对贸易保护主义，稳妥应对经贸摩擦，促进贸易和投资自由化便利化。</p><p style=\"text-align: left;\">五年来加强生态环境保护，促进绿色低碳发展</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，五年来，我们加强生态环境保护，促进绿色低碳发展。坚持绿水青山就是金山银山的理念，健全生态文明制度体系，处理好发展和保护的关系，不断提升可持续发展能力。</p><p style=\"text-align: left;\">加强污染治理和生态建设。坚持精准治污、科学治污、依法治污，深入推进污染防治攻坚。注重多污染物协同治理和区域联防联控，地级及以上城市空气质量优良天数比例达86.5%、上升4个百分点。基本消除地级及以上城市黑臭水体，推进重要河湖、近岸海域污染防治。加大土壤污染风险防控和修复力度，强化固体废物和新污染物治理。全面划定耕地和永久基本农田保护红线、生态保护红线和城镇开发边界。坚持山水林田湖草沙一体化保护和系统治理，实施一批重大生态工程，全面推行河湖长制、林长制。深入实施长江流域重点水域十年禁渔。加强生物多样性保护。完善生态保护补偿制度。森林覆盖率、湿地保护率分别达到24%、50%以上，水土流失、荒漠化、沙化土地面积分别净减少10.6万、3.8万、3.3万平方公里。人民群众越来越多享受到蓝天白云、绿水青山。</p><p style=\"text-align: left;\">稳步推进节能降碳。统筹能源安全稳定供应和绿色低碳发展，科学有序推进碳达峰碳中和。优化能源结构，实现超低排放的煤电机组超过10.5亿千瓦，可再生能源装机规模由6.5亿千瓦增至12亿千瓦以上，清洁能源消费占比由20.8%上升到25%以上。</p><p style=\"text-align: left;\">五年来切实保障和改善民生，加快社会事业发展</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，五年来，我们切实保障和改善民生，加快社会事业发展。贯彻以人民为中心的发展思想，持续增加民生投入，着力保基本、兜底线、促公平，提升公共服务水平，在发展中不断增进民生福祉。</p><p style=\"text-align: left;\">促进教育公平和质量提升。财政性教育经费占国内生产总值比例每年都保持在4%以上，学生人均经费投入大幅增加。持续加强农村义务教育薄弱环节建设，基本消除城镇大班额，推动解决进城务工人员子女入学问题，减轻义务教育阶段学生负担。持续实施营养改善计划，每年惠及3700多万学生。保障教师特别是乡村教师工资待遇。多渠道增加幼儿园供给。普及高中阶段教育目标实现。职业教育适应性增强，职业院校办学条件持续改善。高校招生持续加大对中西部地区和农村地区倾斜力度。大幅提高经济困难高校学生国家助学贷款额度。深入实施“强基计划”和基础学科拔尖人才培养计划，建设288个基础学科拔尖学生培养基地，接续推进世界一流大学和一流学科建设。</p><p style=\"text-align: left;\">提升医疗卫生服务能力。深入推进健康中国行动，深化医药卫生体制改革，把基本医疗卫生制度作为公共产品向全民提供，进一步缓解群众看病难、看病贵问题。持续提高基本医保和大病保险水平，城乡居民医保人均财政补助标准从450元提高到610元。将更多群众急需药品纳入医保报销范围。住院和门诊费用实现跨省直接结算，惠及5700多万人次。推行药品和医用耗材集中带量采购，降低费用负担超过4000亿元。设置13个国家医学中心，布局建设76个国家区域医疗中心。全面推开公立医院综合改革，持续提升县域医疗卫生服务能力，完善分级诊疗体系。促进中医药传承创新发展。基本公共卫生服务经费人均财政补助标准从50元提高到84元。改革完善疾病预防控制体系，组建国家疾病预防控制局，健全重大疫情防控救治和应急物资保障体系，努力保障人民健康。</p><p style=\"text-align: left;\">加强社会保障和服务。建立基本养老保险基金中央调剂制度，连续上调退休人员基本养老金，提高城乡居民基础养老金最低标准，稳步提升城乡低保、优待抚恤、失业和工伤保障等标准。积极应对人口老龄化，推动老龄事业和养老产业发展。发展社区和居家养老服务，在税费、用房、水电气价格等方面给予政策支持。推进医养结合。实施三孩生育政策及配套支持措施。完善退役军人管理保障制度，提高保障水平。加强妇女、儿童权益保障。健全残疾人关爱服务体系。健全社会救助体系。</p><p style=\"text-align: left;\">丰富人民群众精神文化生活。培育和践行社会主义核心价值观。深化群众性精神文明创建。发展新闻出版、广播影视、文学艺术、哲学社会科学和档案等事业，加强智库建设。扎实推进媒体深度融合。提升国际传播效能。加强和创新互联网内容建设。弘扬中华优秀传统文化，加强文物和文化遗产保护传承。实施文化惠民工程，公共图书馆、博物馆、美术馆、文化馆站向社会免费开放。深入推进全民阅读。支持文化产业发展。加强国家科普能力建设。体育健儿勇创佳绩，全民健身广泛开展。</p><p style=\"text-align: left;\">五年来推进政府依法履职和治理创新，保持社会大局稳定</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，五年来，我们推进政府依法履职和治理创新，保持社会大局稳定。推动完善法律法规和规章制度，提请全国人大常委会审议法律议案50件，制定修订行政法规180件次。依法接受同级人大及其常委会的监督，自觉接受人民政协的民主监督，主动接受社会和舆论监督。认真办理人大代表建议和政协委员提案。加强审计、统计监督。持续深化政务公开。开展国务院大督查。支持工会、共青团、妇联等群团组织更好发挥作用。</p><p style=\"text-align: left;\">加强和创新社会治理。推动市域社会治理现代化，完善基层治理，优化社区服务。支持社会组织、人道救助、社会工作、志愿服务、公益慈善等健康发展。深入推进信访积案化解。推进社会信用体系建设。完善公共法律服务体系。严格食品药品疫苗监管。开展安全生产专项整治。改革和加强应急管理，提高防灾减灾救灾能力，做好洪涝干旱、森林草原火灾、地质灾害、地震等防御和气象服务。深入推进国家安全体系和能力建设。持续加强社会治安综合治理，严厉打击各类违法犯罪，开展扫黑除恶专项斗争，依法严惩黑恶势力及其“保护伞”，平安中国、法治中国建设取得新进展。</p><p style=\"text-align: left;\">五年来认真贯彻落实党中央全面从严治党战略部署</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，五年来，各级政府认真贯彻落实党中央全面从严治党战略部署，扎实开展“不忘初心、牢记使命”主题教育和党史学习教育，弘扬伟大建党精神，严格落实中央八项规定精神，持之以恒纠治“四风”，重点纠治形式主义、官僚主义，“三公”经费大幅压减。严厉惩处违规建设楼堂馆所和偷税逃税等行为。加强廉洁政府建设。政府工作人员自觉接受法律监督、监察监督和人民监督。</p><p style=\"text-align: left;\">做好经济社会发展工作，没有捷径，实干为要。五年来，坚持以习近平新时代中国特色社会主义思想为指导，全面贯彻党的基本理论、基本路线、基本方略。坚持以经济建设为中心，着力推动高质量发展，事不畏难、行不避艰，要求以实干践行承诺，凝心聚力抓发展；以民之所望为施政所向，始终把人民放在心中最高位置，一切以人民利益为重，仔细倾听群众呼声，深入了解群众冷暖，着力解决人民群众急难愁盼问题；坚持实事求是，尊重客观规律，坚决反对空谈浮夸、做表面文章、搞形象工程甚至盲目蛮干；以改革的办法、锲而不舍的精神解难题、激活力，激励敢于担当，对庸政懒政者问责。尊重人民群众首创精神，充分调动各方面积极性，进而汇聚起推动发展的强大力量。</p><p style=\"text-align: left;\">民族、宗教、侨务、国防、港澳台、外交工作五年成就</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，过去五年，民族、宗教、侨务等工作创新完善。巩固和发展平等团结互助和谐的社会主义民族关系，民族团结进步呈现新气象。贯彻党的宗教工作基本方针，推进我国宗教中国化逐步深入。持续做好侨务工作，充分发挥海外侨胞在参与祖国现代化建设中的独特优势和重要作用。</p><p style=\"text-align: left;\">坚持党对人民军队的绝对领导，国防和军队建设取得一系列新的重大成就、发生一系列重大变革。人民军队深入推进政治建军、改革强军、科技强军、人才强军、依法治军，深入推进练兵备战，现代化水平和实战能力显著提升。</p><p style=\"text-align: left;\">港澳台工作取得新进展。依照宪法和基本法有效实施对特别行政区的全面管治权，制定实施香港特别行政区维护国家安全法，落实“爱国者治港”、“爱国者治澳”原则，推动香港进入由乱到治走向由治及兴的新阶段。深入推进粤港澳大湾区建设，支持港澳发展经济、改善民生、防控疫情、保持稳定。贯彻新时代党解决台湾问题的总体方略，坚决开展反分裂、反干涉重大斗争，持续推动两岸关系和平发展。</p><p style=\"text-align: left;\">中国特色大国外交全面推进。习近平主席等党和国家领导人出访多国，通过线上和线下方式出席二十国集团领导人峰会、亚太经合组织领导人非正式会议、联合国成立75周年系列高级别会议、东亚合作领导人系列会议、中欧领导人会晤等一系列重大外交活动。成功举办上合组织青岛峰会、金砖国家领导人会晤、全球发展高层对话会、“一带一路”国际合作高峰论坛、中非合作论坛北京峰会等多场重大主场外交活动。坚持敢于斗争、善于斗争，坚决维护我国主权、安全、发展利益。积极拓展全球伙伴关系，致力于建设开放型世界经济，维护多边主义，推动构建人类命运共同体。</p><p style=\"text-align: left;\">直面问题挑战，尽心竭力改进政府工作，不负人民重托</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，这些年我国发展取得的成就，是以习近平同志为核心的党中央坚强领导的结果，是习近平新时代中国特色社会主义思想科学指引的结果，是全党全军全国各族人民团结奋斗的结果。</p><p style=\"text-align: left;\">在看到发展成就的同时，我们也清醒认识到，我国是一个发展中大国，仍处于社会主义初级阶段，发展不平衡不充分问题仍然突出。当前发展面临诸多困难挑战。外部环境不确定性加大，全球通胀仍处于高位，世界经济和贸易增长动能减弱，外部打压遏制不断上升。国内经济增长企稳向上基础尚需巩固，需求不足仍是突出矛盾，民间投资和民营企业预期不稳，中小微企业和个体工商户有不少困难，稳就业任务艰巨，一些基层财政收支矛盾较大。房地产市场风险隐患较多，一些中小金融机构风险暴露。发展仍有不少体制机制障碍。科技创新能力还不强。生态环境保护任重道远。防灾减灾等城乡基础设施仍有明显薄弱环节。一些民生领域存在不少短板。形式主义、官僚主义现象仍较突出。一些领域、行业、地方腐败现象时有发生。人民群众对政府工作还有一些意见和建议应予重视。要直面问题挑战，尽心竭力改进政府工作，不负人民重托。</p><p style=\"text-align: left;\">今年经济社会发展总体要求</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，今年是全面贯彻党的二十大精神的开局之年。做好政府工作，要在以习近平同志为核心的党中央坚强领导下，以习近平新时代中国特色社会主义思想为指导，全面贯彻落实党的二十大精神，按照中央经济工作会议部署，扎实推进中国式现代化，坚持稳中求进工作总基调，完整、准确、全面贯彻新发展理念，加快构建新发展格局，着力推动高质量发展，更好统筹国内国际两个大局，更好统筹疫情防控和经济社会发展，更好统筹发展和安全，全面深化改革开放，大力提振市场信心，把实施扩大内需战略同深化供给侧结构性改革有机结合起来，突出做好稳增长、稳就业、稳物价工作，有效防范化解重大风险，推动经济运行整体好转，实现质的有效提升和量的合理增长，持续改善民生，保持社会大局稳定，为全面建设社会主义现代化国家开好局起好步。</p><p style=\"text-align: left;\">今年发展主要预期目标</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中指出，今年发展主要预期目标是：国内生产总值增长5%左右；城镇新增就业1200万人左右，城镇调查失业率5.5%左右；居民消费价格涨幅3%左右；居民收入增长与经济增长基本同步；进出口促稳提质，国际收支基本平衡；粮食产量保持在1.3万亿斤以上；单位国内生产总值能耗和主要污染物排放量继续下降，重点控制化石能源消费，生态环境质量稳定改善。</p><p style=\"text-align: left;\">要坚持稳字当头、稳中求进，保持政策连续性针对性，加强各类政策协调配合，形成共促高质量发展合力。积极的财政政策要加力提效。赤字率拟按3%安排。完善税费优惠政策，对现行减税降费、退税缓税等措施，该延续的延续，该优化的优化。做好基层“三保”工作。稳健的货币政策要精准有力。保持广义货币供应量和社会融资规模增速同名义经济增速基本匹配，支持实体经济发展。保持人民币汇率在合理均衡水平上的基本稳定。产业政策要发展和安全并举。促进传统产业改造升级，培育壮大战略性新兴产业，着力补强产业链薄弱环节。科技政策要聚焦自立自强。完善新型举国体制，发挥好政府在关键核心技术攻关中的组织作用，突出企业科技创新主体地位。社会政策要兜牢民生底线。落实落细就业优先政策，把促进青年特别是高校毕业生就业工作摆在更加突出的位置，切实保障好基本民生。</p><p style=\"text-align: left;\">当前我国疫情防控已进入“乙类乙管”常态化防控阶段，要更加科学、精准、高效做好防控工作，围绕保健康、防重症，重点做好老年人、儿童、患基础性疾病群体的疫情防控和医疗救治，推进疫苗迭代升级和新药研制，切实保障群众就医用药需求，守护好人民生命安全和身体健康。</p><p style=\"text-align: left;\">今年工作重点</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中表示，今年是政府换届之年，前面报告的经济社会发展多领域、各方面工作，今后还需不懈努力，下面简述几项重点。</p><p style=\"text-align: left;\">（一）着力扩大国内需求。把恢复和扩大消费摆在优先位置。多渠道增加城乡居民收入。稳定大宗消费，推动生活服务消费恢复。政府投资和政策激励要有效带动全社会投资，今年拟安排地方政府专项债券3.8万亿元，加快实施“十四五”重大工程，实施城市更新行动，促进区域优势互补、各展其长，鼓励和吸引更多民间资本参与国家重大工程和补短板项目建设，激发民间投资活力。</p><p style=\"text-align: left;\">（二）加快建设现代化产业体系。围绕制造业重点产业链，集中优质资源合力推进关键核心技术攻关。加强重要能源、矿产资源国内勘探开发和增储上产。加快传统产业和中小企业数字化转型，着力提升高端化、智能化、绿色化水平。加快前沿技术研发和应用推广。完善现代物流体系。大力发展数字经济，提升常态化监管水平，支持平台经济发展。</p><p style=\"text-align: left;\">（三）切实落实“两个毫不动摇”。深化国资国企改革，提高国企核心竞争力。坚持分类改革方向，处理好国企经济责任和社会责任关系，完善中国特色国有企业现代公司治理。依法保护民营企业产权和企业家权益，鼓励支持民营经济和民营企业发展壮大，支持中小微企业和个体工商户发展，构建亲清政商关系。</p><p style=\"text-align: left;\">（四）更大力度吸引和利用外资。扩大市场准入，加大现代服务业领域开放力度。落实好外资企业国民待遇。积极推动加入全面与进步跨太平洋伙伴关系协定（CPTPP）等高标准经贸协议，主动对照相关规则、规制、管理、标准，稳步扩大制度型开放。继续发挥进出口对经济的支撑作用。做好外资企业服务工作，推动外资标志性项目落地建设。</p><p style=\"text-align: left;\">（五）有效防范化解重大经济金融风险。深化金融体制改革，完善金融监管，压实各方责任，防止形成区域性、系统性金融风险。有效防范化解优质头部房企风险，改善资产负债状况，防止无序扩张，促进房地产业平稳发展。防范化解地方政府债务风险，优化债务期限结构，降低利息负担，遏制增量、化解存量。</p><p style=\"text-align: left;\">（六）稳定粮食生产和推进乡村振兴。稳定粮食播种面积，抓好油料生产，实施新一轮千亿斤粮食产能提升行动。完善农资保供稳价应对机制。加强农田水利和高标准农田等基础设施建设。深入实施种业振兴行动。强化农业科技和装备支撑。发展乡村特色产业，拓宽农民增收致富渠道。巩固拓展脱贫攻坚成果，坚决防止出现规模性返贫。</p><p style=\"text-align: left;\">（七）推动发展方式绿色转型。深入推进环境污染防治。加强城乡环境基础设施建设，持续实施重要生态系统保护和修复重大工程。推进煤炭清洁高效利用和技术研发，加快建设新型能源体系。完善支持绿色发展的政策，发展循环经济，推进资源节约集约利用，推动重点领域节能降碳，持续打好蓝天、碧水、净土保卫战。</p><p style=\"text-align: left;\">（八）保障基本民生和发展社会事业。加强住房保障体系建设，支持刚性和改善性住房需求，解决好新市民、青年人等住房问题。推进义务教育优质均衡发展和城乡一体化，大力发展职业教育，推进高等教育创新。推动优质医疗资源扩容下沉和区域均衡布局。加强养老服务保障，完善生育支持政策体系。保障妇女、儿童、老年人、残疾人合法权益。做好军人军属、退役军人和其他优抚对象优待抚恤工作。繁荣发展文化事业和产业。提升社会治理效能。强化安全生产监管和防灾减灾救灾。全面贯彻总体国家安全观，建设更高水平的平安中国。</p><p style=\"text-align: left;\">今年民族、宗教、侨务、国防、港澳台、外交工作</p><p style=\"text-align: left;\">新华社北京3月5日电 &nbsp;国务院总理李克强5日在政府工作报告中表示，我们要以铸牢中华民族共同体意识为主线，坚持和完善民族区域自治制度，促进各民族共同团结奋斗、共同繁荣发展。坚持党的宗教工作基本方针，坚持我国宗教中国化方向，积极引导宗教与社会主义社会相适应。加强和改进侨务工作，汇聚起海内外中华儿女同心奋斗、共创辉煌的强大力量。</p><p style=\"text-align: left;\">我们要深入贯彻习近平强军思想，贯彻新时代军事战略方针，围绕实现建军一百年奋斗目标，边斗争、边备战、边建设，完成好党和人民赋予的各项任务。全面加强练兵备战，创新军事战略指导，大抓实战化军事训练，统筹抓好各方向各领域军事斗争。全面加强军事治理，巩固拓展国防和军队改革成果，加强重大任务战建备统筹，加快实施国防发展重大工程。巩固提高一体化国家战略体系和能力，加强国防科技工业能力建设。各级政府要大力支持国防和军队建设，深入开展“双拥”活动，合力谱写军政军民团结新篇章。</p><p style=\"text-align: left;\">我们要全面准确、坚定不移贯彻“一国两制”、“港人治港”、“澳人治澳”、高度自治的方针，坚持依法治港治澳，维护宪法和基本法确定的特别行政区宪制秩序，落实“爱国者治港”、“爱国者治澳”原则。支持港澳发展经济、改善民生，保持香港、澳门长期繁荣稳定。</p><p style=\"text-align: left;\">我们要坚持贯彻新时代党解决台湾问题的总体方略，坚持一个中国原则和“九二共识”，坚定反“独”促统，推动两岸关系和平发展，推进祖国和平统一进程。两岸同胞血脉相连，要促进两岸经济文化交流合作，完善增进台湾同胞福祉的制度和政策，推动两岸共同弘扬中华文化，同心共创复兴伟业。</p><p style=\"text-align: left;\">我们要坚定奉行独立自主的和平外交政策，坚定不移走和平发展道路，坚持在和平共处五项原则基础上同各国发展友好合作，坚定奉行互利共赢的开放战略，始终做世界和平的建设者、全球发展的贡献者、国际秩序的维护者。中国愿同国际社会一道落实全球发展倡议、全球安全倡议，弘扬全人类共同价值，携手推动构建人类命运共同体，维护世界和平和地区稳定。</p><p style=\"text-align: left;\">奋斗铸就辉煌，实干赢得未来。我们要更加紧密地团结在以习近平同志为核心的党中央周围，高举中国特色社会主义伟大旗帜，以习近平新时代中国特色社会主义思想为指导，全面贯彻党的二十大精神，砥砺前行，推动经济社会持续健康发展，为全面建设社会主义现代化国家、全面推进中华民族伟大复兴，为把我国建设成为富强民主文明和谐美丽的社会主义现代化强国不懈奋斗！</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\"><br></p>', '新华社', '', '2023-04-11 20:07:05');
INSERT INTO `articles` VALUES (22, 2, '热点资讯', '第二届中国基础教育论坛暨中国教育学会第三十四次学术年会召开', '<p style=\"text-align: left;\">2月11日，第二届中国基础教育论坛暨中国教育学会第三十四次学术年会在北京召开。中央教育工作领导小组秘书组组长、教育部党组书记、部长怀进鹏出席开幕式并讲话。教育部党组成员、副部长王嘉毅与中国教育学会会长朱之文共同向160个国家乡村振兴重点帮扶县捐赠2023年《中国基础教育》和《中国教育学刊》两份刊物。</p><p style=\"text-align: left;\">怀进鹏强调，党的二十大报告对“实施科教兴国战略，强化现代化建设人才支撑”作出专章部署，提出办好人民满意教育的总体要求和战略部署，充分体现了党中央对教育强国和作为“国之大者”的基础教育事业的高度重视和殷切期待，为基础教育改革发展指明了方向。我们要始终将立德树人作为根本任务，大力促进教育公平，加快构建高质量教育体系，团结奋斗谱写教育事业改革发展新篇章。一是在办好人民满意的教育上下功夫，深入贯彻以人民为中心的发展思想，在公平上持续用力。二是在遵循教育规律、全面提升育人质量上下功夫，深化新课程改革、教育教学改革、教育评价改革。三是在着力加强教师队伍质量建设上下功夫，从战略高度认识教师工作的极端重要性，把加强教师队伍建设作为教育事业发展最重要的基础工作来抓。四是在持续深化经费保障上下功夫，健全财政教育投入机制，不断优化经费支出结构，不断提高人民群众对教育的满意度。广大基础教育一线校（园）长和老师要不忘初心、牢记使命，学为人师、行为世范，面向全体、因材施教，守正创新、锐意改革，始终忠诚于党和人民的教育事业，培养能够担当民族复兴大任的时代新人。</p><p style=\"text-align: left;\">怀进鹏表示，中国教育学会作为我国教育系统成立最早、覆盖人群最多、影响面最广的教育学术组织和国家平台型教育智库，是党和政府联系基础教育广大教师、校（园）长和专家学者的桥梁和纽带，是推动我国基础教育改革发展和国际交流合作的重要力量。中国教育学会要坚持以习近平新时代中国特色社会主义思想为指导，深入学习贯彻党的二十大精神，发挥组织优势、平台优势、专家优势和资源优势，坚持团结、服务、引领基础教育战线，努力成为教师之家、校（园）长之家、专家之家，成为全国基础教育工作者的精神家园，成为当代教育家的成长摇篮，为实现基础教育高质量发展、服务教育强国持续贡献力量。</p><p style=\"text-align: left;\">论坛以“学习贯彻党的二十大精神 加快建设高质量教育体系——基础教育在行动”为主题，为期两天，以“线下+线上”相结合的方式进行，集中展示中国基础教育改革发展前沿进展、学术成果与实践探索。各省、自治区、直辖市、计划单列市教育学会负责人，中国教育学会各分支机构、实验区负责人以及来自全国各地教育行政部门负责人和中小学（幼儿园）校（园）长、教师等分别在主会场和分会场参加会议。</p><p style=\"text-align: left;\"><br></p>', '教育部', '', '2023-04-11 20:07:49');
INSERT INTO `articles` VALUES (23, 3, '法律常识', '民法典基础知识（十）', '<p style=\"text-align: left;\"><strong>一、什么是不可抗力？</strong><br></p><p style=\"text-align: left;\">《民法典》第180条规定，因不可抗力不能履行民事义务的，不承担民事责任。法律另有规定的，依照其规定。不可抗力是不能预见、不能避免且不能克服的客观情况。</p><p style=\"text-align: left;\">所谓不可抗力，是指独立于人的行为之外，并且不受当事人的意思所支配的现象，包括地震、海啸等自然现象以及战争等社会现象。在法律上，不可抗力通常属于法定的免责事由，产生行为人被免责的法律后果。</p><p style=\"text-align: left;\"><strong>二、因见义勇为而受伤的，由谁承担民事责任？</strong></p><p style=\"text-align: left;\">《民法典》第183条规定，因保护他人民事权益使自己受到损害的，由侵权人承担民事责任，受益人可以给予适当补偿。没有侵权人、侵权人逃逸或者无力承担民事责任，受害人请求补偿的，受益人应当给予适当补偿。</p><p style=\"text-align: left;\">《民法典》保护见义勇为的行为，因保护他人民事权益而使自己受到损害且侵权人逃逸，受害人请求补偿的，受益人应当给予适当补偿。</p>', '本站编辑', '', '2023-04-11 20:09:55');
INSERT INTO `articles` VALUES (24, 3, '法律常识', '民法典基础知识（九）', '<p style=\"text-align: left;\"><strong>一、为了公共利益的需要，依照法律规定的权限和程序进行征地时是否应给予补偿？</strong><br></p><p style=\"text-align: left;\">《民法典》第117条规定，为了公共利益的需要，依照法律规定的权限和程序征收、征用不动产或者动产的，应当给予公平、合理的补偿。<br></p><p style=\"text-align: left;\">为了公共利益的需要，国家相关机关可以依照法律规定的权限和程序对不动产进行征收或征用，但是应当给予不动产权利人公平、合理的补偿。<strong><br></strong></p><p style=\"text-align: left;\"><strong>二、法律保护网络虚拟财产吗？</strong><br></p><p style=\"text-align: left;\">《民法典》第127条规定，法律对数据、网络虚拟财产的保护有规定的，依照其规定。<br></p><p style=\"text-align: left;\">网络虚拟财产具有财产的特性，其被侵犯时依法应当受到保护。近年来，随着互联网的发展，网络游戏日渐盛行。网络游戏账号、网络游戏装备等也在玩家的时间、精力、金钱投入上有了使用价值和交换价值，具备了财产属性。根据《民法典》的规定，民事主体的财产权受法律平等保护。当民事主体所有的数据、网络虚拟财产遭到非法侵犯时，法律同样提供保护。</p>', '本站编辑', '', '2023-04-11 20:10:32');
INSERT INTO `articles` VALUES (25, 3, '法律常识', '民法典基础知识（八）', '<p style=\"text-align: left;\"><strong>一、自然人享有哪些民事权利？</strong><br></p><p style=\"text-align: left;\">《民法典》第109条规定，自然人的人身自由、人格尊严受法律保护。第110条第1款规定，自然人享有生命权、身体权、健康权、姓名权、肖像权、名誉权、荣誉权、隐私权、婚姻自主权等权利。第113条规定，民事主体的财产权利受法律平等保护。<br></p><p style=\"text-align: left;\"><strong>二、个人信息是否受到法律保护？</strong></p><p style=\"text-align: left;\">《民法典》第111条规定，自然人的个人信息受法律保护。任何组织或者个人需要获取他人个人信息的，应当依法取得并确保信息安全，不得非法收集、使用、加工、传输他人个人信息，不得非法买卖、提供或者公开他人个人信息。</p>', '本站编辑', '', '2023-04-11 20:11:42');
INSERT INTO `articles` VALUES (26, 3, '法律常识', '民法典基础知识（七）', '<p style=\"text-align: left;\"><strong>一、具有完全民事行为能力的成年人能协商确定监护人吗？</strong><br></p><p style=\"text-align: left;\">《民法典》第33条规定，具有完全民事行为能力的成年人，可以与其近亲属、其他愿意承担监护人的个人或者组织事先协商，以书面形式确定自己的监护人，在自己丧失或者部分丧失民事行为能力时，由该监护人履行监护职责。<br></p><p style=\"text-align: left;\">随着养老问题越来越受到重视，《民法典》完善了监护制度，规定成年人可在具备民事行为能力时协商确定自己的监护人。</p><p style=\"text-align: left;\"><strong>二、村民委员会能够作为民事主体对外签订合同吗？</strong><br></p><p style=\"text-align: left;\">《民法典》第101条第1款规定，居民委员会、村民委员会具有基层群众性自治组织法人资格，可以从事为履行职能所需要的民事活动。<br></p><p style=\"text-align: left;\">为了适应社会主义新农村的建设和发展，各地有不少村子引入公司运营机制，建立了新型农村集体经济组织。《民法典》规定，居民委员会、村民委员会具有基层群众性自治组织法人资格，可以从事为履行职能所需要的民事活动。</p>', '本站编辑', '', '2023-04-11 20:12:23');
INSERT INTO `articles` VALUES (27, 3, '法律常识', '民法典基础知识（六）', '<p style=\"text-align: left;\"><strong>一、怎么判断未成年人是否为限制民事行为能力人？</strong><br></p><p style=\"text-align: left;\">限制民事行为能力，又称为不完全民事行为能力，是指自然人部分独立地，或者说在一定范围内具有民事行为能力。<br></p><p style=\"text-align: left;\">《民法典》第19条规定，八周岁以上的未成年人为限制民事行为能力人，实施民事法律行为由其法定代理人代理或者经其法定代理人同意、追认；但是，可以独立实施纯获利益的民事法律行为或者与其年龄、智力相适应的民事法律行为。<br></p><p style=\"text-align: left;\">随着社会发展和互联网的普及，现在的未成年人越来越聪明，心智成熟得越来越早。《民法典》规定，八周岁以上的未成年人为限制民事行为能力人，可以独立实施纯获利益的民事法律行为或者与其年龄、智力相适应的民事法律行为。<br></p><p style=\"text-align: left;\"><strong>二、八周岁的未成年人能用压岁钱买玩具吗？</strong><br></p><p style=\"text-align: left;\">八周岁以上的未成年人，可以从事与自己年龄、智力相适应的活动，比如去超市购买价值较小的生活用品、学习用品、玩具等。但这并不意味着他们可以做任何事，例如花光自己甚至家人的钱去打赏主播、私自拿家里的大额钱款去购买网络游戏装备等，此类行为就必须经过家长同意或者追认才能发生效力。<br><br></p>', '本站编辑', '', '2023-04-11 20:13:09');
INSERT INTO `articles` VALUES (28, 4, '法律法规', '中华人民共和国妇女权益保障法', '<p style=\"text-align: center;\">中华人民共和国妇女权益保障法</p><p style=\"text-align: left;\">（1992年4月3日第七届全国人民代表大会第五次会议通过　根据2005年8月28日第十届全国人民代表大会常务委员会第十七次会议《关于修改〈中华人民共和国妇女权益保障法〉的决定》第一次修正　根据2018年10月26日第十三届全国人民代表大会常务委员会第六次会议《关于修改〈中华人民共和国野生动物保护法〉等十五部法律的决定》第二次修正　2022年10月30日第十三届全国人民代表大会常务委员会第三十七次会议修订）</p><p style=\"text-align: left;\">目　　录</p><p style=\"text-align: left;\">第一章　总　　则</p><p style=\"text-align: left;\">第二章　政治权利</p><p style=\"text-align: left;\">第三章　人身和人格权益</p><p style=\"text-align: left;\">第四章　文化教育权益</p><p style=\"text-align: left;\">第五章　劳动和社会保障权益</p><p style=\"text-align: left;\">第六章　财产权益</p><p style=\"text-align: left;\">第七章　婚姻家庭权益</p><p style=\"text-align: left;\">第八章　救济措施</p><p style=\"text-align: left;\">第九章　法律责任</p><p style=\"text-align: left;\">第十章　附　　则</p><p style=\"text-align: left;\">第一章　总　　则</p><p style=\"text-align: left;\">第一条　为了保障妇女的合法权益，促进男女平等和妇女全面发展，充分发挥妇女在全面建设社会主义现代化国家中的作用，弘扬社会主义核心价值观，根据宪法，制定本法。</p><p style=\"text-align: left;\">第二条　男女平等是国家的基本国策。妇女在政治的、经济的、文化的、社会的和家庭的生活等各方面享有同男子平等的权利。</p><p style=\"text-align: left;\">国家采取必要措施，促进男女平等，消除对妇女一切形式的歧视，禁止排斥、限制妇女依法享有和行使各项权益。</p><p style=\"text-align: left;\">国家保护妇女依法享有的特殊权益。</p><p style=\"text-align: left;\">第三条　坚持中国共产党对妇女权益保障工作的领导，建立政府主导、各方协同、社会参与的保障妇女权益工作机制。</p><p style=\"text-align: left;\">各级人民政府应当重视和加强妇女权益的保障工作。</p><p style=\"text-align: left;\">县级以上人民政府负责妇女儿童工作的机构，负责组织、协调、指导、督促有关部门做好妇女权益的保障工作。</p><p style=\"text-align: left;\">县级以上人民政府有关部门在各自的职责范围内做好妇女权益的保障工作。</p><p style=\"text-align: left;\">第四条　保障妇女的合法权益是全社会的共同责任。国家机关、社会团体、企业事业单位、基层群众性自治组织以及其他组织和个人，应当依法保障妇女的权益。</p><p style=\"text-align: left;\">国家采取有效措施，为妇女依法行使权利提供必要的条件。</p><p style=\"text-align: left;\">第五条　国务院制定和组织实施中国妇女发展纲要，将其纳入国民经济和社会发展规划，保障和促进妇女在各领域的全面发展。</p><p style=\"text-align: left;\">县级以上地方各级人民政府根据中国妇女发展纲要，制定和组织实施本行政区域的妇女发展规划，将其纳入国民经济和社会发展规划。</p><p style=\"text-align: left;\">县级以上人民政府应当将妇女权益保障所需经费列入本级预算。</p><p style=\"text-align: left;\">第六条　中华全国妇女联合会和地方各级妇女联合会依照法律和中华全国妇女联合会章程，代表和维护各族各界妇女的利益，做好维护妇女权益、促进男女平等和妇女全面发展的工作。</p><p style=\"text-align: left;\">工会、共产主义青年团、残疾人联合会等群团组织应当在各自的工作范围内，做好维护妇女权益的工作。</p><p style=\"text-align: left;\">第七条　国家鼓励妇女自尊、自信、自立、自强，运用法律维护自身合法权益。</p><p style=\"text-align: left;\">妇女应当遵守国家法律，尊重社会公德、职业道德和家庭美德，履行法律所规定的义务。</p><p style=\"text-align: left;\">第八条　有关机关制定或者修改涉及妇女权益的法律、法规、规章和其他规范性文件，应当听取妇女联合会的意见，充分考虑妇女的特殊权益，必要时开展男女平等评估。</p><p style=\"text-align: left;\">第九条　国家建立健全妇女发展状况统计调查制度，完善性别统计监测指标体系，定期开展妇女发展状况和权益保障统计调查和分析，发布有关信息。</p><p style=\"text-align: left;\">第十条　国家将男女平等基本国策纳入国民教育体系，开展宣传教育，增强全社会的男女平等意识，培育尊重和关爱妇女的社会风尚。</p><p style=\"text-align: left;\">第十一条　国家对保障妇女合法权益成绩显著的组织和个人，按照有关规定给予表彰和奖励。</p><p style=\"text-align: left;\">第二章　政治权利</p><p style=\"text-align: left;\">第十二条　国家保障妇女享有与男子平等的政治权利。</p><p style=\"text-align: left;\">第十三条　妇女有权通过各种途径和形式，依法参与管理国家事务、管理经济和文化事业、管理社会事务。</p><p style=\"text-align: left;\">妇女和妇女组织有权向各级国家机关提出妇女权益保障方面的意见和建议。</p><p style=\"text-align: left;\">第十四条　妇女享有与男子平等的选举权和被选举权。</p><p style=\"text-align: left;\">全国人民代表大会和地方各级人民代表大会的代表中，应当保证有适当数量的妇女代表。国家采取措施，逐步提高全国人民代表大会和地方各级人民代表大会的妇女代表的比例。</p><p style=\"text-align: left;\">居民委员会、村民委员会成员中，应当保证有适当数量的妇女成员。</p><p style=\"text-align: left;\">第十五条　国家积极培养和选拔女干部，重视培养和选拔少数民族女干部。</p><p style=\"text-align: left;\">国家机关、群团组织、企业事业单位培养、选拔和任用干部，应当坚持男女平等的原则，并有适当数量的妇女担任领导成员。</p><p style=\"text-align: left;\">妇女联合会及其团体会员，可以向国家机关、群团组织、企业事业单位推荐女干部。</p><p style=\"text-align: left;\">国家采取措施支持女性人才成长。</p><p style=\"text-align: left;\">第十六条　妇女联合会代表妇女积极参与国家和社会事务的民主协商、民主决策、民主管理和民主监督。</p><p style=\"text-align: left;\">第十七条　对于有关妇女权益保障工作的批评或者合理可行的建议，有关部门应当听取和采纳；对于有关侵害妇女权益的申诉、控告和检举，有关部门应当查清事实，负责处理，任何组织和个人不得压制或者打击报复。</p><p style=\"text-align: left;\">第三章　人身和人格权益</p><p style=\"text-align: left;\">第十八条　国家保障妇女享有与男子平等的人身和人格权益。</p><p style=\"text-align: left;\">第十九条　妇女的人身自由不受侵犯。禁止非法拘禁和以其他非法手段剥夺或者限制妇女的人身自由；禁止非法搜查妇女的身体。</p><p style=\"text-align: left;\">第二十条　妇女的人格尊严不受侵犯。禁止用侮辱、诽谤等方式损害妇女的人格尊严。</p><p style=\"text-align: left;\">第二十一条　妇女的生命权、身体权、健康权不受侵犯。禁止虐待、遗弃、残害、买卖以及其他侵害女性生命健康权益的行为。</p><p style=\"text-align: left;\">禁止进行非医学需要的胎儿性别鉴定和选择性别的人工终止妊娠。</p><p style=\"text-align: left;\">医疗机构施行生育手术、特殊检查或者特殊治疗时，应当征得妇女本人同意；在妇女与其家属或者关系人意见不一致时，应当尊重妇女本人意愿。</p><p style=\"text-align: left;\">第二十二条　禁止拐卖、绑架妇女；禁止收买被拐卖、绑架的妇女；禁止阻碍解救被拐卖、绑架的妇女。</p><p style=\"text-align: left;\">各级人民政府和公安、民政、人力资源和社会保障、卫生健康等部门及村民委员会、居民委员会按照各自的职责及时发现报告，并采取措施解救被拐卖、绑架的妇女，做好被解救妇女的安置、救助和关爱等工作。妇女联合会协助和配合做好有关工作。任何组织和个人不得歧视被拐卖、绑架的妇女。</p><p style=\"text-align: left;\">第二十三条　禁止违背妇女意愿，以言语、文字、图像、肢体行为等方式对其实施性骚扰。</p><p style=\"text-align: left;\">受害妇女可以向有关单位和国家机关投诉。接到投诉的有关单位和国家机关应当及时处理，并书面告知处理结果。</p><p style=\"text-align: left;\">受害妇女可以向公安机关报案，也可以向人民法院提起民事诉讼，依法请求行为人承担民事责任。</p><p style=\"text-align: left;\">第二十四条　学校应当根据女学生的年龄阶段，进行生理卫生、心理健康和自我保护教育，在教育、管理、设施等方面采取措施，提高其防范性侵害、性骚扰的自我保护意识和能力，保障女学生的人身安全和身心健康发展。</p><p style=\"text-align: left;\">学校应当建立有效预防和科学处置性侵害、性骚扰的工作制度。对性侵害、性骚扰女学生的违法犯罪行为，学校不得隐瞒，应当及时通知受害未成年女学生的父母或者其他监护人，向公安机关、教育行政部门报告，并配合相关部门依法处理。</p><p style=\"text-align: left;\">对遭受性侵害、性骚扰的女学生，学校、公安机关、教育行政部门等相关单位和人员应当保护其隐私和个人信息，并提供必要的保护措施。</p><p style=\"text-align: left;\">第二十五条　用人单位应当采取下列措施预防和制止对妇女的性骚扰:</p><p style=\"text-align: left;\">（一）制定禁止性骚扰的规章制度；</p><p style=\"text-align: left;\">（二）明确负责机构或者人员；</p><p style=\"text-align: left;\">（三）开展预防和制止性骚扰的教育培训活动；</p><p style=\"text-align: left;\">（四）采取必要的安全保卫措施；</p><p style=\"text-align: left;\">（五）设置投诉电话、信箱等，畅通投诉渠道；</p><p style=\"text-align: left;\">（六）建立和完善调查处置程序，及时处置纠纷并保护当事人隐私和个人信息；</p><p style=\"text-align: left;\">（七）支持、协助受害妇女依法维权，必要时为受害妇女提供心理疏导；</p><p style=\"text-align: left;\">（八）其他合理的预防和制止性骚扰措施。</p><p style=\"text-align: left;\">第二十六条　住宿经营者应当及时准确登记住宿人员信息，健全住宿服务规章制度，加强安全保障措施；发现可能侵害妇女权益的违法犯罪行为，应当及时向公安机关报告。</p><p style=\"text-align: left;\">第二十七条　禁止卖淫、嫖娼；禁止组织、强迫、引诱、容留、介绍妇女卖淫或者对妇女进行猥亵活动；禁止组织、强迫、引诱、容留、介绍妇女在任何场所或者利用网络进行淫秽表演活动。</p><p style=\"text-align: left;\">第二十八条　妇女的姓名权、肖像权、名誉权、荣誉权、隐私权和个人信息等人格权益受法律保护。</p><p style=\"text-align: left;\">媒体报道涉及妇女事件应当客观、适度，不得通过夸大事实、过度渲染等方式侵害妇女的人格权益。</p><p style=\"text-align: left;\">禁止通过大众传播媒介或者其他方式贬低损害妇女人格。未经本人同意，不得通过广告、商标、展览橱窗、报纸、期刊、图书、音像制品、电子出版物、网络等形式使用妇女肖像，但法律另有规定的除外。</p><p style=\"text-align: left;\">第二十九条　禁止以恋爱、交友为由或者在终止恋爱关系、离婚之后，纠缠、骚扰妇女，泄露、传播妇女隐私和个人信息。</p><p style=\"text-align: left;\">妇女遭受上述侵害或者面临上述侵害现实危险的，可以向人民法院申请人身安全保护令。</p><p style=\"text-align: left;\">第三十条　国家建立健全妇女健康服务体系，保障妇女享有基本医疗卫生服务，开展妇女常见病、多发病的预防、筛查和诊疗，提高妇女健康水平。</p><p style=\"text-align: left;\">国家采取必要措施，开展经期、孕期、产期、哺乳期和更年期的健康知识普及、卫生保健和疾病防治，保障妇女特殊生理时期的健康需求，为有需要的妇女提供心理健康服务支持。</p><p style=\"text-align: left;\">第三十一条　县级以上地方人民政府应当设立妇幼保健机构，为妇女提供保健以及常见病防治服务。</p><p style=\"text-align: left;\">国家鼓励和支持社会力量通过依法捐赠、资助或者提供志愿服务等方式，参与妇女卫生健康事业，提供安全的生理健康用品或者服务，满足妇女多样化、差异化的健康需求。</p><p style=\"text-align: left;\">用人单位应当定期为女职工安排妇科疾病、乳腺疾病检查以及妇女特殊需要的其他健康检查。</p><p style=\"text-align: left;\">第三十二条　妇女依法享有生育子女的权利，也有不生育子女的自由。</p><p style=\"text-align: left;\">第三十三条　国家实行婚前、孕前、孕产期和产后保健制度，逐步建立妇女全生育周期系统保健制度。医疗保健机构应当提供安全、有效的医疗保健服务，保障妇女生育安全和健康。</p><p style=\"text-align: left;\">有关部门应当提供安全、有效的避孕药具和技术，保障妇女的健康和安全。</p><p style=\"text-align: left;\">第三十四条　各级人民政府在规划、建设基础设施时，应当考虑妇女的特殊需求，配备满足妇女需要的公共厕所和母婴室等公共设施。</p><p style=\"text-align: left;\">第四章　文化教育权益</p><p style=\"text-align: left;\">第三十五条　国家保障妇女享有与男子平等的文化教育权利。</p><p style=\"text-align: left;\">第三十六条　父母或者其他监护人应当履行保障适龄女性未成年人接受并完成义务教育的义务。</p><p style=\"text-align: left;\">对无正当理由不送适龄女性未成年人入学的父母或者其他监护人，由当地乡镇人民政府或者县级人民政府教育行政部门给予批评教育，依法责令其限期改正。居民委员会、村民委员会应当协助政府做好相关工作。</p><p style=\"text-align: left;\">政府、学校应当采取有效措施，解决适龄女性未成年人就学存在的实际困难，并创造条件，保证适龄女性未成年人完成义务教育。</p><p style=\"text-align: left;\">第三十七条　学校和有关部门应当执行国家有关规定，保障妇女在入学、升学、授予学位、派出留学、就业指导和服务等方面享有与男子平等的权利。</p><p style=\"text-align: left;\">学校在录取学生时，除国家规定的特殊专业外，不得以性别为由拒绝录取女性或者提高对女性的录取标准。</p><p style=\"text-align: left;\">各级人民政府应当采取措施，保障女性平等享有接受中高等教育的权利和机会。</p><p style=\"text-align: left;\">第三十八条　各级人民政府应当依照规定把扫除妇女中的文盲、半文盲工作，纳入扫盲和扫盲后继续教育规划，采取符合妇女特点的组织形式和工作方法，组织、监督有关部门具体实施。</p><p style=\"text-align: left;\">第三十九条　国家健全全民终身学习体系，为妇女终身学习创造条件。</p><p style=\"text-align: left;\">各级人民政府和有关部门应当采取措施，根据城镇和农村妇女的需要，组织妇女接受职业教育和实用技术培训。</p><p style=\"text-align: left;\">第四十条　国家机关、社会团体和企业事业单位应当执行国家有关规定，保障妇女从事科学、技术、文学、艺术和其他文化活动，享有与男子平等的权利。</p><p style=\"text-align: left;\">第五章　劳动和社会保障权益</p><p style=\"text-align: left;\">第四十一条　国家保障妇女享有与男子平等的劳动权利和社会保障权利。</p><p style=\"text-align: left;\">第四十二条　各级人民政府和有关部门应当完善就业保障政策措施，防止和纠正就业性别歧视，为妇女创造公平的就业创业环境，为就业困难的妇女提供必要的扶持和援助。</p><p style=\"text-align: left;\">第四十三条　用人单位在招录（聘）过程中，除国家另有规定外，不得实施下列行为:</p><p style=\"text-align: left;\">（一）限定为男性或者规定男性优先；</p><p style=\"text-align: left;\">（二）除个人基本信息外，进一步询问或者调查女性求职者的婚育情况；</p><p style=\"text-align: left;\">（三）将妊娠测试作为入职体检项目；</p><p style=\"text-align: left;\">（四）将限制结婚、生育或者婚姻、生育状况作为录（聘）用条件；</p><p style=\"text-align: left;\">（五）其他以性别为由拒绝录（聘）用妇女或者差别化地提高对妇女录（聘）用标准的行为。</p><p style=\"text-align: left;\">第四十四条　用人单位在录（聘）用女职工时，应当依法与其签订劳动（聘用）合同或者服务协议，劳动（聘用）合同或者服务协议中应当具备女职工特殊保护条款，并不得规定限制女职工结婚、生育等内容。</p><p style=\"text-align: left;\">职工一方与用人单位订立的集体合同中应当包含男女平等和女职工权益保护相关内容，也可以就相关内容制定专章、附件或者单独订立女职工权益保护专项集体合同。</p><p style=\"text-align: left;\">第四十五条　实行男女同工同酬。妇女在享受福利待遇方面享有与男子平等的权利。</p><p style=\"text-align: left;\">第四十六条　在晋职、晋级、评聘专业技术职称和职务、培训等方面，应当坚持男女平等的原则，不得歧视妇女。</p><p style=\"text-align: left;\">第四十七条　用人单位应当根据妇女的特点，依法保护妇女在工作和劳动时的安全、健康以及休息的权利。</p><p style=\"text-align: left;\">妇女在经期、孕期、产期、哺乳期受特殊保护。</p><p style=\"text-align: left;\">第四十八条　用人单位不得因结婚、怀孕、产假、哺乳等情形，降低女职工的工资和福利待遇，限制女职工晋职、晋级、评聘专业技术职称和职务，辞退女职工，单方解除劳动（聘用）合同或者服务协议。</p><p style=\"text-align: left;\">女职工在怀孕以及依法享受产假期间，劳动（聘用）合同或者服务协议期满的，劳动（聘用）合同或者服务协议期限自动延续至产假结束。但是，用人单位依法解除、终止劳动（聘用）合同、服务协议，或者女职工依法要求解除、终止劳动（聘用）合同、服务协议的除外。</p><p style=\"text-align: left;\">用人单位在执行国家退休制度时，不得以性别为由歧视妇女。</p><p style=\"text-align: left;\">第四十九条　人力资源和社会保障部门应当将招聘、录取、晋职、晋级、评聘专业技术职称和职务、培训、辞退等过程中的性别歧视行为纳入劳动保障监察范围。</p><p style=\"text-align: left;\">第五十条　国家发展社会保障事业，保障妇女享有社会保险、社会救助和社会福利等权益。</p><p style=\"text-align: left;\">国家提倡和鼓励为帮助妇女而开展的社会公益活动。</p><p style=\"text-align: left;\">第五十一条　国家实行生育保险制度，建立健全婴幼儿托育服务等与生育相关的其他保障制度。</p><p style=\"text-align: left;\">国家建立健全职工生育休假制度，保障孕产期女职工依法享有休息休假权益。</p><p style=\"text-align: left;\">地方各级人民政府和有关部门应当按照国家有关规定，为符合条件的困难妇女提供必要的生育救助。</p><p style=\"text-align: left;\">第五十二条　各级人民政府和有关部门应当采取必要措施，加强贫困妇女、老龄妇女、残疾妇女等困难妇女的权益保障，按照有关规定为其提供生活帮扶、就业创业支持等关爱服务。</p><p style=\"text-align: left;\">第六章　财产权益</p><p style=\"text-align: left;\">第五十三条　国家保障妇女享有与男子平等的财产权利。</p><p style=\"text-align: left;\">第五十四条　在夫妻共同财产、家庭共有财产关系中，不得侵害妇女依法享有的权益。</p><p style=\"text-align: left;\">第五十五条　妇女在农村集体经济组织成员身份确认、土地承包经营、集体经济组织收益分配、土地征收补偿安置或者征用补偿以及宅基地使用等方面，享有与男子平等的权利。</p><p style=\"text-align: left;\">申请农村土地承包经营权、宅基地使用权等不动产登记，应当在不动产登记簿和权属证书上将享有权利的妇女等家庭成员全部列明。征收补偿安置或者征用补偿协议应当将享有相关权益的妇女列入，并记载权益内容。</p><p style=\"text-align: left;\">第五十六条　村民自治章程、村规民约，村民会议、村民代表会议的决定以及其他涉及村民利益事项的决定，不得以妇女未婚、结婚、离婚、丧偶、户无男性等为由，侵害妇女在农村集体经济组织中的各项权益。</p><p style=\"text-align: left;\">因结婚男方到女方住所落户的，男方和子女享有与所在地农村集体经济组织成员平等的权益。</p><p style=\"text-align: left;\">第五十七条　国家保护妇女在城镇集体所有财产关系中的权益。妇女依照法律、法规的规定享有相关权益。</p><p style=\"text-align: left;\">第五十八条　妇女享有与男子平等的继承权。妇女依法行使继承权，不受歧视。</p><p style=\"text-align: left;\">丧偶妇女有权依法处分继承的财产，任何组织和个人不得干涉。</p><p style=\"text-align: left;\">第五十九条　丧偶儿媳对公婆尽了主要赡养义务的，作为第一顺序继承人，其继承权不受子女代位继承的影响。</p><p style=\"text-align: left;\">第七章　婚姻家庭权益</p><p style=\"text-align: left;\">第六十条　国家保障妇女享有与男子平等的婚姻家庭权利。</p><p style=\"text-align: left;\">第六十一条　国家保护妇女的婚姻自主权。禁止干涉妇女的结婚、离婚自由。</p><p style=\"text-align: left;\">第六十二条　国家鼓励男女双方在结婚登记前，共同进行医学检查或者相关健康体检。</p><p style=\"text-align: left;\">第六十三条　婚姻登记机关应当提供婚姻家庭辅导服务，引导当事人建立平等、和睦、文明的婚姻家庭关系。</p><p style=\"text-align: left;\">第六十四条　女方在怀孕期间、分娩后一年内或者终止妊娠后六个月内，男方不得提出离婚；但是，女方提出离婚或者人民法院认为确有必要受理男方离婚请求的除外。</p><p style=\"text-align: left;\">第六十五条　禁止对妇女实施家庭暴力。</p><p style=\"text-align: left;\">县级以上人民政府有关部门、司法机关、社会团体、企业事业单位、基层群众性自治组织以及其他组织，应当在各自的职责范围内预防和制止家庭暴力，依法为受害妇女提供救助。</p><p style=\"text-align: left;\">第六十六条　妇女对夫妻共同财产享有与其配偶平等的占有、使用、收益和处分的权利，不受双方收入状况等情形的影响。</p><p style=\"text-align: left;\">对夫妻共同所有的不动产以及可以联名登记的动产，女方有权要求在权属证书上记载其姓名；认为记载的权利人、标的物、权利比例等事项有错误的，有权依法申请更正登记或者异议登记，有关机构应当按照其申请依法办理相应登记手续。</p><p style=\"text-align: left;\">第六十七条　离婚诉讼期间，夫妻一方申请查询登记在对方名下财产状况且确因客观原因不能自行收集的，人民法院应当进行调查取证，有关部门和单位应当予以协助。</p><p style=\"text-align: left;\">离婚诉讼期间，夫妻双方均有向人民法院申报全部夫妻共同财产的义务。一方隐藏、转移、变卖、损毁、挥霍夫妻共同财产，或者伪造夫妻共同债务企图侵占另一方财产的，在离婚分割夫妻共同财产时，对该方可以少分或者不分财产。</p><p style=\"text-align: left;\">第六十八条　夫妻双方应当共同负担家庭义务，共同照顾家庭生活。</p><p style=\"text-align: left;\">女方因抚育子女、照料老人、协助男方工作等负担较多义务的，有权在离婚时要求男方予以补偿。补偿办法由双方协议确定；协议不成的，可以向人民法院提起诉讼。</p><p style=\"text-align: left;\">第六十九条　离婚时，分割夫妻共有的房屋或者处理夫妻共同租住的房屋，由双方协议解决；协议不成的，可以向人民法院提起诉讼。</p><p style=\"text-align: left;\">第七十条　父母双方对未成年子女享有平等的监护权。</p><p style=\"text-align: left;\">父亲死亡、无监护能力或者有其他情形不能担任未成年子女的监护人的，母亲的监护权任何组织和个人不得干涉。</p><p style=\"text-align: left;\">第七十一条　女方丧失生育能力的，在离婚处理子女抚养问题时，应当在最有利于未成年子女的条件下，优先考虑女方的抚养要求。</p><p style=\"text-align: left;\">第八章　救济措施</p><p style=\"text-align: left;\">第七十二条　对侵害妇女合法权益的行为，任何组织和个人都有权予以劝阻、制止或者向有关部门提出控告或者检举。有关部门接到控告或者检举后，应当依法及时处理，并为控告人、检举人保密。</p><p style=\"text-align: left;\">妇女的合法权益受到侵害的，有权要求有关部门依法处理，或者依法申请调解、仲裁，或者向人民法院起诉。</p><p style=\"text-align: left;\">对符合条件的妇女，当地法律援助机构或者司法机关应当给予帮助，依法为其提供法律援助或者司法救助。</p><p style=\"text-align: left;\">第七十三条　妇女的合法权益受到侵害的，可以向妇女联合会等妇女组织求助。妇女联合会等妇女组织应当维护被侵害妇女的合法权益，有权要求并协助有关部门或者单位查处。有关部门或者单位应当依法查处，并予以答复；不予处理或者处理不当的，县级以上人民政府负责妇女儿童工作的机构、妇女联合会可以向其提出督促处理意见，必要时可以提请同级人民政府开展督查。</p><p style=\"text-align: left;\">受害妇女进行诉讼需要帮助的，妇女联合会应当给予支持和帮助。</p><p style=\"text-align: left;\">第七十四条　用人单位侵害妇女劳动和社会保障权益的，人力资源和社会保障部门可以联合工会、妇女联合会约谈用人单位，依法进行监督并要求其限期纠正。</p><p style=\"text-align: left;\">第七十五条　妇女在农村集体经济组织成员身份确认等方面权益受到侵害的，可以申请乡镇人民政府等进行协调，或者向人民法院起诉。</p><p style=\"text-align: left;\">乡镇人民政府应当对村民自治章程、村规民约，村民会议、村民代表会议的决定以及其他涉及村民利益事项的决定进行指导，对其中违反法律、法规和国家政策规定，侵害妇女合法权益的内容责令改正；受侵害妇女向农村土地承包仲裁机构申请仲裁或者向人民法院起诉的，农村土地承包仲裁机构或者人民法院应当依法受理。</p><p style=\"text-align: left;\">第七十六条　县级以上人民政府应当开通全国统一的妇女权益保护服务热线，及时受理、移送有关侵害妇女合法权益的投诉、举报；有关部门或者单位接到投诉、举报后，应当及时予以处置。</p><p style=\"text-align: left;\">鼓励和支持群团组织、企业事业单位、社会组织和个人参与建设妇女权益保护服务热线，提供妇女权益保护方面的咨询、帮助。</p><p style=\"text-align: left;\">第七十七条　侵害妇女合法权益，导致社会公共利益受损的，检察机关可以发出检察建议；有下列情形之一的，检察机关可以依法提起公益诉讼:</p><p style=\"text-align: left;\">（一）确认农村妇女集体经济组织成员身份时侵害妇女权益或者侵害妇女享有的农村土地承包和集体收益、土地征收征用补偿分配权益和宅基地使用权益；</p><p style=\"text-align: left;\">（二）侵害妇女平等就业权益；</p><p style=\"text-align: left;\">（三）相关单位未采取合理措施预防和制止性骚扰；</p><p style=\"text-align: left;\">（四）通过大众传播媒介或者其他方式贬低损害妇女人格；</p><p style=\"text-align: left;\">（五）其他严重侵害妇女权益的情形。</p><p style=\"text-align: left;\">第七十八条　国家机关、社会团体、企业事业单位对侵害妇女权益的行为，可以支持受侵害的妇女向人民法院起诉。</p><p style=\"text-align: left;\">第九章　法律责任</p><p style=\"text-align: left;\">第七十九条　违反本法第二十二条第二款规定，未履行报告义务的，依法对直接负责的主管人员和其他直接责任人员给予处分。</p><p style=\"text-align: left;\">第八十条　违反本法规定，对妇女实施性骚扰的，由公安机关给予批评教育或者出具告诫书，并由所在单位依法给予处分。</p><p style=\"text-align: left;\">学校、用人单位违反本法规定，未采取必要措施预防和制止性骚扰，造成妇女权益受到侵害或者社会影响恶劣的，由上级机关或者主管部门责令改正；拒不改正或者情节严重的，依法对直接负责的主管人员和其他直接责任人员给予处分。</p><p style=\"text-align: left;\">第八十一条　违反本法第二十六条规定，未履行报告等义务的，依法给予警告、责令停业整顿或者吊销营业执照、吊销相关许可证，并处一万元以上五万元以下罚款。</p><p style=\"text-align: left;\">第八十二条　违反本法规定，通过大众传播媒介或者其他方式贬低损害妇女人格的，由公安、网信、文化旅游、广播电视、新闻出版或者其他有关部门依据各自的职权责令改正，并依法给予行政处罚。</p><p style=\"text-align: left;\">第八十三条　用人单位违反本法第四十三条和第四十八条规定的，由人力资源和社会保障部门责令改正；拒不改正或者情节严重的，处一万元以上五万元以下罚款。</p><p style=\"text-align: left;\">第八十四条　违反本法规定，对侵害妇女权益的申诉、控告、检举，推诿、拖延、压制不予查处，或者对提出申诉、控告、检举的人进行打击报复的，依法责令改正，并对直接负责的主管人员和其他直接责任人员给予处分。</p><p style=\"text-align: left;\">国家机关及其工作人员未依法履行职责，对侵害妇女权益的行为未及时制止或者未给予受害妇女必要帮助，造成严重后果的，依法对直接负责的主管人员和其他直接责任人员给予处分。</p><p style=\"text-align: left;\">违反本法规定，侵害妇女人身和人格权益、文化教育权益、劳动和社会保障权益、财产权益以及婚姻家庭权益的，依法责令改正，直接负责的主管人员和其他直接责任人员属于国家工作人员的，依法给予处分。</p><p style=\"text-align: left;\">第八十五条　违反本法规定，侵害妇女的合法权益，其他法律、法规规定行政处罚的，从其规定；造成财产损失或者人身损害的，依法承担民事责任；构成犯罪的，依法追究刑事责任。</p><p style=\"text-align: left;\">第十章　附　　则</p><p style=\"text-align: left;\">第八十六条　本法自2023年1月1日起施行。</p>', '全国人民代表大会', '', '2023-04-11 20:15:44');
INSERT INTO `articles` VALUES (29, 4, '法律法规', '国家人权行动计划（2021—2025年）', '<p style=\"text-align: left;\">中华人民共和国国务院新闻办公室</p><p style=\"text-align: left;\">2021年9月 </p><p style=\"text-align: left;\">《 人民日报 》（ 2021年09月10日 &nbsp; 第 10 版）</p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">目 &nbsp;录</p><p style=\"text-align: left;\">导 &nbsp;言</p><p style=\"text-align: left;\">一、经济、社会和文化权利</p><p style=\"text-align: left;\">（一）基本生活水准权利</p><p style=\"text-align: left;\">（二）工作权利</p><p style=\"text-align: left;\">（三）社会保障权利</p><p style=\"text-align: left;\">（四）财产权益</p><p style=\"text-align: left;\">（五）健康权利</p><p style=\"text-align: left;\">（六）受教育权</p><p style=\"text-align: left;\">（七）文化权利</p><p style=\"text-align: left;\">二、公民权利和政治权利</p><p style=\"text-align: left;\">（一）生命权</p><p style=\"text-align: left;\">（二）人身权利</p><p style=\"text-align: left;\">（三）个人信息权益</p><p style=\"text-align: left;\">（四）宗教信仰自由</p><p style=\"text-align: left;\">（五）选举权和被选举权</p><p style=\"text-align: left;\">（六）知情权和参与权</p><p style=\"text-align: left;\">（七）表达权和监督权</p><p style=\"text-align: left;\">（八）获得公正审判的权利</p><p style=\"text-align: left;\">三、环境权利</p><p style=\"text-align: left;\">（一）污染防治</p><p style=\"text-align: left;\">（二）生态环境信息公开</p><p style=\"text-align: left;\">（三）环境决策公众参与</p><p style=\"text-align: left;\">（四）环境公益诉讼和生态环境损害赔偿</p><p style=\"text-align: left;\">（五）国土空间生态保护修复</p><p style=\"text-align: left;\">（六）应对气候变化</p><p style=\"text-align: left;\">四、特定群体权益保障</p><p style=\"text-align: left;\">（一）少数民族权益</p><p style=\"text-align: left;\">（二）妇女权益</p><p style=\"text-align: left;\">（三）儿童权益</p><p style=\"text-align: left;\">（四）老年人权益</p><p style=\"text-align: left;\">（五）残疾人权益</p><p style=\"text-align: left;\">五、人权教育和研究</p><p style=\"text-align: left;\">（一）学校人权教育</p><p style=\"text-align: left;\">（二）人权研究</p><p style=\"text-align: left;\">（三）人权知识培训</p><p style=\"text-align: left;\">（四）人权知识普及</p><p style=\"text-align: left;\">六、参与全球人权治理</p><p style=\"text-align: left;\">（一）履行国际人权条约义务</p><p style=\"text-align: left;\">（二）深度参与联合国人权机构工作</p><p style=\"text-align: left;\">（三）开展建设性人权对话与合作</p><p style=\"text-align: left;\">（四）为全球人权事业作出中国贡献</p><p style=\"text-align: left;\">七、实施、监督和评估</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">导 &nbsp;言</p><p style=\"text-align: left;\">自2009年以来，中国先后制定实施了三期国家人权行动计划，人民的生活水平持续提升，各项权利得到更加切实保障，保护特定群体权益的政策和法律措施更加完善，人权法治保障进一步加强，全面参与全球人权治理，为世界人权事业发展作出了重要贡献。</p><p style=\"text-align: left;\">2021—2025年是中国在全面建成小康社会、实现第一个百年奋斗目标之后，乘势而上开启全面建设社会主义现代化国家新征程、向第二个百年奋斗目标进军的第一个五年。</p><p style=\"text-align: left;\">全面建设社会主义现代化国家是中国人权事业发展的新起点。中国已经进入高质量发展阶段，中国人权事业发展具备了多方面的优势和条件。同时，社会主要矛盾已经转化为人民日益增长的美好生活需要和不平衡不充分的发展之间的矛盾，人民对美好生活的向往更加强烈，对人权保障的要求不断提高。当今世界正经历百年未有之大变局，叠加全球新冠肺炎疫情大流行，国际环境日趋复杂，不稳定性不确定性明显增强，中国和世界人权事业发展面临新挑战。</p><p style=\"text-align: left;\">在总结前三期国家人权行动计划执行情况和实施经验的基础上，依据国家尊重和保障人权的宪法原则，遵循《世界人权宣言》和有关国际人权公约精神，结合《中华人民共和国国民经济和社会发展第十四个五年规划和2035年远景目标纲要》，立足促进人权事业全面发展，中国政府制定《国家人权行动计划（2021—2025年）》（以下简称《行动计划》），确定2021—2025年尊重、保护和促进人权的阶段性目标和任务。</p><p style=\"text-align: left;\">制定和实施《行动计划》的指导思想是：坚持以习近平新时代中国特色社会主义思想为指导，坚持以人民为中心的发展思想，坚持人民幸福生活是最大的人权，将促进人的全面发展、全体人民共同富裕作为人权事业发展的出发点和落脚点，发展全过程人民民主，维护社会公平正义，着力解决人民群众急难愁盼问题，使全体人民的各项人权得到更高水平的保障，不断增强人民对于人权保障的获得感、幸福感、安全感。</p><p style=\"text-align: left;\">制定和实施《行动计划》的基本原则是：依法推进，将人权事业纳入法治轨道；协调推进，使各项人权全面协调发展；务实推进，把人权的普遍原则与中国实际相结合；平等推进，充分保障所有社会成员平等参与、平等发展的权利；合力推进，政府、企事业单位、社会组织共同促进人权事业的发展；智慧推进，充分利用数字技术拓展所有人自由全面发展的空间。</p><p style=\"text-align: left;\">制定和实施《行动计划》的目标是：</p><p style=\"text-align: left;\">——将促进全体人民的自由全面共同发展作为人权事业发展的总目标。坚持以人民为中心，将满足人民对人权保障的新需求作为奋斗方向。坚持人民主体地位，坚持发展为了人民，发展依靠人民，发展成果由人民共享，增进人民的获得感、幸福感、安全感。</p><p style=\"text-align: left;\">——充分保障人民的经济社会文化权利，不断实现人民对美好生活的向往，为人的全面发展创造更加有利的经济社会文化条件。</p><p style=\"text-align: left;\">——切实保障公民权利和政治权利，促进人民有效社会参与，为实现人的全面发展提供更为坚实的民主法治基础。</p><p style=\"text-align: left;\">——坚持绿水青山就是金山银山理念，坚持尊重自然、顺应自然、保护自然，促进人与自然和谐共生，推进生态文明建设，建设美丽中国，为全人类和子孙后代共享发展创造可持续条件。</p><p style=\"text-align: left;\">——加强对特定群体权益的平等保护和特殊扶助，促进所有人平等分享发展成果，为实现所有人全面发展提供政策支持。</p><p style=\"text-align: left;\">——广泛开展人权教育、研究、培训和知识普及，营造全社会尊重和保障人权的文化氛围。</p><p style=\"text-align: left;\">——积极参与全球人权治理，深度参与联合国人权机制工作，推动建设更加公平公正合理包容的全球人权治理体系，共同构建人类命运共同体。</p><p style=\"text-align: left;\">《行动计划》由国务院新闻办公室和外交部牵头编制，经国家人权行动计划联席会议机制审核同意，现授权国务院新闻办公室发布。</p><p style=\"text-align: left;\">一、经济、社会和文化权利</p><p style=\"text-align: left;\">巩固脱贫攻坚成果，开展乡村振兴建设，贯彻就业优先政策，实施健康中国战略，完善社会保障体系，推动教育公平发展，加强公共文化服务，促进全体人民共同富裕，保障公民的经济、社会和文化权利。</p><p style=\"text-align: left;\">（一）基本生活水准权利</p><p style=\"text-align: left;\">接续推进脱贫地区发展。保障居民基本住房、用水、食品安全和出行便利。</p><p style=\"text-align: left;\">——巩固拓展脱贫攻坚成果同乡村振兴有效衔接。建立完善农村低收入人口和欠发达地区帮扶机制，保持主要帮扶政策和财政投入力度总体稳定。做好易地扶贫搬迁后续帮扶，加强大型搬迁安置区新型城镇化建设。</p><p style=\"text-align: left;\">——夯实粮食安全基础。保障粮、棉、油、糖、肉、奶等重要农产品供给安全。坚持最严格的耕地保护制度，严守18亿亩耕地红线。建设国家粮食安全产业带，建成10.75亿亩集中连片高标准农田。</p><p style=\"text-align: left;\">——实施食品安全战略。开展食品安全放心工程建设攻坚行动，着力提升智慧监管能力，推动健全从农田到餐桌全过程食品安全监管体系。</p><p style=\"text-align: left;\">——提升用水安全保障水平。新增水利工程供水能力290亿立方米。实施农村供水保障工程和农村饮用水安全工程建设，农村自来水普及率达到88%以上。</p><p style=\"text-align: left;\">——完善住房保障体系。加快完善以公租房、保障性租赁住房和共有产权住房为主体的住房保障体系。公租房实行实物保障和货币补贴并举，对低保低收入住房困难家庭应保尽保；推进保障性租赁住房建设，帮助新市民、青年人等群体缓解住房困难问题。发展共有产权住房，帮助有一定支付能力又买不起商品住房的群体拥有产权住房。</p><p style=\"text-align: left;\">——加强住房安全保障。继续实施农村危房改造，用三年左右时间完成农村房屋安全隐患排查整治工作，支持地震高烈度设防地区实施农房抗震改造。</p><p style=\"text-align: left;\">——优化交通条件。推动干线铁路建设，实现国家高速公路贯通互联，加快沿边抵边公路建设，对繁忙路段实施扩容改造。加强乡镇对外公路建设改造，实施自然村（组）通硬化路建设。</p><p style=\"text-align: left;\">——弥合城乡数字鸿沟。统筹推进智慧城市与数字乡村建设，促进城乡信息化融合发展，提升全民数字素养与技能。</p><p style=\"text-align: left;\">（二）工作权利</p><p style=\"text-align: left;\">全面贯彻就业优先政策，消除就业和职业歧视，完善工资福利制度，健全劳动关系协调机制，落实安全生产管理制度，加强劳动法律实施监督。</p><p style=\"text-align: left;\">——促进就业。实现更加充分更高质量就业，城镇调查失业率控制在5.5%以内。增加非全日制就业机会，支持和规范发展新就业形态，为农村外出返乡人员提供创业服务。实施创新创业带头人培育行动，引导建设各类农村创业创新示范园区和孵化实训基地。完善进城务工青年、灵活就业青年等群体的劳动就业合法权益。扩大公益性岗位安置，着力帮扶生育后再就业妇女、残疾人、零就业家庭成员等困难人员就业。</p><p style=\"text-align: left;\">——促进脱贫人口稳定就业。加大脱贫人口有组织劳务输出力度。支持脱贫地区在涉农项目建设和管护方面广泛采取以工代赈方式。延续支持扶贫车间的优惠政策。调整优化生态护林员政策。统筹用好乡村公益岗位。</p><p style=\"text-align: left;\">——加强农民工职业技能培训。实施职业技能提升行动和农民工稳就业职业技能培训、百万青年技能培训行动、康养职业技能培训等专项计划。每年培训农民工700万人次。支持企业开展岗前培训、新型学徒制培训和岗位技能提升培训。</p><p style=\"text-align: left;\">——保障劳动者获得合理报酬。健全工资决定、合理增长和支付保障机制，完善最低工资标准和工资指导线形成机制，积极推行工资集体协商制度。规范劳务派遣用工行为，保障劳动者同工同酬。制定互联网平台就业劳动保障政策。完善欠薪治理长效机制，依法纠正拖欠劳动报酬等违法违规行为。</p><p style=\"text-align: left;\">——打击和惩处强迫劳动。加强劳动者权益保护，依法惩处强迫劳动犯罪。</p><p style=\"text-align: left;\">——健全落实安全生产管理制度。修改安全生产法，制定危险化学品安全法、煤矿安全条例等法律法规。健全企业全过程安全生产管理制度，落实安全生产责任制。实施《工伤预防五年行动计划（2021—2025年）》。重点行业工伤事故发生率降低20%左右。</p><p style=\"text-align: left;\">（三）社会保障权利</p><p style=\"text-align: left;\">坚持应保尽保原则，按照兜底线、织密网、建机制的要求，加快健全覆盖全民、统筹城乡、公平统一、可持续的多层次社会保障体系。</p><p style=\"text-align: left;\">——完善社会保障体制机制。为参加城乡居民养老保险的缴费困难群体代缴部分或全部保费。落实职工基本养老金合理调整机制。完善企业年金、职业年金制度，推动个人养老金发展。落实职工基本养老保险遗属待遇和病残津贴政策。</p><p style=\"text-align: left;\">——健全社会救助制度体系。及时有针对性地给予困难群众医疗、教育、住房、就业等专项救助。完善农村特困人员救助供养制度，合理提高救助供养水平和服务质量。健全分层分类的社会救助体系，完善最低生活保障制度，特困人员救助供养覆盖的未成年人年龄从16周岁延长至18周岁。对符合临时救助条件的家庭或个人给予应急性、过渡性生活保障。</p><p style=\"text-align: left;\">（四）财产权益</p><p style=\"text-align: left;\">贯彻实施民法典，健全产权保护制度，保障农民土地承包经营权流转和宅基地使用权，深入推进农村集体产权制度改革，健全自然资源资产产权体系，完善数据产权保障制度。</p><p style=\"text-align: left;\">——健全产权保护制度。修改土地管理法实施条例。加强数据、知识、环境等领域产权制度建设，健全自然资源资产产权制度和法律法规。</p><p style=\"text-align: left;\">——强化产权司法执法保护。依法平等保护国有、民营、外资等各种所有制企业产权。依法保护企业家的财产权和创新收益。依法严厉打击侵犯公民、企业和组织合法财产权益的盗窃、抢劫、抢夺、诈骗、敲诈勒索和侵犯知识产权等各类犯罪行为。推动涉企冤错案件依法甄别纠正常态化机制化。积极探索中国特色现代企业规制司法制度。</p><p style=\"text-align: left;\">——保障农民财产权利。落实第二轮土地承包到期后再延长30年政策，完善农村承包地所有权、承包权、经营权分置制度。探索农村宅基地所有权、资格权、使用权分置实现形式。依法保障进城落户农民农村土地承包权、宅基地使用权、集体收益分配权。深化集体林权制度改革，规范草原承包经营和流转，推进草原确权登记颁证。</p><p style=\"text-align: left;\">——实行严格的知识产权保护制度。完善知识产权案件诉讼制度。健全知识产权侵权惩罚性赔偿制度，加大损害赔偿力度。改革国有知识产权归属和权益分配机制，扩大科研机构和高等院校知识产权处置自主权。</p><p style=\"text-align: left;\">（五）健康权利</p><p style=\"text-align: left;\">深入实施健康中国行动，为人民提供全方位全生命周期健康服务。重大疫情和突发公共卫生事件应急能力显著增强，人民身心健康素质明显提高，健康公平显著改善。</p><p style=\"text-align: left;\">——构建强大公共卫生体系。坚持人民至上、生命至上，有效防控、阻击新冠肺炎疫情，不惜一切代价维护人民生命安全和身体健康。改革疾病预防控制体系，强化监测预警、风险评估、流行病学调查、检验检测、应急处置等职能。加强公共卫生学院和人才队伍建设。扩大国家免疫规划，加强重大传染病防治，强化慢性病预防、早期筛查和综合干预。完善心理健康和精神卫生服务体系。升级改造20个国家重大传染病防控救治基地。依托现有疾控机构建设15个左右区域公共卫生中心。升级改造20个国家紧急医学救援基地。</p><p style=\"text-align: left;\">——完善医疗卫生服务体系建设。加强公立医院建设。完善城乡医疗服务网络，促进乡镇卫生院和社区卫生服务中心的服务能力达到国家标准。推动省市优质医疗资源支持县级医院发展。加快建设分级诊疗体系，积极发展医疗联合体。支持社会办医，鼓励有经验的执业医师开办诊所。</p><p style=\"text-align: left;\">——完善慢性病、地方病防治体系。国家慢性病综合防控示范区建设覆盖全国20%的县（区）。重大慢性病过早死亡率在2015年基础上降低20%。持续控制和消除地方病、血吸虫病危害。</p><p style=\"text-align: left;\">——提升医护人员培养质量与规模。扩大儿科、全科等短缺医师规模，每千人口拥有注册护士数提高到3.8人。加快壮大全科医生队伍，开展人员培训，每万人口平均拥有全科医生3.93人。</p><p style=\"text-align: left;\">——健全全民医保制度。巩固完善全民医保制度。完善基本医疗保险门诊共济保障机制，健全重大疾病医疗保险和救助制度。将符合条件的互联网医疗服务按规定纳入医保支付范围，落实异地就医结算。稳步建立长期护理保险制度。</p><p style=\"text-align: left;\">——提高精神健康服务水平。完善社会化心理健康服务体系，缓解学业、职业、生活和情感等社会压力，加强创伤后心理建设。完善严重精神障碍诊断报告、随访管理服务，严重精神障碍患者管理率达到90%。</p><p style=\"text-align: left;\">——持续提升青少年健康和体质水平。倡导合理用眼、合理膳食、科学运动等健康生活方式。青少年体质达标率不低于90%，青年学生近视检出率初中生不高于65%、高中生不高于75%。</p><p style=\"text-align: left;\">——确保食品药品安全。加强食品药品安全风险监测、抽检和监管执法，强化快速通报和快速反应。构建药品和疫苗全生命周期管理机制，完善药品电子追溯体系。依法严厉打击食品、药品领域违法犯罪行为。探索建立食品药品安全领域民事公益诉讼惩罚性赔偿适用。</p><p style=\"text-align: left;\">——促进中医药发展。坚持中西医并重和优势互补，大力发展中医药事业。打造30个左右国家中医药传承创新中心，形成一批中医药特色学科和优势学科。</p><p style=\"text-align: left;\">——推进智慧医疗。完善电子健康档案和病历、电子处方等数据库，加快医疗卫生机构数据共享。推广远程医疗，推进医学影像辅助判读、临床辅助诊断等应用。运用大数据提升对医疗机构和医疗行为的监管能力。</p><p style=\"text-align: left;\">——广泛开展全民健身运动。完善全民健身公共服务体系，推进社会体育场地设施建设和学校场馆开放共享，提高健身步道等便民健身场所覆盖面，因地制宜发展体育公园。</p><p style=\"text-align: left;\">（六）受教育权</p><p style=\"text-align: left;\">提高学前教育普及率、义务教育巩固率、高中阶段入学率。保证教育公平，提升教育质量，促进人的全面发展。</p><p style=\"text-align: left;\">——加大对原深度贫困地区的教育投入。继续实施中央预算内投资教育专项计划，建设东中西部高校全国性对口支援对接平台，支持原深度贫困地区巩固教育脱贫攻坚成果，阻断贫困代际传递。</p><p style=\"text-align: left;\">——推进学前教育全面普及。继续提升学前教育普及水平，大力发展公办幼儿园，积极扶持普惠性民办幼儿园。学前教育毛入园率提高到90%以上。</p><p style=\"text-align: left;\">——促进义务教育优质均衡发展。推动义务教育优质均衡发展和城乡一体化，加快义务教育学校标准化建设。提升义务教育巩固水平，健全控辍保学工作责任体系。持续改善乡村小规模学校和乡镇寄宿制学校条件。</p><p style=\"text-align: left;\">——提升高中阶段教育普及水平。加强县域普通高中建设，鼓励普通高中多样化特色化发展。加大对中西部贫困地区和高海拔地区高中阶段教育的扶持力度。高中阶段教育毛入学率提高到92%以上。</p><p style=\"text-align: left;\">——完善职业教育体系。构建职业中等教育和职业高等教育有机衔接、职业教育和普通教育相互融通的职业教育体系。推动产教融合发展，完善校企合作制度。逐步分类推进中等职业教育免除学杂费。新增劳动力平均受教育年限不低于14年。</p><p style=\"text-align: left;\">——促进高等教育高质量发展。实施高等学校创新能力提升计划，分类建设一流大学和一流学科，高等教育毛入学率提高到60%。实施中西部高等教育振兴计划，扩大“双一流”高校对中西部、民族地区和农村地区招生规模。</p><p style=\"text-align: left;\">——切实保障困境儿童受教育权利。加强对留守儿童的关爱保护，完善留守儿童教育服务体系。加快城镇学校扩容增位，保障农业转移人口随迁子女平等享有基本公共教育服务。继续实施家庭经济困难学生资助政策和农村义务教育学生营养改善计划。</p><p style=\"text-align: left;\">——预防性侵害和性骚扰。建立预防性侵害和性骚扰的工作制度，对未成年人开展符合其年龄的性教育，提高未成年人防范性侵害和性骚扰的自我保护意识和能力。对性侵害和性骚扰未成年人的违法犯罪活动及时向公安机关、教育行政部门报告，为遭受性侵害和性骚扰的未成年人提供保护和维权服务。</p><p style=\"text-align: left;\">——遏制校园欺凌。落实未成年人保护法和《未成年人学校保护规定》。建立学生欺凌防控工作制度，定期对全体学生开展防治欺凌专题教育活动，保障每一位儿童身心健康成长。</p><p style=\"text-align: left;\">——有效预防在线教育数字鸿沟。为家庭生活困难的学生提供必要的信息技术设备，确保全体学生公平享有优质在线教育。</p><p style=\"text-align: left;\">（七）文化权利</p><p style=\"text-align: left;\">完善公共文化服务体系，提升全民阅读服务水平，加强中华优秀传统文化传承和保护，促进新兴文化产业发展，更充分保障公民文化权利。</p><p style=\"text-align: left;\">——完善公共文化服务基础设施。做好公共图书馆、文化馆、博物馆、美术馆、乡镇（街道）综合文化站、村（社区）综合性文化服务中心等的规划建设。健全各级各类公共文化服务基础设施。</p><p style=\"text-align: left;\">——补齐公共文化服务短板。落实国家基本公共服务标准要求，加强基层文化建设，增加供给总量，优化供给结构，推动优质文化资源向农村地区、革命老区、民族地区、边疆地区倾斜，缩小城乡和地区之间公共文化服务差距。实施广播电视惠民工程，完善农村文化基础设施网络。</p><p style=\"text-align: left;\">——推动公共数字文化建设。统筹推进公共文化数字化重点工程建设。实施全国智慧图书馆体系建设、公共文化云建设等项目。</p><p style=\"text-align: left;\">——提升全民阅读服务水平。推进社区图书馆建设，优化基层阅读资源配置，改善公共场所阅读环境和阅读障碍者的阅读条件，推进农家书屋数字化升级，力争国民综合阅读率达到83%。</p><p style=\"text-align: left;\">——构建中华优秀传统文化传承体系。推进国家级非物质文化遗产代表性传承人记录工作，实施中国非物质文化遗产传承人研修培训计划。实施中华典籍整理工程，整理出版300种中华典籍。实施国家古籍数字化工程。</p><p style=\"text-align: left;\">——提升文化遗产系统性保护水平。加强对国家重大文化和自然遗产地、大遗址、重点文物保护单位、历史文化名城名镇名村的保护建设，加大对非国有博物馆业务帮扶。加强安阳殷墟等遗址保护，开展国家考古遗址公园建设。建设20个国家重点区域考古标本库房、30个国家级文化生态保护区和20个国家级非物质文化遗产馆。继续推进各级传统村落保护名录建设。</p><p style=\"text-align: left;\">——推进国家文化公园建设。整合长城、大运河、长征、黄河沿线等重要文化资源，强化文物和非物质文化遗产真实完整保护传承，打造中华文化重要标志。</p><p style=\"text-align: left;\">——促进新兴文化产业发展。推进文化业态创新，发展创意文化产业。发展积极健康的网络文化，推动网络文艺繁荣有序发展。</p><p style=\"text-align: left;\">二、公民权利和政治权利</p><p style=\"text-align: left;\">扩大公民自主参与和自由发展空间，完善人身权利、个人信息权益、财产权利和宗教信仰自由权利保障制度，加强人权法治保障，提升选举权和被选举权、知情权和参与权、表达权和监督权保障水平，切实尊重和保障公民权利和政治权利。</p><p style=\"text-align: left;\">（一）生命权</p><p style=\"text-align: left;\">保护公民的生命安全和生命尊严在常态和应急状态下均不受非法侵害。</p><p style=\"text-align: left;\">——全面提高公共安全保障能力。完善和落实安全生产责任制，加强安全生产监管执法，有效遏制重特大安全事故。提高安全生产水平，加强生物安全风险防控。</p><p style=\"text-align: left;\">——完善应急状态下生命权保障体系。修改突发事件应对法，构建系统完备、科学规范、运行有效的应急管理法律体系，加强应急管理标准化工作。健全应急救助体系，提升自然灾害防御工程标准，加强应急物资保障体系建设，提高应急救助水平和物资保障能力。</p><p style=\"text-align: left;\">——严格慎重适用死刑。强化死刑复核程序，规范死刑复核监督程序，严格落实死刑案件报备和审查机制。细化死刑案件法律适用标准和诉讼程序规则，确保死刑只适用于极少数罪行极其严重的犯罪分子。</p><p style=\"text-align: left;\">（二）人身权利</p><p style=\"text-align: left;\">在立法、执法和司法中充分尊重和保障公民的各项人身权利，坚持依法惩治犯罪与保障人权相统一。</p><p style=\"text-align: left;\">——依法保障人格权利。实施人格权侵害禁令制度，制定相关指导意见或者司法解释，使人格权得到及时有效的保护。</p><p style=\"text-align: left;\">——依法惩处“软暴力”犯罪。禁止采取跟踪贴靠、滋扰纠缠、聚众造势等侵害人身权利的手段实施“软暴力”。</p><p style=\"text-align: left;\">——严禁非法拘禁。对有组织地多次短时间非法拘禁他人的，以非法拘禁罪定罪处罚。加大力度查处国家机关工作人员利用职权实施非法拘禁等侵犯公民人身权利的犯罪。</p><p style=\"text-align: left;\">——降低审前羁押率。落实社会危险性条件证明制度，对无社会危险性或者采取非羁押性强制措施足以防止社会危害性的依法不捕。健全羁押必要性审查机制，推进延长羁押期限实质化审查，规范和完善取保候审等非羁押监管措施。</p><p style=\"text-align: left;\">——严禁刑讯逼供。严禁刑讯逼供和以威胁、引诱、欺骗以及其他非法方法收集证据，不得强迫任何人证实自己有罪。落实执法全过程记录机制。全面覆盖各类执法活动，实现不同执法环节、不同记录方式有机衔接，各类执法记录资料闭环管理。</p><p style=\"text-align: left;\">——健全监督机制。加强对刑事立案和侦查活动的监督。加强对剥夺、限制人身自由强制措施的监督。完善刑事执行监督机制。</p><p style=\"text-align: left;\">——保障被羁押人、罪犯合法权利。完善看守所管理制度，加强被羁押人权利保障。严禁体罚、虐待、侮辱、殴打罪犯或纵容他人殴打罪犯。加强监所医疗卫生专业化建设，提升医疗救治能力，保障被羁押人的生命和健康。</p><p style=\"text-align: left;\">——实施社区矫正法。维护社区矫正对象合法权益。充分发挥社会力量的作用，加强对社区矫正对象的教育帮扶。</p><p style=\"text-align: left;\">——保障戒毒人员合法权利。落实禁毒法和戒毒条例，完善自愿戒毒、社区戒毒、强制隔离戒毒、社区康复等多种措施相互补充的戒毒康复工作体制。加强教育戒治工作体系，提升教育矫治、戒毒医疗、生活卫生、安全管理水平，提高教育戒治质量。</p><p style=\"text-align: left;\">（三）个人信息权益</p><p style=\"text-align: left;\">加强个人信息保护，完善有关法律制度、监管执法和宣传，切实维护网络和数据安全。</p><p style=\"text-align: left;\">——完善个人信息保护法律制度。制定个人信息保护法，明确处理个人信息应遵循的原则，细化、完善个人信息处理规则，严格限制处理敏感个人信息，明确个人在个人信息处理活动中的权利，强化个人信息处理者的合规管理义务，设置严格的法律责任。制定出台个人信息保护配套法规、标准及司法解释，不断完善个人信息保护制度体系。</p><p style=\"text-align: left;\">——深入开展个人信息保护监管执法和宣传。加强个人信息保护监管执法工作，通过开展系列专项行动严格查处违法收集、使用个人信息行为，建立个人信息侵权投诉举报制度，加强执法队伍能力和执法水平建设，不断提高执法水平。通过国家网络安全宣传周以及报刊、广播、电视、网络、新媒体等，多渠道、多形式开展个人信息保护普法宣传、教育，推动公民个人信息保护意识的提升。</p><p style=\"text-align: left;\">——维护网络和数据安全。加强网络安全法、数据安全法等法律实施工作，落实网络安全和数据安全保护相关制度措施，保障网络免受干扰、破坏或者未经授权的访问，防止网络数据泄露或者被窃取、篡改，加强网络和数据安全风险监测，及时处置安全事件，严厉打击窃取网络数据、非法买卖个人信息等违法犯罪活动，切实保障网络数据和个人信息安全。</p><p style=\"text-align: left;\">（四）宗教信仰自由</p><p style=\"text-align: left;\">贯彻宗教信仰自由政策，依法保障公民宗教信仰自由，促进宗教关系和谐，依法管理宗教事务，坚持独立自主自办原则，积极引导宗教与社会主义社会相适应，保障宗教界人士和信教群众的合法权益。</p><p style=\"text-align: left;\">——贯彻落实宗教信仰自由政策。完善宗教事务管理制度体系，制定出台《宗教事务条例》配套规章，依法规范政府管理宗教事务的行为，保障宗教界人士和信教群众的合法权益。开展法治宣传教育，引导宗教界和信教群众增强法治观念，依法维护合法权益，在法律范围内开展活动。开展宗教活动场所法人登记，加大宗教活动场所合法权益的保障力度。大力支持宗教团体办好宗教院校，健全宗教人才培养体系。</p><p style=\"text-align: left;\">——维护和保障宗教界合法权益。维护佛道教界合法权益，支持佛道教界提升信仰建设、文化建设，维护佛道教活动场所清净庄严氛围。保障穆斯林群众合法权益，支持中国伊斯兰教协会组织穆斯林群众赴沙特朝觐。加大对天主教、基督教神学思想建设及成果转化的支持和保护。支持和鼓励各宗教依法依规开展公益慈善活动。</p><p style=\"text-align: left;\">——开展宗教领域国际交流。支持和鼓励各宗教在独立自主、平等友好、相互尊重的基础上开展对外交流，建立、发展、巩固同海外宗教界的友好关系。</p><p style=\"text-align: left;\">——抵制宗教极端主义。反对宗教极端思想，帮助信教群众抵御极端思想侵害。依法惩治冒用宗教名义从事暴力恐怖和民族分裂活动的违法犯罪行为。</p><p style=\"text-align: left;\">（五）选举权和被选举权</p><p style=\"text-align: left;\">保障公民依法行使选举权和被选举权。采取直接选举和间接选举相结合的方式，选举产生全国人民代表大会代表和地方各级人民代表大会代表。</p><p style=\"text-align: left;\">——完成五级人民代表大会换届选举。严格落实选举法，最广泛动员和组织全国10亿多选民参加选举，直接选举产生200多万名县乡两级人大代表，并逐级选举产生设区的市级人大代表、省级人大代表、全国人大代表。</p><p style=\"text-align: left;\">——增加基层人大代表数量。保证各级人大代表中有适当数量的工人、农民、专业技术人员基层代表。依照2020年修改的选举法，重新确定县乡人大代表名额，新增县乡人大代表名额向基层群众、社区工作者等倾斜。</p><p style=\"text-align: left;\">——保障流动人口的选举权和被选举权。为流动人口参选创造便利条件。放宽流动人口特别是已经取得居住证的流动人口在现居住地参选的条件。</p><p style=\"text-align: left;\">（六）知情权和参与权</p><p style=\"text-align: left;\">保障公民、法人和其他组织依法获取政府信息，保证人民依法通过各种途径和形式管理国家事务、经济文化事业和社会事务，切实保障公民的知情权和参与权。</p><p style=\"text-align: left;\">——加强基层政务公开标准化规范化建设。全面推进基层政务决策公开、执行公开、管理公开、服务公开、结果公开。建设全国统一的基层政务公开标准体系。加强和改善信息无障碍服务环境，为老年人、残疾人等网上获取政务信息、办理服务事项、享有公共服务提供便利。</p><p style=\"text-align: left;\">——加强突发事件信息发布。健全公共安全、重大疫情、灾害事故应急报道机制，及时准确发布权威信息，主动回应社会关切。</p><p style=\"text-align: left;\">——全面推进司法公开。完善审判公开、检务公开的内容、程序和方式并动态调整。各级检察机关设立新闻发言人，健全新闻发布常态化机制，落实重大案件信息发布制度。</p><p style=\"text-align: left;\">——完善公民对立法、监督工作的参与机制。完善法律法规规章制定、国民经济和社会发展规划纲要编制等重大公共决策的公众参与机制。法律草案公开征求意见，完善公众意见采纳情况反馈机制。发挥全国人大常委会法工委基层立法联系点、预算工委基层联系点作用，充分听取人民群众对相关工作的意见建议。</p><p style=\"text-align: left;\">——推动协商民主广泛、多层、制度化发展。统筹推进政党协商、人大协商、政府协商、政协协商、人民团体协商、基层协商以及社会组织协商。全国政协通过双周协商座谈会、提案办理协商、网络议政、远程协商、对口协商、界别协商等形式，拓展协商广度和深度。健全城乡社区协商制度，拓宽群众参与基层治理的制度化渠道。</p><p style=\"text-align: left;\">——完善基层群众自治制度。推进基层直接民主制度化、规范化、程序化。鼓励制定居民自治章程、居民公约、村规民约、村民自治章程。落实村（居）务公开制度。</p><p style=\"text-align: left;\">——健全企事业单位民主管理制度。深入推进厂务公开，规范民主程序，保障职工知情权、参与权、表达权和监督权，有效参与企事业单位民主管理。发挥职工代表大会在涉及职工福利及社会保险制度政策落实过程中的积极作用。企业制定、修改或者决定直接涉及劳动者切身利益的规章制度或者重大事项方案，必须提交职代会审议，并向职工公开。</p><p style=\"text-align: left;\">——鼓励社会力量参与社会治理。发挥群团组织和社会组织在社会治理中的作用，畅通和规范市场主体、新社会阶层、社会工作者和志愿者等参与社会治理的途径。</p><p style=\"text-align: left;\">（七）表达权和监督权</p><p style=\"text-align: left;\">依法保障公民的表达权和监督权，丰富表达手段，畅通公民诉求表达渠道，发挥公民监督在监督体系中的作用。</p><p style=\"text-align: left;\">——依法保障和规范网络表达。依法建好用好互联网，为公民通过网络反映问题、表达诉求、建言献策提供充分的便利。运用大数据、云计算、人工智能等技术手段，全面收集、及时回应民众的意见建议。</p><p style=\"text-align: left;\">——完善信访制度。完善和落实信访制度，依法及时就地解决群众合理诉求。推进诉访分离和分类处理工作，将信访纳入法治化轨道。构建汇集社情民意工作平台，健全人民建议征集制度，及时了解民情、听取民意、汇集民智。完善网上受理信访制度，有序推进信访信息系统互联互通、信息共享。健全集中解决信访突出问题长效机制，落实信访工作责任制，加大解决信访问题的力度，切实维护公民合法权益。</p><p style=\"text-align: left;\">——完善人大监督制度。加强宪法实施和监督，落实宪法解释程序机制，推进合宪性审查，建立健全涉及宪法问题的事先审查和咨询制度，完善备案审查制度。</p><p style=\"text-align: left;\">——加强对行政权力的监督。研究起草行政程序法，修改行政复议法，加大对行政行为的监督纠错力度。保障公民和社会组织通过申请行政复议、提起行政诉讼对行政机关进行监督的权利。</p><p style=\"text-align: left;\">——强化社会监督。鼓励媒体和公民通过新闻报道和舆论进行社会监督。完善人民监督员监督制度。细化保护检举控告人情形的法律规定，明确界定打击报复行为及其法律后果。</p><p style=\"text-align: left;\">——加强基层监督。建立健全村（居）务监督委员会，促进村级事务公开公平公正，切实保障村（居）民合法权益和村（居）集体利益。编制村级小微权力清单，建立健全小微权力监督制度，形成群众监督、村务委员会监督、上级党组织和有关部门监督与会计核算监督、审计监督等全程实时、多方联网的监督体系。</p><p style=\"text-align: left;\">（八）获得公正审判的权利</p><p style=\"text-align: left;\">全面落实司法责任制，推进审判体系和审判能力现代化，保障当事人获得公正审判的权利，努力让人民群众在每一个司法案件中感受到公平正义。</p><p style=\"text-align: left;\">——全面落实司法责任制。确保人民法院依法独立公正行使审判权，健全司法履职保障和违法审判责任追究机制。构建公正高效的检察权运行机制和公平合理的司法责任认定、追究机制，强化检察机关法律监督职能。</p><p style=\"text-align: left;\">——完善执法司法制约监督体系改革和建设。推进公安机关受立案制度改革，切实落实执法过错责任追究制度。加强检察机关对刑事立案、侦查、审判活动的监督。</p><p style=\"text-align: left;\">——深化以审判为中心的刑事诉讼制度改革。保护当事人诉讼权利。严格执行庭前会议、非法证据排除、法庭调查三项规程，推进庭审实质化。完善对证人、鉴定人、被害人、报案人等的保护措施。完善认罪认罚从宽制度。</p><p style=\"text-align: left;\">——加强律师执业权利保障。推进刑事案件律师辩护全覆盖。保障刑事辩护律师依法执业权利。加强和改进看守所律师会见工作，进一步增加律师会见室数量，完善预约会见机制。</p><p style=\"text-align: left;\">——促进和规范法律援助工作。制定法律援助法。向经济困难公民和符合法定条件的其他当事人无偿提供法律咨询、代理、刑事辩护、值班律师法律帮助等法律服务。落实刑事诉讼法及相关配套制度关于法律援助规定，提升刑事法律援助服务的可及性和实效性。</p><p style=\"text-align: left;\">——推进人民陪审员制度改革。实施人民陪审员法，落实人民陪审员选任新机制，完善人民陪审员参审和管理保障机制，强化人民陪审员实质参审能力，细化人民陪审员参审案件范围。</p><p style=\"text-align: left;\">——加强诉讼服务体系建设。努力提供普惠均等、便捷高效、智能精准的诉讼服务。探索建立民生案件“绿色通道”，对涉及弱势群体和基本民生的案件实行快立、快审、快结、快执。</p><p style=\"text-align: left;\">——推进智慧诉讼服务建设。研发智能辅助软件，为当事人提供诉讼风险评估、诉前调解建议、自助查询咨询、业务网上办理等服务，切实减轻人民群众诉累。</p><p style=\"text-align: left;\">——完善公益诉讼法律制度。拓展公益诉讼案件范围，探索建立生态环境、食品药品安全领域民事公益诉讼惩罚性赔偿制度。</p><p style=\"text-align: left;\">——完善国家赔偿和司法救助制度。完善国家赔偿和司法救助制度，加强国家赔偿审判工作，依法维护赔偿请求人合法权益。建立健全司法救助与社会救助的衔接机制。</p><p style=\"text-align: left;\">三、环境权利</p><p style=\"text-align: left;\">实施可持续发展战略，落实减污降碳总要求，推动绿色发展，构建生态文明体系，完善生态环境法律法规制度体系，加快推动绿色低碳发展，改善生态环境质量，不断满足人民群众日益增长的优美生态环境需要，促进人与自然和谐共生。</p><p style=\"text-align: left;\">（一）污染防治</p><p style=\"text-align: left;\">深入打好污染防治攻坚战，持续改善生态环境质量。</p><p style=\"text-align: left;\">——推进空气质量持续改善。继续开展大气污染防治行动，统筹大气污染物与温室气体协同减排，推进全国空气质量持续改善，基本消除重污染天气。地级及以上城市PM2.5平均浓度下降10%，氮氧化物和挥发性有机物排放总量分别下降10%以上。</p><p style=\"text-align: left;\">——持续强化水污染防治。开展城市黑臭水体治理环境保护专项行动，基本消除城市建成区内黑臭水体。加强城镇（园区）污水处理环境管理，推进乡镇级集中式饮用水水源保护区划定。开展重点海域综合治理，推进美丽海湾保护与建设。</p><p style=\"text-align: left;\">——推进土壤污染防治。巩固提升农用地安全利用水平，实施农药化肥减量行动，治理农膜污染。推动土壤污染重点监管单位落实土壤污染防治义务。</p><p style=\"text-align: left;\">——加强污水和废弃物治理。推进城镇污水管网全覆盖，城市污泥无害化处置率达到90%，地级及以上缺水城市污水资源化利用率超过25%。以主要产业基地为重点布局危险废弃物集中利用处置设施。加快建设地级及以上城市医疗废弃物集中处理设施，健全县域医疗废弃物收集转运处置体系。</p><p style=\"text-align: left;\">——优化城乡垃圾治理。地级及以上城市基本建立生活垃圾分类投放、分类收集、分类运输、分类处理系统，全国城市生活垃圾回收利用率达到35%以上。全面推进农村生活垃圾治理，基本实现收运处置体系全覆盖。</p><p style=\"text-align: left;\">——加强环境法治建设。完善生态环境法律法规制度体系。推动黄河治理、噪声污染防治、海洋环境保护、碳排放权交易管理、生态环境监测等法律法规的制修订工作。稳步推进生态环境标准制修订。推进生态环境保护综合执法改革。依法严厉打击生态环境领域违法犯罪行为。</p><p style=\"text-align: left;\">（二）生态环境信息公开</p><p style=\"text-align: left;\">加大环境信息公开力度，切实保障公众知情权。</p><p style=\"text-align: left;\">——加强政府环境信息公开。负有生态环境保护职责的部门通过政府网站、公报、新闻发布会以及媒体等便于公众知晓的方式，主动向社会公开生态环境信息。</p><p style=\"text-align: left;\">——完善企业环境信息依法披露制度。制定环境信息依法披露管理办法，推进环境信息依法披露制度改革，确定强制性披露主体和披露内容，完善披露形式，及时披露主要环境信息。规范上市公司、发债企业环境信息依法披露，强化环境信息强制性披露行业管理，建立环境信息共享机制。</p><p style=\"text-align: left;\">（三）环境决策公众参与</p><p style=\"text-align: left;\">制定实施环境影响评价法，落实环境影响评价公众参与办法，促进公众有效参与环境决策。</p><p style=\"text-align: left;\">——鼓励公众参与环境影响评价。对于可能造成不良环境影响并直接涉及公众环境权益的专项规划，鼓励公众参与环境影响评价。</p><p style=\"text-align: left;\">——健全公众环境监督机制。支持全国各地“12369环境保护投诉举报电话”与“12345政务服务便民热线”归并运行工作。做好微信网络环保举报工作，督促解决公众身边突出生态环境问题。</p><p style=\"text-align: left;\">（四）环境公益诉讼和生态环境损害赔偿</p><p style=\"text-align: left;\">深入开展环境公益诉讼，拓宽环境公益诉讼受案范围，完善环境公益诉讼制度和生态环境损害赔偿制度。</p><p style=\"text-align: left;\">——加强环境公益诉讼制度建设。探索开展预防性环境公益诉讼，研究制定环境保护禁止令制度相关规则，发布环境公益诉讼指导性案例。</p><p style=\"text-align: left;\">——完善生态环境损害赔偿制度。省级、地级政府作为本行政区域生态环境损害索赔权利人，依法追究损害生态环境责任者的赔偿责任，修复生态环境，维护国家生态安全。</p><p style=\"text-align: left;\">（五）国土空间生态保护修复</p><p style=\"text-align: left;\">坚持山水林田湖草系统治理，加大生态保护和修复力度，着力提高生态系统自我修复能力和稳定性，共建地球生命共同体。</p><p style=\"text-align: left;\">——实施重要生态系统保护和修复重大工程。加快推进青藏高原生态屏障区、黄河重点生态区、长江重点生态区和东北森林带、北方防沙带、南方丘陵山地带、海岸带等生态屏障建设。加强长江、黄河等大江大河和重要湖泊湿地生态保护治理，湿地保护率提高到55%。推行草原森林河流湖泊休养生息，健全耕地休耕轮作制度。构建国家—地方互联互通的重要生态系统保护和修复重大工程监测监管平台，开展生态状况评价监测。</p><p style=\"text-align: left;\">——构建自然保护地体系。严格管控自然保护地范围内非生态活动，推进三江源、东北虎豹、大熊猫和海南热带雨林等国家公园建设，新整合设立秦岭、黄河口等国家公园。</p><p style=\"text-align: left;\">——实施生物多样性保护重大工程。建设珍稀濒危野生动植物基因保存库、救护繁育场所，专项拯救48种极度濒危野生动物和50种极小种群野生植物。</p><p style=\"text-align: left;\">（六）应对气候变化</p><p style=\"text-align: left;\">减少温室气体排放，增强气候变化适应能力，积极参与全球气候变化治理，实现可持续发展。</p><p style=\"text-align: left;\">——努力实现碳达峰、碳中和目标。制定《关于全面贯彻新发展理念做好碳达峰碳中和工作的意见》，编制《2030年前碳排放达峰行动方案》，构建碳达峰、碳中和“1+N”政策体系，落实应对气候变化国家自主贡献目标。完善能源消费总量和强度双控制度，重点控制化石能源消费。实施以碳强度控制为主、碳排放总量控制为辅的制度，支持有条件的地方和重点行业、重点企业率先达到碳排放峰值。提升生态碳汇能力，强化国土空间规划和用途管控，有效发挥森林、草原、湿地、海洋、土壤、冻土的固碳作用，提升生态系统碳汇增量。加强青藏高原综合科学考察研究。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">　　——提升适应气候变化能力。编制实施《国家适应气候变化战略2035》，加强全球气候变暖对中国承受力脆弱地区影响的监测和评估，提升城乡建设、农业生产、基础设施适应气候变化能力。</p><p style=\"text-align: left;\">——加强气候变化国际合作。坚持公平、共同但有区别的责任及各自能力原则，建设性参与和引领应对气候变化国际合作，推动落实《联合国气候变化框架公约》及其《巴黎协定》，积极开展气候变化南南合作。</p><p style=\"text-align: left;\">四、特定群体权益保障</p><p style=\"text-align: left;\">完善对少数民族、妇女、儿童、老年人、残疾人等各类特定群体权益的平等保障和特殊保护，建立常态和非常态相结合的特定群体权益保障机制，促进所有人的全面发展。</p><p style=\"text-align: left;\">（一）少数民族权益</p><p style=\"text-align: left;\">坚持和完善民族区域自治制度，铸牢中华民族共同体意识，支持民族地区加快发展，保障各少数民族的合法权益。</p><p style=\"text-align: left;\">——依法治理民族事务。全面贯彻落实民族区域自治法，健全民族工作法律法规体系，确保各族公民在法律面前人人平等，反对一切形式的民族歧视。</p><p style=\"text-align: left;\">——保障少数民族平等参与管理国家和社会事务的权利。全国人民代表大会代表名额的分配保证各民族都有适当数量的代表。有少数民族聚居的地方，保证每一聚居的少数民族都有代表参加当地的人民代表大会。保证55个少数民族都有本民族的全国人大代表，人口特少的民族至少也有一名代表。积极培养、选拔少数民族干部。</p><p style=\"text-align: left;\">——保障少数民族经济发展权利。完善差别化区域政策，支持和帮助民族地区加快发展，深入推进兴边富民行动，不断提高各族群众生活水平。</p><p style=\"text-align: left;\">——保障少数民族受教育权利。提高民族地区教育质量和水平，加大国家通用语言文字推广力度。加快普及民族地区学前教育，均衡发展民族地区义务教育。培养各级各类少数民族人才。进一步健全教育对口支援机制。</p><p style=\"text-align: left;\">——保障少数民族学习使用和发展本民族语言文字的权利。国家依法保障少数民族语言文字在行政司法、新闻出版、广播影视、文化教育等各领域的合法使用。加强对少数民族语言文字的科学保护。</p><p style=\"text-align: left;\">——保障少数民族文化权利。推动各民族文化的传承保护和创新交融，提升民族地区公共文化服务水平。保护和传承少数民族传统文化。做好少数民族古籍保护、抢救、整理、出版和研究工作。开展少数民族古籍数字化建设。办好第六届全国少数民族文艺会演。开展好少数民族语电视剧译制片源捐赠工作。</p><p style=\"text-align: left;\">（二）妇女权益</p><p style=\"text-align: left;\">贯彻落实男女平等基本国策，全面落实《中国妇女发展纲要（2021—2030年）》。持续改善妇女发展环境，促进妇女依法平等行使权利、参与经济社会发展，共享发展成果。</p><p style=\"text-align: left;\">——促进妇女平等参与管理国家和社会事务。保障妇女平等享有政治权利，推动妇女广泛参与社会事务和民主管理。逐步提高女性在各级人大代表、政协委员中的比例。培养选拔更多女干部。村（居）民委员会成员中女性比例稳步提高。大力推动基层妇女广泛参与协商议事。</p><p style=\"text-align: left;\">——完善妇女权益保障制度。修改妇女权益保障法。检察机关积极探索开展相关领域公益诉讼。对通过大众传媒或者其他方式贬低损害妇女人格、侵害妇女财产权益、在就业等领域歧视妇女以及不履行预防和制止家庭暴力职责等侵害不特定多数妇女合法权益、损害社会公共利益的行为，检察机关可以发出检察建议或提起公益诉讼。</p><p style=\"text-align: left;\">——保障妇女人身权利。倡导性别平等、责任共担的新型家庭文化。预防和制止针对妇女的一切形式家庭暴力，依法适用人身安全保护令和家庭暴力告诫制度，有效预防和依法打击性侵、拐卖妇女的犯罪行为。提升预防和制止性骚扰的法律意识，有效遏制针对女性的性骚扰。保护妇女免遭网络违法犯罪行为的侵害。</p><p style=\"text-align: left;\">——保障妇女财产权益。保障妇女在家庭关系中的财产所有权和继承权，保障妇女对婚姻家庭关系中共有财产享有知情权和平等处理权。保障农村妇女平等享有土地承包经营权、宅基地使用权等不动产权益，平等享有集体经济组织收益分配、土地征收或征用安置补偿权益。</p><p style=\"text-align: left;\">——保障妇女平等享有受教育权利。依法保障女性平等接受义务教育、高中阶段教育及高等教育的权利。提高女性科学素养，培养女性科技人才，为女性终身学习提供支持。</p><p style=\"text-align: left;\">——保障妇女平等就业权利。消除就业性别歧视。规范招聘行为，用人单位在招聘过程中，除国家另有规定外，不得限定男性或者规定男性优先。将就业性别歧视问题纳入劳动保障监察工作之中。对涉嫌就业性别歧视的用人单位开展联合约谈。</p><p style=\"text-align: left;\">——促进工作场所性别平等。用人单位建立健全劳动就业、薪酬待遇、职业发展、生育保护以及帮助职工平衡工作和家庭责任、预防和制止职场性骚扰等工作场所性别平等制度机制，推动建立家庭友好型工作场所。支持和帮助女性实现家庭和工作的平衡。</p><p style=\"text-align: left;\">——缓解妇女的家庭育儿负担。发展普惠托育服务体系。支持150个城市利用社会力量发展综合托育服务机构和社区托育服务设施。着力补齐农村地区和城市新增人口集中地区资源短板，进一步完善普惠性学前教育保障机制。加强中小学课后服务，探索实施父母育儿假，减轻家庭生育、养育、教育负担。</p><p style=\"text-align: left;\">——保障妇女享有卫生健康服务。加大妇女常见病防治力度，完善宫颈癌、乳腺癌综合防治体系和救助政策。探索开展青春期、育龄期、孕产期、更老年期保健专科服务，提供妇女全生命周期健康服务。巩固母婴安全五项制度，确保全国孕产妇死亡率持续稳中有降。加强符合妇女需求的母婴室、卫生厕所建设。</p><p style=\"text-align: left;\">——关心关爱困境妇女。各级人民政府和有关部门采取必要措施，确保低收入、残疾、患重病、留守、单亲母亲等困境妇女享有相应的权益保障，为其提供必要的生活救助和关爱服务。</p><p style=\"text-align: left;\">（三）儿童权益</p><p style=\"text-align: left;\">坚持儿童优先原则，全面落实《中国儿童发展纲要（2021—2030年）》，保障儿童生存、发展、受保护和参与的权利，缩小儿童发展的城乡、区域和群体差距，促进儿童健康、全面发展。</p><p style=\"text-align: left;\">——保障未成年人的人格权。学校、幼儿园的教职员工应当尊重未成年人人格尊严，不得对未成年人实施体罚、变相体罚或者其他侮辱人格尊严的行为。禁止对儿童进行殴打、体罚、虐待等一切形式的家庭暴力。任何组织或者个人不得通过网络以文字、图片、音视频等形式，对未成年人实施侮辱、诽谤、威胁或者恶意损害形象等网络欺凌行为。保护未成年人隐私权和个人信息，新闻媒体采访报道涉及未成年人事件应当客观、审慎和适度，不得侵犯未成年人的名誉、隐私和其他合法权益。</p><p style=\"text-align: left;\">——保障儿童的知情权和参与权。重视听取并采纳儿童的合理意见。未成年人的父母或者其他监护人应当根据未成年人的年龄和智力发展状况，在作出与未成年人权益有关的决定前，听取未成年人的意见，充分考虑其真实意愿。</p><p style=\"text-align: left;\">——提高儿童健康权保障水平。完善儿童健康服务体系，预防和控制儿童疾病，减少儿童死亡和严重出生缺陷发生。引导儿童科学均衡饮食。有效控制儿童肥胖和近视，实施学龄前儿童营养改善计划。儿童每天接触户外自然光不少于1小时，保障儿童每天至少1小时中等及以上强度运动，合理安排儿童作息。加强儿童心理健康教育和服务。中小学校普遍配备心理健康教育教师。</p><p style=\"text-align: left;\">——保障残疾儿童权利。完善儿童残疾筛查、诊断、治疗、康复一体化工作机制，建立残疾报告和信息共享制度。建设残疾儿童康复救助定点机构，推动残疾儿童普遍享有基本康复服务。保障具备学习能力的适龄残疾儿童少年不失学辍学。加强残疾儿童教育的师资能力和资源建设。</p><p style=\"text-align: left;\">——关爱困境儿童。加强困境儿童分类保障。健全孤儿和事实无人抚养儿童保障机制。支持儿童福利机构建设，提升孤弃儿童集中养治教康水平。孤儿、事实无人抚养儿童保障标准不低于当地平均生活水平。加强对农村留守儿童的关爱保护。落实流浪儿童街面巡查和转介处置职责，依法依规为流浪儿童提供生活照料、身份查询、接送返回等服务。</p><p style=\"text-align: left;\">——加强对未成年人的司法保护。全面实施修订后的未成年人保护法、预防未成年人犯罪法，完善家庭、学校、社会、网络、政府、司法各领域的未成年人保护，严厉打击侵害未成年人的违法犯罪行为，有效预防未成年人犯罪。青少年犯罪率控制在全球较低水平。健全未成年人司法工作体系。全面推行未成年被害人“一站式”取证工作机制，保障未成年人的名誉权、隐私权和其他合法权益。推动完善刑事案件未成年被害人司法救助制度。</p><p style=\"text-align: left;\">——禁止使用童工。任何组织或者个人不得招用未满16周岁未成年人。禁止任何单位或者个人为不满16周岁的未成年人介绍就业。禁止不满16周岁的未成年人从事个体经营活动。依法查处使用童工的违法行为。</p><p style=\"text-align: left;\">——预防和依法严惩性侵害未成年人的违法犯罪行为。加强未成年人预防性侵害教育，提高未成年人家庭、学校、社区识别防范性侵害和发现报告的意识和能力，落实强制报告制度。建立全国统一的性侵害、虐待、拐卖、暴力伤害等违法犯罪人员信息查询系统，完善落实入职查询、从业禁止制度。探索建立性侵害未成年人犯罪人员信息公开制度。严格落实外籍教师无犯罪证明备案制度。</p><p style=\"text-align: left;\">——预防控制针对儿童的暴力伤害。宣传倡导对儿童暴力零容忍理念，强化国家、社会、学校、家庭保护责任，落实儿童暴力伤害发现、报告、干预机制。密切接触未成年人的机构和人员履行强制报告责任，鼓励公众依法劝阻、制止、检举、控告针对儿童的暴力行为。依法严惩针对儿童实施暴力的违法犯罪行为。提高儿童遭受意外伤害和暴力伤害的紧急救援、医疗救助、康复服务水平。</p><p style=\"text-align: left;\">——有效预防未成年人犯罪。立足于教育和保护相结合，坚持预防为主、提前干预，对未成年人的不良行为和严重不良行为及时进行分级预防、干预和矫治。推动落实家庭教育指导工作，教育未成年人的父母或者其他监护人培养未成年人法律素养，提高守法意识。落实未成年人违法犯罪分级干预制度。依法从严惩治胁迫、教唆、引诱、欺骗等利用未成年人实施有组织犯罪的行为。</p><p style=\"text-align: left;\">——完善落实未成年人监护制度。强化父母或者其他监护人履行对未成年子女的抚养、教育和保护职责，依法规范父母或者其他监护人委托他人照护未成年子女的行为。强化村（居）民委员会对父母或者其他监护人监护和委托照护的监督责任，村（居）民委员会儿童主任切实做好未成年人监护风险或受到监护侵害情况的发现、核实、报告工作。符合法定情形的未成年人由县级以上民政部门代表国家进行监护，确保突发事件情况下无人照料的未成年人及时获得临时监护。</p><p style=\"text-align: left;\">（四）老年人权益</p><p style=\"text-align: left;\">提高老年人权益保障水平，实现老有所养、老有所依、老有所乐、老有所为。</p><p style=\"text-align: left;\">——健全老年人福利保障。推进基本养老服务体系建设，逐步提高老年人福利水平，强化对失能、部分失能特困老年人的兜底保障，完善经济困难高龄失能老年人补贴制度和独居留守老年人探访关爱制度，逐步实现老有所养。</p><p style=\"text-align: left;\">——健全养老服务体系。构建居家社区机构相协调、医养康养相结合的养老服务体系和健康支撑体系。支持城市依托基层医疗卫生资源建设医养结合设施。优化城乡养老机构床位结构，全国养老机构护理型床位占比不低于55%，更好满足高龄失能失智老年人护理服务需求。支持500个区县建设连锁化运营、标准化管理的示范性社区居家养老服务网络，提供失能护理、日间照料以及助餐助浴助洁助医助行等服务。推进城市新建城区、新建居住（社）区按照标准配套建设社区养老服务设施，配建设施达标率达到100%；老旧城区、已建成居住（社）区基本补齐社区养老服务设施。培养培训200万名养老护理员，每千名老年人配1名及以上社会工作者。</p><p style=\"text-align: left;\">——实施老年人家庭适老化改造工程。为特殊困难高龄、失能、残疾老年人家庭实施适老化改造，配备康复辅助器具和防走失装置等设施。发展康复辅助器具社区服务体系。</p><p style=\"text-align: left;\">——促进老年人实现“老有所为”。继续办好老年大学，扩大老年教育资源供给，为老年人的再学习和再发展创造更多的条件和机会。</p><p style=\"text-align: left;\">——为老年人交通出行提供便利。优化交通信号配时和交通标志标线等交通管理设施，完善行人驻足区、过街安全岛等二次过街设施，为老年人等群体提供出行便利和安全保障。</p><p style=\"text-align: left;\">——提供适老智慧服务。运用智能技术，对社区和居家养老服务设施、医疗康复设施和机构进行无障碍化、便捷化、智能化改造。推进大数据、人工智能、5G技术在居家护理、点餐用餐、健康管理、远程就诊、紧急救助、智慧出行、消防安全、休闲娱乐等方面的应用。指导基础电信企业持续完善线下营业厅“面对面”服务，推动与老年人基本生活密切相关的互联网网站、移动互联网应用完成适老化改造，扩大适老化智能终端产品供给。切实解决老年人运用智能技术困难，便利老年人使用智能化产品和服务。</p><p style=\"text-align: left;\">——加强对老年人权益的司法保障。依法及时化解财产、赡养、婚姻等方面涉及老年人权益的纠纷，防范和惩治诈骗、非法集资等严重侵害老年人权益的犯罪行为。</p><p style=\"text-align: left;\">（五）残疾人权益</p><p style=\"text-align: left;\">促进残疾人的平等参与和社会融入，加强对困难和重度残疾人帮扶力度，保障残疾人共享社会发展成果。</p><p style=\"text-align: left;\">——保障残疾人参与权。涉及残疾人权益的重要立法充分听取残疾人、残疾人组织的意见。不断拓展残疾人和残疾人组织民主参与、民主协商渠道，有效保障残疾人的知情权、参与权、表达权和监督权。</p><p style=\"text-align: left;\">——完善残疾人福利保障。落实困难残疾人生活补贴和重度残疾人护理补贴制度，鼓励有条件的地区扩大补贴范围，普遍建立补贴标准动态调整机制。落实为重度残疾人缴纳城乡居民基本养老保险个人缴费等政策。支持符合条件的农村低收入残疾人家庭实施危房改造。对符合条件的城镇残疾人家庭优先配租公租房。</p><p style=\"text-align: left;\">——提升残疾人康复服务质量。推动残疾预防和康复服务一体化发展。加强残疾人康复医疗，落实残疾儿童康复救助制度，实施残疾人精准康复行动，为残疾人提供基本康复服务。健全残疾人康复服务设施，加强残疾人社区康复，建成康复大学。开展全国残疾人康复专业技术人员规范化培训。在精神卫生服务能力不足的地区建设100个精神卫生福利设施，为困难精神障碍患者提供集中养护、康复服务。</p><p style=\"text-align: left;\">——完善特殊教育保障机制。巩固适龄残疾儿童少年义务教育普及水平，积极发展学前特殊教育，着力发展以职业教育为主的高中阶段特殊教育，稳步发展高等特殊教育，推动融合教育发展。有效推进孤独症儿童教育。推广国家通用手语和国家通用盲文。禁止任何基于残疾的教育歧视。</p><p style=\"text-align: left;\">——促进残疾人就业。落实残疾人就业支持政策，多渠道、多形式促进残疾人就业创业。落实税费减免政策，为残疾人自主创业和企业吸纳残疾人就业提供更多的补贴支持。推动残疾人就业辅导员制度建设，开发更多残疾人就业岗位。为城乡200万残疾人提供职业技能培训，实现城镇新增就业残疾人数达到50万人。</p><p style=\"text-align: left;\">——全面推进无障碍环境建设。编制《无障碍通用规范》。推动城市道路、公共交通、居住社区、公共服务设施和残疾人服务设施、残疾人集中就业单位等加快无障碍环境建设和改造。补贴110万户困难重度残疾人家庭无障碍设施改造，提升社区无障碍建设水平。加快推进信息无障碍建设，支持研发生产信息无障碍终端产品，探索传统无障碍设施设备数字化和智能化升级。加强手语、盲文学科建设和人才培养。确保提供合理便利，促进残疾人的深度融入和平等参与。</p><p style=\"text-align: left;\">——支持残疾人智能辅助器具的研发和生产。利用先进智能技术，在建筑、设施、交通工具、生活用品、环境、方案和服务等的设计中，改良传统残疾人辅助器具，研发新型残疾人辅助器具。</p><p style=\"text-align: left;\">五、人权教育和研究</p><p style=\"text-align: left;\">将人权教育纳入国民教育体系，开展人权研究，加强人权培训，普及人权知识，增强全社会尊重和保障人权的意识。</p><p style=\"text-align: left;\">（一）学校人权教育</p><p style=\"text-align: left;\">在各级各类学校开展生动活泼、形式多样的人权教育，使学生牢固树立人权意识。</p><p style=\"text-align: left;\">——加强中小学人权教育。将有关珍惜生命、平等意识、保护隐私、未成年人权益等人权知识融入中小学相关课程，丰富教育方式手段，拓展教育实践活动，以生动活泼的形式促进人权知识在中小学的普及。</p><p style=\"text-align: left;\">——鼓励高校开展人权通识教育和专业人才培养。持续支持高等院校开设人权相关专业课和通识课，编写人权相关教材。加强人权学科建设和人才培养。探索在师范类院校建立人权师资培训中心。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">　　——支持国家人权教育与培训基地的建设。加强国家人权教育与培训基地建设，再增加3家。依托国家人权教育与培训基地探索建设人权国际教育交流中心。</p><p style=\"text-align: left;\">（二）人权研究</p><p style=\"text-align: left;\">立足中国人权实践，加强人权研究能力建设，不断推出优秀人权研究成果。</p><p style=\"text-align: left;\">——开展人权研究。鼓励高校、研究机构开展人权理论、制度、政策研究，结合中国和世界人权发展实践，不断丰富发展人权理论。定期编写出版《中国人权事业发展报告》蓝皮书。</p><p style=\"text-align: left;\">——支持人权机构建设。支持在社科院、党校（行政学院）系统建立国家人权研究机构，新设3家国家人权研究基地。</p><p style=\"text-align: left;\">——支持人权学术出版物。支持人权学术期刊建设，鼓励人权研究者发表高质量学术研究论文。加大国家社科基金等对人权理论和政策研究的资助力度。支持出版人权研究专著、论文集，奖励优秀人权研究成果。</p><p style=\"text-align: left;\">——支持举办人权研讨会。鼓励举办多种形式的人权理论和政策研讨会，在国内外开展广泛的人权学术交流。</p><p style=\"text-align: left;\">（三）人权知识培训</p><p style=\"text-align: left;\">在公共部门和企事业单位开展人权知识培训，形成尊重和保障人权的职场文化。</p><p style=\"text-align: left;\">——强化公职人员人权知识培训。将人权知识培训作为公务员考试、初任培训和任职培训的重要内容，并在年度培训中结合工作实际开展人权培训。把人权法治教育作为国家工作人员学法用法工作的重要内容。编写出版人权知识培训教材。探索建立人权教育培训示范单位。</p><p style=\"text-align: left;\">——支持企事业单位人权知识培训。鼓励企事业单位建立常设性的人权培训制度，在人力资源培训中增加人权内容，形成尊重和保障人权的企事业文化，加强对海外中国企业的人权知识培训。探索评设人权培训示范企业。</p><p style=\"text-align: left;\">（四）人权知识普及</p><p style=\"text-align: left;\">运用多种形式，广泛传播、普及人权知识，形成尊重和保障人权的社会氛围。</p><p style=\"text-align: left;\">——以多种形式普及人权知识。在博物馆、科技馆、图书馆、文化馆等公共活动场所开展人权专题展览、讲座等活动，运用报刊、广播、电视、网络新媒体等渠道向民众普及人权知识。鼓励中国人权网等网站介绍人权动态，提供人权类文献数据资源。</p><p style=\"text-align: left;\">——加强人权议题新闻发布。围绕中国人权事业发展和国际人权热点举行新闻发布会、吹风会、新闻茶座，发表白皮书和专题报告，及时提供权威信息。发布涉及人权司法保障的典型案例。</p><p style=\"text-align: left;\">六、参与全球人权治理</p><p style=\"text-align: left;\">认真践行国际承诺，深度参与国际人权事务，推动完善全球人权治理体系，构建人类命运共同体。</p><p style=\"text-align: left;\">（一）履行国际人权条约义务</p><p style=\"text-align: left;\">及时向相关人权条约机构提交履约报告，与条约机构开展建设性对话，结合中国国情采纳和落实条约机构合理可行的建议。</p><p style=\"text-align: left;\">——参加联合国经社文权利委员会审议中国执行《经济、社会及文化权利国际公约》第三次履约报告。</p><p style=\"text-align: left;\">——撰写《禁止酷刑和其他残忍、不人道或有辱人格的待遇或处罚公约》第七次履约报告，并提交联合国禁止酷刑委员会审议。</p><p style=\"text-align: left;\">——撰写《儿童权利公约》第五次、第六次合并报告（包括《〈儿童权利公约〉关于买卖儿童、儿童卖淫和儿童色情制品问题的任择议定书》《〈儿童权利公约〉关于儿童卷入武装冲突问题的任择议定书》相关内容），并提交联合国儿童权利委员会审议。</p><p style=\"text-align: left;\">——撰写《消除一切形式种族歧视国际公约》第十八至二十次合并履约报告，并提交联合国消除种族歧视委员会审议。</p><p style=\"text-align: left;\">——参加联合国残疾人权利委员会审议中国执行《残疾人权利公约》第二、三次合并履约报告。</p><p style=\"text-align: left;\">——撰写中国执行《消除对妇女一切形式歧视公约》第九次履约报告，并提交联合国消除对妇女歧视委员会审议。</p><p style=\"text-align: left;\">——鼓励和支持国内社会组织积极参与中国执行相关人权条约履约审议准备工作。</p><p style=\"text-align: left;\">（二）深度参与联合国人权机构工作</p><p style=\"text-align: left;\">深度参与联合国人权机构工作，发挥引领性和建设性作用，维护国际人权事业健康可持续发展。</p><p style=\"text-align: left;\">——在联合国人权理事会等多边人权机构提出中国倡议和主张。推动同等重视经济、社会、文化权利和公民、政治权利，以公正、客观和非选择性方式开展工作，反对将人权问题政治化。竞选2024—2026年度人权理事会成员。进一步发挥中国社会组织作用，积极参与联合国人权理事会等多边人权机制活动。</p><p style=\"text-align: left;\">——参加联合国人权理事会国别人权审议。认真落实中国在人权理事会第三轮国别人权审议中接受的建议，积极参与第四轮国别人权审议。鼓励和支持国内社会组织参与第四轮国别人权审议相关工作。</p><p style=\"text-align: left;\">——继续参与联合国人权条约机构改革进程。推动条约机构在公约授权范围内客观、公正、独立开展工作。</p><p style=\"text-align: left;\">（三）开展建设性人权对话与合作</p><p style=\"text-align: left;\">继续在平等和相互尊重的基础上，开展国际人权交流与合作，促进相互理解，增进人权共识。</p><p style=\"text-align: left;\">——积极拓展与联合国人权事务高级专员办公室交流与合作。继续与人权理事会特别机制开展合作，认真答复特别机制来函。根据接待能力，邀请有关特别机制访华。继续推荐中国专家竞聘特别机制。</p><p style=\"text-align: left;\">——稳步推进人权对话与交流。继续在平等和相互尊重基础上与有关国家开展人权对话，增进了解，相互借鉴。加强与发展中国家和集团人权磋商与合作，向有需要的发展中国家提供人权技术援助。继续举办“南南人权论坛”，参与亚欧非正式人权研讨会等区域、次区域人权交流活动。在求同存异、相互尊重、互学互鉴的基础上与各国政党就人权议题开展交流。</p><p style=\"text-align: left;\">——鼓励和支持国内社会组织积极参与国际人权交流合作。继续举办北京人权论坛、中欧人权研讨会、中美司法与人权研讨会、中德人权研讨会等国际会议。</p><p style=\"text-align: left;\">（四）为全球人权事业作出中国贡献</p><p style=\"text-align: left;\">倡导和平、发展、公平、正义、民主、自由的全人类共同价值，促进全球人权治理更加公平、公正、合理、包容，推动构建人类命运共同体。</p><p style=\"text-align: left;\">——全面落实联合国2030年可持续发展议程。继续将2030年可持续发展议程纳入国家中长期发展规划，全面推进国内落实工作。继续分享落实经验，深化务实合作。帮助其他发展中国家做好2030年可持续发展议程的落实工作，为保护和促进发展权作出更大贡献。</p><p style=\"text-align: left;\">——积极参与国际人权规则制定和议程设置。完善全球人权治理，推动构建公平正义、合理有效的国际人权体系，为国际人权事业发展贡献中国智慧和方案。</p><p style=\"text-align: left;\">——继续帮助发展中国家提升发展能力。提供发展援助，开展人道主义援助，以合作促发展，以发展促人权，在维护世界和平与发展、保护促进发展权等方面作出贡献。加大对非洲国家特别是非洲最不发达国家援助力度，深化南南合作，减少贫困、改善民生、促进共同发展。</p><p style=\"text-align: left;\">——构建人类卫生健康命运共同体。积极应对重大公共卫生事件，支持世卫组织在全球抗疫合作中发挥应有作用。加强“一带一路”卫生（含中医药）合作，共建“健康丝绸之路”。积极落实“中非公共卫生合作计划”，共同应对重大突发性疾病挑战，支持非洲公共卫生防控和救治体系建设。建立30个中非对口医院合作机制，加快建设非洲疾控中心总部，助力非洲提升疾病防控能力。</p><p style=\"text-align: left;\">——促进全球供应链中的负责任商业行为。促进工商业在对外经贸合作、投资中，遵循《联合国工商业与人权指导原则》，实施人权尽责，履行尊重和促进人权的社会责任。建设性参与联合国工商业与人权条约谈判进程。</p><p style=\"text-align: left;\">七、实施、监督和评估</p><p style=\"text-align: left;\">完善国家人权行动计划联席会议机制，强化《行动计划》实施与监督，完善第三方评估机制，全方位、多层次保障《行动计划》的全面落实。</p><p style=\"text-align: left;\">——各级地方政府、中央和国家机关各有关部门应高度重视，结合各部门工作职责和各地区特点，制定并细化《行动计划》实施方案，采取切实有效的措施，确保顺利完成各项目标任务。</p><p style=\"text-align: left;\">——国家人权行动计划联席会议机制开展阶段性调研、检查、监督和评估，逐步完善第三方评估机制，开发并建立量化评估指标体系，及时公布评估报告。</p><p style=\"text-align: left;\">——将《行动计划》作为人权教育、研究、培训和知识普及的重要内容，使各级党政干部充分认识《行动计划》的重要性，切实提高实施工作的自觉性和主动性。</p><p style=\"text-align: left;\">——鼓励新闻媒体在《行动计划》的宣传、实施、监督和评估等方面发挥积极作用。</p><p style=\"text-align: left;\">（新华社北京9月9日电）</p><p style=\"text-align: left;\"><br></p>', '本站编辑', '', '2023-04-11 20:57:03');
INSERT INTO `articles` VALUES (30, 4, '法律法规', '中华人民共和国国防法', '<p style=\"text-align: left;\">（1997年3月14日第八届全国人民代表大会第五次会议通过　根据2009年8月27日第十一届全国人民代表大会常务委员会第十次会议《关于修改部分法律的决定》修正　2020年12月26日第十三届全国人民代表大会常务委员会第二十四次会议修订）<br></p><p style=\"text-align: left;\">目　　录</p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">第一章　总　　则</p><p style=\"text-align: left;\">第二章　国家机构的国防职权</p><p style=\"text-align: left;\">第三章　武装力量</p><p style=\"text-align: left;\">第四章　边防、海防、空防和其他重大安全领域防卫</p><p style=\"text-align: left;\">第五章　国防科研生产和军事采购</p><p style=\"text-align: left;\">第六章　国防经费和国防资产</p><p style=\"text-align: left;\">第七章　国防教育</p><p style=\"text-align: left;\">第八章　国防动员和战争状态 </p><p style=\"text-align: left;\">第九章　公民、组织的国防义务和权利 </p><p style=\"text-align: left;\">第十章　军人的义务和权益 </p><p style=\"text-align: left;\">第十一章　对外军事关系 </p><p style=\"text-align: left;\">第十二章　附　　则</p><p style=\"text-align: left;\"> </p><p style=\"text-align: center;\"><strong>第一章 总 则</strong></p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">第一条　为了建设和巩固国防，保障改革开放和社会主义现代化建设的顺利进行，实现中华民族伟大复兴，根据宪法，制定本法。 </p><p style=\"text-align: left;\">第二条　国家为防备和抵抗侵略，制止武装颠覆和分裂，保卫国家主权、统一、领土完整、安全和发展利益所进行的军事活动，以及与军事有关的政治、经济、外交、科技、教育等方面的活动，适用本法。 </p><p style=\"text-align: left;\">第三条　国防是国家生存与发展的安全保障。</p><p style=\"text-align: left;\">国家加强武装力量建设，加强边防、海防、空防和其他重大安全领域防卫建设，发展国防科研生产，普及全民国防教育，完善国防动员体系，实现国防现代化。 </p><p style=\"text-align: left;\">第四条　国防活动坚持以马克思列宁主义、毛泽东思想、邓小平理论、“三个代表”重要思想、科学发展观、习近平新时代中国特色社会主义思想为指导，贯彻习近平强军思想，坚持总体国家安全观，贯彻新时代军事战略方针，建设与我国国际地位相称、与国家安全和发展利益相适应的巩固国防和强大武装力量。</p><p style=\"text-align: left;\">第五条　国家对国防活动实行统一的领导。 </p><p style=\"text-align: left;\">第六条　中华人民共和国奉行防御性国防政策，独立自主、自力更生地建设和巩固国防，实行积极防御，坚持全民国防。 </p><p style=\"text-align: left;\">国家坚持经济建设和国防建设协调、平衡、兼容发展，依法开展国防活动，加快国防和军队现代化，实现富国和强军相统一。 </p><p style=\"text-align: left;\">第七条　保卫祖国、抵抗侵略是中华人民共和国每一个公民的神圣职责。 </p><p style=\"text-align: left;\">中华人民共和国公民应当依法履行国防义务。 </p><p style=\"text-align: left;\">一切国家机关和武装力量、各政党和各人民团体、企业事业组织、社会组织和其他组织，都应当支持和依法参与国防建设，履行国防职责，完成国防任务。 </p><p style=\"text-align: left;\">第八条　国家和社会尊重、优待军人，保障军人的地位和合法权益，开展各种形式的拥军优属活动，让军人成为全社会尊崇的职业。 </p><p style=\"text-align: left;\">中国人民解放军和中国人民武装警察部队开展拥政爱民活动，巩固军政军民团结。 </p><p style=\"text-align: left;\">第九条　中华人民共和国积极推进国际军事交流与合作，维护世界和平，反对侵略扩张行为。 </p><p style=\"text-align: left;\">第十条　对在国防活动中作出贡献的组织和个人，依照有关法律、法规的规定给予表彰和奖励。 </p><p style=\"text-align: left;\">第十一条　任何组织和个人违反本法和有关法律，拒绝履行国防义务或者危害国防利益的，依法追究法律责任。 </p><p style=\"text-align: left;\">公职人员在国防活动中，滥用职权、玩忽职守、徇私舞弊的，依法追究法律责任。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: center;\"><strong>第二章 国家机构的国防职权</strong></p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">第十二条　全国人民代表大会依照宪法规定，决定战争和和平的问题，并行使宪法规定的国防方面的其他职权。 </p><p style=\"text-align: left;\">全国人民代表大会常务委员会依照宪法规定，决定战争状态的宣布，决定全国总动员或者局部动员，并行使宪法规定的国防方面的其他职权。 </p><p style=\"text-align: left;\">第十三条　中华人民共和国主席根据全国人民代表大会的决定和全国人民代表大会常务委员会的决定，宣布战争状态，发布动员令，并行使宪法规定的国防方面的其他职权。 </p><p style=\"text-align: left;\">第十四条　国务院领导和管理国防建设事业，行使下列职权： </p><p style=\"text-align: left;\">（一）编制国防建设的有关发展规划和计划； </p><p style=\"text-align: left;\">（二）制定国防建设方面的有关政策和行政法规； </p><p style=\"text-align: left;\">（三）领导和管理国防科研生产； </p><p style=\"text-align: left;\">（四）管理国防经费和国防资产； </p><p style=\"text-align: left;\">（五）领导和管理国民经济动员工作和人民防空、国防交通等方面的建设和组织实施工作； </p><p style=\"text-align: left;\">（六）领导和管理拥军优属工作和退役军人保障工作； </p><p style=\"text-align: left;\">（七）与中央军事委员会共同领导民兵的建设，征兵工作，边防、海防、空防和其他重大安全领域防卫的管理工作； </p><p style=\"text-align: left;\">（八）法律规定的与国防建设事业有关的其他职权。</p><p style=\"text-align: left;\">第十五条　中央军事委员会领导全国武装力量，行使下列职权： </p><p style=\"text-align: left;\">（一）统一指挥全国武装力量； </p><p style=\"text-align: left;\">（二）决定军事战略和武装力量的作战方针； </p><p style=\"text-align: left;\">（三）领导和管理中国人民解放军、中国人民武装警察部队的建设，制定规划、计划并组织实施； </p><p style=\"text-align: left;\">（四）向全国人民代表大会或者全国人民代表大会常务委员会提出议案； </p><p style=\"text-align: left;\">（五）根据宪法和法律，制定军事法规，发布决定和命令； </p><p style=\"text-align: left;\">（六）决定中国人民解放军、中国人民武装警察部队的体制和编制，规定中央军事委员会机关部门、战区、军兵种和中国人民武装警察部队等单位的任务和职责； </p><p style=\"text-align: left;\">（七）依照法律、军事法规的规定，任免、培训、考核和奖惩武装力量成员； </p><p style=\"text-align: left;\">（八）决定武装力量的武器装备体制，制定武器装备发展规划、计划，协同国务院领导和管理国防科研生产； </p><p style=\"text-align: left;\">（九）会同国务院管理国防经费和国防资产； </p><p style=\"text-align: left;\">（十）领导和管理人民武装动员、预备役工作； </p><p style=\"text-align: left;\">（十一）组织开展国际军事交流与合作； </p><p style=\"text-align: left;\">（十二）法律规定的其他职权。 </p><p style=\"text-align: left;\">第十六条　中央军事委员会实行主席负责制。 </p><p style=\"text-align: left;\">第十七条　国务院和中央军事委员会建立协调机制，解决国防事务的重大问题。 </p><p style=\"text-align: left;\">中央国家机关与中央军事委员会机关有关部门可以根据情况召开会议，协调解决有关国防事务的问题。</p><p style=\"text-align: left;\">第十八条　地方各级人民代表大会和县级以上地方各级人民代表大会常务委员会在本行政区域内，保证有关国防事务的法律、法规的遵守和执行。 </p><p style=\"text-align: left;\">地方各级人民政府依照法律规定的权限，管理本行政区域内的征兵、民兵、国民经济动员、人民防空、国防交通、国防设施保护，以及退役军人保障和拥军优属等工作。 </p><p style=\"text-align: left;\">第十九条　地方各级人民政府和驻地军事机关根据需要召开军地联席会议，协调解决本行政区域内有关国防事务的问题。 </p><p style=\"text-align: left;\">军地联席会议由地方人民政府的负责人和驻地军事机关的负责人共同召集。军地联席会议的参加人员由会议召集人确定。 </p><p style=\"text-align: left;\">军地联席会议议定的事项，由地方人民政府和驻地军事机关根据各自职责和任务分工办理，重大事项应当分别向上级报告。</p><p style=\"text-align: left;\"> </p><p style=\"text-align: center;\"><strong>第三章 武装力量</strong></p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">第二十条　中华人民共和国的武装力量属于人民。它的任务是巩固国防，抵抗侵略，保卫祖国，保卫人民的和平劳动，参加国家建设事业，全心全意为人民服务。 </p><p style=\"text-align: left;\">第二十一条　中华人民共和国的武装力量受中国共产党领导。武装力量中的中国共产党组织依照中国共产党章程进行活动。 </p><p style=\"text-align: left;\">第二十二条　中华人民共和国的武装力量，由中国人民解放军、中国人民武装警察部队、民兵组成。 </p><p style=\"text-align: left;\">中国人民解放军由现役部队和预备役部队组成，在新时代的使命任务是为巩固中国共产党领导和社会主义制度，为捍卫国家主权、统一、领土完整，为维护国家海外利益，为促进世界和平与发展，提供战略支撑。现役部队是国家的常备军，主要担负防卫作战任务，按照规定执行非战争军事行动任务。预备役部队按照规定进行军事训练、执行防卫作战任务和非战争军事行动任务；根据国家发布的动员令，由中央军事委员会下达命令转为现役部队。 </p><p style=\"text-align: left;\">中国人民武装警察部队担负执勤、处置突发社会安全事件、防范和处置恐怖活动、海上维权执法、抢险救援和防卫作战以及中央军事委员会赋予的其他任务。</p><p style=\"text-align: left;\">民兵在军事机关的指挥下，担负战备勤务、执行非战争军事行动任务和防卫作战任务。 </p><p style=\"text-align: left;\">第二十三条　中华人民共和国的武装力量必须遵守宪法和法律。 </p><p style=\"text-align: left;\">第二十四条　中华人民共和国武装力量建设坚持走中国特色强军之路，坚持政治建军、改革强军、科技强军、人才强军、依法治军，加强军事训练，开展政治工作，提高保障水平，全面推进军事理论、军队组织形态、军事人员和武器装备现代化，构建中国特色现代作战体系，全面提高战斗力，努力实现党在新时代的强军目标。 </p><p style=\"text-align: left;\">第二十五条　中华人民共和国武装力量的规模应当与保卫国家主权、安全、发展利益的需要相适应。 </p><p style=\"text-align: left;\">第二十六条　中华人民共和国的兵役分为现役和预备役。军人和预备役人员的服役制度由法律规定。 </p><p style=\"text-align: left;\">中国人民解放军、中国人民武装警察部队依照法律规定实行衔级制度。 </p><p style=\"text-align: left;\">第二十七条　中国人民解放军、中国人民武装警察部队在规定岗位实行文职人员制度。 </p><p style=\"text-align: left;\">第二十八条　中国人民解放军军旗、军徽是中国人民解放军的象征和标志。中国人民武装警察部队旗、徽是中国人民武装警察部队的象征和标志。 </p><p style=\"text-align: left;\">公民和组织应当尊重中国人民解放军军旗、军徽和中国人民武装警察部队旗、徽。 </p><p style=\"text-align: left;\">中国人民解放军军旗、军徽和中国人民武装警察部队旗、徽的图案、样式以及使用管理办法由中央军事委员会规定。 </p><p style=\"text-align: left;\">第二十九条　国家禁止任何组织或者个人非法建立武装组织，禁止非法武装活动，禁止冒充军人或者武装力量组织。</p><p style=\"text-align: center;\"><strong> </strong></p><p style=\"text-align: center;\"><strong>第四章 边防、海防、空防和其他重大安全领域防卫</strong></p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">第三十条　中华人民共和国的领陆、领水、领空神圣不可侵犯。国家建设强大稳固的现代边防、海防和空防，采取有效的防卫和管理措施，保卫领陆、领水、领空的安全，维护国家海洋权益。 </p><p style=\"text-align: left;\">国家采取必要的措施，维护在太空、电磁、网络空间等其他重大安全领域的活动、资产和其他利益的安全。 </p><p style=\"text-align: left;\">第三十一条　中央军事委员会统一领导边防、海防、空防和其他重大安全领域的防卫工作。</p><p style=\"text-align: left;\">中央国家机关、地方各级人民政府和有关军事机关，按照规定的职权范围，分工负责边防、海防、空防和其他重大安全领域的管理和防卫工作，共同维护国家的安全和利益。</p><p style=\"text-align: left;\">第三十二条　国家根据边防、海防、空防和其他重大安全领域防卫的需要，加强防卫力量建设，建设作战、指挥、通信、测控、导航、防护、交通、保障等国防设施。各级人民政府和军事机关应当依照法律、法规的规定，保障国防设施的建设，保护国防设施的安全。</p><p style=\"text-align: left;\"> </p><p style=\"text-align: center;\"><strong>第五章 国防科研生产和军事采购</strong></p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">第三十三条　国家建立和完善国防科技工业体系，发展国防科研生产，为武装力量提供性能先进、质量可靠、配套完善、便于操作和维修的武器装备以及其他适用的军用物资，满足国防需要。 </p><p style=\"text-align: left;\">第三十四条　国防科技工业实行军民结合、平战结合、军品优先、创新驱动、自主可控的方针。 </p><p style=\"text-align: left;\">国家统筹规划国防科技工业建设，坚持国家主导、分工协作、专业配套、开放融合，保持规模适度、布局合理的国防科研生产能力。 </p><p style=\"text-align: left;\">第三十五条　国家充分利用全社会优势资源，促进国防科学技术进步，加快技术自主研发，发挥高新技术在武器装备发展中的先导作用，增加技术储备，完善国防知识产权制度，促进国防科技成果转化，推进科技资源共享和协同创新，提高国防科研能力和武器装备技术水平。 </p><p style=\"text-align: left;\">第三十六条　国家创造有利的环境和条件，加强国防科学技术人才培养，鼓励和吸引优秀人才进入国防科研生产领域，激发人才创新活力。</p><p style=\"text-align: left;\">国防科学技术工作者应当受到全社会的尊重。国家逐步提高国防科学技术工作者的待遇，保护其合法权益。 </p><p style=\"text-align: left;\">第三十七条　国家依法实行军事采购制度，保障武装力量所需武器装备和物资、工程、服务的采购供应。 </p><p style=\"text-align: left;\">第三十八条　国家对国防科研生产实行统一领导和计划调控；注重发挥市场机制作用，推进国防科研生产和军事采购活动公平竞争。 </p><p style=\"text-align: left;\">国家为承担国防科研生产任务和接受军事采购的组织和个人依法提供必要的保障条件和优惠政策。地方各级人民政府应当依法对承担国防科研生产任务和接受军事采购的组织和个人给予协助和支持。 </p><p style=\"text-align: left;\">承担国防科研生产任务和接受军事采购的组织和个人应当保守秘密，及时高效完成任务，保证质量，提供相应的服务保障。 </p><p style=\"text-align: left;\">国家对供应武装力量的武器装备和物资、工程、服务，依法实行质量责任追究制度。</p><p style=\"text-align: left;\"> </p><p style=\"text-align: center;\"><strong>第六章 国防经费和国防资产</strong></p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">第三十九条　国家保障国防事业的必要经费。国防经费的增长应当与国防需求和国民经济发展水平相适应。 </p><p style=\"text-align: left;\">国防经费依法实行预算管理。 </p><p style=\"text-align: left;\">第四十条　国家为武装力量建设、国防科研生产和其他国防建设直接投入的资金、划拨使用的土地等资源，以及由此形成的用于国防目的的武器装备和设备设施、物资器材、技术成果等属于国防资产。 </p><p style=\"text-align: left;\">国防资产属于国家所有。 </p><p style=\"text-align: left;\">第四十一条　国家根据国防建设和经济建设的需要，确定国防资产的规模、结构和布局，调整和处分国防资产。 </p><p style=\"text-align: left;\">国防资产的管理机构和占有、使用单位，应当依法管理国防资产，充分发挥国防资产的效能。</p><p style=\"text-align: left;\">第四十二条　国家保护国防资产不受侵害，保障国防资产的安全、完整和有效。</p><p style=\"text-align: left;\">禁止任何组织或者个人破坏、损害和侵占国防资产。未经国务院、中央军事委员会或者国务院、中央军事委员会授权的机构批准，国防资产的占有、使用单位不得改变国防资产用于国防的目的。国防资产中的技术成果，在坚持国防优先、确保安全的前提下，可以根据国家有关规定用于其他用途。 </p><p style=\"text-align: left;\">国防资产的管理机构或者占有、使用单位对不再用于国防目的的国防资产，应当按照规定报批，依法改作其他用途或者进行处置。</p><p style=\"text-align: left;\"> </p><p style=\"text-align: center;\"><strong>第七章 国防教育</strong></p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">第四十三条　国家通过开展国防教育，使全体公民增强国防观念、强化忧患意识、掌握国防知识、提高国防技能、发扬爱国主义精神，依法履行国防义务。 </p><p style=\"text-align: left;\">普及和加强国防教育是全社会的共同责任。 </p><p style=\"text-align: left;\">第四十四条　国防教育贯彻全民参与、长期坚持、讲求实效的方针，实行经常教育与集中教育相结合、普及教育与重点教育相结合、理论教育与行为教育相结合的原则。 </p><p style=\"text-align: left;\">第四十五条　国防教育主管部门应当加强国防教育的组织管理，其他有关部门应当按照规定的职责做好国防教育工作。 </p><p style=\"text-align: left;\">军事机关应当支持有关机关和组织开展国防教育工作，依法提供有关便利条件。 </p><p style=\"text-align: left;\">一切国家机关和武装力量、各政党和各人民团体、企业事业组织、社会组织和其他组织，都应当组织本地区、本部门、本单位开展国防教育。</p><p style=\"text-align: left;\">学校的国防教育是全民国防教育的基础。各级各类学校应当设置适当的国防教育课程，或者在有关课程中增加国防教育的内容。普通高等学校和高中阶段学校应当按照规定组织学生军事训练。 </p><p style=\"text-align: left;\">公职人员应当积极参加国防教育，提升国防素养，发挥在全民国防教育中的模范带头作用。 </p><p style=\"text-align: left;\">第四十六条　各级人民政府应当将国防教育纳入国民经济和社会发展计划，保障国防教育所需的经费。</p><p style=\"text-align: left;\"> </p><p style=\"text-align: center;\"><strong>第八章 国防动员和战争状态</strong></p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">第四十七条　中华人民共和国的主权、统一、领土完整、安全和发展利益遭受威胁时，国家依照宪法和法律规定，进行全国总动员或者局部动员。 </p><p style=\"text-align: left;\">第四十八条　国家将国防动员准备纳入国家总体发展规划和计划，完善国防动员体制，增强国防动员潜力，提高国防动员能力。</p><p style=\"text-align: left;\">第四十九条　国家建立战略物资储备制度。战略物资储备应当规模适度、储存安全、调用方便、定期更换，保障战时的需要。 </p><p style=\"text-align: left;\">第五十条　国家国防动员领导机构、中央国家机关、中央军事委员会机关有关部门按照职责分工，组织国防动员准备和实施工作。</p><p style=\"text-align: left;\">一切国家机关和武装力量、各政党和各人民团体、企业事业组织、社会组织、其他组织和公民，都必须依照法律规定完成国防动员准备工作；在国家发布动员令后，必须完成规定的国防动员任务。 </p><p style=\"text-align: left;\">第五十一条　国家根据国防动员需要，可以依法征收、征用组织和个人的设备设施、交通工具、场所和其他财产。 </p><p style=\"text-align: left;\">县级以上人民政府对被征收、征用者因征收、征用所造成的直接经济损失，按照国家有关规定给予公平、合理的补偿。 </p><p style=\"text-align: left;\">第五十二条　国家依照宪法规定宣布战争状态，采取各种措施集中人力、物力和财力，领导全体公民保卫祖国、抵抗侵略。</p><p style=\"text-align: left;\"> </p><p style=\"text-align: center;\"><strong>第九章 公民、组织的国防义务和权利</strong></p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">第五十三条　依照法律服兵役和参加民兵组织是中华人民共和国公民的光荣义务。 </p><p style=\"text-align: left;\">各级兵役机关和基层人民武装机构应当依法办理兵役工作，按照国务院和中央军事委员会的命令完成征兵任务，保证兵员质量。有关国家机关、人民团体、企业事业组织、社会组织和其他组织，应当依法完成民兵和预备役工作，协助完成征兵任务。 </p><p style=\"text-align: left;\">第五十四条　企业事业组织和个人承担国防科研生产任务或者接受军事采购，应当按照要求提供符合质量标准的武器装备或者物资、工程、服务。</p><p style=\"text-align: left;\">企业事业组织和个人应当按照国家规定在与国防密切相关的建设项目中贯彻国防要求，依法保障国防建设和军事行动的需要。车站、港口、机场、道路等交通设施的管理、运营单位应当为军人和军用车辆、船舶的通行提供优先服务，按照规定给予优待。 </p><p style=\"text-align: left;\">第五十五条　公民应当接受国防教育。 </p><p style=\"text-align: left;\">公民和组织应当保护国防设施，不得破坏、危害国防设施。 </p><p style=\"text-align: left;\">公民和组织应当遵守保密规定，不得泄露国防方面的国家秘密，不得非法持有国防方面的秘密文件、资料和其他秘密物品。 </p><p style=\"text-align: left;\">第五十六条　公民和组织应当支持国防建设，为武装力量的军事训练、战备勤务、防卫作战、非战争军事行动等活动提供便利条件或者其他协助。 </p><p style=\"text-align: left;\">国家鼓励和支持符合条件的公民和企业投资国防事业，保障投资者的合法权益并依法给予政策优惠。</p><p style=\"text-align: left;\">第五十七条　公民和组织有对国防建设提出建议的权利，有对危害国防利益的行为进行制止或者检举的权利。 </p><p style=\"text-align: left;\">第五十八条　民兵、预备役人员和其他公民依法参加军事训练，担负战备勤务、防卫作战、非战争军事行动等任务时，应当履行自己的职责和义务；国家和社会保障其享有相应的待遇，按照有关规定对其实行抚恤优待。 </p><p style=\"text-align: left;\">公民和组织因国防建设和军事活动在经济上受到直接损失的，可以依照国家有关规定获得补偿。</p><p style=\"text-align: left;\"> </p><p style=\"text-align: center;\"><strong>第十章 军人的义务和权益</strong></p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">第五十九条　军人必须忠于祖国，忠于中国共产党，履行职责，英勇战斗，不怕牺牲，捍卫祖国的安全、荣誉和利益。 </p><p style=\"text-align: left;\">第六十条　军人必须模范地遵守宪法和法律，遵守军事法规，执行命令，严守纪律。 </p><p style=\"text-align: left;\">第六十一条　军人应当发扬人民军队的优良传统，热爱人民，保护人民，积极参加社会主义现代化建设，完成抢险救灾等任务。 </p><p style=\"text-align: left;\">第六十二条　军人应当受到全社会的尊崇。</p><p style=\"text-align: left;\">国家建立军人功勋荣誉表彰制度。 </p><p style=\"text-align: left;\">国家采取有效措施保护军人的荣誉、人格尊严，依照法律规定对军人的婚姻实行特别保护。 </p><p style=\"text-align: left;\">军人依法履行职责的行为受法律保护。 </p><p style=\"text-align: left;\">第六十三条　国家和社会优待军人。 </p><p style=\"text-align: left;\">国家建立与军事职业相适应、与国民经济发展相协调的军人待遇保障制度。</p><p style=\"text-align: left;\">第六十四条　国家建立退役军人保障制度，妥善安置退役军人，维护退役军人的合法权益。 </p><p style=\"text-align: left;\">第六十五条　国家和社会抚恤优待残疾军人，对残疾军人的生活和医疗依法给予特别保障。 </p><p style=\"text-align: left;\">因战、因公致残或者致病的残疾军人退出现役后，县级以上人民政府应当及时接收安置，并保障其生活不低于当地的平均生活水平。 </p><p style=\"text-align: left;\">第六十六条　国家和社会优待军人家属，抚恤优待烈士家属和因公牺牲、病故军人的家属。</p><p style=\"text-align: center;\"><strong> </strong></p><p style=\"text-align: center;\"><strong>第十一章 对外军事关系</strong></p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">第六十七条　中华人民共和国坚持互相尊重主权和领土完整、互不侵犯、互不干涉内政、平等互利、和平共处五项原则，维护以联合国为核心的国际体系和以国际法为基础的国际秩序，坚持共同、综合、合作、可持续的安全观，推动构建人类命运共同体，独立自主地处理对外军事关系，开展军事交流与合作。 </p><p style=\"text-align: left;\">第六十八条　中华人民共和国遵循以联合国宪章宗旨和原则为基础的国际关系基本准则，依照国家有关法律运用武装力量，保护海外中国公民、组织、机构和设施的安全，参加联合国维和、国际救援、海上护航、联演联训、打击恐怖主义等活动，履行国际安全义务，维护国家海外利益。</p><p style=\"text-align: left;\">第六十九条　中华人民共和国支持国际社会实施的有利于维护世界和地区和平、安全、稳定的与军事有关的活动，支持国际社会为公正合理地解决国际争端以及国际军备控制、裁军和防扩散所做的努力，参与安全领域多边对话谈判，推动制定普遍接受、公正合理的国际规则。 </p><p style=\"text-align: left;\">第七十条　中华人民共和国在对外军事关系中遵守同外国、国际组织缔结或者参加的有关条约和协定。</p><p style=\"text-align: left;\"> </p><p style=\"text-align: center;\"><strong>第十二章 附 则</strong></p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\">第七十一条　本法所称军人，是指在中国人民解放军服现役的军官、军士、义务兵等人员。 </p><p style=\"text-align: left;\">本法关于军人的规定，适用于人民武装警察。 </p><p style=\"text-align: left;\">第七十二条　中华人民共和国特别行政区的防务，由特别行政区基本法和有关法律规定。 </p><p style=\"text-align: left;\">第七十三条　本法自2021年1月1日起施行。</p><p style=\"text-align: left;\"><br></p>', '本站编辑', '', '2023-04-11 20:58:00');
INSERT INTO `articles` VALUES (31, 4, '法律法规', '中华人民共和国预防未成年人犯罪法', '<p style=\"text-align: left;\"><br><br></p><p style=\"text-align: left;\"><span style=\"font-size: 16px;\">（1999年6月28日第九届全国人民代表大会常务委员会第十次会议通过 根据2012年10月26日第十一届全国人民代表大会常务委员会第二十九次会议《关于修改〈中华人民共和国预防未成年人犯罪法〉的决定》修正 2020年12月26日第十三届全国人民代表大会常务委员会第二十四次会议修订）</span></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\"><span style=\"font-size: 16px;\">目 录</span></p><p style=\"text-align: left;\"><span style=\"font-size: 16px;\">第一章 总 则</span></p><p style=\"text-align: left;\"><span style=\"font-size: 16px;\">第二章 预防犯罪的教育</span></p><p style=\"text-align: left;\"><span style=\"font-size: 16px;\">第三章 对不良行为的干预</span></p><p style=\"text-align: left;\"><span style=\"font-size: 16px;\">第四章 对严重不良行为的矫治</span></p><p style=\"text-align: left;\"><span style=\"font-size: 16px;\">第五章 对重新犯罪的预防</span></p><p style=\"text-align: left;\"><span style=\"font-size: 16px;\">第六章 法律责任</span></p><p style=\"text-align: left;\"><span style=\"font-size: 16px;\">第七章 附 则</span></p><p style=\"text-align: center;\"><strong>第一章 总 则</strong></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">第一条　为了保障未成年人身心健康，培养未成年人良好品行，有效预防未成年人违法犯罪，制定本法。</p><p style=\"text-align: left;\">第二条　预防未成年人犯罪，立足于教育和保护未成年人相结合，坚持预防为主、提前干预，对未成年人的不良行为和严重不良行为及时进行分级预防、干预和矫治。</p><p style=\"text-align: left;\">第三条　开展预防未成年人犯罪工作，应当尊重未成年人人格尊严，保护未成年人的名誉权、隐私权和个人信息等合法权益。</p><p style=\"text-align: left;\">第四条　预防未成年人犯罪，在各级人民政府组织下，实行综合治理。</p><p style=\"text-align: left;\">国家机关、人民团体、社会组织、企业事业单位、居民委员会、村民委员会、学校、家庭等各负其责、相互配合，共同做好预防未成年人犯罪工作，及时消除滋生未成年人违法犯罪行为的各种消极因素，为未成年人身心健康发展创造良好的社会环境。</p><p style=\"text-align: left;\">第五条　各级人民政府在预防未成年人犯罪方面的工作职责是：</p><p style=\"text-align: left;\">（一）制定预防未成年人犯罪工作规划；</p><p style=\"text-align: left;\">（二）组织公安、教育、民政、文化和旅游、市场监督管理、网信、卫生健康、新闻出版、电影、广播电视、司法行政等有关部门开展预防未成年人犯罪工作；</p><p style=\"text-align: left;\">（三）为预防未成年人犯罪工作提供政策支持和经费保障；</p><p style=\"text-align: left;\">（四）对本法的实施情况和工作规划的执行情况进行检查；</p><p style=\"text-align: left;\">（五）组织开展预防未成年人犯罪宣传教育；</p><p style=\"text-align: left;\">（六）其他预防未成年人犯罪工作职责。</p><p style=\"text-align: left;\">第六条　国家加强专门学校建设，对有严重不良行为的未成年人进行专门教育。专门教育是国民教育体系的组成部分，是对有严重不良行为的未成年人进行教育和矫治的重要保护处分措施。</p><p style=\"text-align: left;\">省级人民政府应当将专门教育发展和专门学校建设纳入经济社会发展规划。县级以上地方人民政府成立专门教育指导委员会，根据需要合理设置专门学校。</p><p style=\"text-align: left;\">专门教育指导委员会由教育、民政、财政、人力资源社会保障、公安、司法行政、人民检察院、人民法院、共产主义青年团、妇女联合会、关心下一代工作委员会、专门学校等单位，以及律师、社会工作者等人员组成，研究确定专门学校教学、管理等相关工作。</p><p style=\"text-align: left;\">专门学校建设和专门教育具体办法，由国务院规定。</p><p style=\"text-align: left;\">第七条　公安机关、人民检察院、人民法院、司法行政部门应当由专门机构或者经过专业培训、熟悉未成年人身心特点的专门人员负责预防未成年人犯罪工作。</p><p style=\"text-align: left;\">第八条　共产主义青年团、妇女联合会、工会、残疾人联合会、关心下一代工作委员会、青年联合会、学生联合会、少年先锋队以及有关社会组织，应当协助各级人民政府及其有关部门、人民检察院和人民法院做好预防未成年人犯罪工作，为预防未成年人犯罪培育社会力量，提供支持服务。</p><p style=\"text-align: left;\">第九条　国家鼓励、支持和指导社会工作服务机构等社会组织参与预防未成年人犯罪相关工作，并加强监督。</p><p style=\"text-align: left;\">第十条　任何组织或者个人不得教唆、胁迫、引诱未成年人实施不良行为或者严重不良行为，以及为未成年人实施上述行为提供条件。</p><p style=\"text-align: left;\">第十一条　未成年人应当遵守法律法规及社会公共道德规范，树立自尊、自律、自强意识，增强辨别是非和自我保护的能力，自觉抵制各种不良行为以及违法犯罪行为的引诱和侵害。</p><p style=\"text-align: left;\">第十二条　预防未成年人犯罪，应当结合未成年人不同年龄的生理、心理特点，加强青春期教育、心理关爱、心理矫治和预防犯罪对策的研究。</p><p style=\"text-align: left;\">第十三条　国家鼓励和支持预防未成年人犯罪相关学科建设、专业设置、人才培养及科学研究，开展国际交流与合作。</p><p style=\"text-align: left;\">第十四条　国家对预防未成年人犯罪工作有显著成绩的组织和个人，给予表彰和奖励。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: center;\"><strong>第二章 预防犯罪的教育</strong></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">第十五条　国家、社会、学校和家庭应当对未成年人加强社会主义核心价值观教育，开展预防犯罪教育，增强未成年人的法治观念，使未成年人树立遵纪守法和防范违法犯罪的意识，提高自我管控能力。</p><p style=\"text-align: left;\">第十六条　未成年人的父母或者其他监护人对未成年人的预防犯罪教育负有直接责任，应当依法履行监护职责，树立优良家风，培养未成年人良好品行；发现未成年人心理或者行为异常的，应当及时了解情况并进行教育、引导和劝诫，不得拒绝或者怠于履行监护职责。</p><p style=\"text-align: left;\">第十七条　教育行政部门、学校应当将预防犯罪教育纳入学校教学计划，指导教职员工结合未成年人的特点，采取多种方式对未成年学生进行有针对性的预防犯罪教育。</p><p style=\"text-align: left;\">第十八条　学校应当聘任从事法治教育的专职或者兼职教师，并可以从司法和执法机关、法学教育和法律服务机构等单位聘请法治副校长、校外法治辅导员。</p><p style=\"text-align: left;\">第十九条　学校应当配备专职或者兼职的心理健康教育教师，开展心理健康教育。学校可以根据实际情况与专业心理健康机构合作，建立心理健康筛查和早期干预机制，预防和解决学生心理、行为异常问题。</p><p style=\"text-align: left;\">学校应当与未成年学生的父母或者其他监护人加强沟通，共同做好未成年学生心理健康教育；发现未成年学生可能患有精神障碍的，应当立即告知其父母或者其他监护人送相关专业机构诊治。</p><p style=\"text-align: left;\">第二十条　教育行政部门应当会同有关部门建立学生欺凌防控制度。学校应当加强日常安全管理，完善学生欺凌发现和处置的工作流程，严格排查并及时消除可能导致学生欺凌行为的各种隐患。</p><p style=\"text-align: left;\">第二十一条　教育行政部门鼓励和支持学校聘请社会工作者长期或者定期进驻学校，协助开展道德教育、法治教育、生命教育和心理健康教育，参与预防和处理学生欺凌等行为。</p><p style=\"text-align: left;\">第二十二条　教育行政部门、学校应当通过举办讲座、座谈、培训等活动，介绍科学合理的教育方法，指导教职员工、未成年学生的父母或者其他监护人有效预防未成年人犯罪。</p><p style=\"text-align: left;\">学校应当将预防犯罪教育计划告知未成年学生的父母或者其他监护人。未成年学生的父母或者其他监护人应当配合学校对未成年学生进行有针对性的预防犯罪教育。</p><p style=\"text-align: left;\">第二十三条　教育行政部门应当将预防犯罪教育的工作效果纳入学校年度考核内容。</p><p style=\"text-align: left;\">第二十四条　各级人民政府及其有关部门、人民检察院、人民法院、共产主义青年团、少年先锋队、妇女联合会、残疾人联合会、关心下一代工作委员会等应当结合实际，组织、举办多种形式的预防未成年人犯罪宣传教育活动。有条件的地方可以建立青少年法治教育基地，对未成年人开展法治教育。</p><p style=\"text-align: left;\">第二十五条　居民委员会、村民委员会应当积极开展有针对性的预防未成年人犯罪宣传活动，协助公安机关维护学校周围治安，及时掌握本辖区内未成年人的监护、就学和就业情况，组织、引导社区社会组织参与预防未成年人犯罪工作。</p><p style=\"text-align: left;\">第二十六条　青少年宫、儿童活动中心等校外活动场所应当把预防犯罪教育作为一项重要的工作内容，开展多种形式的宣传教育活动。</p><p style=\"text-align: left;\">第二十七条　职业培训机构、用人单位在对已满十六周岁准备就业的未成年人进行职业培训时，应当将预防犯罪教育纳入培训内容。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: center;\"><strong>第三章 对不良行为的干预</strong></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">第二十八条　本法所称不良行为，是指未成年人实施的不利于其健康成长的下列行为：</p><p style=\"text-align: left;\">（一）吸烟、饮酒；</p><p style=\"text-align: left;\">（二）多次旷课、逃学；</p><p style=\"text-align: left;\">（三）无故夜不归宿、离家出走；</p><p style=\"text-align: left;\">（四）沉迷网络；</p><p style=\"text-align: left;\">（五）与社会上具有不良习性的人交往，组织或者参加实施不良行为的团伙；</p><p style=\"text-align: left;\">（六）进入法律法规规定未成年人不宜进入的场所；</p><p style=\"text-align: left;\">（七）参与赌博、变相赌博，或者参加封建迷信、邪教等活动；</p><p style=\"text-align: left;\">（八）阅览、观看或者收听宣扬淫秽、色情、暴力、恐怖、极端等内容的读物、音像制品或者网络信息等；</p><p style=\"text-align: left;\">（九）其他不利于未成年人身心健康成长的不良行为。</p><p style=\"text-align: left;\">第二十九条　未成年人的父母或者其他监护人发现未成年人有不良行为的，应当及时制止并加强管教。</p><p style=\"text-align: left;\">第三十条　公安机关、居民委员会、村民委员会发现本辖区内未成年人有不良行为的，应当及时制止，并督促其父母或者其他监护人依法履行监护职责。</p><p style=\"text-align: left;\">第三十一条　学校对有不良行为的未成年学生，应当加强管理教育，不得歧视；对拒不改正或者情节严重的，学校可以根据情况予以处分或者采取以下管理教育措施：</p><p style=\"text-align: left;\">（一）予以训导；</p><p style=\"text-align: left;\">（二）要求遵守特定的行为规范；</p><p style=\"text-align: left;\">（三）要求参加特定的专题教育；</p><p style=\"text-align: left;\">（四）要求参加校内服务活动；</p><p style=\"text-align: left;\">（五）要求接受社会工作者或者其他专业人员的心理辅导和行为干预；</p><p style=\"text-align: left;\">（六）其他适当的管理教育措施。</p><p style=\"text-align: left;\">第三十二条　学校和家庭应当加强沟通，建立家校合作机制。学校决定对未成年学生采取管理教育措施的，应当及时告知其父母或者其他监护人；未成年学生的父母或者其他监护人应当支持、配合学校进行管理教育。</p><p style=\"text-align: left;\">第三十三条　未成年学生偷窃少量财物，或者有殴打、辱骂、恐吓、强行索要财物等学生欺凌行为，情节轻微的，可以由学校依照本法第三十一条规定采取相应的管理教育措施。</p><p style=\"text-align: left;\">第三十四条　未成年学生旷课、逃学的，学校应当及时联系其父母或者其他监护人，了解有关情况；无正当理由的，学校和未成年学生的父母或者其他监护人应当督促其返校学习。</p><p style=\"text-align: left;\">第三十五条　未成年人无故夜不归宿、离家出走的，父母或者其他监护人、所在的寄宿制学校应当及时查找，必要时向公安机关报告。</p><p style=\"text-align: left;\">收留夜不归宿、离家出走未成年人的，应当及时联系其父母或者其他监护人、所在学校；无法取得联系的，应当及时向公安机关报告。</p><p style=\"text-align: left;\">第三十六条　对夜不归宿、离家出走或者流落街头的未成年人，公安机关、公共场所管理机构等发现或者接到报告后，应当及时采取有效保护措施，并通知其父母或者其他监护人、所在的寄宿制学校，必要时应当护送其返回住所、学校；无法与其父母或者其他监护人、学校取得联系的，应当护送未成年人到救助保护机构接受救助。</p><p style=\"text-align: left;\">第三十七条　未成年人的父母或者其他监护人、学校发现未成年人组织或者参加实施不良行为的团伙，应当及时制止；发现该团伙有违法犯罪嫌疑的，应当立即向公安机关报告。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: center;\"><strong>第四章 对严重不良行为的矫治</strong></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">第三十八条　本法所称严重不良行为，是指未成年人实施的有刑法规定、因不满法定刑事责任年龄不予刑事处罚的行为，以及严重危害社会的下列行为：</p><p style=\"text-align: left;\">（一）结伙斗殴，追逐、拦截他人，强拿硬要或者任意损毁、占用公私财物等寻衅滋事行为；</p><p style=\"text-align: left;\">（二）非法携带枪支、弹药或者弩、匕首等国家规定的管制器具；</p><p style=\"text-align: left;\">（三）殴打、辱骂、恐吓，或者故意伤害他人身体；</p><p style=\"text-align: left;\">（四）盗窃、哄抢、抢夺或者故意损毁公私财物；<br></p><p style=\"text-align: left;\">（五）传播淫秽的读物、音像制品或者信息等；<br></p><p style=\"text-align: left;\">（六）卖淫、嫖娼，或者进行淫秽表演；</p><p style=\"text-align: left;\">（七）吸食、注射毒品，或者向他人提供毒品；<br></p><p style=\"text-align: left;\">（八）参与赌博赌资较大；<br></p><p style=\"text-align: left;\">（九）其他严重危害社会的行为。<br></p><p style=\"text-align: left;\">第三十九条　未成年人的父母或者其他监护人、学校、居民委员会、村民委员会发现有人教唆、胁迫、引诱未成年人实施严重不良行为的，应当立即向公安机关报告。公安机关接到报告或者发现有上述情形的，应当及时依法查处；对人身安全受到威胁的未成年人，应当立即采取有效保护措施。<br></p><p style=\"text-align: left;\">第四十条　公安机关接到举报或者发现未成年人有严重不良行为的，应当及时制止，依法调查处理，并可以责令其父母或者其他监护人消除或者减轻违法后果，采取措施严加管教。<br></p><p style=\"text-align: left;\">第四十一条　对有严重不良行为的未成年人，公安机关可以根据具体情况，采取以下矫治教育措施：<br></p><p style=\"text-align: left;\">（一）予以训诫；<br></p><p style=\"text-align: left;\">（二）责令赔礼道歉、赔偿损失；<br></p><p style=\"text-align: left;\">（三）责令具结悔过；<br></p><p style=\"text-align: left;\">（四）责令定期报告活动情况；<br></p><p style=\"text-align: left;\">（五）责令遵守特定的行为规范，不得实施特定行为、接触特定人员或者进入特定场所；<br></p><p style=\"text-align: left;\">（六）责令接受心理辅导、行为矫治；<br></p><p style=\"text-align: left;\">（七）责令参加社会服务活动；<br></p><p style=\"text-align: left;\">（八）责令接受社会观护，由社会组织、有关机构在适当场所对未成年人进行教育、监督和管束；</p><p style=\"text-align: left;\">（九）其他适当的矫治教育措施。</p><p style=\"text-align: left;\">第四十二条　公安机关在对未成年人进行矫治教育时，可以根据需要邀请学校、居民委员会、村民委员会以及社会工作服务机构等社会组织参与。</p><p style=\"text-align: left;\">未成年人的父母或者其他监护人应当积极配合矫治教育措施的实施，不得妨碍阻挠或者放任不管。</p><p style=\"text-align: left;\">第四十三条　对有严重不良行为的未成年人，未成年人的父母或者其他监护人、所在学校无力管教或者管教无效的，可以向教育行政部门提出申请，经专门教育指导委员会评估同意后，由教育行政部门决定送入专门学校接受专门教育。</p><p style=\"text-align: left;\">第四十四条　未成年人有下列情形之一的，经专门教育指导委员会评估同意，教育行政部门会同公安机关可以决定将其送入专门学校接受专门教育：</p><p style=\"text-align: left;\">（一）实施严重危害社会的行为，情节恶劣或者造成严重后果；</p><p style=\"text-align: left;\">（二）多次实施严重危害社会的行为；</p><p style=\"text-align: left;\">（三）拒不接受或者配合本法第四十一条规定的矫治教育措施；</p><p style=\"text-align: left;\">（四）法律、行政法规规定的其他情形。</p><p style=\"text-align: left;\">第四十五条　未成年人实施刑法规定的行为、因不满法定刑事责任年龄不予刑事处罚的，经专门教育指导委员会评估同意，教育行政部门会同公安机关可以决定对其进行专门矫治教育。</p><p style=\"text-align: left;\">省级人民政府应当结合本地的实际情况，至少确定一所专门学校按照分校区、分班级等方式设置专门场所，对前款规定的未成年人进行专门矫治教育。</p><p style=\"text-align: left;\">前款规定的专门场所实行闭环管理，公安机关、司法行政部门负责未成年人的矫治工作，教育行政部门承担未成年人的教育工作。</p><p style=\"text-align: left;\">第四十六条　专门学校应当在每个学期适时提请专门教育指导委员会对接受专门教育的未成年学生的情况进行评估。对经评估适合转回普通学校就读的，专门教育指导委员会应当向原决定机关提出书面建议，由原决定机关决定是否将未成年学生转回普通学校就读。</p><p style=\"text-align: left;\">原决定机关决定将未成年学生转回普通学校的，其原所在学校不得拒绝接收；因特殊情况，不适宜转回原所在学校的，由教育行政部门安排转学。</p><p style=\"text-align: left;\">第四十七条　专门学校应当对接受专门教育的未成年人分级分类进行教育和矫治，有针对性地开展道德教育、法治教育、心理健康教育，并根据实际情况进行职业教育；对没有完成义务教育的未成年人，应当保证其继续接受义务教育。</p><p style=\"text-align: left;\">专门学校的未成年学生的学籍保留在原学校，符合毕业条件的，原学校应当颁发毕业证书。</p><p style=\"text-align: left;\">第四十八条　专门学校应当与接受专门教育的未成年人的父母或者其他监护人加强联系，定期向其反馈未成年人的矫治和教育情况，为父母或者其他监护人、亲属等看望未成年人提供便利。</p><p style=\"text-align: left;\">第四十九条　未成年人及其父母或者其他监护人对本章规定的行政决定不服的，可以依法提起行政复议或者行政诉讼。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: center;\"><strong>第五章 对重新犯罪的预防</strong></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">第五十条　公安机关、人民检察院、人民法院办理未成年人刑事案件，应当根据未成年人的生理、心理特点和犯罪的情况，有针对性地进行法治教育。</p><p style=\"text-align: left;\">对涉及刑事案件的未成年人进行教育，其法定代理人以外的成年亲属或者教师、辅导员等参与有利于感化、挽救未成年人的，公安机关、人民检察院、人民法院应当邀请其参加有关活动。</p><p style=\"text-align: left;\">第五十一条　公安机关、人民检察院、人民法院办理未成年人刑事案件，可以自行或者委托有关社会组织、机构对未成年犯罪嫌疑人或者被告人的成长经历、犯罪原因、监护、教育等情况进行社会调查；根据实际需要并经未成年犯罪嫌疑人、被告人及其法定代理人同意，可以对未成年犯罪嫌疑人、被告人进行心理测评。</p><p style=\"text-align: left;\">社会调查和心理测评的报告可以作为办理案件和教育未成年人的参考。</p><p style=\"text-align: left;\">第五十二条　公安机关、人民检察院、人民法院对于无固定住所、无法提供保证人的未成年人适用取保候审的，应当指定合适成年人作为保证人，必要时可以安排取保候审的未成年人接受社会观护。</p><p style=\"text-align: left;\">第五十三条　对被拘留、逮捕以及在未成年犯管教所执行刑罚的未成年人，应当与成年人分别关押、管理和教育。对未成年人的社区矫正，应当与成年人分别进行。</p><p style=\"text-align: left;\">对有上述情形且没有完成义务教育的未成年人，公安机关、人民检察院、人民法院、司法行政部门应当与教育行政部门相互配合，保证其继续接受义务教育。</p><p style=\"text-align: left;\">第五十四条　未成年犯管教所、社区矫正机构应当对未成年犯、未成年社区矫正对象加强法治教育，并根据实际情况对其进行职业教育。</p><p style=\"text-align: left;\">第五十五条　社区矫正机构应当告知未成年社区矫正对象安置帮教的有关规定，并配合安置帮教工作部门落实或者解决未成年社区矫正对象的就学、就业等问题。</p><p style=\"text-align: left;\">第五十六条　对刑满释放的未成年人，未成年犯管教所应当提前通知其父母或者其他监护人按时接回，并协助落实安置帮教措施。没有父母或者其他监护人、无法查明其父母或者其他监护人的，未成年犯管教所应当提前通知未成年人原户籍所在地或者居住地的司法行政部门安排人员按时接回，由民政部门或者居民委员会、村民委员会依法对其进行监护。</p><p style=\"text-align: left;\">第五十七条　未成年人的父母或者其他监护人和学校、居民委员会、村民委员会对接受社区矫正、刑满释放的未成年人，应当采取有效的帮教措施，协助司法机关以及有关部门做好安置帮教工作。</p><p style=\"text-align: left;\">居民委员会、村民委员会可以聘请思想品德优秀，作风正派，热心未成年人工作的离退休人员、志愿者或其他人员协助做好前款规定的安置帮教工作。</p><p style=\"text-align: left;\">第五十八条　刑满释放和接受社区矫正的未成年人，在复学、升学、就业等方面依法享有与其他未成年人同等的权利，任何单位和个人不得歧视。</p><p style=\"text-align: left;\">第五十九条　未成年人的犯罪记录依法被封存的，公安机关、人民检察院、人民法院和司法行政部门不得向任何单位或者个人提供，但司法机关因办案需要或者有关单位根据国家有关规定进行查询的除外。依法进行查询的单位和个人应当对相关记录信息予以保密。</p><p style=\"text-align: left;\">未成年人接受专门矫治教育、专门教育的记录，以及被行政处罚、采取刑事强制措施和不起诉的记录，适用前款规定。</p><p style=\"text-align: left;\">第六十条　人民检察院通过依法行使检察权，对未成年人重新犯罪预防工作等进行监督。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: center;\"><strong>第六章 法律责任</strong></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">第六十一条　公安机关、人民检察院、人民法院在办理案件过程中发现实施严重不良行为的未成年人的父母或者其他监护人不依法履行监护职责的，应当予以训诫，并可以责令其接受家庭教育指导。</p><p style=\"text-align: left;\">第六十二条　学校及其教职员工违反本法规定，不履行预防未成年人犯罪工作职责，或者虐待、歧视相关未成年人的，由教育行政等部门责令改正，通报批评；情节严重的，对直接负责的主管人员和其他直接责任人员依法给予处分。构成违反治安管理行为的，由公安机关依法予以治安管理处罚。</p><p style=\"text-align: left;\">教职员工教唆、胁迫、引诱未成年人实施不良行为或者严重不良行为，以及品行不良、影响恶劣的，教育行政部门、学校应当依法予以解聘或者辞退。</p><p style=\"text-align: left;\">第六十三条　违反本法规定，在复学、升学、就业等方面歧视相关未成年人的，由所在单位或者教育、人力资源社会保障等部门责令改正；拒不改正的，对直接负责的主管人员或者其他直接责任人员依法给予处分。</p><p style=\"text-align: left;\">第六十四条　有关社会组织、机构及其工作人员虐待、歧视接受社会观护的未成年人，或者出具虚假社会调查、心理测评报告的，由民政、司法行政等部门对直接负责的主管人员或者其他直接责任人员依法给予处分，构成违反治安管理行为的，由公安机关予以治安管理处罚。</p><p style=\"text-align: left;\">第六十五条　教唆、胁迫、引诱未成年人实施不良行为或者严重不良行为，构成违反治安管理行为的，由公安机关依法予以治安管理处罚。</p><p style=\"text-align: left;\">第六十六条　国家机关及其工作人员在预防未成年人犯罪工作中滥用职权、玩忽职守、徇私舞弊的，对直接负责的主管人员和其他直接责任人员，依法给予处分。</p><p style=\"text-align: left;\">第六十七条　违反本法规定，构成犯罪的，依法追究刑事责任。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: center;\"><strong>第七章 附 则</strong></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">第六十八条　本法自2021年6月1日起施行。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\"> </p>', '本站编辑', '', '2023-04-11 20:58:51');
INSERT INTO `articles` VALUES (32, 4, '法律法规', '中华人民共和国未成年人保护法', '<p style=\"text-align: left;\"><span style=\"font-size: 16px;\">（1991年9月4日第七届全国人民代表大会常务委员会第二十一次会议通过 2006年12月29日第十届全国人民代表大会常务委员会第二十五次会议第一次修订 根据2012年10月26日第十一届全国人民代表大会常务委员会第二十九次会议《关于修改〈中华人民共和国未成年人保护法〉的决定》修正 2020年10月17日第十三届全国人民代表大会常务委员会第二十二次会议第二次修订）</span></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">目　　录</p><p style=\"text-align: left;\">第一章　总　　则</p><p style=\"text-align: left;\">第二章　家庭保护</p><p style=\"text-align: left;\">第三章　学校保护</p><p style=\"text-align: left;\">第四章　社会保护</p><p style=\"text-align: left;\">第五章　网络保护</p><p style=\"text-align: left;\">第六章　政府保护</p><p style=\"text-align: left;\">第七章　司法保护</p><p style=\"text-align: left;\">第八章　法律责任</p><p style=\"text-align: left;\">第九章　附　　则</p><p style=\"text-align: center;\"><strong>第一章 总 则</strong></p><p style=\"text-align: left;\">第一条　为了保护未成年人身心健康，保障未成年人合法权益，促进未成年人德智体美劳全面发展，培养有理想、有道德、有文化、有纪律的社会主义建设者和接班人，培养担当民族复兴大任的时代新人，根据宪法，制定本法。</p><p style=\"text-align: left;\">第二条　本法所称未成年人是指未满十八周岁的公民。</p><p style=\"text-align: left;\">第三条　国家保障未成年人的生存权、发展权、受保护权、参与权等权利。</p><p style=\"text-align: left;\">未成年人依法平等地享有各项权利，不因本人及其父母或者其他监护人的民族、种族、性别、户籍、职业、宗教信仰、教育程度、家庭状况、身心健康状况等受到歧视。</p><p style=\"text-align: left;\">第四条　保护未成年人，应当坚持最有利于未成年人的原则。处理涉及未成年人事项，应当符合下列要求：</p><p style=\"text-align: left;\">（一）给予未成年人特殊、优先保护；</p><p style=\"text-align: left;\">（二）尊重未成年人人格尊严；</p><p style=\"text-align: left;\">（三）保护未成年人隐私权和个人信息；</p><p style=\"text-align: left;\">（四）适应未成年人身心健康发展的规律和特点；</p><p style=\"text-align: left;\">（五）听取未成年人的意见；</p><p style=\"text-align: left;\">（六）保护与教育相结合。</p><p style=\"text-align: left;\">第五条　国家、社会、学校和家庭应当对未成年人进行理想教育、道德教育、科学教育、文化教育、法治教育、国家安全教育、健康教育、劳动教育，加强爱国主义、集体主义和中国特色社会主义的教育，培养爱祖国、爱人民、爱劳动、爱科学、爱社会主义的公德，抵制资本主义、封建主义和其他腐朽思想的侵蚀，引导未成年人树立和践行社会主义核心价值观。</p><p style=\"text-align: left;\">第六条　保护未成年人，是国家机关、武装力量、政党、人民团体、企业事业单位、社会组织、城乡基层群众性自治组织、未成年人的监护人以及其他成年人的共同责任。</p><p style=\"text-align: left;\">国家、社会、学校和家庭应当教育和帮助未成年人维护自身合法权益，增强自我保护的意识和能力。</p><p style=\"text-align: left;\">第七条　未成年人的父母或者其他监护人依法对未成年人承担监护职责。</p><p style=\"text-align: left;\">国家采取措施指导、支持、帮助和监督未成年人的父母或者其他监护人履行监护职责。</p><p style=\"text-align: left;\">第八条　县级以上人民政府应当将未成年人保护工作纳入国民经济和社会发展规划，相关经费纳入本级政府预算。</p><p style=\"text-align: left;\">第九条　县级以上人民政府应当建立未成年人保护工作协调机制，统筹、协调、督促和指导有关部门在各自职责范围内做好未成年人保护工作。协调机制具体工作由县级以上人民政府民政部门承担，省级人民政府也可以根据本地实际情况确定由其他有关部门承担。</p><p style=\"text-align: left;\">第十条　共产主义青年团、妇女联合会、工会、残疾人联合会、关心下一代工作委员会、青年联合会、学生联合会、少年先锋队以及其他人民团体、有关社会组织，应当协助各级人民政府及其有关部门、人民检察院、人民法院做好未成年人保护工作，维护未成年人合法权益。</p><p style=\"text-align: left;\">第十一条　任何组织或者个人发现不利于未成年人身心健康或者侵犯未成年人合法权益的情形，都有权劝阻、制止或者向公安、民政、教育等有关部门提出检举、控告。</p><p style=\"text-align: left;\">国家机关、居民委员会、村民委员会、密切接触未成年人的单位及其工作人员，在工作中发现未成年人身心健康受到侵害、疑似受到侵害或者面临其他危险情形的，应当立即向公安、民政、教育等有关部门报告。</p><p style=\"text-align: left;\">有关部门接到涉及未成年人的检举、控告或者报告，应当依法及时受理、处置，并以适当方式将处理结果告知相关单位和人员。</p><p style=\"text-align: left;\">第十二条　国家鼓励和支持未成年人保护方面的科学研究，建设相关学科、设置相关专业，加强人才培养。</p><p style=\"text-align: left;\">第十三条　国家建立健全未成年人统计调查制度，开展未成年人健康、受教育等状况的统计、调查和分析，发布未成年人保护的有关信息。</p><p style=\"text-align: left;\">第十四条　国家对保护未成年人有显著成绩的组织和个人给予表彰和奖励。</p><p style=\"text-align: center;\"><strong>第二章 家庭保护</strong></p><p style=\"text-align: left;\">第十五条　未成年人的父母或者其他监护人应当学习家庭教育知识，接受家庭教育指导，创造良好、和睦、文明的家庭环境。</p><p style=\"text-align: left;\">共同生活的其他成年家庭成员应当协助未成年人的父母或者其他监护人抚养、教育和保护未成年人。</p><p style=\"text-align: left;\">第十六条　未成年人的父母或者其他监护人应当履行下列监护职责：</p><p style=\"text-align: left;\">（一）为未成年人提供生活、健康、安全等方面的保障；</p><p style=\"text-align: left;\">（二）关注未成年人的生理、心理状况和情感需求；</p><p style=\"text-align: left;\">（三）教育和引导未成年人遵纪守法、勤俭节约，养成良好的思想品德和行为习惯；</p><p style=\"text-align: left;\">（四）对未成年人进行安全教育，提高未成年人的自我保护意识和能力；</p><p style=\"text-align: left;\">（五）尊重未成年人受教育的权利，保障适龄未成年人依法接受并完成义务教育；</p><p style=\"text-align: left;\">（六）保障未成年人休息、娱乐和体育锻炼的时间，引导未成年人进行有益身心健康的活动；</p><p style=\"text-align: left;\">（七）妥善管理和保护未成年人的财产；</p><p style=\"text-align: left;\">（八）依法代理未成年人实施民事法律行为；</p><p style=\"text-align: left;\">（九）预防和制止未成年人的不良行为和违法犯罪行为，并进行合理管教；</p><p style=\"text-align: left;\">（十）其他应当履行的监护职责。</p><p style=\"text-align: left;\">第十七条　未成年人的父母或者其他监护人不得实施下列行为：</p><p style=\"text-align: left;\">（一）虐待、遗弃、非法送养未成年人或者对未成年人实施家庭暴力；</p><p style=\"text-align: left;\">（二）放任、教唆或者利用未成年人实施违法犯罪行为；</p><p style=\"text-align: left;\">（三）放任、唆使未成年人参与邪教、迷信活动或者接受恐怖主义、分裂主义、极端主义等侵害；</p><p style=\"text-align: left;\">（四）放任、唆使未成年人吸烟（含电子烟，下同）、饮酒、赌博、流浪乞讨或者欺凌他人；</p><p style=\"text-align: left;\">（五）放任或者迫使应当接受义务教育的未成年人失学、辍学；</p><p style=\"text-align: left;\">（六）放任未成年人沉迷网络，接触危害或者可能影响其身心健康的图书、报刊、电影、广播电视节目、音像制品、电子出版物和网络信息等；</p><p style=\"text-align: left;\">（七）放任未成年人进入营业性娱乐场所、酒吧、互联网上网服务营业场所等不适宜未成年人活动的场所；</p><p style=\"text-align: left;\">（八）允许或者迫使未成年人从事国家规定以外的劳动；</p><p style=\"text-align: left;\">（九）允许、迫使未成年人结婚或者为未成年人订立婚约；</p><p style=\"text-align: left;\">（十）违法处分、侵吞未成年人的财产或者利用未成年人牟取不正当利益；</p><p style=\"text-align: left;\">（十一）其他侵犯未成年人身心健康、财产权益或者不依法履行未成年人保护义务的行为。</p><p style=\"text-align: left;\">第十八条　未成年人的父母或者其他监护人应当为未成年人提供安全的家庭生活环境，及时排除引发触电、烫伤、跌落等伤害的安全隐患；采取配备儿童安全座椅、教育未成年人遵守交通规则等措施，防止未成年人受到交通事故的伤害；提高户外安全保护意识，避免未成年人发生溺水、动物伤害等事故。</p><p style=\"text-align: left;\">第十九条　未成年人的父母或者其他监护人应当根据未成年人的年龄和智力发展状况，在作出与未成年人权益有关的决定前，听取未成年人的意见，充分考虑其真实意愿。</p><p style=\"text-align: left;\">第二十条　未成年人的父母或者其他监护人发现未成年人身心健康受到侵害、疑似受到侵害或者其他合法权益受到侵犯的，应当及时了解情况并采取保护措施；情况严重的，应当立即向公安、民政、教育等部门报告。</p><p style=\"text-align: left;\">第二十一条　未成年人的父母或者其他监护人不得使未满八周岁或者由于身体、心理原因需要特别照顾的未成年人处于无人看护状态，或者将其交由无民事行为能力、限制民事行为能力、患有严重传染性疾病或者其他不适宜的人员临时照护。</p><p style=\"text-align: left;\">未成年人的父母或者其他监护人不得使未满十六周岁的未成年人脱离监护单独生活。</p><p style=\"text-align: left;\">第二十二条　未成年人的父母或者其他监护人因外出务工等原因在一定期限内不能完全履行监护职责的，应当委托具有照护能力的完全民事行为能力人代为照护；无正当理由的，不得委托他人代为照护。</p><p style=\"text-align: left;\">未成年人的父母或者其他监护人在确定被委托人时，应当综合考虑其道德品质、家庭状况、身心健康状况、与未成年人生活情感上的联系等情况，并听取有表达意愿能力未成年人的意见。</p><p style=\"text-align: left;\">具有下列情形之一的，不得作为被委托人：</p><p style=\"text-align: left;\">（一）曾实施性侵害、虐待、遗弃、拐卖、暴力伤害等违法犯罪行为；</p><p style=\"text-align: left;\">（二）有吸毒、酗酒、赌博等恶习；</p><p style=\"text-align: left;\">（三）曾拒不履行或者长期怠于履行监护、照护职责；</p><p style=\"text-align: left;\">（四）其他不适宜担任被委托人的情形。</p><p style=\"text-align: left;\">第二十三条　未成年人的父母或者其他监护人应当及时将委托照护情况书面告知未成年人所在学校、幼儿园和实际居住地的居民委员会、村民委员会，加强和未成年人所在学校、幼儿园的沟通；与未成年人、被委托人至少每周联系和交流一次，了解未成年人的生活、学习、心理等情况，并给予未成年人亲情关爱。</p><p style=\"text-align: left;\">未成年人的父母或者其他监护人接到被委托人、居民委员会、村民委员会、学校、幼儿园等关于未成年人心理、行为异常的通知后，应当及时采取干预措施。</p><p style=\"text-align: left;\">第二十四条　未成年人的父母离婚时，应当妥善处理未成年子女的抚养、教育、探望、财产等事宜，听取有表达意愿能力未成年人的意见。不得以抢夺、藏匿未成年子女等方式争夺抚养权。</p><p style=\"text-align: left;\">未成年人的父母离婚后，不直接抚养未成年子女的一方应当依照协议、人民法院判决或者调解确定的时间和方式，在不影响未成年人学习、生活的情况下探望未成年子女，直接抚养的一方应当配合，但被人民法院依法中止探望权的除外。</p><p style=\"text-align: center;\"><strong>第三章 学校保护</strong></p><p style=\"text-align: left;\">第二十五条　学校应当全面贯彻国家教育方针，坚持立德树人，实施素质教育，提高教育质量，注重培养未成年学生认知能力、合作能力、创新能力和实践能力，促进未成年学生全面发展。</p><p style=\"text-align: left;\">学校应当建立未成年学生保护工作制度，健全学生行为规范，培养未成年学生遵纪守法的良好行为习惯。</p><p style=\"text-align: left;\">第二十六条　幼儿园应当做好保育、教育工作，遵循幼儿身心发展规律，实施启蒙教育，促进幼儿在体质、智力、品德等方面和谐发展。</p><p style=\"text-align: left;\">第二十七条　学校、幼儿园的教职员工应当尊重未成年人人格尊严，不得对未成年人实施体罚、变相体罚或者其他侮辱人格尊严的行为。</p><p style=\"text-align: left;\">第二十八条　学校应当保障未成年学生受教育的权利，不得违反国家规定开除、变相开除未成年学生。</p><p style=\"text-align: left;\">学校应当对尚未完成义务教育的辍学未成年学生进行登记并劝返复学；劝返无效的，应当及时向教育行政部门书面报告。</p><p style=\"text-align: left;\">第二十九条　学校应当关心、爱护未成年学生，不得因家庭、身体、心理、学习能力等情况歧视学生。对家庭困难、身心有障碍的学生，应当提供关爱；对行为异常、学习有困难的学生，应当耐心帮助。</p><p style=\"text-align: left;\">学校应当配合政府有关部门建立留守未成年学生、困境未成年学生的信息档案，开展关爱帮扶工作。</p><p style=\"text-align: left;\">第三十条　学校应当根据未成年学生身心发展特点，进行社会生活指导、心理健康辅导、青春期教育和生命教育。</p><p style=\"text-align: left;\">第三十一条　学校应当组织未成年学生参加与其年龄相适应的日常生活劳动、生产劳动和服务性劳动，帮助未成年学生掌握必要的劳动知识和技能，养成良好的劳动习惯。</p><p style=\"text-align: left;\">第三十二条　学校、幼儿园应当开展勤俭节约、反对浪费、珍惜粮食、文明饮食等宣传教育活动，帮助未成年人树立浪费可耻、节约为荣的意识，养成文明健康、绿色环保的生活习惯。</p><p style=\"text-align: left;\">第三十三条　学校应当与未成年学生的父母或者其他监护人互相配合，合理安排未成年学生的学习时间，保障其休息、娱乐和体育锻炼的时间。</p><p style=\"text-align: left;\">学校不得占用国家法定节假日、休息日及寒暑假期，组织义务教育阶段的未成年学生集体补课，加重其学习负担。</p><p style=\"text-align: left;\">幼儿园、校外培训机构不得对学龄前未成年人进行小学课程教育。</p><p style=\"text-align: left;\">第三十四条　学校、幼儿园应当提供必要的卫生保健条件，协助卫生健康部门做好在校、在园未成年人的卫生保健工作。</p><p style=\"text-align: left;\">第三十五条　学校、幼儿园应当建立安全管理制度，对未成年人进行安全教育，完善安保设施、配备安保人员，保障未成年人在校、在园期间的人身和财产安全。</p><p style=\"text-align: left;\">学校、幼儿园不得在危及未成年人人身安全、身心健康的校舍和其他设施、场所中进行教育教学活动。</p><p style=\"text-align: left;\">学校、幼儿园安排未成年人参加文化娱乐、社会实践等集体活动，应当保护未成年人的身心健康，防止发生人身伤害事故。</p><p style=\"text-align: left;\">第三十六条　使用校车的学校、幼儿园应当建立健全校车安全管理制度，配备安全管理人员，定期对校车进行安全检查，对校车驾驶人进行安全教育，并向未成年人讲解校车安全乘坐知识，培养未成年人校车安全事故应急处理技能。</p><p style=\"text-align: left;\">第三十七条　学校、幼儿园应当根据需要，制定应对自然灾害、事故灾难、公共卫生事件等突发事件和意外伤害的预案，配备相应设施并定期进行必要的演练。</p><p style=\"text-align: left;\">未成年人在校内、园内或者本校、本园组织的校外、园外活动中发生人身伤害事故的，学校、幼儿园应当立即救护，妥善处理，及时通知未成年人的父母或者其他监护人，并向有关部门报告。</p><p style=\"text-align: left;\">第三十八条　学校、幼儿园不得安排未成年人参加商业性活动，不得向未成年人及其父母或者其他监护人推销或者要求其购买指定的商品和服务。</p><p style=\"text-align: left;\">学校、幼儿园不得与校外培训机构合作为未成年人提供有偿课程辅导。</p><p style=\"text-align: left;\">第三十九条　学校应当建立学生欺凌防控工作制度，对教职员工、学生等开展防治学生欺凌的教育和培训。</p><p style=\"text-align: left;\">学校对学生欺凌行为应当立即制止，通知实施欺凌和被欺凌未成年学生的父母或者其他监护人参与欺凌行为的认定和处理；对相关未成年学生及时给予心理辅导、教育和引导；对相关未成年学生的父母或者其他监护人给予必要的家庭教育指导。</p><p style=\"text-align: left;\">对实施欺凌的未成年学生，学校应当根据欺凌行为的性质和程度，依法加强管教。对严重的欺凌行为，学校不得隐瞒，应当及时向公安机关、教育行政部门报告，并配合相关部门依法处理。</p><p style=\"text-align: left;\">第四十条　学校、幼儿园应当建立预防性侵害、性骚扰未成年人工作制度。对性侵害、性骚扰未成年人等违法犯罪行为，学校、幼儿园不得隐瞒，应当及时向公安机关、教育行政部门报告，并配合相关部门依法处理。</p><p style=\"text-align: left;\">学校、幼儿园应当对未成年人开展适合其年龄的性教育，提高未成年人防范性侵害、性骚扰的自我保护意识和能力。对遭受性侵害、性骚扰的未成年人，学校、幼儿园应当及时采取相关的保护措施。</p><p style=\"text-align: left;\">第四十一条　婴幼儿照护服务机构、早期教育服务机构、校外培训机构、校外托管机构等应当参照本章有关规定，根据不同年龄阶段未成年人的成长特点和规律，做好未成年人保护工作。</p><p style=\"text-align: center;\"><strong>第四章 社会保护</strong></p><p style=\"text-align: left;\">第四十二条　全社会应当树立关心、爱护未成年人的良好风尚。</p><p style=\"text-align: left;\">国家鼓励、支持和引导人民团体、企业事业单位、社会组织以及其他组织和个人，开展有利于未成年人健康成长的社会活动和服务。</p><p style=\"text-align: left;\">第四十三条　居民委员会、村民委员会应当设置专人专岗负责未成年人保护工作，协助政府有关部门宣传未成年人保护方面的法律法规，指导、帮助和监督未成年人的父母或者其他监护人依法履行监护职责，建立留守未成年人、困境未成年人的信息档案并给予关爱帮扶。</p><p style=\"text-align: left;\">居民委员会、村民委员会应当协助政府有关部门监督未成年人委托照护情况，发现被委托人缺乏照护能力、怠于履行照护职责等情况，应当及时向政府有关部门报告，并告知未成年人的父母或者其他监护人，帮助、督促被委托人履行照护职责。</p><p style=\"text-align: left;\">第四十四条　爱国主义教育基地、图书馆、青少年宫、儿童活动中心、儿童之家应当对未成年人免费开放；博物馆、纪念馆、科技馆、展览馆、美术馆、文化馆、社区公益性互联网上网服务场所以及影剧院、体育场馆、动物园、植物园、公园等场所，应当按照有关规定对未成年人免费或者优惠开放。</p><p style=\"text-align: left;\">国家鼓励爱国主义教育基地、博物馆、科技馆、美术馆等公共场馆开设未成年人专场，为未成年人提供有针对性的服务。</p><p style=\"text-align: left;\">国家鼓励国家机关、企业事业单位、部队等开发自身教育资源，设立未成年人开放日，为未成年人主题教育、社会实践、职业体验等提供支持。</p><p style=\"text-align: left;\">国家鼓励科研机构和科技类社会组织对未成年人开展科学普及活动。</p><p style=\"text-align: left;\">第四十五条　城市公共交通以及公路、铁路、水路、航空客运等应当按照有关规定对未成年人实施免费或者优惠票价。</p><p style=\"text-align: left;\">第四十六条　国家鼓励大型公共场所、公共交通工具、旅游景区景点等设置母婴室、婴儿护理台以及方便幼儿使用的坐便器、洗手台等卫生设施，为未成年人提供便利。</p><p style=\"text-align: left;\">第四十七条　任何组织或者个人不得违反有关规定，限制未成年人应当享有的照顾或者优惠。</p><p style=\"text-align: left;\">第四十八条　国家鼓励创作、出版、制作和传播有利于未成年人健康成长的图书、报刊、电影、广播电视节目、舞台艺术作品、音像制品、电子出版物和网络信息等。</p><p style=\"text-align: left;\">第四十九条　新闻媒体应当加强未成年人保护方面的宣传，对侵犯未成年人合法权益的行为进行舆论监督。新闻媒体采访报道涉及未成年人事件应当客观、审慎和适度，不得侵犯未成年人的名誉、隐私和其他合法权益。</p><p style=\"text-align: left;\">第五十条　禁止制作、复制、出版、发布、传播含有宣扬淫秽、色情、暴力、邪教、迷信、赌博、引诱自杀、恐怖主义、分裂主义、极端主义等危害未成年人身心健康内容的图书、报刊、电影、广播电视节目、舞台艺术作品、音像制品、电子出版物和网络信息等。</p><p style=\"text-align: left;\">第五十一条　任何组织或者个人出版、发布、传播的图书、报刊、电影、广播电视节目、舞台艺术作品、音像制品、电子出版物或者网络信息，包含可能影响未成年人身心健康内容的，应当以显著方式作出提示。</p><p style=\"text-align: left;\">第五十二条　禁止制作、复制、发布、传播或者持有有关未成年人的淫秽色情物品和网络信息。</p><p style=\"text-align: left;\">第五十三条　任何组织或者个人不得刊登、播放、张贴或者散发含有危害未成年人身心健康内容的广告；不得在学校、幼儿园播放、张贴或者散发商业广告；不得利用校服、教材等发布或者变相发布商业广告。</p><p style=\"text-align: left;\">第五十四条　禁止拐卖、绑架、虐待、非法收养未成年人，禁止对未成年人实施性侵害、性骚扰。</p><p style=\"text-align: left;\">禁止胁迫、引诱、教唆未成年人参加黑社会性质组织或者从事违法犯罪活动。</p><p style=\"text-align: left;\">禁止胁迫、诱骗、利用未成年人乞讨。</p><p style=\"text-align: left;\">第五十五条　生产、销售用于未成年人的食品、药品、玩具、用具和游戏游艺设备、游乐设施等，应当符合国家或者行业标准，不得危害未成年人的人身安全和身心健康。上述产品的生产者应当在显著位置标明注意事项，未标明注意事项的不得销售。</p><p style=\"text-align: left;\">第五十六条　未成年人集中活动的公共场所应当符合国家或者行业安全标准，并采取相应安全保护措施。对可能存在安全风险的设施，应当定期进行维护，在显著位置设置安全警示标志并标明适龄范围和注意事项；必要时应当安排专门人员看管。</p><p style=\"text-align: left;\">大型的商场、超市、医院、图书馆、博物馆、科技馆、游乐场、车站、码头、机场、旅游景区景点等场所运营单位应当设置搜寻走失未成年人的安全警报系统。场所运营单位接到求助后，应当立即启动安全警报系统，组织人员进行搜寻并向公安机关报告。</p><p style=\"text-align: left;\">公共场所发生突发事件时，应当优先救护未成年人。</p><p style=\"text-align: left;\">第五十七条　旅馆、宾馆、酒店等住宿经营者接待未成年人入住，或者接待未成年人和成年人共同入住时，应当询问父母或者其他监护人的联系方式、入住人员的身份关系等有关情况；发现有违法犯罪嫌疑的，应当立即向公安机关报告，并及时联系未成年人的父母或者其他监护人。</p><p style=\"text-align: left;\">第五十八条　学校、幼儿园周边不得设置营业性娱乐场所、酒吧、互联网上网服务营业场所等不适宜未成年人活动的场所。营业性歌舞娱乐场所、酒吧、互联网上网服务营业场所等不适宜未成年人活动场所的经营者，不得允许未成年人进入；游艺娱乐场所设置的电子游戏设备，除国家法定节假日外，不得向未成年人提供。经营者应当在显著位置设置未成年人禁入、限入标志；对难以判明是否是未成年人的，应当要求其出示身份证件。</p><p style=\"text-align: left;\">第五十九条　学校、幼儿园周边不得设置烟、酒、彩票销售网点。禁止向未成年人销售烟、酒、彩票或者兑付彩票奖金。烟、酒和彩票经营者应当在显著位置设置不向未成年人销售烟、酒或者彩票的标志；对难以判明是否是未成年人的，应当要求其出示身份证件。</p><p style=\"text-align: left;\">任何人不得在学校、幼儿园和其他未成年人集中活动的公共场所吸烟、饮酒。</p><p style=\"text-align: left;\">第六十条　禁止向未成年人提供、销售管制刀具或者其他可能致人严重伤害的器具等物品。经营者难以判明购买者是否是未成年人的，应当要求其出示身份证件。</p><p style=\"text-align: left;\">第六十一条　任何组织或者个人不得招用未满十六周岁未成年人，国家另有规定的除外。</p><p style=\"text-align: left;\">营业性娱乐场所、酒吧、互联网上网服务营业场所等不适宜未成年人活动的场所不得招用已满十六周岁的未成年人。</p><p style=\"text-align: left;\">招用已满十六周岁未成年人的单位和个人应当执行国家在工种、劳动时间、劳动强度和保护措施等方面的规定，不得安排其从事过重、有毒、有害等危害未成年人身心健康的劳动或者危险作业。</p><p style=\"text-align: left;\">任何组织或者个人不得组织未成年人进行危害其身心健康的表演等活动。经未成年人的父母或者其他监护人同意，未成年人参与演出、节目制作等活动，活动组织方应当根据国家有关规定，保障未成年人合法权益。</p><p style=\"text-align: left;\">第六十二条　密切接触未成年人的单位招聘工作人员时，应当向公安机关、人民检察院查询应聘者是否具有性侵害、虐待、拐卖、暴力伤害等违法犯罪记录；发现其具有前述行为记录的，不得录用。</p><p style=\"text-align: left;\">密切接触未成年人的单位应当每年定期对工作人员是否具有上述违法犯罪记录进行查询。通过查询或者其他方式发现其工作人员具有上述行为的，应当及时解聘。</p><p style=\"text-align: left;\">第六十三条　任何组织或者个人不得隐匿、毁弃、非法删除未成年人的信件、日记、电子邮件或者其他网络通讯内容。</p><p style=\"text-align: left;\">除下列情形外，任何组织或者个人不得开拆、查阅未成年人的信件、日记、电子邮件或者其他网络通讯内容：</p><p style=\"text-align: left;\">（一）无民事行为能力未成年人的父母或者其他监护人代未成年人开拆、查阅；</p><p style=\"text-align: left;\">（二）因国家安全或者追查刑事犯罪依法进行检查；</p><p style=\"text-align: left;\">（三）紧急情况下为了保护未成年人本人的人身安全。</p><p style=\"text-align: center;\"><strong>第五章 网络保护</strong></p><p style=\"text-align: left;\">第六十四条　国家、社会、学校和家庭应当加强未成年人网络素养宣传教育，培养和提高未成年人的网络素养，增强未成年人科学、文明、安全、合理使用网络的意识和能力，保障未成年人在网络空间的合法权益。</p><p style=\"text-align: left;\">第六十五条　国家鼓励和支持有利于未成年人健康成长的网络内容的创作与传播，鼓励和支持专门以未成年人为服务对象、适合未成年人身心健康特点的网络技术、产品、服务的研发、生产和使用。</p><p style=\"text-align: left;\">第六十六条　网信部门及其他有关部门应当加强对未成年人网络保护工作的监督检查，依法惩处利用网络从事危害未成年人身心健康的活动，为未成年人提供安全、健康的网络环境。</p><p style=\"text-align: left;\">第六十七条　网信部门会同公安、文化和旅游、新闻出版、电影、广播电视等部门根据保护不同年龄阶段未成年人的需要，确定可能影响未成年人身心健康网络信息的种类、范围和判断标准。</p><p style=\"text-align: left;\">第六十八条　新闻出版、教育、卫生健康、文化和旅游、网信等部门应当定期开展预防未成年人沉迷网络的宣传教育，监督网络产品和服务提供者履行预防未成年人沉迷网络的义务，指导家庭、学校、社会组织互相配合，采取科学、合理的方式对未成年人沉迷网络进行预防和干预。</p><p style=\"text-align: left;\">任何组织或者个人不得以侵害未成年人身心健康的方式对未成年人沉迷网络进行干预。</p><p style=\"text-align: left;\">第六十九条　学校、社区、图书馆、文化馆、青少年宫等场所为未成年人提供的互联网上网服务设施，应当安装未成年人网络保护软件或者采取其他安全保护技术措施。</p><p style=\"text-align: left;\">智能终端产品的制造者、销售者应当在产品上安装未成年人网络保护软件，或者以显著方式告知用户未成年人网络保护软件的安装渠道和方法。</p><p style=\"text-align: left;\">第七十条　学校应当合理使用网络开展教学活动。未经学校允许，未成年学生不得将手机等智能终端产品带入课堂，带入学校的应当统一管理。</p><p style=\"text-align: left;\">学校发现未成年学生沉迷网络的，应当及时告知其父母或者其他监护人，共同对未成年学生进行教育和引导，帮助其恢复正常的学习生活。</p><p style=\"text-align: left;\">第七十一条　未成年人的父母或者其他监护人应当提高网络素养，规范自身使用网络的行为，加强对未成年人使用网络行为的引导和监督。</p><p style=\"text-align: left;\">未成年人的父母或者其他监护人应当通过在智能终端产品上安装未成年人网络保护软件、选择适合未成年人的服务模式和管理功能等方式，避免未成年人接触危害或者可能影响其身心健康的网络信息，合理安排未成年人使用网络的时间，有效预防未成年人沉迷网络。</p><p style=\"text-align: left;\">第七十二条　信息处理者通过网络处理未成年人个人信息的，应当遵循合法、正当和必要的原则。处理不满十四周岁未成年人个人信息的，应当征得未成年人的父母或者其他监护人同意，但法律、行政法规另有规定的除外。</p><p style=\"text-align: left;\">未成年人、父母或者其他监护人要求信息处理者更正、删除未成年人个人信息的，信息处理者应当及时采取措施予以更正、删除，但法律、行政法规另有规定的除外。</p><p style=\"text-align: left;\">第七十三条　网络服务提供者发现未成年人通过网络发布私密信息的，应当及时提示，并采取必要的保护措施。</p><p style=\"text-align: left;\">第七十四条　网络产品和服务提供者不得向未成年人提供诱导其沉迷的产品和服务。</p><p style=\"text-align: left;\">网络游戏、网络直播、网络音视频、网络社交等网络服务提供者应当针对未成年人使用其服务设置相应的时间管理、权限管理、消费管理等功能。</p><p style=\"text-align: left;\">以未成年人为服务对象的在线教育网络产品和服务，不得插入网络游戏链接，不得推送广告等与教学无关的信息。</p><p style=\"text-align: left;\">第七十五条　网络游戏经依法审批后方可运营。</p><p style=\"text-align: left;\">国家建立统一的未成年人网络游戏电子身份认证系统。网络游戏服务提供者应当要求未成年人以真实身份信息注册并登录网络游戏。</p><p style=\"text-align: left;\">网络游戏服务提供者应当按照国家有关规定和标准，对游戏产品进行分类，作出适龄提示，并采取技术措施，不得让未成年人接触不适宜的游戏或者游戏功能。</p><p style=\"text-align: left;\">网络游戏服务提供者不得在每日二十二时至次日八时向未成年人提供网络游戏服务。</p><p style=\"text-align: left;\">第七十六条　网络直播服务提供者不得为未满十六周岁的未成年人提供网络直播发布者账号注册服务；为年满十六周岁的未成年人提供网络直播发布者账号注册服务时，应当对其身份信息进行认证，并征得其父母或者其他监护人同意。</p><p style=\"text-align: left;\">第七十七条　任何组织或者个人不得通过网络以文字、图片、音视频等形式，对未成年人实施侮辱、诽谤、威胁或者恶意损害形象等网络欺凌行为。</p><p style=\"text-align: left;\">遭受网络欺凌的未成年人及其父母或者其他监护人有权通知网络服务提供者采取删除、屏蔽、断开链接等措施。网络服务提供者接到通知后，应当及时采取必要的措施制止网络欺凌行为，防止信息扩散。</p><p style=\"text-align: left;\">第七十八条　网络产品和服务提供者应当建立便捷、合理、有效的投诉和举报渠道，公开投诉、举报方式等信息，及时受理并处理涉及未成年人的投诉、举报。</p><p style=\"text-align: left;\">第七十九条　任何组织或者个人发现网络产品、服务含有危害未成年人身心健康的信息，有权向网络产品和服务提供者或者网信、公安等部门投诉、举报。</p><p style=\"text-align: left;\">第八十条　网络服务提供者发现用户发布、传播可能影响未成年人身心健康的信息且未作显著提示的，应当作出提示或者通知用户予以提示；未作出提示的，不得传输相关信息。</p><p style=\"text-align: left;\">网络服务提供者发现用户发布、传播含有危害未成年人身心健康内容的信息的，应当立即停止传输相关信息，采取删除、屏蔽、断开链接等处置措施，保存有关记录，并向网信、公安等部门报告。</p><p style=\"text-align: left;\">网络服务提供者发现用户利用其网络服务对未成年人实施违法犯罪行为的，应当立即停止向该用户提供网络服务，保存有关记录，并向公安机关报告。</p><p style=\"text-align: center;\"><strong>第六章 政府保护</strong></p><p style=\"text-align: left;\">第八十一条　县级以上人民政府承担未成年人保护协调机制具体工作的职能部门应当明确相关内设机构或者专门人员，负责承担未成年人保护工作。</p><p style=\"text-align: left;\">乡镇人民政府和街道办事处应当设立未成年人保护工作站或者指定专门人员，及时办理未成年人相关事务；支持、指导居民委员会、村民委员会设立专人专岗，做好未成年人保护工作。</p><p style=\"text-align: left;\">第八十二条　各级人民政府应当将家庭教育指导服务纳入城乡公共服务体系，开展家庭教育知识宣传，鼓励和支持有关人民团体、企业事业单位、社会组织开展家庭教育指导服务。</p><p style=\"text-align: left;\">第八十三条　各级人民政府应当保障未成年人受教育的权利，并采取措施保障留守未成年人、困境未成年人、残疾未成年人接受义务教育。</p><p style=\"text-align: left;\">对尚未完成义务教育的辍学未成年学生，教育行政部门应当责令父母或者其他监护人将其送入学校接受义务教育。</p><p style=\"text-align: left;\">第八十四条　各级人民政府应当发展托育、学前教育事业，办好婴幼儿照护服务机构、幼儿园，支持社会力量依法兴办母婴室、婴幼儿照护服务机构、幼儿园。</p><p style=\"text-align: left;\">县级以上地方人民政府及其有关部门应当培养和培训婴幼儿照护服务机构、幼儿园的保教人员，提高其职业道德素质和业务能力。</p><p style=\"text-align: left;\">第八十五条　各级人民政府应当发展职业教育，保障未成年人接受职业教育或者职业技能培训，鼓励和支持人民团体、企业事业单位、社会组织为未成年人提供职业技能培训服务。</p><p style=\"text-align: left;\">第八十六条　各级人民政府应当保障具有接受普通教育能力、能适应校园生活的残疾未成年人就近在普通学校、幼儿园接受教育；保障不具有接受普通教育能力的残疾未成年人在特殊教育学校、幼儿园接受学前教育、义务教育和职业教育。</p><p style=\"text-align: left;\">各级人民政府应当保障特殊教育学校、幼儿园的办学、办园条件，鼓励和支持社会力量举办特殊教育学校、幼儿园。</p><p style=\"text-align: left;\">第八十七条　地方人民政府及其有关部门应当保障校园安全，监督、指导学校、幼儿园等单位落实校园安全责任，建立突发事件的报告、处置和协调机制。</p><p style=\"text-align: left;\">第八十八条　公安机关和其他有关部门应当依法维护校园周边的治安和交通秩序，设置监控设备和交通安全设施，预防和制止侵害未成年人的违法犯罪行为。</p><p style=\"text-align: left;\">第八十九条　地方人民政府应当建立和改善适合未成年人的活动场所和设施，支持公益性未成年人活动场所和设施的建设和运行，鼓励社会力量兴办适合未成年人的活动场所和设施，并加强管理。</p><p style=\"text-align: left;\">地方人民政府应当采取措施，鼓励和支持学校在国家法定节假日、休息日及寒暑假期将文化体育设施对未成年人免费或者优惠开放。</p><p style=\"text-align: left;\">地方人民政府应当采取措施，防止任何组织或者个人侵占、破坏学校、幼儿园、婴幼儿照护服务机构等未成年人活动场所的场地、房屋和设施。</p><p style=\"text-align: left;\">第九十条　各级人民政府及其有关部门应当对未成年人进行卫生保健和营养指导，提供卫生保健服务。</p><p style=\"text-align: left;\">卫生健康部门应当依法对未成年人的疫苗预防接种进行规范，防治未成年人常见病、多发病，加强传染病防治和监督管理，做好伤害预防和干预，指导和监督学校、幼儿园、婴幼儿照护服务机构开展卫生保健工作。</p><p style=\"text-align: left;\">教育行政部门应当加强未成年人的心理健康教育，建立未成年人心理问题的早期发现和及时干预机制。卫生健康部门应当做好未成年人心理治疗、心理危机干预以及精神障碍早期识别和诊断治疗等工作。</p><p style=\"text-align: left;\">第九十一条　各级人民政府及其有关部门对困境未成年人实施分类保障，采取措施满足其生活、教育、安全、医疗康复、住房等方面的基本需要。</p><p style=\"text-align: left;\">第九十二条　具有下列情形之一的，民政部门应当依法对未成年人进行临时监护：</p><p style=\"text-align: left;\">（一）未成年人流浪乞讨或者身份不明，暂时查找不到父母或者其他监护人；</p><p style=\"text-align: left;\">（二）监护人下落不明且无其他人可以担任监护人；</p><p style=\"text-align: left;\">（三）监护人因自身客观原因或者因发生自然灾害、事故灾难、公共卫生事件等突发事件不能履行监护职责，导致未成年人监护缺失；</p><p style=\"text-align: left;\">（四）监护人拒绝或者怠于履行监护职责，导致未成年人处于无人照料的状态；</p><p style=\"text-align: left;\">（五）监护人教唆、利用未成年人实施违法犯罪行为，未成年人需要被带离安置；</p><p style=\"text-align: left;\">（六）未成年人遭受监护人严重伤害或者面临人身安全威胁，需要被紧急安置；</p><p style=\"text-align: left;\">（七）法律规定的其他情形。</p><p style=\"text-align: left;\">第九十三条　对临时监护的未成年人，民政部门可以采取委托亲属抚养、家庭寄养等方式进行安置，也可以交由未成年人救助保护机构或者儿童福利机构进行收留、抚养。</p><p style=\"text-align: left;\">临时监护期间，经民政部门评估，监护人重新具备履行监护职责条件的，民政部门可以将未成年人送回监护人抚养。</p><p style=\"text-align: left;\">第九十四条　具有下列情形之一的，民政部门应当依法对未成年人进行长期监护：</p><p style=\"text-align: left;\">（一）查找不到未成年人的父母或者其他监护人；</p><p style=\"text-align: left;\">（二）监护人死亡或者被宣告死亡且无其他人可以担任监护人；</p><p style=\"text-align: left;\">（三）监护人丧失监护能力且无其他人可以担任监护人；</p><p style=\"text-align: left;\">（四）人民法院判决撤销监护人资格并指定由民政部门担任监护人；</p><p style=\"text-align: left;\">（五）法律规定的其他情形。</p><p style=\"text-align: left;\">第九十五条　民政部门进行收养评估后，可以依法将其长期监护的未成年人交由符合条件的申请人收养。收养关系成立后，民政部门与未成年人的监护关系终止。</p><p style=\"text-align: left;\">第九十六条　民政部门承担临时监护或者长期监护职责的，财政、教育、卫生健康、公安等部门应当根据各自职责予以配合。</p><p style=\"text-align: left;\">县级以上人民政府及其民政部门应当根据需要设立未成年人救助保护机构、儿童福利机构，负责收留、抚养由民政部门监护的未成年人。</p><p style=\"text-align: left;\">第九十七条　县级以上人民政府应当开通全国统一的未成年人保护热线，及时受理、转介侵犯未成年人合法权益的投诉、举报；鼓励和支持人民团体、企业事业单位、社会组织参与建设未成年人保护服务平台、服务热线、服务站点，提供未成年人保护方面的咨询、帮助。</p><p style=\"text-align: left;\">第九十八条　国家建立性侵害、虐待、拐卖、暴力伤害等违法犯罪人员信息查询系统，向密切接触未成年人的单位提供免费查询服务。</p><p style=\"text-align: left;\">第九十九条　地方人民政府应当培育、引导和规范有关社会组织、社会工作者参与未成年人保护工作，开展家庭教育指导服务，为未成年人的心理辅导、康复救助、监护及收养评估等提供专业服务。</p><p style=\"text-align: center;\"><strong>第七章 司法保护</strong></p><p style=\"text-align: left;\">第一百条　公安机关、人民检察院、人民法院和司法行政部门应当依法履行职责，保障未成年人合法权益。</p><p style=\"text-align: left;\">第一百零一条　公安机关、人民检察院、人民法院和司法行政部门应当确定专门机构或者指定专门人员，负责办理涉及未成年人案件。办理涉及未成年人案件的人员应当经过专门培训，熟悉未成年人身心特点。专门机构或者专门人员中，应当有女性工作人员。</p><p style=\"text-align: left;\">公安机关、人民检察院、人民法院和司法行政部门应当对上述机构和人员实行与未成年人保护工作相适应的评价考核标准。</p><p style=\"text-align: left;\">第一百零二条　公安机关、人民检察院、人民法院和司法行政部门办理涉及未成年人案件，应当考虑未成年人身心特点和健康成长的需要，使用未成年人能够理解的语言和表达方式，听取未成年人的意见。</p><p style=\"text-align: left;\">第一百零三条　公安机关、人民检察院、人民法院、司法行政部门以及其他组织和个人不得披露有关案件中未成年人的姓名、影像、住所、就读学校以及其他可能识别出其身份的信息，但查找失踪、被拐卖未成年人等情形除外。</p><p style=\"text-align: left;\">第一百零四条　对需要法律援助或者司法救助的未成年人，法律援助机构或者公安机关、人民检察院、人民法院和司法行政部门应当给予帮助，依法为其提供法律援助或者司法救助。</p><p style=\"text-align: left;\">法律援助机构应当指派熟悉未成年人身心特点的律师为未成年人提供法律援助服务。</p><p style=\"text-align: left;\">法律援助机构和律师协会应当对办理未成年人法律援助案件的律师进行指导和培训。</p><p style=\"text-align: left;\">第一百零五条　人民检察院通过行使检察权，对涉及未成年人的诉讼活动等依法进行监督。</p><p style=\"text-align: left;\">第一百零六条　未成年人合法权益受到侵犯，相关组织和个人未代为提起诉讼的，人民检察院可以督促、支持其提起诉讼；涉及公共利益的，人民检察院有权提起公益诉讼。</p><p style=\"text-align: left;\">第一百零七条　人民法院审理继承案件，应当依法保护未成年人的继承权和受遗赠权。</p><p style=\"text-align: left;\">人民法院审理离婚案件，涉及未成年子女抚养问题的，应当尊重已满八周岁未成年子女的真实意愿，根据双方具体情况，按照最有利于未成年子女的原则依法处理。</p><p style=\"text-align: left;\">第一百零八条　未成年人的父母或者其他监护人不依法履行监护职责或者严重侵犯被监护的未成年人合法权益的，人民法院可以根据有关人员或者单位的申请，依法作出人身安全保护令或者撤销监护人资格。</p><p style=\"text-align: left;\">被撤销监护人资格的父母或者其他监护人应当依法继续负担抚养费用。</p><p style=\"text-align: left;\">第一百零九条　人民法院审理离婚、抚养、收养、监护、探望等案件涉及未成年人的，可以自行或者委托社会组织对未成年人的相关情况进行社会调查。</p><p style=\"text-align: left;\">第一百一十条　公安机关、人民检察院、人民法院讯问未成年犯罪嫌疑人、被告人，询问未成年被害人、证人，应当依法通知其法定代理人或者其成年亲属、所在学校的代表等合适成年人到场，并采取适当方式，在适当场所进行，保障未成年人的名誉权、隐私权和其他合法权益。</p><p style=\"text-align: left;\">人民法院开庭审理涉及未成年人案件，未成年被害人、证人一般不出庭作证；必须出庭的，应当采取保护其隐私的技术手段和心理干预等保护措施。</p><p style=\"text-align: left;\">第一百一十一条　公安机关、人民检察院、人民法院应当与其他有关政府部门、人民团体、社会组织互相配合，对遭受性侵害或者暴力伤害的未成年被害人及其家庭实施必要的心理干预、经济救助、法律援助、转学安置等保护措施。</p><p style=\"text-align: left;\">第一百一十二条　公安机关、人民检察院、人民法院办理未成年人遭受性侵害或者暴力伤害案件，在询问未成年被害人、证人时，应当采取同步录音录像等措施，尽量一次完成；未成年被害人、证人是女性的，应当由女性工作人员进行。</p><p style=\"text-align: left;\">第一百一十三条　对违法犯罪的未成年人，实行教育、感化、挽救的方针，坚持教育为主、惩罚为辅的原则。</p><p style=\"text-align: left;\">对违法犯罪的未成年人依法处罚后，在升学、就业等方面不得歧视。</p><p style=\"text-align: left;\">第一百一十四条　公安机关、人民检察院、人民法院和司法行政部门发现有关单位未尽到未成年人教育、管理、救助、看护等保护职责的，应当向该单位提出建议。被建议单位应当在一个月内作出书面回复。</p><p style=\"text-align: left;\">第一百一十五条　公安机关、人民检察院、人民法院和司法行政部门应当结合实际，根据涉及未成年人案件的特点，开展未成年人法治宣传教育工作。</p><p style=\"text-align: left;\">第一百一十六条　国家鼓励和支持社会组织、社会工作者参与涉及未成年人案件中未成年人的心理干预、法律援助、社会调查、社会观护、教育矫治、社区矫正等工作。</p><p style=\"text-align: center;\"><strong>第八章 法律责任</strong></p><p style=\"text-align: left;\">第一百一十七条　违反本法第十一条第二款规定，未履行报告义务造成严重后果的，由上级主管部门或者所在单位对直接负责的主管人员和其他直接责任人员依法给予处分。</p><p style=\"text-align: left;\">第一百一十八条　未成年人的父母或者其他监护人不依法履行监护职责或者侵犯未成年人合法权益的，由其居住地的居民委员会、村民委员会予以劝诫、制止；情节严重的，居民委员会、村民委员会应当及时向公安机关报告。</p><p style=\"text-align: left;\">公安机关接到报告或者公安机关、人民检察院、人民法院在办理案件过程中发现未成年人的父母或者其他监护人存在上述情形的，应当予以训诫，并可以责令其接受家庭教育指导。</p><p style=\"text-align: left;\">第一百一十九条　学校、幼儿园、婴幼儿照护服务等机构及其教职员工违反本法第二十七条、第二十八条、第三十九条规定的，由公安、教育、卫生健康、市场监督管理等部门按照职责分工责令改正；拒不改正或者情节严重的，对直接负责的主管人员和其他直接责任人员依法给予处分。</p><p style=\"text-align: left;\">第一百二十条　违反本法第四十四条、第四十五条、第四十七条规定，未给予未成年人免费或者优惠待遇的，由市场监督管理、文化和旅游、交通运输等部门按照职责分工责令限期改正，给予警告；拒不改正的，处一万元以上十万元以下罚款。</p><p style=\"text-align: left;\">第一百二十一条　违反本法第五十条、第五十一条规定的，由新闻出版、广播电视、电影、网信等部门按照职责分工责令限期改正，给予警告，没收违法所得，可以并处十万元以下罚款；拒不改正或者情节严重的，责令暂停相关业务、停产停业或者吊销营业执照、吊销相关许可证，违法所得一百万元以上的，并处违法所得一倍以上十倍以下的罚款，没有违法所得或者违法所得不足一百万元的，并处十万元以上一百万元以下罚款。</p><p style=\"text-align: left;\">第一百二十二条　场所运营单位违反本法第五十六条第二款规定、住宿经营者违反本法第五十七条规定的，由市场监督管理、应急管理、公安等部门按照职责分工责令限期改正，给予警告；拒不改正或者造成严重后果的，责令停业整顿或者吊销营业执照、吊销相关许可证，并处一万元以上十万元以下罚款。</p><p style=\"text-align: left;\">第一百二十三条　相关经营者违反本法第五十八条、第五十九条第一款、第六十条规定的，由文化和旅游、市场监督管理、烟草专卖、公安等部门按照职责分工责令限期改正，给予警告，没收违法所得，可以并处五万元以下罚款；拒不改正或者情节严重的，责令停业整顿或者吊销营业执照、吊销相关许可证，可以并处五万元以上五十万元以下罚款。</p><p style=\"text-align: left;\">第一百二十四条　违反本法第五十九条第二款规定，在学校、幼儿园和其他未成年人集中活动的公共场所吸烟、饮酒的，由卫生健康、教育、市场监督管理等部门按照职责分工责令改正，给予警告，可以并处五百元以下罚款；场所管理者未及时制止的，由卫生健康、教育、市场监督管理等部门按照职责分工给予警告，并处一万元以下罚款。</p><p style=\"text-align: left;\">第一百二十五条　违反本法第六十一条规定的，由文化和旅游、人力资源和社会保障、市场监督管理等部门按照职责分工责令限期改正，给予警告，没收违法所得，可以并处十万元以下罚款；拒不改正或者情节严重的，责令停产停业或者吊销营业执照、吊销相关许可证，并处十万元以上一百万元以下罚款。</p><p style=\"text-align: left;\">第一百二十六条　密切接触未成年人的单位违反本法第六十二条规定，未履行查询义务，或者招用、继续聘用具有相关违法犯罪记录人员的，由教育、人力资源和社会保障、市场监督管理等部门按照职责分工责令限期改正，给予警告，并处五万元以下罚款；拒不改正或者造成严重后果的，责令停业整顿或者吊销营业执照、吊销相关许可证，并处五万元以上五十万元以下罚款，对直接负责的主管人员和其他直接责任人员依法给予处分。</p><p style=\"text-align: left;\">第一百二十七条　信息处理者违反本法第七十二条规定，或者网络产品和服务提供者违反本法第七十三条、第七十四条、第七十五条、第七十六条、第七十七条、第八十条规定的，由公安、网信、电信、新闻出版、广播电视、文化和旅游等有关部门按照职责分工责令改正，给予警告，没收违法所得，违法所得一百万元以上的，并处违法所得一倍以上十倍以下罚款，没有违法所得或者违法所得不足一百万元的，并处十万元以上一百万元以下罚款，对直接负责的主管人员和其他责任人员处一万元以上十万元以下罚款；拒不改正或者情节严重的，并可以责令暂停相关业务、停业整顿、关闭网站、吊销营业执照或者吊销相关许可证。</p><p style=\"text-align: left;\">第一百二十八条　国家机关工作人员玩忽职守、滥用职权、徇私舞弊，损害未成年人合法权益的，依法给予处分。</p><p style=\"text-align: left;\">第一百二十九条　违反本法规定，侵犯未成年人合法权益，造成人身、财产或者其他损害的，依法承担民事责任。</p><p style=\"text-align: left;\">违反本法规定，构成违反治安管理行为的，依法给予治安管理处罚；构成犯罪的，依法追究刑事责任。</p><p style=\"text-align: center;\"><strong>第九章 附 则</strong></p><p style=\"text-align: left;\">第一百三十条　本法中下列用语的含义：</p><p style=\"text-align: left;\">（一）密切接触未成年人的单位，是指学校、幼儿园等教育机构；校外培训机构；未成年人救助保护机构、儿童福利机构等未成年人安置、救助机构；婴幼儿照护服务机构、早期教育服务机构；校外托管、临时看护机构；家政服务机构；为未成年人提供医疗服务的医疗机构；其他对未成年人负有教育、培训、监护、救助、看护、医疗等职责的企业事业单位、社会组织等。</p><p style=\"text-align: left;\">（二）学校，是指普通中小学、特殊教育学校、中等职业学校、专门学校。</p><p style=\"text-align: left;\">（三）学生欺凌，是指发生在学生之间，一方蓄意或者恶意通过肢体、语言及网络等手段实施欺压、侮辱，造成另一方人身伤害、财产损失或者精神损害的行为。</p><p style=\"text-align: left;\">第一百三十一条　对中国境内未满十八周岁的外国人、无国籍人，依照本法有关规定予以保护。</p><p style=\"text-align: left;\">第一百三十二条　本法自2021年6月1日起施行。</p><p style=\"text-align: left;\"><br></p>', '本站编辑', '', '2023-04-11 20:59:39');
INSERT INTO `articles` VALUES (33, 5, '依法治校', '河北：枣强县职教中心联合县公安局举办法治教育讲座', '<p style=\"text-align: left;\">为加强法治教育宣传，增强同学们的法律意识、法治观念和自我保护意识，让同学们养成尊法、学法、守法、用法的好习惯。4月7日枣强县职教中心联合县公安局举办法治教育讲座。本次讲座特别邀请了县公安局法治大队长王又法、副大队长马占磊，职教中心党支部书记、校长陈国忠发言，讲座由副校长武世荣主持。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202304/11164846z9ax.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: left;\">王又法大队长结合学生特点，以亲切的话语、具体的案例向同学们讲解了常见的法律知识，增强了每位同学的法律意识和法治观念。讲座受到在场师生的热烈欢迎和广泛好评。</p><p style=\"text-align: left;\">校长陈国忠做总结发言，他表示，以人为本，依法治校，以德立校，以严安校，以学强校、精心打造枣强职教品牌、共同创建平安和谐校园是我们每一位师生义不容辞的责任。希望通过本次讲座，同学们在中职三年的时间里可以不断加强守法的自觉性，努力增强素质和才干，为进入高一级学校和踏入社会奠定良好的基础。</p><p style=\"text-align: left;\">本次法治教育讲座进一步推进了学校的依法治校工作，增强了全体师生的法律意识，营造了平安、文明、和谐的校园氛围。</p>', '枣强县教育局', '', '2023-04-11 21:17:37');
INSERT INTO `articles` VALUES (34, 5, '依法治校', '教育部全国青少年普法网赴日照市金海岸小学调研', '<p style=\"text-align: left;\">为深入贯彻落实习近平法治思想，宣传贯彻党的二十大精神，落实教育系统“八五”普法规划，切实提高教育系统普法的针对性和实效性，2023年3月31日，教育部全国青少年普法网工作组赴日照市金海岸小学（以下简称“金海岸小学”）调研青少年法治教育工作。山东省教育厅政策法规处干部贾旭，日照市教育局副局长张曙及相关工作人员、东港区教体局相关负责人参与调研。金海岸小学副校长孙运玲汇报学校法治教育工作开展情况，校长赵庆英、副书记宋凯华及学校相关部门负责人参与交流座谈会。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202304/03181022pfj3.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: center;\">金海岸小学校园法治文化广场</p><p style=\"text-align: left;\">金海岸小学高度重视法治教育工作，将法治教育与传统文化、艺术教育相结合，形成了富有特色的普法教育模式。在调研过程中，工作组一行现场观摩剪纸社团、合唱社团、吟诵社团开展的法治主题实践活动，观看《宪法在身边》主题队课。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202304/03180045eo17.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: center;\">合唱社团演唱法治主题歌曲</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202304/031758053xrb.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: center;\">剪纸社团创作法治主题剪纸作品</p><p style=\"text-align: left;\">在交流座谈会上，与会人员就学校法治教育工作开展情况、全国学生“学宪法 讲宪法”活动组织效果、学校法治教育课程发展等事项展开了深入讨论。工作组对金海岸小学在青少年法治教育方面所做出的努力和取得的成绩给予了充分肯定，并就学校进一步加强和改进法治教育工作提出了宝贵意见和建议。</p><p style=\"text-align: left;\">金海岸小学从2016年起积极参与全国学生“学宪法 讲宪法”系列活动，学生参与率常年保持100%，形成了浓厚的校园法治文化氛围。2022年，金海岸小学被评为日照市“七五”普法依法治理工作突出单位。</p>', '教育部全国青少年普法网', '', '2023-04-11 21:18:49');
INSERT INTO `articles` VALUES (35, 5, '依法治校', '安徽：长丰县北城实验小学开展法治课进校园活动', '<p style=\"text-align: left;\">为不断深化普法教育，推动“法律进校园\"活动的深入开展，努力营造校园“尊法、学法、守法、用法”的良好氛围，提高青少年儿童的法律素质和法治意识，全力构建和谐文明校园，长丰县北城实验小学特邀双墩镇派出所胡警官于2023年3月23日主讲一堂法治课。</p><p style=\"text-align: left;\">本次法治课以“什么是法律”“未成年人应该承担什么样的法律责任”“如何预防电信诈骗”“反邪教组织”“毒品危害”等为主题展开，胡警官用一个个具体的事例，深入浅出地为孩子们讲述了什么是违法犯罪行为，并用生动的案例、深刻的教训警示学生要遵纪守法，教育大家要充分认识违法行为的严重危害，正确处理好各种关系，充分珍惜在校学习的机会。</p><p style=\"text-align: left;\">此次讲座提高了学生的法治观念，增强了学生辨别是非和自我保护、自我防范的能力，使学生懂得从我做起，从小事做起，珍惜校园美好时光，远离违法犯罪行为，做一名尊法、学法、守法、用法的好学生。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/27102900ll11.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p>', '长丰县北城实验小学', '', '2023-04-11 21:19:39');
INSERT INTO `articles` VALUES (36, 5, '依法治校', '甘肃：兰州市城关区教育系统召开“八五”普法之法人学法暨依法治校示范校经验交流会', '<p style=\"text-align: left;\">为认真贯彻落实党的二十大精神，切实遵从全面依法治国基本方略，深入推进依法治教工作，3月16日下午，城关区教育局召开了教育系统“八五”普法之法人学法暨依法治校示范校经验交流会，会议由城关区教育局副局长甘永武同志主持，会议邀请了兰州大学法学院党委委员、副院长迟方旭教授为大家进行法治培训。</p><p style=\"text-align: left;\">参加会议的有区属中小学、幼儿园正、副校（园）长、局直各单位负责人、局机关科室正、副科长（正、副主任）200余人。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/21104124fo7o.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: left;\">会前，在和政路小学一楼设置了专门的展示厅，由云麓山学校、南山路小学分别展示依法治校示范校经验交流材料，与会人员可在进入会场前观看展板，翻阅材料，学习“依法治校”示范校先进做法。<br></p><p style=\"text-align: left;\">会议开始，由迟方旭教授为大家解读了《义务教育法》和《甘肃省义务教育条例》。迟教授指出当前义务教育整体发展水平有了明显提升，但教育资源不均衡的问题依然存在，必须坚持把义务教育摆在重要位置, 所有适龄儿童、少年必须接受义务教育。</p><p style=\"text-align: left;\">会上，由云麓山学校、四十六中、南山路小学、鼓楼巷幼儿园四所学校校（园）长主要围绕法治宣传教育、建章立制、师生权益保护等方面依次做了“依法治校”示范校工作经验交流分享。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/21104135pmr3.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: left;\">会议最后，城关区教育局副局长甘永武同志安排了2023年度全区普法工作，并强调，2023年将在全区教育系统开展中小学法治宣传教育系列活动，从领导干部学习教育、多渠道培养学生、示范单位创建引领、分类别竞赛等方面，多管齐下、共同发力，努力实现“弘扬社会主义法治理念和法治精神”，推进城关依法治教、依法治校工作，提升教育治理体系和治理能力现代化水平，办好人民满意的教育。</p>', '兰州市城关区教育局', '', '2023-04-11 21:20:21');
INSERT INTO `articles` VALUES (37, 5, '依法治校', '上海：奉贤区司法局华东理工大学法学院实践教学基地揭牌', '<p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/131021504f1m.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: left;\">为深入学习贯彻党的二十大精神，推动习近平法治思想融入高校教育，提升高校法治教育水平，助力法治政府示范建设，2023年3月9日，区司法局与华东理工大学法学院举行“三合一”进高校活动签约仪式暨华东理工大学法学院实践教学基地揭牌仪式。奉贤区司法局党委书记、局长，区行政复议局局长顾斌泉、华东理工大学法学院院长彭德雷出席并签订协议，法学院党委书记丁灏、区司法局党委委员、副局长，区行政复议局副局长褚玉兰进行揭牌，法学院副院长周登谅、党委副书记周建国、吴亮教授和区司法局相关科室工作人员参加活动。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/13102204fm75.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: left;\">座谈会上，顾斌泉表示，开展行政机关负责人出庭、旁听、讲评“三合一”进高校活动意义重大又恰逢其时，奉贤区司法局必定会发挥好“三合一”活动工作制度优势，同时也亟需华东理工大学法学院深厚学术底蕴和强劲教研实力的专业支撑，双方优势互补，合作共进的前景分外广阔。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/1310220913gq.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: left;\">座谈会上，彭德雷介绍了华东理工大学法学院的概况，并希望在未来能够持续强化双方互动，把习近平法治思想融入相关课程，积极拓展“三合一”工作维度，把课堂教学和社会实践有机结合，更好地服务区域发展。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/13102215x4bz.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: center;\">顾斌泉、彭德雷代表双方签订框架协议</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/13102321ky3j.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: center;\"><br></p><p style=\"text-align: center;\">丁灏、褚玉兰为实践教学基地揭牌</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/13102325lf0d.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: center;\">华东理工大学法学院向顾斌泉、褚玉兰颁发了校外导师聘书</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/13102330johs.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p>', '奉贤区司法局', '', '2023-04-11 21:21:04');
INSERT INTO `articles` VALUES (38, 6, '安全校园', '河北：枣强县教育局开展“检察长法治讲堂——教职工篇”专题讲座', '<p style=\"text-align: left;\">为深入贯彻落实党的二十大精神，推进法治中国建设，有效提升广大教职工法治素养，使广大教职工更好地肩负起法治工作教育者、实践者、推动者的责任，依法加强师德师风建设，积极打造法治校园、平安校园，3月30日枣强县教育局邀请枣强县人民检察院党组书记、检察长任荣到教育局开展“检察长法治讲堂——教职工篇”专题讲座。</p><p style=\"text-align: left;\">法治讲座通过线下讲座和线上同步直播的方式召开，为全县3000余名教职工讲授法治课。局务会成员、各股室主任、各学区中心校、乡镇中学校长、县直中小学校长、幼儿园园长等在主会场出席会议；各校班子成员、班主任、法治教师在分会场参与。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202304/11162431qsvh.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: left;\">讲座内容丰富、形式新颖，讲解深入浅出、生动精彩，具有很强的针对性和启示性，使广大教职工受益匪浅。法治课上，县检察院检察长任荣详细解读了“一号检察建议”的由来及核心内容，并以实际案例作为切入点，深入讲解了强制报告、从业禁止等制度，强调强制报告是法定义务，报告免责，不报告追责。希望广大教职工切实履行好教书育人的职责，进一步强化校园安全管理，携手共同守护未成年人的健康成长。</p><p style=\"text-align: left;\">县教育局刘东利局长强调，要以本次法治讲座为契机，将学校法治教育做到“常态化”，常抓不懈，号召教职员工采取多种方式、多种途径加强法治学习、提高法治意识，全面提升依法治教水平；加强师德师风建设，坚持法定职责必须为、法无授权不可为，教职工坚决不得以身试法、跨越法治红线，要时刻关心、关注、关爱孩子的健康成长，发现侵害未成年人身心健康的违法行为在第一时间向公安机关报告。各校要坚决落实从业禁止制度，把好单位用人关。不断推进依法治教、依法办学、依法治校水平，落实落细未成年人保护有关法律法规，为建设“平安校园”“平安枣强”创造良好的法治环境和教育环境。</p><p style=\"text-align: left;\">此次法治教育课让教职工们认识到依法治教、依法从教的重要性与必要性，大家纷纷表示，要把学习成果切实转化成实绩实效，以良好的工作作风全力推进教育振兴。 </p>', '枣强县教育局', '', '2023-04-11 21:22:14');
INSERT INTO `articles` VALUES (39, 6, '安全校园', '河南：开封市紧抓校园安全问题 持续提升平安校园覆盖面', '<p style=\"text-align: left;\">日前，开封市紧抓校园安全问题，全面落实平安建设各项工作部署，持续提升平安校园覆盖面，有效推动师生安全教育到位、应急疏散演练到位、隐患排查整治到位、全员安全责任落实到位，深入开展安全教育、安全防范、隐患排查、信访稳定及信息化管理五项工作能力提升行动，切实为全市高质量发展保驾护航。</p><p style=\"text-align: left;\">在校园内，开封市推动全市域学校校园专职保安员配备率、护学岗设置率、校园封闭化管理达标率、一键式紧急报警和视频监控系统覆盖率、与公安机关联网率“5个100%”全面提升，覆盖全市2400多所中小学校，全面提升校园人防物防技防水平。</p><p style=\"text-align: left;\">离开校园，学校周边的安全环境更不可忽视。放学高峰期人流量加大，交通拥堵，开封市警、校联动，做好高峰勤务，疏导交通，护卫学生安全回家路。并联合多部门，推动校园周边200米安全区建设，全力做好学校周边安全守护，有效净化校园周边环境。</p><p style=\"text-align: left;\">在校园教育硬件设施齐备后，更需要师资软件强硬，营造家庭教育为第一教育阵营。2022年开封市组织130万余名师生开展线上学习，利用宣传教育平台制作推送消防安全、交通安全、极端天气等有针对性的预警文章10余期。家庭和学校是两个分开的教育阵地，放学了不意味着教育工作暂停，而是要加强学校与家庭的沟通联合，把家庭作为首位的教育阵地。</p><p style=\"text-align: left;\">同时，开封市开创“教育走进家庭，学校办给家长”的校外教育模式，采取线上线下相结合、集中和分散相结合、学校教师授课和邀请相关专家举办讲座相结合等多种形式，加强对家长关于校园安全知识、校园欺凌等犯罪案件开展知识讲座与高效互动，联合有关部门制作宣传教育影视作品，设计师生、家长关于安全知识的答题和问卷，加强家校互通、家长互学的教育形式，高效学习校园安全知识。</p><p style=\"text-align: left;\">除了线上线下家校共学，还建设首批8个校园安全教育体验式场馆，让师生、家长真切体验突遇灾难、犯罪现象如何应对、如何避险，让孩子们真实感受、亲历教学，丰富教育形式，全面提升教育成效。</p><p style=\"text-align: left;\">经过积极争取，开封市2022年从教育附加经费中安排安全风险防控经费150万元，建成市教育体育局安全管理指挥中心，加快推进市属学校“三零”平安企事业单位智能感知体系建设工作和将学校校园纳入“雪亮工程”覆盖范围工作。2023年，开封市将不断加大安全经费投入，全面提升平安建设水平，持续夯实全员安全责任，利用两年时间，分两步推进实施，完成县（区）教育行政部门、城镇中小学校、农村中小学校本地、本校视频监控平台建设及视频接入工作，基本实现“雪亮工程”校园全覆盖，提升开封市学校校园及周边综合治安治理水平，维护当前校园安全稳定良好局面。</p>', '开封市人民政府', '', '2023-04-11 21:22:58');
INSERT INTO `articles` VALUES (40, 6, '安全校园', '浙江：台州一中开展2023年春季校园安全系列活动', '<p style=\"text-align: left;\">为进一步深化“平安校园”建设，切实保障广大师生的生命财产安全，台州一中开展了2023年春季“校园安全”系列活动，严格执行校园安全规范，积极落实各项护校安园措施，建立健全校园安全防范治理长效工作机制，为师生安全和教育教学的正常开展保驾护航。</p><p style=\"text-align: left;\">一、全面落实责任</p><p style=\"text-align: left;\">为了全面贯彻落实“预防为主，安全第一，综合治理”的工作方针，不断增强教师的综治安全防范意识，维护教师自身安全和保护学生安全，杜绝意外事故的发生，确保校园平安、稳定、和谐发展，学校与各部门、年级段和全体教师签订安全责任书，强化安全责任意识，切实加大安全管理力度，创建平安校园。</p><p style=\"text-align: left;\">二、严格排查整改</p><p style=\"text-align: left;\">学校把学生宿舍安全作为学生管理工作的重点，为进一步强化学生宿舍安全管理工作，2023年春季，学校全面开展宿舍安全隐患排查工作，学校组织班主任进寝着重对大功率电器、易燃易爆品、私拉乱接电线、管制刀具等进行安全隐患排查。</p><p style=\"text-align: left;\">三、强化教育宣传</p><p style=\"text-align: left;\">为进一步提高师生安全意识与防范电信网络诈骗的意识和能力，守护广大师生的财产安全，学校组织开展“校园安全”主题班会，普及禁毒宣传、交通安全、反诈骗等相关安全教育知识。此外，学校还将反诈宣防延伸到家庭，组织学生与家长一起学习《台州市涉诈风险提示单》。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/271021518yp3.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/27102159y972.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: left;\">四、开展安全演练</p><p style=\"text-align: left;\">为进一步提升处置校园突发事件能力，学校邀请了椒江区反恐大队刘副大队长来校指导，组织安保人员进行反恐防暴应急演练。同时，为牢固树立消防安全意识，提高火灾自救防范能力，3月20日，学校组织开展了消防安全演练，教职员工演练了各类灭火器材的使用方法及火灾扑救的组织处理，取得了明显实效。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/271022023rec.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: left;\">此次春季“校园安全”系列活动提高了全体师生的安全防范意识和技能，为广大师生的生命财产安全提供了更有力的保障。</p><p style=\"text-align: left;\"><br></p>', '本站编辑', '', '2023-04-11 21:23:36');
INSERT INTO `articles` VALUES (41, 6, '安全校园', '江西：探索依法治校新路径 抚州高新首创“学生法治委员”', '<p style=\"text-align: left;\">高校校园“法治委员”是高新公安分局“基层社会治理提升年”活动创新项目，也是深化“三大专项行动”、推进“平安校园”建设活动的重要抓手。为充分发动和依靠“学生力量”，高新公安分局创新警校合作模式，在辖区抚州医学院、江西中医药高等专科学校、抚州职业技术学院、抚州幼儿师范高等专科学校、抚州市技工学校等5所高校打造了抚州市首个高校中心警务室，并创建了班级法治委员制度，创新在每个班级班委中增设一名遵纪守法、品学兼优的同学担任法治委员，推出了“法治委员课堂”“法治委员调解”“法治委员联防队”等一系列做法，让隐形的“警力”乘着“法治委员”的翅膀紧紧守护高校平安，开创了校园法治教育的新思路。自“法治委员”制度实施以来，该5所高校警情总量同比下降56.7%，案件警情同比下降48.9%，打造出了一张“以点带面，以点连线”的高校警务新样板。</p><p style=\"text-align: center;\"><strong>同学身边的普法“宣讲员”</strong></p><p style=\"text-align: center;\"><br></p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/211044494o5y.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: left;\">“心莫贪，擦亮眼，绷根弦，防诈骗；电信诈骗，五花八门，勿信不理，四平八稳；密码不说，要钱不汇，链接不点！大家要记好哦！”在抚州职业技术学院的一间学生寝室，法治委员周逸轩正在苦口婆心地和大家解读着手中的防诈宣传册，并用顺口溜的方式，将手册中的各个要点向同学们讲授。</p><p style=\"text-align: left;\">为创新法治教育形式，弥补传统说教不足，在今年的开学季，高新公安分局发动高校各班级的587名法治委员给班级学生上好警示课。他们或是召开班级会议，或是走进同学宿舍，使出“浑身解数”开展法治宣传，切实增强同学们学法、懂法、守法、用法意识，提高抵御违法犯罪的本领。特别是针对当下电信诈骗在高校频发的特点，法治委员面对面、零距离和同学们讲解日常生活中常见的各类诈骗方式，以及犯罪分子的作案手段、规律特点，并向同学们科普识别、防范电诈的小技巧。同时，对于一些比较“调皮”的学生，发动法治委员真诚地和他们“交朋友”，仔细聆听他们的心声，灌以真实的案件、惨痛的教训，和他们开展警示教育，真正让法治入心。</p><p style=\"text-align: center;\"><strong>学生矛盾纠纷的“调解员”</strong></p><p style=\"text-align: left;\">2022年11月10日，在某大学的篮球场，两伙学生因争抢打球场地引发了矛盾，双方互相辱骂导致情绪升级，冲突一触即发。路过的法治委员张亚军，紧急出面劝阻，在后续发现情况加入进来的几个法治委员的一起努力下，冲突双方学生最终握手言和。</p><p style=\"text-align: left;\">当前，高新区5所高校在校师生达5万余人，校园矛盾纠纷多出、诉求形式多样，成为校园安全管理工作中的难点。为将“法”的宣教融入“治”的行动，高新公安分局充分发挥“校园法治委员”桥梁纽带作用，对上帮助公安机关、学校、老师强化校园管理，对下利用“朋辈教育”化解同学之间的矛盾纠纷，当好“解铃人”。特别是针对校园多发的情感类、经济类纠纷，法治委员第一时间发现、第一时间开展矛盾疏导工作，做到“大事化小、小事化了”。在校园法治委员的助力下，2022年以来，156起学生矛盾在苗头初期被化解，有效防止校园欺凌事件10起，调解学生矛盾纠纷300余件，并成功化解1起有自杀倾向的学生感情纠纷事件，为预防化解校园矛盾纠纷拉起一道坚实的“隐形屏障”，进一步推动平安校园建设。</p><p style=\"text-align: center;\"><strong>校园安全隐患的“发现者”</strong></p><p style=\"text-align: left;\">这里的应急灯坏了、那里的安全提示标志不明显、又发现一辆停放得小车车门未锁......这是校园法治委员整理上来的工作台账其中一页。创造一个安全的校园环境至关重要，而如何发挥学生的主体作用，如何调动学生的积极性更好地参与学校安全隐患的排查解决，是营造安全校园环境至关重要的一环。</p><p style=\"text-align: left;\">自高校法治委员选配后，高新公安分局高校中心警务室民警积极发动法治委员自愿报名加入警校联防队，将警务室民辅警、学校保卫处、保安队、志愿者师生等力量组织起来，建群管理，为各高校组建了一支可用、能用、有用的群防群治队伍，其中学生法治委员的占比均达到70%以上。他们即是校园一线安全工作的“发现者”，也是班级同学安全状况感知的“触角”，是高校群防群治工作的“中坚力量”。警校联防队组建后，以排查化解涉校安全隐患，严密防范遏制涉校案事件为着力点，重点围绕消防安全监管、安全设施配备运行、食堂食品管理、宿舍用电安全等，定时开展安全巡查与隐患排查，并将发现的问题形成工作台账，及时反馈给学校有关部门，再由学校明确专人负责、限时整改。2022年以来，法治委员共摸排出校园安全隐患51处，提出工作建议60余条，及时消除了校园隐患漏洞，优化了校园环境，切实提高了校园的安全自治能力。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202303/21104456spem.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p>', '抚州市公安局高新分局', '', '2023-04-11 21:24:17');
INSERT INTO `articles` VALUES (42, 6, '安全校园', '江苏：泰州市海陵区“三个强化”筑牢校园安全稳定屏障', '<p style=\"text-align: left;\">今年以来，海陵区教育局始终坚持“安全第一、预防为主、综合治理”安全工作总要求，把校园安全摆在全局工作首要位置，牢固树立校园安全红线意识、底线思维，进一步完善校园安全体系，推动校园安全治理模式向事前预防转型，坚持在“三个强化”上细谋划、下真功、求实效，全力筑牢校园安全稳定坚实屏障。</p><p style=\"text-align: left;\"><strong>一是强化思想认识，安全防控不存侥幸。</strong>全区始终把校园安全当作基础性、先导性、系统性工作来抓，在三个“不”字上做文章、聚合力、深落实。一是疫情防控不松懈。紧盯疫情防控这个牛鼻子，始终坚持底线思维，认真落实“乙类乙管”下疫情防控各项举措，做好常态化防控和疫情流行期间应急处置相结合，最大程度保障师生健康。二是安全防控不拖拉。健全各类安全应急预案，抓实应急演练，不断提升应对突发事件的处置能力。根据《学校“1234+N”消防安全标准化建设》要求，开展学校消防安全标准化建设试点与推广工作。三是隐患排治不怠慢。充分发挥安全管理片区职能，推行局班子成员挂钩化解重大（较大）安全风险隐患制度，各学校对照《海陵区校园重点部位安全风险隐患排查指南》，常态化开展安全风险隐患排查整治，形成闭环。</p><p style=\"text-align: left;\"><strong>二是强化协同配合，安全治理不留死角。</strong>充分发挥区教育安全专委会职能，公安、交警、消防救援、市场监管等多部门协同配合，形成齐抓共管、多向发力、多路并行的安全管理态势。一是强化三防建设，夯实校园安全基石。加强安全队伍建设，进一步健全安全管理专门机构，配齐配强专兼职安管干部和保安，开展校园保安技能培训。强化校园警务室建设，严格落实校园全封闭管理。深化“雪亮工程”建设，持续推动智慧技防校园升级改造。二是强化家校共管，堵牢学生安全缺口。加强师生安全教育，搭建家校共管平台，采取教师家访、召开家长会、发放告家长书等形式定期推送安全提示信息，持续抓好上放学高峰时段教职工车辆错时和分流措施。三是强化部门协同，拧紧周边治安阀门。会同公安、镇街等部门深入开展校车行经线路安全隐患排查整治，切实保障校车运行安全。</p><p style=\"text-align: left;\"><strong>三是强化宣传教育，安全宣传不留盲区。</strong>严格按照早部署、早宣传、早预防、持续抓的原则做好校园安全宣教工作。一是创新教育宣传形式。坚持线上线下相结合，利用电子显示屏、家长会、告家长书和公众号等形式，让安全教育渗入每个学生家庭，实现家校共育。二是科学安排教育内容。围绕“5.12”防灾减灾日、“11.9”消防日、“12.2”交通安全日等重要节点，通过校园网、手机短信、两微一端等形式，广泛开展预防溺水、火灾、禁毒、性侵害、食物中毒、电信网络诈骗、交通事故、自然灾害等方面的安全教育。三是营造良好教育氛围。结合不同学段安全教育特点，科学安排安全教育主题活动，定期开展不同类型，不同内容的安全宣教活动，做到月月有主题、周周有宣传、日日有提醒。营造浓厚的宣传氛围，让安防意识入脑入心。</p>', '泰州市海陵区人民政府', '', '2023-04-11 21:24:59');
INSERT INTO `articles` VALUES (43, 7, '政策解读', '教育部语用司负责人就《普通话水平测试规程》答记者问', '<p style=\"text-align: left;\">为贯彻落实党的二十大精神，加大国家通用语言文字推广力度，根据《普通话水平测试管理规定》（教育部令第51号）要求，国家语委修订了《普通话水平测试规程》，自2023年4月1日起施行。教育部语用司负责人就《规程》修订有关问题回答了记者的提问。</p><p style=\"text-align: left;\">一、请简要介绍一下《规程》的修订背景和过程。</p><p style=\"text-align: left;\">答：旧版《普通话水平测试规程》是2003年依据原《普通话水平测试管理规定》（教育部令第16号，51号令颁布后该文件已废止）制定的，2008年又印发了《计算机辅助普通话水平测试操作规程（试行）》，两个规程同时作为普通话水平测试工作的具体“操作指南”。2021年51号令印发后，考虑到上位依据发生变化，计算机辅助测试也已成为主要测试方式，我们遂启动了《规程》的修订工作，根据测试工作的实践经验和发展需要，在充分调研的基础上，形成了《规程》修订稿。修订稿在各省级教育行政部门、测试机构进行两轮次征求意见，听取了部内有关司局和有关专家的意见建议。起草组对意见建议逐条研究、充分吸收，不断修改完善，形成了新修订的《规程》。</p><p style=\"text-align: left;\">二、请介绍一下此次修订的总体考虑。</p><p style=\"text-align: left;\">答：此次修订旨在细化落实教育部令第51号中关于测试规程的有关要求，根据工作实际和发展方向，对2003年印发的旧规程以及2008年的《计算机辅助普通话水平测试操作规程（试行）》等原有分散的规定进行整合提升，同时兼顾与2022年印发的《普通话水平测试等级证书管理办法》（国语函〔2022〕2号）等有关配套文件协调一致、互为支撑，力求全面体现51号令要求的科学性、规范性、便利性和安全性。</p><p style=\"text-align: left;\">三、在体现科学规范方面，《规程》做了哪些修订？</p><p style=\"text-align: left;\">答：一是明确测试方式。2003年印发的旧规程主要适用于人工测试，2008年补充印发的《计算机辅助普通话水平测试操作规程（试行）》主要适用于计算机辅助测试。本次修订明确“普通话水平测试采用计算机辅助测试”，所有测试流程均通过测试信息化管理系统，按计算机辅助测试方式实施，人工测试只作为特殊情况下适用予以保留。这既是对多年来信息化建设成果的肯定和继承，也符合语言测试信息化发展的方向。二是取消备测室和备测时间。2003和2008年印发的规程要求有备测室和备测时间，供应试人熟悉试卷、做好准备。在具体实施中，各地的备测时间、内容、方式不尽相同，存在一些不规范现象。修订过程中，一方面借鉴国内外其他种类语言测试方式，另一方面考虑普通话水平测试试题均来自国家测试机构编制并公开出版的《普通话水平测试实施纲要》，朗读作品和说话题目作为重点测试项目均已公开，应试人在测试前可充分准备，因此本次修订将取消备测环节。</p><p style=\"text-align: left;\">四、在便民高效方面，《规程》做了哪些修订？</p><p style=\"text-align: left;\">答：一是要求信息公开。规定测试站点信息、测试站点的测试计划和安排等情况要及时向社会公开。二是明确和缩短成绩评定时限。旧规程未明确规定成绩评定时限，仅要求复审在30个工作日内完成，本次修订将复审认定时限纳入成绩认定总体考虑，规定一级乙等及以下成绩的认定（含复审）时限原则上在当次测试结束后30个工作日内完成，一级甲等的成绩认定顺延15个工作日。三是保障应试人合理权益。明确应试人对测试成绩有异议，可以在测试成绩发布后15个工作日内向其参加测试的站点提出复核申请，并明确了等级证书更补程序。</p><p style=\"text-align: left;\">五、在安全管理方面，《规程》做了哪些修订？</p><p style=\"text-align: left;\">答：一是明确站点设置原则。要求测试站点的设立应充分考虑社会需求，布局合理，不得设立在社会培训机构、中介机构或其他营利性机构或组织，测试站点建设要求由国家测试机构另行制定。二是强化数据安全。专设“数据档案”新章节，对数据类型、数据信息、数据管理等提出明确要求。三是加强规范管理。专设“违规处理”新章节，明确对违规机构进行警告处理、暂停测试资格处理及站点撤销的规定，以及对应试人作弊行为的处理规定。</p><p style=\"text-align: left;\"><br></p>', '教育部', '', '2023-04-11 21:26:13');
INSERT INTO `articles` VALUES (44, 7, '政策解读', '全面构建协同育人新格局 ——教育部基础教育司负责人就《关于健全学校家庭社会协同育人机制的意见》答记者问', '<p style=\"text-align: left;\">近日，教育部等十三部门联合印发了《关于健全学校家庭社会协同育人机制的意见》（以下简称《意见》）。教育部基础教育司负责人就《意见》有关内容回答了记者的提问。</p><p style=\"text-align: left;\">一、请问《意见》出台的背景是什么？</p><p style=\"text-align: left;\">答：一是贯彻落实党中央、国务院决策部署。习近平总书记在全国教育大会上指出，办好教育事业，家庭、学校、政府、社会都有责任。党的十九届五中全会提出健全学校家庭社会协同育人机制，《家庭教育促进法》规定建立健全家庭学校社会协同育人机制，《“十四五”规划和2035年远景目标纲要》和2022年政府工作报告都确定了健全学校家庭社会协同育人机制的工作任务。党的二十大报告进一步要求，健全学校家庭社会育人机制。会同相关部门研制《意见》已列入教育部2022年工作要点。</p><p style=\"text-align: left;\">二是着力破解存在的突出问题。近年来，各地积极探索推进学校家庭社会协同育人，取得了明显成效，但还存在职责定位不够清晰、协同机制不够健全、条件保障不够到位等突出问题。为切实解决这些问题，有必要研究制定《意见》，明确各方育人职责和相互协同机制，形成更加完善的协同育人机制。</p><p style=\"text-align: left;\">二、请简要介绍一下《意见》的研制过程？</p><p style=\"text-align: left;\">答：《意见》的研制过程主要经历了四个阶段：</p><p style=\"text-align: left;\">一是开展课题研究。委托北京师范大学相关专家成立课题组，对学校家庭社会协同育人机制有关理论问题进行深入研究，汇总整理相关法律法规、政策文件、国际经验等，召开多场座谈会听取相关方面代表意见，研究提出协同育人工作问题清单和文件起草建议，为文稿起草奠定基础。</p><p style=\"text-align: left;\">二是总结地方经验。全面梳理各地在推进学校家庭社会协同育人方面已经开展的有关工作、出台的政策措施，提炼总结省市县和学校层面的成熟经验和有效做法，在文稿起草过程中予以充分吸收，纳入相关条目，确保文稿提出的政策举措具有可操作性。</p><p style=\"text-align: left;\">三是研究起草文稿。根据调研情况和各地提出的工作举措、政策建议起草文稿，进一步明确了指导思想、工作原则、工作目标、工作举措和保障措施。</p><p style=\"text-align: left;\">四是广泛征求意见。先后征求了相关专家、各省级教育行政部门和有关部门意见，同时在北京召开调研座谈会，听取区级教育行政部门和校长、教师、家长代表的意见建议。在认真研究、充分吸纳各方意见的基础上，进一步修改完善。</p><p style=\"text-align: left;\">三、请问《意见》提出的总体要求是什么？</p><p style=\"text-align: left;\">答：在指导思想上，以习近平新时代中国特色社会主义思想为指导，认真贯彻落实习近平总书记关于教育和注重家庭家教家风建设的重要论述，全面贯彻党的教育方针，落实立德树人根本任务，弘扬中华优秀传统文化，坚持科学教育观念，增强协同育人共识，积极构建学校家庭社会协同育人新格局，着力培养德智体美劳全面发展的社会主义建设者和接班人。</p><p style=\"text-align: left;\">在工作原则上，一是坚持育人为本。用新时代党的创新理论铸魂育人，广泛践行社会主义核心价值观，遵循学生成长规律和教育规律，深入落实“双减”政策，大力发展素质教育。二是坚持政府统筹。充分发挥政府统筹协调作用，加强系统谋划，推动部门联动，强化条件保障，促进资源共享和协同育人有效实施。三是坚持协同共育。明确学校家庭社会协同育人责任，完善工作机制，促进各展优势、密切配合、相互支持，切实增强育人合力，共同担负起学生成长成才的重要责任。四是坚持问题导向。强化专业指导，鼓励实践探索，着力解决制度建设、指导服务、条件保障等方面存在的突出问题，不断增强协同育人的科学性针对性实效性。</p><p style=\"text-align: left;\">在工作目标上，提出到“十四五”时期末，政府对学校家庭社会协同育人工作的统筹领导更加有力，制度体系基本建立健全。学校积极主导、家庭主动尽责、社会有效支持的协同育人机制更加完善，促进学生全面发展健康成长的良好氛围更加浓厚。学校教育主阵地作用进一步强化，家庭教育指导服务更加专业；家长科学育儿观念基本树立，履行家庭教育主体责任更加到位；城乡社区家庭教育指导服务站点普遍建立，社会育人资源利用更加充分。到2035年，形成定位清晰、机制健全、联动紧密、科学高效的学校家庭社会协同育人机制。</p><p style=\"text-align: left;\">四、请问《意见》提出的主要举措有哪些？</p><p style=\"text-align: left;\">答：《意见》提出了三个方面的重要举措。一是学校充分发挥协同育人主导作用。要及时沟通学生情况，创新日常沟通途径，认真落实家访制度，全面掌握并向家长及时沟通学生在校期间的思想情绪、学业状况、行为表现和身心发展等情况，同时向家长了解学生在家中的有关情况。要加强家庭教育指导，把做好家庭教育指导服务作为重要职责，纳入学校工作计划，充分发挥学校专业指导优势；切实加强教师家庭教育指导能力建设，将教师家庭教育指导水平与绩效纳入教师考评体系。建立健全学校家庭教育指导委员会、家长学校和家长委员会，落实家长会、学校开放日、家长接待日等制度，每学期至少组织2次家庭教育指导活动。要用好社会育人资源，把统筹用好各类社会资源作为强化实践育人的重要途径，积极拓展校外教育空间，着力培养学生社会责任感、创新精神和实践能力。要主动加强同社会有关单位的联系沟通，建立相对稳定的社会实践教育基地和资源目录清单，依据不同基地资源情况联合开发社会实践课程。二是家长切实履行家庭教育主体责任。要提高家庭教育水平，家长要强化家庭是第一个课堂、家长是第一任老师的责任意识，注重家庭建设，为子女健康成长创造良好家庭环境，树立科学家庭教育观念，掌握正确家庭教育方法。要主动协同学校教育。家长要积极参加学校组织的家庭教育指导和家校互动活动，自觉学习家庭教育知识和方法，主动参与家长委员会有关工作，充分理解学校正常教育教学工作，积极配合学校依法依规严格管理教育学生。要引导子女体验社会。家长要充分认识社会实践大课堂对子女教育的重要作用，根据子女年龄情况，主动利用节假日、休息日等闲暇时间带领或支持子女通过多种方式体验社会，帮助子女更好亲近自然、开阔眼界、增长见识、提高素质。三是社会有效支持服务全面育人。要完善社会家庭教育服务体系。将家庭教育指导作为城乡社区公共服务重要内容，积极构建普惠性家庭教育公共服务体系。对居民委员会、村民委员会、婚姻登记机构、收养登记机构、公共文化服务机构、开放大学、老年大学、社区学院等各类机构开展家庭教育指导服务提出了要求。要推进社会资源开放共享。社区要面向中小学生积极开展各种公益性课外实践活动，促进学生身体健康，增强社会责任感。各类爱国主义教育基地、法治教育基地、研学实践基地、科普教育基地和图书馆、博物馆、文化馆、非遗馆、美术馆、纪念馆、科技馆、演出场馆、体育场馆、国家公园、青少年宫、儿童活动中心等，要面向中小学生及学龄前儿童免费或优惠开放。鼓励支持社会有关方面提供寓教于乐的优秀儿童文化精品，丰富学生精神文化生活，提升学生审美鉴赏能力。要净化社会育人环境。深入开展儿童图书、音像等出版物清理整顿，健全网络综合治理体系，加大网络有害信息、网络游戏沉迷、不良网络行为治理力度，严肃查处违法违规网站平台，督促企业严格落实主体责任，着力打造有利于青少年健康成长的清朗社会文化及良好网络生态。</p><p style=\"text-align: left;\">五、请问如何做好《意见》的贯彻落实？</p><p style=\"text-align: left;\">答：一要加强组织领导。要将构建学校家庭社会协同育人机制作为贯彻落实党中央、国务院决策部署的重大政治任务，强化党委领导、政府统筹，积极推动健全学校家庭社会密切协同的育人机制。二要强化专业支撑。推动有关高等院校、科研机构、专业团体开展协同育人理论与实践研究，加强理论建设与专业人才培养，积极推进家庭教育指导专家队伍建设。将家庭教育指导纳入师范生培养和教师业务培训重要内容。鼓励高等院校面向大学生开设家庭教育选修课。三要营造良好氛围。深入宣传学校家庭社会协同育人的政策举措、实际成效和典型案例，广泛传播科学教育理念和正确家庭教育方法，强化正面宣传和舆论引导，大力营造全社会各方面关心支持协同育人的良好氛围。</p>', '教育部', '', '2023-04-11 21:26:53');
INSERT INTO `articles` VALUES (45, 7, '政策解读', '开启新时代高素质专业化创新型教师队伍建设新征程 ——教育部教师工作司负责人就《新时代基础教育强师计划》答记者问', '<p style=\"text-align: left;\">日前，教育部等八部门联合印发《新时代基础教育强师计划》（以下简称《强师计划》），教育部教师工作司负责人就相关问题回答了记者提问。</p><p style=\"text-align: left;\">一、请介绍一下《强师计划》出台的背景</p><p style=\"text-align: left;\">答：高质量教师是高质量教育发展的中坚力量。党的十八大以来，习近平总书记就加强教师队伍建设作出系列重要指示，强调教师是教育工作的中坚力量，广大教师要做“四有”好老师、“四个引路人”，实现“四个相统一”，各级党委和政府要坚持把教师队伍建设作为基础工作。习近平总书记指出，要着力构建优质均衡的基本公共教育服务体系，加强中西部欠发达地区教师定向培养和精准培训。李克强总理强调，要依法加强义务教育教师收入保障，加大在职培训、学历教育力度，提高义务教育教师素质。</p><p style=\"text-align: left;\">为深入贯彻习近平总书记的重要指示精神，我部近年来先后出台职业教育和高等教育领域教师队伍建设文件。为构建全口径教师队伍建设政策体系，全面落实《中共中央 国务院关于全面深化新时代教师队伍建设改革的意见》精神，教育部会同相关部门出台《新时代基础教育强师计划》。</p><p style=\"text-align: left;\">二、请介绍一下《强师计划》的研制经历了哪些过程？</p><p style=\"text-align: left;\">答：一是集中办公研制文件。在广泛调研基础上，组织专家学者、基层教育行政部门负责人和一线校长、教师组成工作专班，集中进行文件研制，并就重点问题同相关部委多次沟通，形成文件初稿。二是反复沟通征求意见。强师计划征求意见稿面向中宣部等8部门、32个省级教育行政部门、6所部属师范大学和部分综合性大学征求意见。全面征求中央教育工作领导小组成员意见。三是多次会议专题研究。教育部分管负责同志多次主持召开工作协调会，听取高校、地方教育行政部门和知名专家学者意见建议并做专题研讨。经中央教育工作领导小组会议审议通过，会签相关部门。</p><p style=\"text-align: left;\">三、《强师计划》的目标任务是什么?</p><p style=\"text-align: left;\">答：立足“十四五”、面向2035，《强师计划》目标任务包括两个层面：一是到2025年，建成一批国家师范教育基地，形成一批可复制可推广的教师队伍建设改革经验，培养一批硕士层次中小学教师和教育领军人才，完善部属师范大学示范、地方师范院校为主体的农村教师培养支持服务体系，欠发达地区中小学教师紧缺情况逐渐缓解，教师培训实现专业化、标准化，教师发展保障有力，教师队伍管理服务水平显著提升。二是到2035年，适应教育现代化和建成教育强国要求，构建开放、协同、联动的高水平教师教育体系，建立完善的教师专业发展机制，教师数量和质量基本满足基础教育发展需求，教师队伍整体素质和教育教学水平明显提升，尊师重教蔚然成风。</p><p style=\"text-align: left;\">四、《强师计划》有哪些主要内容？</p><p style=\"text-align: left;\">答：《强师计划》包括总体要求、具体措施、实施保障三部分。其中，15条具体措施主要围绕四个方面：一是提升教师能力素质，包括提升教师思想政治素质、加强和改进师德师风建设、实施高素质教师人才培育计划、深化精准培训改革；二是推动优质师资均衡，包括实施中西部欠发达地区优秀教师定向培养计划（以下简称优师计划）、优化义务教育教师资源配置；三是加强教师教育体系建设，包括建设国家师范教育基地、建立教师教育协同创新平台、改进师范院校评价；四是深化管理综合改革，包括开展国家教师队伍建设改革试点、进一步完善教师资格制度、优化教职工编制配置、深化教师职称改革完善岗位管理制度、加强教师工资待遇保障、推进教师队伍建设信息化。</p><p style=\"text-align: left;\">五、《强师计划》有哪些政策创新点？</p><p style=\"text-align: left;\">答：《强师计划》以高素质教师人才培养为引领，以高水平教师教育体系建设为支撑，以提升教师思想政治素质、师德师风水平和教育教学能力为重点，提出新时代基础教育教师队伍筑基提质、补短扶弱、做优建强的思路举措，促进教师数量、素质、结构协调发展，推动教师队伍建设创新：</p><p style=\"text-align: left;\">一是以高水平师范院校为引领，加强教师教育体系建设。支持建设一批国家师范教育基地，打造师范教育高质量发展的排头兵、领头雁。会同国家发展改革委实施“十四五”教育强国推进工程，支持一批师范院校加强教学科研设施建设，每校支持1亿元，实现新世纪以来国家层面在高等教育重大专项中，专门面向师范院校和师范专业建设进行的最大规模投入。鼓励支持建立教师教育协同创新平台，推进师范院校在教育科学研究、教师教育师资队伍建设、师范人才培养和基础教育服务等领域开展合作。支持有条件的地方师专升本。</p><p style=\"text-align: left;\">二是以创新人才培养为目标，提升教师培养层次。实施高素质教师人才培育计划，深化本硕整体设计、分段考核的人才培养模式改革，推进高素质复合型硕士层次高中教师培养试点和部属师范大学公费师范生攻读教育硕士工作，继续实施农村学校教育硕士师资培养计划，扩大教育硕士培养规模。同时，深入实施名师名校长领航计划，培养造就基础教育领域的教育家、大先生。</p><p style=\"text-align: left;\">三是以中西部欠发达地区教师为重点，推动师资优质均衡。继续实施中西部欠发达地区优秀教师定向培养计划，依托部属师范大学和地方高水平师范院校为832个脱贫县和中西部陆地边境县定向培养本科层次师范生，中央财政给予支持。深入推进“县管校聘”管理改革，完善交流轮岗、支教讲学激励机制，引导优秀人才向乡村学校流动。通过落实补助政策、倾斜职称评聘、加强周转宿舍建设和住房保障等，着力增强乡村教师职业吸引力。</p><p style=\"text-align: left;\">四是以深化评价改革为牵引，提升教师队伍治理水平。落实教育评价改革要求，严格落实师德师风第一标准。深化职称改革，实行分类评价，出台完善中小学岗位设置管理的指导意见。绩效工资分配向班主任、教育教学效果突出的一线教师、从事特殊教育随班就读工作的教师倾斜。改进师范院校评价，探索师范类“双一流”建设评价机制。支持地方政府统筹，有关部门密切配合，开展国家教师队伍建设改革试点。</p><p style=\"text-align: left;\">五是以信息技术应用为抓手，推动教师队伍建设提质增效。支持师范生和教师管理信息系统、教师资格管理信息系统、国家教师管理服务信息化平台等的建设和完善，加强信息系统安全防护，为教师队伍建设提供信息化决策和便捷化服务支撑。深入实施人工智能助推教师队伍建设试点行动，探索教师管理优化、教师教育改革、教育教学方法创新、教育精准帮扶的新路径和新模式，进一步发挥教师在人工智能与教育融合中的作用。</p><p style=\"text-align: left;\">六、如何做好计划的组织实施工作？ </p><p style=\"text-align: left;\">答：一分部署，九分落实。强师计划的组织实施是一项系统工程，做好落实工作，一要全面加强党的领导。以习近平新时代中国特色社会主义思想为指导，强化政治引领，推动发挥地方党委教育工作领导小组作用，成立强师工作专班，制定具体实施方案。二要抓好宣传部署。教育部将通过刊发理论文章等多种形式加大宣传，通过召开工作部署会、组织干部专题培训班等方式，帮助各地、各相关高校深入领会文件精神，明确目标任务，把握工作重点。各地教育行政部门也要做好文件精神的传达和部署落实工作，深入做好政策解读，及时回应社会关切。三要建立协同机制。加强统筹，建立多部门协同联动的推进机制，中央和地方共同支持，将教师队伍建设作为教育投入重点予以优先保障，制订出台教师激励支持政策，在全社会营造尊师重教的良好风尚，确保各项政策措施全面落实到位。四要加强督导督促。各级教育督导部门要将强师计划实施情况纳入政府履行教育职责评价内容，加强督导检查并强化督导结果运用。特别是严格落实经费监管制度，规范经费使用，确保资金使用效益。对于各地各校落实强师计划的创新举措、典型经验，要及时总结、推动交流、大力推广。</p>', '教育部', '', '2023-04-11 21:27:48');
INSERT INTO `articles` VALUES (46, 7, '政策解读', '推动中小学幼儿园校（园）长履职尽责的重大制度安排 ——国务院教育督导委员会办公室负责人就《关于开展中小学幼儿园校（园）长任期结束综合督导评估工作的意见》答记者问', '<p style=\"text-align: left;\">1．为什么要出台《关于开展中小学幼儿园校（园）长任期结束综合督导评估工作的意见》？ </p><p style=\"text-align: left;\">答：开展学校督导，是教育督导的重要职能。校（园）长是中小学幼儿园的法人，是学校（幼儿园）管理的总负责人，在贯彻落实党的教育方针和党中央、国务院教育决策中起着至关重要的作用。通过强有力的督导，强化校（园）长的主体责任，对于落实立德树人根本任务、规范办学行为、提高教育教学质量意义重大。近年来，有关部门在加强中小学幼儿园校（园）长队伍建设上采取了诸多措施，但总的看，全国中小学幼儿园管理水平仍参差不齐，校（园）长责任意识不够强、履职尽责不到位的情况仍不同程度存在。</p><p style=\"text-align: left;\">2020年2月，中共中央办公厅、国务院办公厅印发《关于深化新时代教育督导体制机制改革的意见》，明确提出“学校校（园）长在一个任期结束时，要接受一次综合督导”。这是强化中小学幼儿园校（园）长职责的重大举措，是加强学校督导、推动提高中小学幼儿园管理水平的制度创新。为落实上述要求，迫切需要制定专门文件，对开展中小学幼儿园校（园）长任期结束综合督导评估工作作出具体安排和部署。</p><p style=\"text-align: left;\">2．开展中小学幼儿园校（园）长任期结束综合督导评估工作的总体考虑是什么？</p><p style=\"text-align: left;\">答：开展中小学幼儿园校（园）长任期结束综合督导评估，涉及面广，关注度高，十分复杂和敏感。《意见》深入贯彻中央要求，立足地方和学校实际，对这项工作进行了系统设计。</p><p style=\"text-align: left;\">指导思想是：以习近平新时代中国特色社会主义思想为指导，深入贯彻党的十九大和十九届历次全会精神，全面落实党的教育方针，牢牢把握社会主义办学方向，坚持立德树人根本任务，遵循教育教学规律，强化校（园）长职责和使命担当，激发办学活力，着力提升中小学幼儿园管理水平，推动中小学幼儿园切实规范办学行为，不断提高教育质量和办学水平，努力让每个孩子享有公平而有质量的教育。 </p><p style=\"text-align: left;\">基本原则有四条：一是坚持正确导向，发挥督导评估的指挥棒作用，落实“五育并举”，坚持发展性评价，体现问题导向，引导督促校（园）长提高政治站位，敢于担当，着力解决广大中小学和幼儿园事业发展中面临的突出问题。二是加强统筹协调，贯彻落实中组部、教育部有关文件对中小学幼儿园校（园）长职责的规定，加强与学前教育办园行为评估、基础教育质量监测、责任督学挂牌督导、巡视巡查、经济责任审计等工作的协调，避免增加基层负担。三是体现因地制宜，既强化对校（园）长履职的统一要求，又充分考虑地区和学校的差异性，针对不同规模、不同类型学校和幼儿园的特点，合理制定评估指标，推动办出特色。四是创新方式方法，注重运用信息技术手段，采取线上线下相结合、明察暗访相结合、定性定量相结合等方式，优化工作流程，确保督出效果。</p><p style=\"text-align: left;\">3．中小学幼儿园校（园）长任期结束综合督导评估的具体对象是谁？</p><p style=\"text-align: left;\">答：中小学幼儿园校（园）长综合督导评估的对象为具备独立法人资格的中小学幼儿园校（园）长（含民办和中外合作办学机构），暂不包括特殊教育学校。其中，义务教育学校包括小学、初中和九年一贯制学校，普通高中学校包括高级中学、完全中学、十二年一贯制学校和中外合作办学机构。</p><p style=\"text-align: left;\">4．中小学幼儿园校（园）长任期结束综合督导评估的重点包括哪些方面？</p><p style=\"text-align: left;\">答：《意见》从7个方面明确了综合督导评估的重点：一是党的建设。主要评价履行“一岗双责”、基层党组织建设、师生思想政治工作、以党建带队建团建、党建和业务融合、党风廉政建设、防控意识形态风险等情况。二是立德树人。主要评价落实“五育”并举、建立健全学校教育质量内部保障制度、执行国家课程方案和课程标准、教学教研、减轻作业负担、课后服务、校本课程建设、学生体质和心理健康、学生评价等情况。三是学校治理。主要评价推进依法治校，完善学校章程及内部管理制度，制定学校发展规划，执行民主集中制、“三重一大”决策制度、教职工代表大会制度、工会制度和家长委员会制度，加强财务管理、后勤管理、家校共育、校园文化建设、校务公开等情况。四是教师工作。主要评价师德师风、教师配置、教师专业发展、教师职称评聘、教师考核评价、教师培训、班主任和少先队辅导员工作、教师身心健康等情况。五是规范办学。主要评价落实“五项管理”、规范招生、规范收费、学籍管理、对外合作、校风教风学风等情况。六是安全卫生。主要评价建立健全安全风险防控制度、保安资质审核、开展安全教育、防治校园欺凌、实验室及危险化学品安全、食品饮水安全、校园安全事件处置等情况。七是满意度。主要是学生、教师、家长、社区等各相关方面对学校的评价及满意度情况。</p><p style=\"text-align: left;\">5．对中小学幼儿园校（园）长任期结束综合督导评估，各级教育督导部门分别承担哪些职责？</p><p style=\"text-align: left;\">答：《意见》明确了国家和地方各级教育督导部门的职责。具体是：</p><p style=\"text-align: left;\">国务院教育督导委员会负责统筹指导。主要是根据国家有关法律法规和中小学幼儿园办学标准，确定中小学幼儿园校（园）长任期结束综合督导评估重点。每年组织对全国各地工作开展情况进行抽查，以此监测全国总体情况，并以适当方式发布监测报告。</p><p style=\"text-align: left;\">省级教育督导部门负责制订本地区督导评估实施方案。实施方案要明确督导评估的指标体系、工作进度安排和组织实施方式。指标体系要落实本意见确定的督导评估重点，符合本地实际情况，考虑学校差异。每年对省域内总体情况进行监测并组织抽查。各省（自治区、直辖市）可根据实际情况确定抽查比例或数量。</p><p style=\"text-align: left;\">地市级教育督导部门负责落实省级综合督导评估实施方案，指导县级开展中小学幼儿园校（园）长任期结束综合督导评估工作，并直接督导评估地市直属学校幼儿园校（园）长，监测全市总体情况。</p><p style=\"text-align: left;\">县级教育督导部门负责具体组织实施辖区内中小学幼儿园校（园）长任期结束综合督导评估工作。要结合本地实际，制订可落地可操作的具体工作计划，建立健全工作机制，落实条件保障，规范组织实施，确保取得实效。</p><p style=\"text-align: left;\">6．开展中小学幼儿园校（园）长任期结束综合督导评估，主要有哪些程序？</p><p style=\"text-align: left;\">答：《意见》对综合督导评估的实施程序作出了明确规定。主要包括4个环节：</p><p style=\"text-align: left;\">一是确定安排。各地组织深入细致的调研摸底，建立中小学幼儿园校（园）长任期情况数据库，动态掌握校（园）长任期情况。每年年底前，明确任期届满人员，确定下一年度接受综合督导的校（园）长名单。根据名单，合理确定年度综合督导工作安排。</p><p style=\"text-align: left;\">二是下达通知。每年初，由负责具体组织实施的教育督导部门将本年度接受综合督导的校（园）长名单正式通知学校。相关学校要提前做好各项相应准备。</p><p style=\"text-align: left;\">三是实施评估。组建由督学、教育行政干部和有关专家组成的督导评估组，对照评估指标，开展入校督导评估工作，形成综合督导评估报告。</p><p style=\"text-align: left;\">四是反馈意见。教育督导部门向被评估的校（园）长或继任者及所在学校（幼儿园）反馈评估意见，重点针对存在问题向学校（幼儿园）及继任者提出整改要求。</p><p style=\"text-align: left;\">7．对开展中小学幼儿园校（园）长任期结束综合督导评估，有哪些工作要求？</p><p style=\"text-align: left;\">答：为确保综合督导评估的权威性、客观性、公信力，《意见》提出了3个方面的明确要求：</p><p style=\"text-align: left;\">一是评估对象要全覆盖。中小学幼儿园校（园）长任期结束综合督导评估原则上以5年为一个周期，覆盖周期内所有任期结束的校（园）长。校（园）长离任及学校（幼儿园）发生重大事故时，应在当年接受综合督导评估。</p><p style=\"text-align: left;\">二是评估结论要客观。综合督导评估要立足学校（幼儿园）实际，严格按规定程序实施，注重多方了解情况，广泛听取师生意见。相关结论和意见要有事实或数据支撑，避免主观臆断。</p><p style=\"text-align: left;\">三是工作纪律要严格。中小学幼儿园校（园）长任期结束综合督导评估要严格遵守党风廉政要求，不得利用督导评估谋取任何个人利益，严禁偏袒和徇私舞弊。要尊重基层，避免干扰学校教育教学秩序，力戒形式主义，注重实效。</p><p style=\"text-align: left;\">8．对用好中小学幼儿园校（园）长任期结束综合督导评估结果，有哪些考虑？</p><p style=\"text-align: left;\">答：《意见》非常重视综合督导评估结果运用，主要有4个方面考虑。一是县级以上党委、政府及教育主管部门要将中小学幼儿园校（园）长任期结束综合督导评估报告，作为办学情况诊断、重大决策、校（园）长考核的重要依据。二是有关部门要将综合督导评估结果作为校（园）长表彰奖励、选拔任用的重要参考和依据。三是对问题突出、整改不力的校（园）长，要依照《教育督导问责办法》进行严肃问责。四是各级教育督导部门要以适当方式公开中小学幼儿园校（园）长任期结束综合督导评估报告，主动接受社会监督。</p><p style=\"text-align: left;\">9．对中小学幼儿园校（园）长任期结束综合督导评估试点工作是如何考虑的？</p><p style=\"text-align: left;\">答：中小学幼儿园校（园）长任期结束综合督导评估量大、面广，加之各地差异较大，需稳妥推进。为此，《意见》要求先试点，再逐步推开。2022年，国务院教育督导委员会办公室将综合考虑地域分布、事业发展水平、工作基础等情况，确定部分省份率先开展中小学幼儿园校（园）长任期结束综合督导评估试点工作，总结经验。在试点的基础上，适时推开，逐步在全国范围内实施。</p><p style=\"text-align: left;\">10．制定《中小学幼儿园校（园）长任期结束综合督导评估参考要点》，主要考虑是什么？</p><p style=\"text-align: left;\">答：全国有约23万所中小学、29万所幼儿园，区域和校际差异很大。开展中小学幼儿园校（园）长任期结束综合督导评估，既要强化国家统一要求，又要充分考虑各地的实际情况，避免“一刀切”。为此，《意见》在明确7个方面督导评估重点的基础上，针对幼儿园园长、义务教育学校校长、普通高中学校校长，分别制定了督导评估参考要点。各地可结合实际，在参考要点基础上，研究制定本地区的督导评估指标。这样的做法，一方面，增强了督导评估的针对性，体现了不同学段的特点；另一方面，给各地留下了充足空间，确保督导评估符合地方和学校的实际。</p><p style=\"text-align: left;\"><br></p>', '教育部', '', '2023-04-11 21:28:35');
INSERT INTO `articles` VALUES (47, 7, '政策解读', '教育部有关负责人就《关于减轻中小学教师负担进一步营造教育教学良好环境的若干意见》答记者问', '<h1 style=\"text-align: center;\">为中小学教师减负 为教育提质增效加油</h1><h2 style=\"text-align: center;\">——教育部有关负责人就《关于减轻中小学教师负担进一步营造教育教学良好环境的若干意见》答记者问</h2><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">　　近日，中共中央办公厅、国务院办公厅印发了《关于减轻中小学教师负担进一步营造教育教学良好环境的若干意见》。教育部有关负责人就有关问题，回答了记者提问。</p><p style=\"text-align: left;\">　　<strong>问：请介绍一下为中小学教师减负文件出台的背景和意义。</strong></p><p style=\"text-align: left;\">　　答：一是落实基层减负，贯彻中央决策部署。今年3月，中央提出将2019年作为“基层减负年”并明确了基层减负的硬杠杠。教师工作点多面广线长，教师队伍占据事业单位人员半壁江山，有关中小学教师负担过重报道时有出现，成为广大基层教师反映强烈的突出问题。中小学教师减负意见的出台，有利于切实减少对中小学校和教师不必要的干扰，营造良好的教育教学环境，让教师全身心投入教书育人工作，落实好立德树人根本任务。二是加强制度建设，为中小学教师减负增能。当前，由于一些历史的和体制机制方面的原因，教师特别是中小学教师的额外工作负担过重，各种督查、检查、评比、考核等事项名目多、频率高，各类调研、统计、信息采集等活动交叉重复、布置随意。还有一些地方和部门，在落实安全稳定、扫黑除恶、创优评先等工作时，经常向学校和教师摊派任务，影响学校正常的教育教学秩序，占用教师大量时间、耗费大量精力。究其原因，表面上体现为广大教师承担了与教育教学科研不相干的事务，疲于应付、忙于琐事，但从根本上反映了当前在尊重教师专业发展规律、理顺现代学校和教师管理体制机制方面存在的制度性问题。出台为中小学教师减负的文件，有针对性地回答了目前减轻中小学教师负担、加强教师统筹管理中的一系列机制性问题，标志着中小学教师管理制度建设体系日益趋向完善。三是弘扬尊师重教，办人民满意的教育。教育是国之大计，党之大计。教师是教育的第一资源，承载着为党育人、为国育才的历史使命，肩负着培养社会主义建设者和接班人的时代重任。出台教师减负文件，要求各级党委和政府牢固树立教师的天职是教书育人的理念，呼吁社会各界把宁静还给学校，把时间还给教师，这既是积极回应基层一线教师热切期盼的迫切需要，也是弘扬全社会尊师重教，推进教育事业公平而有质量发展的迫切需要。</p><p style=\"text-align: left;\">　　<strong>问：请介绍一下为中小学教师减负文件的研制过程。</strong></p><p style=\"text-align: left;\">　　答：一是深刻领会中央精神。认真学习习近平新时代中国特色社会主义思想和党的十九大精神，深刻领会习近平总书记关于教育的重要论述特别是关于教师工作的重要指示批示精神，坚决贯彻落实全国教育大会精神和党中央、国务院关于为中小学教师减负的系列决策部署，明确减负方向，把握减负原则。二是深入开展调研。通过实地调查和文献梳理，全面总结近年来中小学教师承担的工作负担种类和来源。2018年6月，教育部成立调研工作小组，先后分赴东中西部9省18市实地调研，召开教育部门管理人员、中小学幼儿园教师校长座谈会18场次，访谈教师约400名。对 28个省份的250个县市、1430所学校的中小学教师进行了问卷调研。同时，系统梳理各地开展教师减负工作的成功经验，借鉴发达国家教师管理改革经验，明确文件起草思路和重点举措，形成初稿。三是广泛征求意见。通过召开全国教育行政部门、县市教育局长、中小学一线教师校长座谈会，两会代表委员恳谈会等，广泛听取意见建议。书面征求了20个中央国家机关和有关单位、教育部内8个司局和全国32个省份教育部门的意见。教育部党组书记、部长陈宝生，亲自带队赴山东开展为中小学教师减负专项调研。根据各方意见，修改形成文件定稿。</p><p style=\"text-align: left;\">　　<strong>问：请介绍一下为中小学教师减负的总体思路。</strong></p><p style=\"text-align: left;\">　　答：一是贯彻中央精神。全面贯彻落实习近平总书记关于教育的重要论述特别是关于教师工作的重要指示批示精神，将其作为为中小学教师减负、进一步优化教育教学环境的指导思想和主线遵循，贯穿文件稿的始终。二是立足解决问题。坚持问题导向，紧紧抓住当前中央关注、教师关心、舆论关注的突出问题，认真梳理分类，找准问题根源，坚持因地制宜，定向精准发力，力戒形式主义，力求标本兼治，切实为中小学教师减负，把时间还给教师。三是坚持正确导向。减负不等于没有负担，要认清减负的根本目的是提质增效，减掉中小学教师不应该承担的与教育教学无关的事项，改变形式主义、官僚主义的工作方式方法，提高教育教学质量和效率。优先保障教育教学活动，确保有足够的时间和精力研究教学、备课充电，提高专业化水平。同时，也不是说中小学教师跟地方工作完全脱节，要强化中小学教师责任意识，参加社会事务也是教育的一部分，要找好两者之间的平衡。四是抓住关键环节。教师工作负担来源广，渠道多，减轻教师负担工作切忌眉毛胡子一把抓，平均用力。要抓住各种督查检查评比考核、各类调研统计信息采集、社会事务进校园、抽调借用等关键问题、关键环节，开展重点治理、优先治理。五是明确责任主体。中小学教师面临的额外的工作负担来自方方面面，必须厘清责任，坚持刚性治理。省级党委和政府要根据意见精神，列出具体减负清单；党委教育工作领导小组要加强统筹协调、宣传引导、督促落实；教育部门负责具体组织实施；教育督导部门要加强督导</p><p style=\"text-align: left;\">　　<strong>问：为中小学教师减负文件的主要内容是什么？</strong></p><p style=\"text-align: left;\">　　答：文件包括六个方面20条，可分为3大部分：一是提高思想认识，包括第1、2、3条。阐述了重要意义，分析主要问题，提出总体要求，强调聚焦教师主责主业，坚决反对官僚主义、形式主义，让中小学教师从一些无谓的事务中解脱出来。二是明确减负内容和主要举措，包括第4至17条。分别从统筹规范督查检查评比考核事项、社会事务进校园、相关报表填写工作、抽调借用中小学教师事宜等四个方面，明确了减负的主要任务、责任主体和重点举措。三是加强组织实施，包括第18、19、20条。从加强组织领导、加强督导、加强引导等三个方面明确了落实工作要求。</p><p style=\"text-align: left;\">　　<strong>问：如何统筹规范督查检查评比考核事项？</strong></p><p style=\"text-align: left;\">　　答：各种督查、检查、评比、考核等事项名目过多过频，是中小学教师负担的主要来源之一。2019年，中办印发《关于统筹规范督查检查考核工作的通知》，为基层开展督查检查评比考核工作指明了方向，提供了遵循。一是依法依规开展督查检查评比考核。文件规定涉及中小学校和教师的督查检查评比考核工作，由各级党委和政府统一部署，同级教育行政部门统筹协调开展，按照归口管理原则，实行年度计划和审批报备制度，年初分别报同级党委办公厅（室）、政府办公厅（室）研究审核，由党委办公厅（室）统一报党委审批。除教育部门外，其他部门不得自行设置以中小学教师为对象的督查检查评比考核事项，确需开展的要商教育部门，按程序报批后实施。二是全面清理精简现有督查检查评比考核事项。省级党委和政府对现有涉及中小学校和教师的督查检查评比考核事项，进行一次集中清理，严格控制总量和频次，在现有基础上减少50%以上，清理后保留的事项实行清单管理制度。三是改进督查检查评比考核的方式方法。督查检查评比考核是开展工作的必要且非常有效的方式之一，我们要坚持开展，但是如果缺乏科学思维和方法，重留痕轻过程，只看材料不看实际，就容易走向形式主义。要完善考核评价体系，体现差别化原则，坚持走群众路线，加强常态化了解，坚决纠正机械式做法。</p><p style=\"text-align: left;\">　　<strong>问：如何统筹规范社会事务进校园？</strong></p><p style=\"text-align: left;\">　　答：首先要明确为中小学教师减负文件中的社会事务，指的是面对社会，以满足社会公共需要为基本目标，直接或间接为国民经济和社会发展提供服务或创造条件，不以营利为主要目的的社会活动。随着时代发展，社会事务的内涵将会不断丰富。需要注意的是，一些地方政府部门和企业等单位开展的如庆典、招商、拆迁等活动不属于此类社会事务的范畴，此类活动是与教育教学无关的事项，要坚决杜绝。</p><p style=\"text-align: left;\">　　文件对社会事务的统筹规范做出了明确规定。一是教师是社会成员的重要组成部分，在不影响教育教学主责主业的前提下，有责任有义务参与社会建设。各级党委和政府统一部署的社会事务中确需中小学教师参与的，应由教育部门严格按要求依程序统筹安排。二是在开展涉及中小学校和教师的社会事务时，要充分尊重教育教学规律。如有的地方在幼儿园开展扫黑除恶等活动，对幼儿进行标语教育及知识答题活动，脱离教学实际，增加教师负担。三是要防范“一刀切”的形式主义做法。开展社会事务时，要以实现教育效果，保证教育教学质量为目的，由教育部门整体规划、分类指导、统筹安排进入校园。</p><p style=\"text-align: left;\">　　<strong>问：如何统筹规范精简相关报表填写工作？</strong></p><p style=\"text-align: left;\">　　答：一是规范精简各类报表填写。各级党委和政府及教育部门要严把各类报表填写入口关，精简内容和次数，统筹安排，坚决杜绝多头填报、重复填报。二是提升数据采集和管理的信息化水平。各级教育部门和学校通过加强信息管理系统建设，建立健全各类教育信息数据库，进一步规范基本信息的采集和管理，明确管理方责任，努力做到一次采集，最大限度使用。三是严格规范教育统计和调研工作。除国家统计局外，其他部门开展涉及中小学校和教师的教育统计工作时，须向同级人民政府统计机构报请审批备案。针对中小学教师开展的调研活动，须经教育部门同意并由教育部门部署开展。</p><p style=\"text-align: left;\">　　<strong>问：如何统筹规范抽调借用中小学教师事宜？</strong></p><p style=\"text-align: left;\">　　答：教育部门统筹中小学教师的安排使用工作，严格限制和规范有关部门对中小学教师的抽调借用。对于借用中小学教师参与贯彻落实党和国家重大决策部署任务的，在不影响学校正常教育教学情况下，应经县级以上教育部门同意，并报同级党委审批备案，借用期限原则上不超过半年。</p><p style=\"text-align: left;\">　　文件明确规定了对教师培训活动的管理。组织教师参加培训、学习等工作中存在着形式主义问题，有些培训走形式、走过场，内容脱离教育教学，缺乏针对性，浪费教师大量时间精力。因此，对于教育系统内部开展的培训，要结合教师工作和生活实际，优化内容，改进形式，合理开展，避免硬性安排。在培训结果考核方面，对教师培训机构提出更高要求，注重采取多种方式，通过多个维度观测教师的教学水平，不能一张卷子考所有人。对于非教育教学方面的培训，教育部门要严格把关，除人力资源社会保障部门依法依规开展的培训之外，认真研判，合理取消，同类合并，不得拉教师拼凑充数，把无关培训摊派给教师。</p><p style=\"text-align: left;\">　　<strong>问：如何确保为中小学教师减负政策举措落地见效？</strong></p><p style=\"text-align: left;\">　　答：一分部署，九分落实。一是加强领导。教师减负工作涉及多部门多领域，各级党委和政府要高度重视，切实履行责任，把减轻中小学教师负担工作纳入重要议事日程，严把审批和报备关口。各级党委教育工作领导小组要加强统筹协调、宣传引导、督促落实。各级教育部门要在党委和政府的领导下，认真落实好组织实施工作。二是加强督导。国务院教育督导委员会要把减轻中小学教师负担工作纳入对省级人民政府履行教育职责的督导之中。省级教育督导部门要把减轻中小学教师负担工作作为教育督导和开学检查的重要内容。对于执行不力、落实不到位的要严肃问责。三是加强引导。各级党委和政府及其教育部门要加大宣传力度，大力倡导尊师重教，努力引导有关部门和全社会进一步理解教育工作、关心中小学教师发展，共同营造良好的教育生态环境。</p><p style=\"text-align: left;\"><br></p>', '本站编辑', '', '2023-04-11 21:29:31');
INSERT INTO `articles` VALUES (48, 8, '法律故事', '知网滥用支配地位实施垄断行为被罚', '<p style=\"text-align: justify;\">知网垄断案正是以典型案例的方式，警醒市场主体一定要重视竞争合规工作，通过以案说法的方式倡导了公平竞争制度和公平竞争文化，并推动企业建立体系化的竞争合规机制。<br></p><p style=\"text-align: justify;\">一直身处“垄断争议”舆论旋涡的中国知网（中国国家知识基础设施，英文名称China National Knowledge Infrastructure，简称CNKI），终于迎来处罚结果。12月26日，国家市场监管总局发布调查结果：2014年以来，知网滥用支配地位实施垄断行为，通过连续大幅提高服务价格、拆分数据库变相涨价等方式，实施了以不公平的高价销售其数据库服务的行为。对此市场监管总局作出处罚决定：责令知网停止独家合作行为，不得以不公平的高价销售数据库服务，并处以罚款8760万元。<br></p><p style=\"text-align: justify;\">业内专家对此评析指出，知网垄断案正是以典型案例的方式，警醒市场主体一定要重视竞争合规工作，通过以案说法的方式倡导了公平竞争制度和公平竞争文化，并推动企业建立体系化的竞争合规机制。<br></p><p style=\"text-align: justify;\"><strong>综合认定知网支配地位</strong><br></p><p style=\"text-align: justify;\">知网作为学术平台，主要业务是向高校、科研院所、公共图书馆等用户销售网络数据库服务，同时提供学术不端检测等增值服务。<br></p><p style=\"text-align: justify;\">市场监管总局认定，知网由同方知网（北京）技术有限公司、同方知网数字出版技术股份有限公司、《中国学术期刊（光盘版）》电子杂志社有限公司共同运营，三家公司共同决策、实施了本案滥用市场支配地位行为。<br></p><p style=\"text-align: justify;\">按照反垄断法相关规定，对于如何认定某市场主体滥用市场支配地位行为，主要从以下步骤进行：界定相关市场；认定经营者在相关市场上是否具有市场支配地位；认定具有市场支配地位的经营者是否从事了滥用行为且没有正当理由；分析滥用行为是否产生了排除、限制竞争后果或者显著损害了公平交易等社会公共利益。<br></p><p style=\"text-align: justify;\">在市场监管总局作出的行政处罚决定书中，对于知网具有相关市场的支配地位主要是从市场份额来认定的。<br></p><p style=\"text-align: justify;\">从知网市场份额看已经超过50%，具有较强市场力量。2014年至2021年，在中国境内中文学术文献网络数据库服务经营者合计服务收入中，知网背后的三家公司市场份额每年分别为59.95%、58.62%、63.27%、66.91%、66.99%、65.22%、68.03%、69.38%，均超过50%。<br></p><p style=\"text-align: justify;\">知网数据库收录了95%以上正式出版的中文学术文献，数量最多、最全。截至2022年5月，知网拥有机构用户2万多家，个人注册用户1.23亿人；当事人覆盖90%以上的高校用户，其他类型用户覆盖率均在60%以上。<br></p><p style=\"text-align: justify;\">知网作为中国境内最大的学术文献网络数据库服务经营者，具有影响和决定交易价格等关键交易条件的能力。2014年至2021年，在知网数据库服务持续涨价的情况下，机构用户数量仍保持年均2%的增长。同时，机构用户普遍希望与当事人开展长期合作维持价格基本稳定，但知网基本以一年为限签订合同，并在续签合同时提高价格，体现了知网对市场具有很强的控制能力。<br></p><p style=\"text-align: justify;\">此外，中文学术文献网络数据库服务同时具有知识产权产品、互联网信息服务、网络出版服务等多重属性。经营者进入中国境内中文学术文献网络数据库服务市场，需要取得《网络出版服务许可证》《电子出版物出版许可证》等资质方可开展经营。新进入者达到用户临界规模难度大。知网围绕中文学术文献网络数据库服务，研究开发了一系列系统或平台，包括学术不端检测系统、期刊采编平台等。以学术不端检测系统为例，与其他主要竞争性平台相比，知网学术不端检测系统具有显著技术优势，已成为国内学术期刊出版单位、高校、科研院所和个人用户普遍使用的检测工具。<br></p><p style=\"text-align: justify;\">根据综合分析，市场监管总局认定知网在中国境内中文学术文献网络数据库服务市场具有支配地位。<br></p><p style=\"text-align: justify;\"><strong>扰乱市场正常竞争秩序</strong><br></p><p style=\"text-align: justify;\">如果仅仅具备市场支配地位，还不足以构成反垄断法打击的违法行为。但是市场监管总局的调查显示，知网滥用其上述市场支配地位，通过独家合作限定学术期刊出版单位、高校只能向其提供学术文献数据，和以不公平高价销售中文学术文献网络数据库服务等手段，排除限制市场竞争。<br></p><p style=\"text-align: justify;\">市场监管总局对外披露，2014年以来，知网通过与学术期刊出版单位、高校签订格式化独家合作协议等方式，要求学术期刊出版单位、高校向当事人独家授权使用期刊、博硕士学位论文等学术资源，不得授权其他竞争性平台使用，并以多种手段保障独家合作协议实施。这些手段包括：知网通过免费提供学术不端检测等服务，要求学术期刊出版单位、高校执行独家合作协议。知网会根据是否独家合作，支付差别性的版权使用费。<br></p><p style=\"text-align: justify;\">此外，2014年以来知网为获取超额利润，滥用其具有的市场支配地位，通过连续大幅提高服务价格、拆分数据库变相涨价等方式，实施了以不公平的高价销售中文学术文献网络数据库服务的行为。比如在成本基本稳定的情况下，超过正常幅度提高数据库服务价格。2014年以来，用户采购当事人数据库服务年均价格涨幅超过30%的有66家，10%至30%的达622家。部分用户2021年数据库采购价格达到2014年的数倍，其中，高于4倍的有91家，高于3倍的有157家。由于购买当事人数据库服务的主要用户为高校、科研院所等财政拨款单位，用户对当事人数据库服务为刚性需求且议价能力弱，只能压缩其他费用支出，以高价购买当事人数据库服务。<br></p><p style=\"text-align: justify;\">对此，市场监管总局认定，知网滥用在中文学术文献网络数据库服务市场的支配地位，通过独家合作维持、巩固、强化自身市场支配地位，排除、限制了相关市场竞争，以不公平的高价销售数据库服务，损害了用户合法利益，阻碍了中文学术文献网络数据库服务市场技术进步和创新发展，影响了学术交流传播，破坏了良好的科研学术生态环境。<br></p><p style=\"text-align: justify;\">国务院反垄断委员会专家咨询组成员、中国政法大学教授时建中分析指出，市场监管总局的行政处罚决定及时纠正知网限制知识获取渠道、提高知识获取成本的违法行为，是促进我国中文学术文献网络数据库服务质量和水平的提升，增进社会整体福利的必要之举。<br></p><p style=\"text-align: justify;\">国务院反垄断委员会专家咨询组成员、中国人民大学教授孟雁北认为，通过知网垄断案，市场监管总局要实现的目标是维护我国中文学术文献网络数据库服务市场的公平竞争，塑造我国学术文献网络数据库服务市场开放、包容、共享的发展理念。<br></p><p style=\"text-align: justify;\"><strong>依法规范促进发展并重</strong><br></p><p style=\"text-align: justify;\">12月26日，市场监管总局对于知网滥用在中国境内中文学术文献网络数据库服务市场的支配地位、排除限制市场竞争、侵害用户合法权益、破坏良好学术生态环境的违法行为作出行政处罚决定：责令知网停止违法行为。包括停止独家合作行为，不得限制学术期刊出版单位、高校等与其他竞争性平台开展学术资源合作。不得实施不公平的高价行为，应以公平、合理、无歧视的价格在中国境内销售中文学术文献网络数据库服务。<br></p><p style=\"text-align: justify;\">同时，对知网所涉三家公司处以2021年度中国境内销售额17.52亿元5%的罚款，计8760万元。<br></p><p style=\"text-align: justify;\">孟雁北认为这是知网垄断案的一大亮点，市场监管总局坚持依法规范和促进发展并重，不仅依法作出了行政处罚决定，而且监督知网消除违法行为后果，要求知网围绕解除独家合作、减轻用户负担、加强内部合规管理等方面进行全面整改。<br></p><p style=\"text-align: justify;\">在市场监管总局依法对知网作出行政处罚决定后，知网按照行政处罚决定，结合社会各方面的意见和建议，制定并对外公开了五个方面共15项的整改措施，承诺接受各方监督，从而使社会公众感受到了反垄断执法成效的落地。<br></p><p style=\"text-align: justify;\">时建中分析指出，对知网开展反垄断调查并进行行政处罚，目的不是罚款，而是规范知网的经营行为，尽快恢复相关市场竞争秩序，充分激发市场活力和创新动力，促进行业持续健康发展，更好地保护消费者利益和社会公共利益。行政处罚若与自查整改形成合力，会收到更好的经济效果、社会效果和法治效果。推动平台经济高质量发展，不仅需要竞争和创新的良性互动，还需要更好地实现有效市场和有为政府的结合。通过实施常态化的反垄断监管，能够有效预防和制止垄断行为，持续净化平台经济领域的竞争环境，维护和激发创新的动力和活力，保障平台经济更加健康地高质量发展，更好地发挥平台经济在推动科技创新和繁荣经济、便利人民生活、参与国际竞争等方面的积极作用。<br></p><p style=\"text-align: left;\"><br></p>', '法治日报', '', '2023-04-11 21:30:38');
INSERT INTO `articles` VALUES (49, 8, '法律故事', '租房养狗损坏家具 双方过错均应担责', '<p style=\"text-align: justify;\">租房的房客偷偷养宠物，导致退房时房间一团糟，房主能否要求赔偿家具损失费及打扫费？日前，福建省武平县人民法院审结该起租赁合同纠纷案。</p><p style=\"text-align: justify;\">法院查明，蔡某将其名下的一套住宅出租给郑某，约定出租屋内不得养宠物，且郑某应在租赁期满后将房屋打扫干净后归还。但郑某搬离出租屋时并未对房屋进行清理打扫，蔡某在收房时发现郑某偷偷养宠物狗，房屋地面、家具、窗帘等到处粘有狗毛，并且沙发、床垫等弥漫着浓重的狗尿味。<br></p><p style=\"text-align: justify;\">此后，蔡某多次联系郑某要求其打扫房屋，沟通无果后只得亲自对房屋进行打扫，发现在使用宠物消毒水、宠物除臭剂、洁厕灵等种种除味方法后均无法消除狗尿味，一气之下丢弃了沙发、床垫，并将郑某诉至法院，要求郑某支付房屋打扫费、家具损失费等合计4000元。<br></p><p style=\"text-align: justify;\">承办法官在详细了解案情后认为，本案事实清楚，证据充分，案件争议不大，通过调解更有利于化解双方的矛盾。郑某在出租屋内养狗、搬离房屋后未对房屋进行清扫，违反了合同约定，应当承担相应的违约责任。沙发、床垫有狗尿骚味，但不意味着沙发、床垫价值的全部丧失，蔡某将沙发、床垫丢弃的行为扩大了损失，亦应承担相应的责任。<br></p><p style=\"text-align: justify;\">经法院释法析理后，双方均认识到自身的过错，郑某同意支付蔡某房屋打扫费，蔡某亦同意减少沙发、床垫的损害赔偿金额，最终双方达成调解协议：郑某支付蔡某房屋打扫费、家具损失费等共计1200元。</p><p style=\"text-align: justify;\"><strong>法官说法</strong></p><p style=\"text-align: justify;\">法官庭后表示，在房屋租赁关系中，承租人如有饲养宠物，租房时最好主动告知出租方并征得其同意。入住后，承租人若无法有效管理宠物，就不要在出租房中饲养，避免出现损坏物品后发生的不必要的争执。<br></p><p style=\"text-align: justify;\">同时，法官提醒租赁双方应当查验承租房屋及屋内设施，列明物品清单，并在房屋租赁合同中对可否饲养宠物加以约定。退租交接时，房东与租户同样应当查清承租房屋的状况，列明交接物品清单，以免产生纠纷时无凭可依。<br></p><p style=\"text-align: left;\"><br></p>', '法治日报', '', '2023-04-11 21:31:11');
INSERT INTO `articles` VALUES (50, 8, '法律故事', '中国古代选官标准和方式的演变', '<p style=\"text-align: justify;\">中国古代的选官标准与方式，大体经历了夏商周三代依据血缘姻缘的世卿世禄制度、战国秦朝依据军功的官爵制度、汉代依据品行的察举征辟、魏晋南北朝依据门第高低的九品中正制、隋唐以降依据官府考试的科举制度五个阶段，演进的原因既有时代政治经济社会变化的原因，也有选官制度演进本身的内在逻辑。</p><p style=\"text-align: justify;\">夏商周三代的选官标准是血缘关系的远近，夏商两朝史料有阙，周朝则相对较详备。武王克商后，受原始社会“非我族类，其心必异”的观念影响，因此，大搞宗法分封。史载周初分封，“兼制天下，立七十一国，姬姓独居五十三人”(《荀子·儒效》)。七十一国中受封的非姬姓贵族，大都与姬姓联姻，周朝达官贵人不是叔伯兄弟，就是甥舅表亲，周礼把这一标准概括为“亲亲、尊尊”（要亲近亲属，尊重在尊位的人）。诉讼首先考虑是否违反父子之亲，君臣之义。在认为符合宗法制度之后，再来考虑罪行大小、损害轻重，决定刑罚裁量。《礼记·王制》说：“凡听五刑之讼，必原父子之亲，立君臣之义以权之。”<br></p><p style=\"text-align: justify;\">当然，关于周朝是否实行贵族世卿世官制度，学界存有争议。郭沫若主编的《中国史稿》在论述西周政治制度时说：“各种各样的官吏，大都是世袭的，世代享有特殊的、神圣不可侵犯的地位。”杨宽著的《战国史》在论及西周的官吏情况时也说：“在周王国和各诸侯国里，世袭的卿大夫便按照声望和资历来担任官职，并享受一定的采邑收入，这就是世卿、世禄制度。”另有一些学者不同意世卿世禄制推行于西周时代的说法。他们认为，《尚书·立政篇》载有周公阐述的西周时代的选官方针。在这篇文献中，周公一再强调“自今后王立政”，要“俊(进)有德”，选拔“成德之彦”，择用“吉士”“常人”，也就是选用有德有才的贤人，而不搞世卿世禄制。还有一部分学者认为世卿世禄制开始出现于春秋中后期。而在笔者看来，周朝去古未远，没有经历过古希腊奴隶社会的三次社会大分工，氏族部落的外壳没有扫除，经济发展水平有限，多种因素决定了周朝统治者必定选用自己的血亲姻亲之人管理国家，虽然周公也说过要选拔“成德之彦”，但这只是在血亲姻亲的小圈子里选拔相对有德有才的人，而非不论血缘关系远近，不论是否沾亲带故，否则，战国各国变法，废除世卿世禄制度，任用客卿、立功人员，就无从谈起。<br></p><p style=\"text-align: justify;\">但春秋战国的事实告诉人们，周朝的世卿世禄制度存在无法避免的弊端，一是在争权夺利方面，血亲姻亲贵族是不择手段，随时撕下温情脉脉的亲情关系的面纱，自相残杀；二是生而富贵，不思进取，能力低下。正如李斯所言，“周文武所封子弟同姓甚众，然后属疏远，相攻击如仇雠，诸侯更相诛伐，周天子弗能禁止”（《史记·秦始皇本纪》）。<br></p><p style=\"text-align: justify;\">血缘姻缘靠不住，战国便转变为以军功、事功为标准选拔官吏。春秋时期晋国灭公族、按功行赏，开风气之先。到了战国，魏国李悝提出“食有劳而禄有功”，韩国申不害创立了一套“循功劳，视次第”的任官制度，规定“见功而与赏，因能而授官”；齐国威王也选贤任能，因功授官，封赏功效卓著的即墨大夫，烹杀逢迎取誉的阿大夫；楚吴起变法，“使封君之子孙三世而收爵禄，绝减百吏之禄秩”，秦国“有军功者，各以率受上爵”，“宗室非有军功论，不得为属籍”。新的军功爵制造就了一批爵禄及身而止，不再传给子孙的新官僚和一批军功地主，所以它不但是新的封建军事制度的一个重要内容，而且又是新的封建官僚制度的一个组成部分。<br></p><p style=\"text-align: justify;\">但按照军功授官予爵制度适用于战争年代，不适合于和平建设年代。到了汉代，军功爵制逐渐变质，由最初的反对世袭，逐渐变成了世袭。<br></p><p style=\"text-align: justify;\">因为世袭制度不会带来社会的发展活力，所以到了汉代，便实行主要依据品行标准的察举征辟。察举即选拔，是一种由下而上推选人才为官的制度；察举制，初期以“乡举里选”为依据，注重乡里舆论对某位士人德才评判的权威性。征辟是一种自上而下选拔官吏的制度，主要有皇帝征聘与公府、州郡辟除两种方式。皇帝征聘是采取特征与聘召的方式，选拔某些有名望的品学兼优的人士，或备顾问，或委任政事。察举、征辟虽然方式不同，但标准都是所谓的品行。大量事实表明，道德品行须出自觉自愿，没有功利引诱，道德品行一旦功利化，即会变质。汉代把道德品行与做官相联系，最后就出现察举者以私意取人，“郡国举孝廉，率取年少能报恩者，耆宿大贤多见废弃”，以及士人虚伪投机，“举秀才，不知书。举孝廉，父别居。寒素清白浊如泥，高第良将怯如鸡”，培养了一批伪君子。<br></p><p style=\"text-align: justify;\">依据道德品行的察举征辟制度不行了，魏晋南北朝时期就采取了依据门第高低的九品中正制。即由各州郡分别推选大中正一人，所推举大中正必为在中央任职官员且德名俱高者，大中正再产生小中正。中正，就是掌管对某一地区人物进行品评的负责人，九品中正制创立之初，评议人物的标准是家世、道德、才能三者并重，但晋以后完全以家世来定品级。出身寒门者行状评语再高也只能定在下品；出身豪门者行状不佳亦能位列上品。于是形成了“上品无寒门，下品无士族”的局面。<br></p><p style=\"text-align: justify;\">主要依据门第高低选拔官员的九品中正制，到了后来成了压抑、埋没人才的等级制度，因此，它必然被依据考试成绩为主要标准的科举制度所代替。科举制度从隋代开始实行，到清光绪三十一年（1905年）举行最后一科进士考试为止。科举考试标准相对最为客观，所以它能行之1298年，且对欧洲文官制度的产生产生了较大影响。但它到了明清时期的“八股取士”，各种规定僵硬死板，命题内容狭窄，限制了人们的思想，丢弃了经世致用的传统。<br></p><p style=\"text-align: justify;\">从依据血缘姻缘任官、到军功赐爵授官、又到品行察举征辟、再到门第高低授官，最后到科举考试任官，追求任官标准的客观化、可量化，防止任人唯亲、高下由心，则是贯穿古代几千年来选拔官员历史的一根主线。</p>', '郝铁川', '', '2023-04-11 21:32:04');
INSERT INTO `articles` VALUES (51, 8, '法律故事', '瀛台泣血录', '<p style=\"text-align: justify;\">北京之美，在于她的庄严大气。故宫那片如大海波涛般起伏的金色屋顶之上，时时望去，便总似在涌动着阵阵的历史烟云。不过，一百年前，在北京这片极美丽的地方却发生了一个极悲惨的故事。这个故事的主角，就是著名的光绪皇帝。甲午战败之后，当那位血气方刚的青年皇帝——光绪皇帝想推行“变法”，以一雪前耻时，那位拥有全中国最高权力的慈禧太后却是残酷镇压，并且将这位热血皇帝囚禁在皇宫西边一个四面环水的小岛之上。</p><p style=\"text-align: justify;\">在一百年前的中国，讲“变法”、讲“政改”，可是要坐牢、杀头的事！连皇帝也不例外。就这样，中国的传统政治，彻底地失去了一次极为难得的“自我革新”的宝贵机会，而中国社会便仍然在一个浸满了封建毒汁的大染缸中一天一天地腐烂下去。<br></p><p style=\"text-align: justify;\">如果用一句话来形容这位“变法皇帝”，那么最为简洁的一句便是：他是一位充满了悲剧色彩的开明皇帝。光绪姓爱新觉罗，名载湉，生于1871年8月14日。他的生父，是道光皇帝的第七子、咸丰皇帝的亲弟弟醇亲王奕譞，而他的生母，是慈禧太后的亲妹妹。这就是说，在他的身上，既有着爱新觉罗氏的血统，同时又有叶赫那拉氏的血统。因此，1875年，当慈禧太后的亲生儿子——年仅19岁的同治皇帝突然死去之时，年方4岁的载湉，便被那位既是伯母又是姨妈的慈禧太后收为自己的继子，并被立为大清皇位的继承人，而慈禧自己，则名正言顺地继续当她的太后，理所当然地继续“垂帘”听政。<br></p><p style=\"text-align: justify;\">自光绪5岁那年开始，慈禧就任命了一位翰林院大学士翁同龢为他的老师。这位翁老师，在毓庆宫里给小皇帝上的第一堂课，便是教光绪学写“天下太平、正大光明”这八个大字。他不仅给这位小皇帝讲授四书五经等儒家经典，与此同时，还结合当时中国的内忧外患，给光绪讲魏源的《海国图志》，讲徐继畲的《瀛寰志略》，讲郭嵩焘的《使西纪程：郭嵩焘集》，讲曾纪泽、马建忠等出使官员在欧美考察的游记见闻等，还经常从军机处挑选一些有关“洋务运动”例如造船、开矿、办铁路、办海军的大臣奏折向这位少年皇帝详加讲解。可以毫不夸张地说，这位小皇帝，在当时得到的，是一种全中国最先进、学科最现代、视野最广阔的“通才教育”。<br></p><p style=\"text-align: justify;\">说起来，光绪虽然名义上在位34年，但由于他前期年纪尚幼，而后期被囚禁在瀛台10年，因此，他实际执政的时间只有11年，而亲身参与及主持过的，便只有一场战争和一场运动。那场战争，就是令全体中国人时至今日都还刻骨铭心的“甲午海战”。而那场运动，就是时至今日仍众说纷纭的“戊戌变法”。<br></p><p style=\"text-align: justify;\">在康有为那种“大变速变”而不是“渐进式改革”的激进思想的鼓动之下，光绪皇帝在1898年6月11日至9月21日的103天里，从紫禁城中像雪片般地向全中国发出了180多道“变法上谕”。变革的范围，遍及政治、经济、军事、文教（偏偏就没有法律、法学）等各个重要的社会领域。“废除科举”，一下子断绝了近千年来全中国读书人入仕的传统出路；“革除冗官”，更是一下子砸烂了许多文武官员的饭碗，这就使得整个中国社会都被卷入一场如天塌地陷般的大动荡之中。简单来说，这场变法，是“未见其利，先受其害”。当时的朝野上下，人心惶惶，京师内外，怨声载道。而当改革遇到了重重阻力，根本无法再推行下去时，康有为这个所谓的“变法奇才”，却只会将一场本来“正大光明”的“维新变法”，急剧地演变为一场“宫廷政变”。当他“围园弑后”的计划被袁世凯出卖之后，这场“维新变法”的最后结局，已是众所周知——荣禄率军在京城大肆搜捕维新党人，康有为、梁启超二人仓皇逃亡海外，“戊戌六君子”喋血刑场，而那时才28岁的光绪皇帝，则成为慈禧太后的阶下之囚。在经过一场太后既是原告、又是法官，没有律师、没有陪审团、只有一大群守旧派官僚围攻乱咬式的审讯之后，这位改革失败的皇帝，便被囚禁在中南海瀛台小岛的一处冷宫中。<br></p><p style=\"text-align: justify;\">“光绪究竟是病死的，还是被毒死的”，史学界一直争论不休。据“清光绪帝死因”专题研究课题组发布研究报告称：“光绪皇帝是死于砒霜中毒。”因为，他头发中的砷含量，比普通人的头发砷含量高出了261倍。<br></p><p style=\"text-align: justify;\">慈禧为什么在自己临死的前一日还要抢先一步毒杀光绪？这个问题在中国的史学界引发了一场争论。对于那位日夜监督他一举一动的慈禧太后来说，怎么可能“放虎归山”，让这位思想叛逆的“变法皇帝”能有机会在她身后东山再起？于是，在慈禧临死的前一日，那位黑衣的死神，便悄然无声地，抢先降落到故宫西边那个四面环水的孤岛上。光绪死了，慈禧也死了。三年之后，那个气息奄奄的大清帝国也终告覆亡。<br></p><p style=\"text-align: justify;\">（文章节选自余定宇《寻找法律的印迹（2）：从独角神兽到“六法全书”》，北京大学出版社出版）<br></p><p style=\"text-align: left;\"><br></p>', '余定宇', '', '2023-04-11 21:32:36');
INSERT INTO `articles` VALUES (52, 8, '法律故事', '秦朝法制的起源与形成', '<p style=\"text-align: justify;\">顾炎武在他所著的《日知录·郡县》中说：“《汉书·地理志》言：‘秦并兼四海，以为周制微弱，终为诸侯所丧，故不立尺土之封，分天下为郡县，荡灭前圣之苗裔，靡有孑遗。’后之文人祖述其说，以为废封建，立郡县，皆始皇之所为也。以余观之，殆不然。”当前一些大、中学的历史教科书中，在叙述秦汉的政治制度和经济制度时，还带有顾炎武所指出的这种倾向，以为秦朝的政治、经济制度，都是秦始皇一个人所创建的。其实，这是不对的。</p><p style=\"text-align: justify;\">第一，秦朝废除宗室贵族分封制度的做法源于春秋时期晋国的灭公族制度。晋国原是西周初年按宗亲所分封的姬姓国家，是周成王的母弟唐叔虞的封地。晋国本应遵循唐叔虞所受的法度，周公所制定的宗法分封等礼制来治理晋国。但到了春秋初年，晋国却抛弃了唐叔虞所受的法度，走上相反的道路。在公元前745年（鲁惠公二十四年），晋昭侯分封其叔父成师于曲沃（山西闻喜），号曲沃桓叔，建立起一个庶子强宗政权。经过四十年，到曲沃武公时，这个强宗把晋国的嫡室政权灭掉，取得了诸侯的地位。这种违背宗法制的“贱妨贵，少陵长”的篡逆事件，竟被周天子（釐王）许可、承认下来。这是晋国公族制度破坏的开始。曲沃武公的儿子晋献公接受了他祖、父两代传下来的经验教训，就不再进行宗亲分封，并进一步对亲近公族剪灭。这样一来，所谓唐叔虞的法度也就不存在了。从此以后，历整个春秋时代，晋国的公子、公孙无受分封者，无在国内任官当政者。消除了公族对公室的威胁牵制，废弃了血统贵族把持政权的传统，给春秋时代晋国建立官僚制度、君主集权等开辟了道路。<br></p><p style=\"text-align: justify;\">商鞅是在三晋政治改革之后，由魏国到秦国去的。商鞅帮助秦孝公变法所建立的新制度，最重要的是：废除贵族世卿世禄制度，规定宗室非有军功，论不得为属籍。有功者显荣，无功者虽富无所芬华。这一改革是对春秋晋国灭公族的做法的法制化。<br></p><p style=\"text-align: justify;\">第二，秦朝的军功爵制起源于春秋晋国的尊贤尚功做法。周朝的公、侯、伯、子、男一套爵制是建立在宗法血缘关系基础上的，而春秋时期晋国在消灭公族、废除宗法分封的同时，实行了择贤举能、论功行赏的办法任用官吏，建立起尊贤尚功的用人制度。晋定公时代，执政的赵鞅发布法令说：“克敌者，上大夫受县，下大夫受郡，士田十万，庶人工商遂，人臣隶圉免。”这里所说的受县、受郡，指的是上、下大夫因军功而得爵位者。这道法令是因军功赐爵位、田宅的具体运用。晋国这一做法被秦、齐等国仿效。<br></p><p style=\"text-align: justify;\">第三，秦朝崇尚法制的制度起源于春秋晋国。晋献公时，以士蒍为理官（掌法令），“以正于朝，朝无奸官，为司空，以正于国，国无败绩”（《国语·晋语八》）。晋襄公时，赵盾为正卿，使范武子士会制定成文法典，“使行诸晋国，以为常法”（《左传·文公六年》）。晋厉公时，“士渥浊为太傅，使修范武子之法”（《左传·成公十八年》）。公元前513年，晋顷公时，铸刑鼎，著士匄（范宣子）所制定的刑书于鼎上（《左传·昭公廿九年》），进一步把一罪一刑立法模式的成文法公布出来。在法制思想的指导下，晋国形成赏罚严明的法制政治。“晋国有法，始乱者死”，直到春秋末年成为大臣遵守的一条政治原则。这种刑赏严明的法制思想和法制政治，在晋国推行了约二百年，成为后来战国时推动各国政治改革的思想前导，法家学术思想的主要历史来源。<br></p><p style=\"text-align: justify;\">秦始皇统一全国后，在全国范围内废除宗法分封制度，确立以宰相为首的官僚制度和郡县制度，确立事皆决于法的法制指导思想。秦国实行法制政治，是由商鞅建立的。商鞅是李悝的弟子，得其师书，既不用于魏，遂挟法入秦，“商君受之以相秦”。他在秦国建立的法制脱胎于三晋，在三晋制度的基础上加以总结提高。尤其是打击公族、废除宗室贵族分封一项，实是来自春秋时晋国的灭公族，并将之进一步法制化。<br></p><p style=\"text-align: justify;\">到秦始皇时，任用法家李斯，完成统一后，又吸收韩非“以法为教，以吏为师”的主张，别黑白而定法制思想于一尊，烧诗书百家语，事皆决于法，法令由一统。法制思想成为指导政治、经济、军事的准则，“普施明法，经纬天下，永为仪则”（《史记·秦始皇本纪》），把法制精神贯彻到各种制度和社会关系之中。<br></p><p style=\"text-align: justify;\">因此，秦朝的法制，源于春秋晋国的剪灭公族分封、实行军功爵制的做法，中经战国三晋法家色彩的政治家们的变法，最后由法家殿军人物的商鞅、李斯、秦始皇完成的。“冰冻三尺，非一日之寒”也！</p>', '郝铁川', '', '2023-04-11 21:33:08');
INSERT INTO `articles` VALUES (53, 9, '国家时事', '李克强签署国务院令 公布《中华人民共和国统计法实施条例》', '<p style=\"text-indent: 32px; text-align: left;\">新华社北京6月19日电　国务院总理李克强日前签署国务院令，公布《中华人民共和国统计法实施条例》，自2017年8月1日起施行。1987年公布施行的《中华人民共和国统计法实施细则》同时废止。</p><p style=\"text-indent: 32px; text-align: left;\">为全面有效贯彻执行修订后的《中华人民共和国统计法》，进一步保障统计数据真实可靠，国务院制定了《中华人民共和国统计法实施条例》。条例主要规定了以下内容:</p><p style=\"text-indent: 32px; text-align: left;\"><strong>一是从源头上规范统计调查活动。</strong>开展统计调查活动既要满足国家宏观决策对统计资料的需求，又要切实减轻统计调查对象的负担，提高统计调查的有效性和质量。为兼顾好两方面的利益，条例从五个方面作了规定，包括夯实防范和惩治统计造假、弄虚作假责任，尽可能减轻统计调查对象的负担，严格要求统计调查项目的制定，明确统计调查项目审批和备案的程序、条件和时限，以及明确国家统计标准的地位等。</p><p style=\"text-indent: 32px; text-align: left;\"><strong>二是加强统计调查的组织实施。</strong>为加强对统计数据生产过程的控制，提高统计数据质量，条例对统计调查的组织实施作了专章规定，对统计调查对象的报送义务和统计调查组织实施机关提出了明确要求。</p><p style=\"text-indent: 32px; text-align: left;\"><strong>三是明确统计资料公布的主体、权限及要求。</strong>包括:区分不同统计资料，明确公布主体及权限；为避免因简单对比统计数据产生误解，规定统计机构和有关部门应当及时公布主要统计指标涵义；公布统计资料应当按照国家有关规定进行，公布前任何单位和个人不得违反国家有关规定对外提供，不得利用尚未公布的统计资料谋取不正当利益等。</p><p style=\"text-indent: 32px; text-align: left;\"><strong>四是强化对统计违法行为的责任追究。</strong>条例列举了对严重统计违法行为失察的具体情形和统计造假行为的典型做法，规定了严格的法律责任。例如，大面积发生或者连续发生统计造假、弄虚作假，统计数据严重失实应当发现而未发现，发现统计数据严重失实不予纠正的，对地方人民政府、政府统计机构或者有关部门、单位的负责人，由任免机关或者监察机关依法给予处分，并由县级以上人民政府统计机构予以通报。侵犯统计机构、统计人员独立行使统计调查、统计报告、统计监督职权，或者采用下发文件、会议布置以及其他方式授意、指使、强令统计调查对象或者其他单位、人员编造虚假统计资料的，责令改正，予以通报；对直接负责的主管人员和其他直接责任人员，由任免机关或者监察机关依法给予处分。</p>', '新华社', '', '2023-04-11 21:34:47');
INSERT INTO `articles` VALUES (54, 9, '国家时事', '第五届云南省学生“学宪法 讲宪法”活动总决赛 通过线上方式成功举办', '<p style=\"text-align: left;\">2020年10月23~24日，由云南省教育厅主办，昆明理工大学、教育部全国青少年普法网协办的第五届云南省学生“学宪法 讲宪法”活动总决赛，通过“腾讯会议”+“雨课堂”的线上方式成功举办。</p><p style=\"text-align: center;\"><br></p><p style=\"text-align: left;\">比赛旨在贯彻落实习近平总书记关于宪法学习宣传教育的系列重要指示精神，进一步在全省青少年学生中普及宪法知识、弘扬宪法精神、树立宪法权威，引导青少年学生自觉成为宪法的忠实崇尚者、自觉遵守者、坚定捍卫者。</p><p style=\"text-align: left;\">比赛分为知识竞赛和演讲比赛两个项目，以宪法知识考察为重点，内容涉及民法典编纂、诚实守信、规则意识、文明素养、劳动教育、公共卫生、疫情防控、环境保护、未成年人保护、交通消防安全、食品安全、应急管理、防范校园欺凌、禁毒和预防网络沉迷等领域的基础法律知识。</p><p style=\"text-align: center;\"><br></p><p style=\"text-align: left;\">在各州市和高校前期选拔的基础上，经过省级演讲视频专家评选和知识竞赛初赛，共有16名知识竞赛选手和9名演讲比赛选手进入最后一轮比赛。</p><p style=\"text-align: center;\"><br></p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">经过激烈角逐，丽江市实验学校李婕、文山市第一初级中学江楠、昆明八中郑骏、昆明理工大学杨学治4名同学分别获得小学组、初中组、高中组、高校组知识竞赛第一名；怒江州泸水市第一完全小学杨欣、临沧市民族中学梁好、大理州民族中学罗翼婕、云南大学白雨珊4名同学分别获得小学组、初中组、高中组、高校组演讲比赛第一名。这8名选手将代表云南省参加今年的全国总决赛。</p>', '云南省教育厅', '', '2023-04-11 21:36:21');
INSERT INTO `articles` VALUES (55, 9, '国家时事', '教育部关于印发《依法治教实施纲要（2016-2020年）》的通知', '<p style=\"text-align: left;\"><span style=\"color: black; font-size: 16px;\">各省、自治区、直辖市教育厅（教委），各计划单列市教育局，新疆生产建设兵团教育局，部属各高等学校：</span></p><p style=\"text-indent: 28px; text-align: left;\"><span style=\"color: black; font-size: 16px;\">为贯彻落实党的十八大和十八届三中、四中、五中全会精神，进一步落实《国家中长期教育改革和发展规划纲要（2010-2020年）》提出的工作任务，落实《法治政府建设实施纲要（2015-2020年）》要求，全面推进依法治教，我部研究制定了《依法治教实施纲要（2016-2020年）》，现印发给你们。请结合本地区、本学校实际，认真组织学习宣传、贯彻落实，切实转变观念，以法治思维和法治方式推进教育综合改革，加快构建政府依法行政、学校依法办学、教师依法执教、社会依法支持和参与教育治理的教育发展新格局，全面推进教育治理体系和治理能力现代化。</span></p><p style=\"text-indent: 28px; text-align: left;\"><span style=\"color: black; font-size: 16px;\">各地和教育部直属高校贯彻落实《依法治教实施纲要（2016-2020年）》的情况，实践中形成的具有示范意义的典型和经验，请及时报我部政策法规司（法制办公室）。</span></p><p style=\"text-indent: 28px; text-align: right;\"><span style=\"font-size: 16px;\">教 育 部</span></p><p style=\"text-indent: 28px; text-align: right;\"><span style=\"color: black; font-size: 16px;\">2016年1月7日</span></p>', '本站编辑', '', '2023-04-11 21:37:09');
INSERT INTO `articles` VALUES (56, 9, '国家时事', '中华人民共和国香港特别行政区维护国家安全法', '<h1 style=\"text-align: center;\">中华人民共和国香港特别行政区维护国家安全法</h1><h2 style=\"text-align: center;\">（2020年6月30日第十三届全国人民代表大会常务委员会第二十次会议通过）</h2><p style=\"text-align: center;\"><strong>中华人民共和国主席令</strong></p><p style=\"text-align: center;\"><strong>第四十九号</strong></p><p style=\"text-align: left;\">《中华人民共和国香港特别行政区维护国家安全法》已由中华人民共和国第十三届全国人民代表大会常务委员会第二十次会议于2020年6月30日通过，现予公布，自公布之日起施行。</p><p style=\"text-align: right;\">中华人民共和国主席 &nbsp;习近平 &nbsp; </p><p style=\"text-align: right;\">2020年6月30日 &nbsp;</p><p style=\"text-align: left;\">　　</p><p style=\"text-align: left;\">目 &nbsp;录</p><p style=\"text-align: left;\">第一章 &nbsp;总 &nbsp;则</p><p style=\"text-align: left;\">第二章 &nbsp;香港特别行政区维护国家安全的职责和机构</p><p style=\"text-align: left;\">第一节 &nbsp;职 &nbsp;责</p><p style=\"text-align: left;\">第二节 &nbsp;机 &nbsp;构</p><p style=\"text-align: left;\">第三章 &nbsp;罪行和处罚</p><p style=\"text-align: left;\">第一节 &nbsp;分裂国家罪</p><p style=\"text-align: left;\">第二节 &nbsp;颠覆国家政权罪</p><p style=\"text-align: left;\">第三节 &nbsp;恐怖活动罪</p><p style=\"text-align: left;\">第四节 &nbsp;勾结外国或者境外势力危害国家安全罪</p><p style=\"text-align: left;\">第五节 &nbsp;其他处罚规定</p><p style=\"text-align: left;\">第六节 &nbsp;效力范围</p><p style=\"text-align: left;\">第四章 &nbsp;案件管辖、法律适用和程序</p><p style=\"text-align: left;\">第五章 &nbsp;中央人民政府驻香港特别行政区维护国家安全机构</p><p style=\"text-align: left;\">第六章 &nbsp;附 &nbsp;则</p><p style=\"text-align: left;\">第一章 &nbsp;总 &nbsp;则</p><p style=\"text-align: left;\">第一条 &nbsp;为坚定不移并全面准确贯彻“一国两制”、“港人治港”、高度自治的方针，维护国家安全，防范、制止和惩治与香港特别行政区有关的分裂国家、颠覆国家政权、组织实施恐怖活动和勾结外国或者境外势力危害国家安全等犯罪，保持香港特别行政区的繁荣和稳定，保障香港特别行政区居民的合法权益，根据中华人民共和国宪法、中华人民共和国香港特别行政区基本法和全国人民代表大会关于建立健全香港特别行政区维护国家安全的法律制度和执行机制的决定，制定本法。</p><p style=\"text-align: left;\">第二条 &nbsp;关于香港特别行政区法律地位的香港特别行政区基本法第一条和第十二条规定是香港特别行政区基本法的根本性条款。香港特别行政区任何机构、组织和个人行使权利和自由，不得违背香港特别行政区基本法第一条和第十二条的规定。</p><p style=\"text-align: left;\">第三条 &nbsp;中央人民政府对香港特别行政区有关的国家安全事务负有根本责任。</p><p style=\"text-align: left;\">香港特别行政区负有维护国家安全的宪制责任，应当履行维护国家安全的职责。</p><p style=\"text-align: left;\">香港特别行政区行政机关、立法机关、司法机关应当依据本法和其他有关法律规定有效防范、制止和惩治危害国家安全的行为和活动。</p><p style=\"text-align: left;\">第四条 &nbsp;香港特别行政区维护国家安全应当尊重和保障人权，依法保护香港特别行政区居民根据香港特别行政区基本法和《公民权利和政治权利国际公约》、《经济、社会与文化权利的国际公约》适用于香港的有关规定享有的包括言论、新闻、出版的自由，结社、集会、游行、示威的自由在内的权利和自由。</p><p style=\"text-align: left;\">第五条 &nbsp;防范、制止和惩治危害国家安全犯罪，应当坚持法治原则。法律规定为犯罪行为的，依照法律定罪处刑；法律没有规定为犯罪行为的，不得定罪处刑。</p><p style=\"text-align: left;\">任何人未经司法机关判罪之前均假定无罪。保障犯罪嫌疑人、被告人和其他诉讼参与人依法享有的辩护权和其他诉讼权利。任何人已经司法程序被最终确定有罪或者宣告无罪的，不得就同一行为再予审判或者惩罚。</p><p style=\"text-align: left;\">第六条 &nbsp;维护国家主权、统一和领土完整是包括香港同胞在内的全中国人民的共同义务。</p><p style=\"text-align: left;\">在香港特别行政区的任何机构、组织和个人都应当遵守本法和香港特别行政区有关维护国家安全的其他法律，不得从事危害国家安全的行为和活动。</p><p style=\"text-align: left;\">香港特别行政区居民在参选或者就任公职时应当依法签署文件确认或者宣誓拥护中华人民共和国香港特别行政区基本法，效忠中华人民共和国香港特别行政区。</p><p style=\"text-align: left;\">第二章 &nbsp;香港特别行政区维护国家安全的职责和机构</p><p style=\"text-align: left;\">第一节 &nbsp;职 &nbsp;责</p><p style=\"text-align: left;\">第七条 &nbsp;香港特别行政区应当尽早完成香港特别行政区基本法规定的维护国家安全立法，完善相关法律。</p><p style=\"text-align: left;\">第八条 &nbsp;香港特别行政区执法、司法机关应当切实执行本法和香港特别行政区现行法律有关防范、制止和惩治危害国家安全行为和活动的规定，有效维护国家安全。</p><p style=\"text-align: left;\">第九条 &nbsp;香港特别行政区应当加强维护国家安全和防范恐怖活动的工作。对学校、社会团体、媒体、网络等涉及国家安全的事宜，香港特别行政区政府应当采取必要措施，加强宣传、指导、监督和管理。</p><p style=\"text-align: left;\">第十条 &nbsp;香港特别行政区应当通过学校、社会团体、媒体、网络等开展国家安全教育，提高香港特别行政区居民的国家安全意识和守法意识。</p><p style=\"text-align: left;\">第十一条 &nbsp;香港特别行政区行政长官应当就香港特别行政区维护国家安全事务向中央人民政府负责，并就香港特别行政区履行维护国家安全职责的情况提交年度报告。</p><p style=\"text-align: left;\">如中央人民政府提出要求，行政长官应当就维护国家安全特定事项及时提交报告。</p><p style=\"text-align: left;\">第二节 &nbsp;机 &nbsp;构</p><p style=\"text-align: left;\">第十二条 &nbsp;香港特别行政区设立维护国家安全委员会，负责香港特别行政区维护国家安全事务，承担维护国家安全的主要责任，并接受中央人民政府的监督和问责。</p><p style=\"text-align: left;\">第十三条 &nbsp;香港特别行政区维护国家安全委员会由行政长官担任主席，成员包括政务司长、财政司长、律政司长、保安局局长、警务处处长、本法第十六条规定的警务处维护国家安全部门的负责人、入境事务处处长、海关关长和行政长官办公室主任。</p><p style=\"text-align: left;\">香港特别行政区维护国家安全委员会下设秘书处，由秘书长领导。秘书长由行政长官提名，报中央人民政府任命。</p><p style=\"text-align: left;\">第十四条 &nbsp;香港特别行政区维护国家安全委员会的职责为：</p><p style=\"text-align: left;\">（一）分析研判香港特别行政区维护国家安全形势，规划有关工作，制定香港特别行政区维护国家安全政策；</p><p style=\"text-align: left;\">（二）推进香港特别行政区维护国家安全的法律制度和执行机制建设；</p><p style=\"text-align: left;\">（三）协调香港特别行政区维护国家安全的重点工作和重大行动。</p><p style=\"text-align: left;\">香港特别行政区维护国家安全委员会的工作不受香港特别行政区任何其他机构、组织和个人的干涉，工作信息不予公开。香港特别行政区维护国家安全委员会作出的决定不受司法复核。</p><p style=\"text-align: left;\">第十五条 &nbsp;香港特别行政区维护国家安全委员会设立国家安全事务顾问，由中央人民政府指派，就香港特别行政区维护国家安全委员会履行职责相关事务提供意见。国家安全事务顾问列席香港特别行政区维护国家安全委员会会议。</p><p style=\"text-align: left;\">第十六条 &nbsp;香港特别行政区政府警务处设立维护国家安全的部门，配备执法力量。</p><p style=\"text-align: left;\">警务处维护国家安全部门负责人由行政长官任命，行政长官任命前须书面征求本法第四十八条规定的机构的意见。警务处维护国家安全部门负责人在就职时应当宣誓拥护中华人民共和国香港特别行政区基本法，效忠中华人民共和国香港特别行政区，遵守法律，保守秘密。</p><p style=\"text-align: left;\">警务处维护国家安全部门可以从香港特别行政区以外聘请合格的专门人员和技术人员，协助执行维护国家安全相关任务。</p><p style=\"text-align: left;\">第十七条 &nbsp;警务处维护国家安全部门的职责为：</p><p style=\"text-align: left;\">（一）收集分析涉及国家安全的情报信息；</p><p style=\"text-align: left;\">（二）部署、协调、推进维护国家安全的措施和行动；</p><p style=\"text-align: left;\">（三）调查危害国家安全犯罪案件；</p><p style=\"text-align: left;\">（四）进行反干预调查和开展国家安全审查；</p><p style=\"text-align: left;\">（五）承办香港特别行政区维护国家安全委员会交办的维护国家安全工作；</p><p style=\"text-align: left;\">（六）执行本法所需的其他职责。</p><p style=\"text-align: left;\">第十八条 &nbsp;香港特别行政区律政司设立专门的国家安全犯罪案件检控部门，负责危害国家安全犯罪案件的检控工作和其他相关法律事务。该部门检控官由律政司长征得香港特别行政区维护国家安全委员会同意后任命。</p><p style=\"text-align: left;\">律政司国家安全犯罪案件检控部门负责人由行政长官任命，行政长官任命前须书面征求本法第四十八条规定的机构的意见。律政司国家安全犯罪案件检控部门负责人在就职时应当宣誓拥护中华人民共和国香港特别行政区基本法，效忠中华人民共和国香港特别行政区，遵守法律，保守秘密。</p><p style=\"text-align: left;\">第十九条 &nbsp;经行政长官批准，香港特别行政区政府财政司长应当从政府一般收入中拨出专门款项支付关于维护国家安全的开支并核准所涉及的人员编制，不受香港特别行政区现行有关法律规定的限制。财政司长须每年就该款项的控制和管理向立法会提交报告。</p><p style=\"text-align: left;\">第三章 &nbsp;罪行和处罚</p><p style=\"text-align: left;\">第一节 &nbsp;分裂国家罪</p><p style=\"text-align: left;\">第二十条 &nbsp;任何人组织、策划、实施或者参与实施以下旨在分裂国家、破坏国家统一行为之一的，不论是否使用武力或者以武力相威胁，即属犯罪：</p><p style=\"text-align: left;\">（一）将香港特别行政区或者中华人民共和国其他任何部分从中华人民共和国分离出去；</p><p style=\"text-align: left;\">（二）非法改变香港特别行政区或者中华人民共和国其他任何部分的法律地位；</p><p style=\"text-align: left;\">（三）将香港特别行政区或者中华人民共和国其他任何部分转归外国统治。</p><p style=\"text-align: left;\">犯前款罪，对首要分子或者罪行重大的，处无期徒刑或者十年以上有期徒刑；对积极参加的，处三年以上十年以下有期徒刑；对其他参加的，处三年以下有期徒刑、拘役或者管制。</p><p style=\"text-align: left;\">第二十一条 &nbsp;任何人煽动、协助、教唆、以金钱或者其他财物资助他人实施本法第二十条规定的犯罪的，即属犯罪。情节严重的，处五年以上十年以下有期徒刑；情节较轻的，处五年以下有期徒刑、拘役或者管制。</p><p style=\"text-align: left;\">第二节 &nbsp;颠覆国家政权罪</p><p style=\"text-align: left;\">第二十二条 &nbsp;任何人组织、策划、实施或者参与实施以下以武力、威胁使用武力或者其他非法手段旨在颠覆国家政权行为之一的，即属犯罪：</p><p style=\"text-align: left;\">（一）推翻、破坏中华人民共和国宪法所确立的中华人民共和国根本制度；</p><p style=\"text-align: left;\">（二）推翻中华人民共和国中央政权机关或者香港特别行政区政权机关；</p><p style=\"text-align: left;\">（三）严重干扰、阻挠、破坏中华人民共和国中央政权机关或者香港特别行政区政权机关依法履行职能；</p><p style=\"text-align: left;\">（四）攻击、破坏香港特别行政区政权机关履职场所及其设施，致使其无法正常履行职能。</p><p style=\"text-align: left;\">犯前款罪，对首要分子或者罪行重大的，处无期徒刑或者十年以上有期徒刑；对积极参加的，处三年以上十年以下有期徒刑；对其他参加的，处三年以下有期徒刑、拘役或者管制。</p><p style=\"text-align: left;\">第二十三条 &nbsp;任何人煽动、协助、教唆、以金钱或者其他财物资助他人实施本法第二十二条规定的犯罪的，即属犯罪。情节严重的，处五年以上十年以下有期徒刑；情节较轻的，处五年以下有期徒刑、拘役或者管制。</p><p style=\"text-align: left;\">第三节 &nbsp;恐怖活动罪</p><p style=\"text-align: left;\">第二十四条 &nbsp;为胁迫中央人民政府、香港特别行政区政府或者国际组织或者威吓公众以图实现政治主张，组织、策划、实施、参与实施或者威胁实施以下造成或者意图造成严重社会危害的恐怖活动之一的，即属犯罪：</p><p style=\"text-align: left;\">（一）针对人的严重暴力；</p><p style=\"text-align: left;\">（二）爆炸、纵火或者投放毒害性、放射性、传染病病原体等物质；</p><p style=\"text-align: left;\">（三）破坏交通工具、交通设施、电力设备、燃气设备或者其他易燃易爆设备；</p><p style=\"text-align: left;\">（四）严重干扰、破坏水、电、燃气、交通、通讯、网络等公共服务和管理的电子控制系统；</p><p style=\"text-align: left;\">（五）以其他危险方法严重危害公众健康或者安全。</p><p style=\"text-align: left;\">犯前款罪，致人重伤、死亡或者使公私财产遭受重大损失的，处无期徒刑或者十年以上有期徒刑；其他情形，处三年以上十年以下有期徒刑。</p><p style=\"text-align: left;\">第二十五条 &nbsp;组织、领导恐怖活动组织的，即属犯罪，处无期徒刑或者十年以上有期徒刑，并处没收财产；积极参加的，处三年以上十年以下有期徒刑，并处罚金；其他参加的，处三年以下有期徒刑、拘役或者管制，可以并处罚金。</p><p style=\"text-align: left;\">本法所指的恐怖活动组织，是指实施或者意图实施本法第二十四条规定的恐怖活动罪行或者参与或者协助实施本法第二十四条规定的恐怖活动罪行的组织。</p><p style=\"text-align: left;\">第二十六条 &nbsp;为恐怖活动组织、恐怖活动人员、恐怖活动实施提供培训、武器、信息、资金、物资、劳务、运输、技术或者场所等支持、协助、便利，或者制造、非法管有爆炸性、毒害性、放射性、传染病病原体等物质以及以其他形式准备实施恐怖活动的，即属犯罪。情节严重的，处五年以上十年以下有期徒刑，并处罚金或者没收财产；其他情形，处五年以下有期徒刑、拘役或者管制，并处罚金。</p><p style=\"text-align: left;\">有前款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。</p><p style=\"text-align: left;\">第二十七条 &nbsp;宣扬恐怖主义、煽动实施恐怖活动的，即属犯罪。情节严重的，处五年以上十年以下有期徒刑，并处罚金或者没收财产；其他情形，处五年以下有期徒刑、拘役或者管制，并处罚金。</p><p style=\"text-align: left;\">第二十八条 &nbsp;本节规定不影响依据香港特别行政区法律对其他形式的恐怖活动犯罪追究刑事责任并采取冻结财产等措施。</p><p style=\"text-align: left;\">第四节 &nbsp;勾结外国或者境外势力危害国家安全罪</p><p style=\"text-align: left;\">第二十九条 &nbsp;为外国或者境外机构、组织、人员窃取、刺探、收买、非法提供涉及国家安全的国家秘密或者情报的；请求外国或者境外机构、组织、人员实施，与外国或者境外机构、组织、人员串谋实施，或者直接或者间接接受外国或者境外机构、组织、人员的指使、控制、资助或者其他形式的支援实施以下行为之一的，均属犯罪：</p><p style=\"text-align: left;\">（一）对中华人民共和国发动战争，或者以武力或者武力相威胁，对中华人民共和国主权、统一和领土完整造成严重危害；</p><p style=\"text-align: left;\">（二）对香港特别行政区政府或者中央人民政府制定和执行法律、政策进行严重阻挠并可能造成严重后果；</p><p style=\"text-align: left;\">（三）对香港特别行政区选举进行操控、破坏并可能造成严重后果；</p><p style=\"text-align: left;\">（四）对香港特别行政区或者中华人民共和国进行制裁、封锁或者采取其他敌对行动；</p><p style=\"text-align: left;\">（五）通过各种非法方式引发香港特别行政区居民对中央人民政府或者香港特别行政区政府的憎恨并可能造成严重后果。</p><p style=\"text-align: left;\">犯前款罪，处三年以上十年以下有期徒刑；罪行重大的，处无期徒刑或者十年以上有期徒刑。</p><p style=\"text-align: left;\">本条第一款规定涉及的境外机构、组织、人员，按共同犯罪定罪处刑。</p><p style=\"text-align: left;\">第三十条 &nbsp;为实施本法第二十条、第二十二条规定的犯罪，与外国或者境外机构、组织、人员串谋，或者直接或者间接接受外国或者境外机构、组织、人员的指使、控制、资助或者其他形式的支援的，依照本法第二十条、第二十二条的规定从重处罚。</p><p style=\"text-align: left;\">第五节 &nbsp;其他处罚规定</p><p style=\"text-align: left;\">第三十一条 &nbsp;公司、团体等法人或者非法人组织实施本法规定的犯罪的，对该组织判处罚金。</p><p style=\"text-align: left;\">公司、团体等法人或者非法人组织因犯本法规定的罪行受到刑事处罚的，应责令其暂停运作或者吊销其执照或者营业许可证。</p><p style=\"text-align: left;\">第三十二条 &nbsp;因实施本法规定的犯罪而获得的资助、收益、报酬等违法所得以及用于或者意图用于犯罪的资金和工具，应当予以追缴、没收。</p><p style=\"text-align: left;\">第三十三条 &nbsp;有以下情形的，对有关犯罪行为人、犯罪嫌疑人、被告人可以从轻、减轻处罚；犯罪较轻的，可以免除处罚：</p><p style=\"text-align: left;\">（一）在犯罪过程中，自动放弃犯罪或者自动有效地防止犯罪结果发生的；</p><p style=\"text-align: left;\">（二）自动投案，如实供述自己的罪行的；</p><p style=\"text-align: left;\">（三）揭发他人犯罪行为，查证属实，或者提供重要线索得以侦破其他案件的。</p><p style=\"text-align: left;\">被采取强制措施的犯罪嫌疑人、被告人如实供述执法、司法机关未掌握的本人犯有本法规定的其他罪行的，按前款第二项规定处理。</p><p style=\"text-align: left;\">第三十四条 &nbsp;不具有香港特别行政区永久性居民身份的人实施本法规定的犯罪的，可以独立适用或者附加适用驱逐出境。</p><p style=\"text-align: left;\">不具有香港特别行政区永久性居民身份的人违反本法规定，因任何原因不对其追究刑事责任的，也可以驱逐出境。</p><p style=\"text-align: left;\">第三十五条 &nbsp;任何人经法院判决犯危害国家安全罪行的，即丧失作为候选人参加香港特别行政区举行的立法会、区议会选举或者出任香港特别行政区任何公职或者行政长官选举委员会委员的资格；曾经宣誓或者声明拥护中华人民共和国香港特别行政区基本法、效忠中华人民共和国香港特别行政区的立法会议员、政府官员及公务人员、行政会议成员、法官及其他司法人员、区议员，即时丧失该等职务，并丧失参选或者出任上述职务的资格。</p><p style=\"text-align: left;\">前款规定资格或者职务的丧失，由负责组织、管理有关选举或者公职任免的机构宣布。</p><p style=\"text-align: left;\">第六节 &nbsp;效力范围</p><p style=\"text-align: left;\">第三十六条 &nbsp;任何人在香港特别行政区内实施本法规定的犯罪的，适用本法。犯罪的行为或者结果有一项发生在香港特别行政区内的，就认为是在香港特别行政区内犯罪。</p><p style=\"text-align: left;\">在香港特别行政区注册的船舶或者航空器内实施本法规定的犯罪的，也适用本法。</p><p style=\"text-align: left;\">第三十七条 &nbsp;香港特别行政区永久性居民或者在香港特别行政区成立的公司、团体等法人或者非法人组织在香港特别行政区以外实施本法规定的犯罪的，适用本法。</p><p style=\"text-align: left;\">第三十八条 &nbsp;不具有香港特别行政区永久性居民身份的人在香港特别行政区以外针对香港特别行政区实施本法规定的犯罪的，适用本法。</p><p style=\"text-align: left;\">第三十九条 &nbsp;本法施行以后的行为，适用本法定罪处刑。</p><p style=\"text-align: left;\">第四章 &nbsp;案件管辖、法律适用和程序</p><p style=\"text-align: left;\">第四十条 &nbsp;香港特别行政区对本法规定的犯罪案件行使管辖权，但本法第五十五条规定的情形除外。</p><p style=\"text-align: left;\">第四十一条 &nbsp;香港特别行政区管辖危害国家安全犯罪案件的立案侦查、检控、审判和刑罚的执行等诉讼程序事宜，适用本法和香港特别行政区本地法律。</p><p style=\"text-align: left;\">未经律政司长书面同意，任何人不得就危害国家安全犯罪案件提出检控。但该规定不影响就有关犯罪依法逮捕犯罪嫌疑人并将其羁押，也不影响该等犯罪嫌疑人申请保释。</p><p style=\"text-align: left;\">香港特别行政区管辖的危害国家安全犯罪案件的审判循公诉程序进行。</p><p style=\"text-align: left;\">审判应当公开进行。因为涉及国家秘密、公共秩序等情形不宜公开审理的，禁止新闻界和公众旁听全部或者一部分审理程序，但判决结果应当一律公开宣布。</p><p style=\"text-align: left;\">第四十二条 &nbsp;香港特别行政区执法、司法机关在适用香港特别行政区现行法律有关羁押、审理期限等方面的规定时，应当确保危害国家安全犯罪案件公正、及时办理，有效防范、制止和惩治危害国家安全犯罪。</p><p style=\"text-align: left;\">对犯罪嫌疑人、被告人，除非法官有充足理由相信其不会继续实施危害国家安全行为的，不得准予保释。</p><p style=\"text-align: left;\">第四十三条 &nbsp;香港特别行政区政府警务处维护国家安全部门办理危害国家安全犯罪案件时，可以采取香港特别行政区现行法律准予警方等执法部门在调查严重犯罪案件时采取的各种措施，并可以采取以下措施：</p><p style=\"text-align: left;\">（一）搜查可能存有犯罪证据的处所、车辆、船只、航空器以及其他有关地方和电子设备；</p><p style=\"text-align: left;\">（二）要求涉嫌实施危害国家安全犯罪行为的人员交出旅行证件或者限制其离境；</p><p style=\"text-align: left;\">（三）对用于或者意图用于犯罪的财产、因犯罪所得的收益等与犯罪相关的财产，予以冻结，申请限制令、押记令、没收令以及充公；</p><p style=\"text-align: left;\">（四）要求信息发布人或者有关服务商移除信息或者提供协助；</p><p style=\"text-align: left;\">（五）要求外国及境外政治性组织，外国及境外当局或者政治性组织的代理人提供资料；</p><p style=\"text-align: left;\">（六）经行政长官批准，对有合理理由怀疑涉及实施危害国家安全犯罪的人员进行截取通讯和秘密监察；</p><p style=\"text-align: left;\">（七）对有合理理由怀疑拥有与侦查有关的资料或者管有有关物料的人员，要求其回答问题和提交资料或者物料。</p><p style=\"text-align: left;\">香港特别行政区维护国家安全委员会对警务处维护国家安全部门等执法机构采取本条第一款规定措施负有监督责任。</p><p style=\"text-align: left;\">授权香港特别行政区行政长官会同香港特别行政区维护国家安全委员会为采取本条第一款规定措施制定相关实施细则。</p><p style=\"text-align: left;\">第四十四条 &nbsp;香港特别行政区行政长官应当从裁判官、区域法院法官、高等法院原讼法庭法官、上诉法庭法官以及终审法院法官中指定若干名法官，也可从暂委或者特委法官中指定若干名法官，负责处理危害国家安全犯罪案件。行政长官在指定法官前可征询香港特别行政区维护国家安全委员会和终审法院首席法官的意见。上述指定法官任期一年。</p><p style=\"text-align: left;\">凡有危害国家安全言行的，不得被指定为审理危害国家安全犯罪案件的法官。在获任指定法官期间，如有危害国家安全言行的，终止其指定法官资格。</p><p style=\"text-align: left;\">在裁判法院、区域法院、高等法院和终审法院就危害国家安全犯罪案件提起的刑事检控程序应当分别由各该法院的指定法官处理。</p><p style=\"text-align: left;\">第四十五条 &nbsp;除本法另有规定外，裁判法院、区域法院、高等法院和终审法院应当按照香港特别行政区的其他法律处理就危害国家安全犯罪案件提起的刑事检控程序。</p><p style=\"text-align: left;\">第四十六条 &nbsp;对高等法院原讼法庭进行的就危害国家安全犯罪案件提起的刑事检控程序，律政司长可基于保护国家秘密、案件具有涉外因素或者保障陪审员及其家人的人身安全等理由，发出证书指示相关诉讼毋须在有陪审团的情况下进行审理。凡律政司长发出上述证书，高等法院原讼法庭应当在没有陪审团的情况下进行审理，并由三名法官组成审判庭。</p><p style=\"text-align: left;\">凡律政司长发出前款规定的证书，适用于相关诉讼的香港特别行政区任何法律条文关于“陪审团”或者“陪审团的裁决”，均应当理解为指法官或者法官作为事实裁断者的职能。</p><p style=\"text-align: left;\">第四十七条 &nbsp;香港特别行政区法院在审理案件中遇有涉及有关行为是否涉及国家安全或者有关证据材料是否涉及国家秘密的认定问题，应取得行政长官就该等问题发出的证明书，上述证明书对法院有约束力。</p><p style=\"text-align: left;\">第五章 &nbsp;中央人民政府驻香港特别行政区维护国家安全机构</p><p style=\"text-align: left;\">第四十八条 &nbsp;中央人民政府在香港特别行政区设立维护国家安全公署。中央人民政府驻香港特别行政区维护国家安全公署依法履行维护国家安全职责，行使相关权力。</p><p style=\"text-align: left;\">驻香港特别行政区维护国家安全公署人员由中央人民政府维护国家安全的有关机关联合派出。</p><p style=\"text-align: left;\">第四十九条 &nbsp;驻香港特别行政区维护国家安全公署的职责为：</p><p style=\"text-align: left;\">（一）分析研判香港特别行政区维护国家安全形势，就维护国家安全重大战略和重要政策提出意见和建议；</p><p style=\"text-align: left;\">（二）监督、指导、协调、支持香港特别行政区履行维护国家安全的职责；</p><p style=\"text-align: left;\">（三）收集分析国家安全情报信息；</p><p style=\"text-align: left;\">（四）依法办理危害国家安全犯罪案件。</p><p style=\"text-align: left;\">第五十条 &nbsp;驻香港特别行政区维护国家安全公署应当严格依法履行职责，依法接受监督，不得侵害任何个人和组织的合法权益。</p><p style=\"text-align: left;\">驻香港特别行政区维护国家安全公署人员除须遵守全国性法律外，还应当遵守香港特别行政区法律。</p><p style=\"text-align: left;\">驻香港特别行政区维护国家安全公署人员依法接受国家监察机关的监督。</p><p style=\"text-align: left;\">第五十一条 &nbsp;驻香港特别行政区维护国家安全公署的经费由中央财政保障。</p><p style=\"text-align: left;\">第五十二条 &nbsp;驻香港特别行政区维护国家安全公署应当加强与中央人民政府驻香港特别行政区联络办公室、外交部驻香港特别行政区特派员公署、中国人民解放军驻香港部队的工作联系和工作协同。</p><p style=\"text-align: left;\">第五十三条 &nbsp;驻香港特别行政区维护国家安全公署应当与香港特别行政区维护国家安全委员会建立协调机制，监督、指导香港特别行政区维护国家安全工作。</p><p style=\"text-align: left;\">驻香港特别行政区维护国家安全公署的工作部门应当与香港特别行政区维护国家安全的有关机关建立协作机制，加强信息共享和行动配合。</p><p style=\"text-align: left;\">第五十四条 &nbsp;驻香港特别行政区维护国家安全公署、外交部驻香港特别行政区特派员公署会同香港特别行政区政府采取必要措施，加强对外国和国际组织驻香港特别行政区机构、在香港特别行政区的外国和境外非政府组织和新闻机构的管理和服务。</p><p style=\"text-align: left;\">第五十五条 &nbsp;有以下情形之一的，经香港特别行政区政府或者驻香港特别行政区维护国家安全公署提出，并报中央人民政府批准，由驻香港特别行政区维护国家安全公署对本法规定的危害国家安全犯罪案件行使管辖权：</p><p style=\"text-align: left;\">（一）案件涉及外国或者境外势力介入的复杂情况，香港特别行政区管辖确有困难的；</p><p style=\"text-align: left;\">（二）出现香港特别行政区政府无法有效执行本法的严重情况的；</p><p style=\"text-align: left;\">（三）出现国家安全面临重大现实威胁的情况的。</p><p style=\"text-align: left;\">第五十六条 &nbsp;根据本法第五十五条规定管辖有关危害国家安全犯罪案件时，由驻香港特别行政区维护国家安全公署负责立案侦查，最高人民检察院指定有关检察机关行使检察权，最高人民法院指定有关法院行使审判权。</p><p style=\"text-align: left;\">第五十七条 &nbsp;根据本法第五十五条规定管辖案件的立案侦查、审查起诉、审判和刑罚的执行等诉讼程序事宜，适用《中华人民共和国刑事诉讼法》等相关法律的规定。</p><p style=\"text-align: left;\">根据本法第五十五条规定管辖案件时，本法第五十六条规定的执法、司法机关依法行使相关权力，其为决定采取强制措施、侦查措施和司法裁判而签发的法律文书在香港特别行政区具有法律效力。对于驻香港特别行政区维护国家安全公署依法采取的措施，有关机构、组织和个人必须遵从。</p><p style=\"text-align: left;\">第五十八条 &nbsp;根据本法第五十五条规定管辖案件时，犯罪嫌疑人自被驻香港特别行政区维护国家安全公署第一次讯问或者采取强制措施之日起，有权委托律师作为辩护人。辩护律师可以依法为犯罪嫌疑人、被告人提供法律帮助。</p><p style=\"text-align: left;\">犯罪嫌疑人、被告人被合法拘捕后，享有尽早接受司法机关公正审判的权利。</p><p style=\"text-align: left;\">第五十九条 &nbsp;根据本法第五十五条规定管辖案件时，任何人如果知道本法规定的危害国家安全犯罪案件情况，都有如实作证的义务。</p><p style=\"text-align: left;\">第六十条 &nbsp;驻香港特别行政区维护国家安全公署及其人员依据本法执行职务的行为，不受香港特别行政区管辖。</p><p style=\"text-align: left;\">持有驻香港特别行政区维护国家安全公署制发的证件或者证明文件的人员和车辆等在执行职务时不受香港特别行政区执法人员检查、搜查和扣押。</p><p style=\"text-align: left;\">驻香港特别行政区维护国家安全公署及其人员享有香港特别行政区法律规定的其他权利和豁免。</p><p style=\"text-align: left;\">第六十一条 &nbsp;驻香港特别行政区维护国家安全公署依据本法规定履行职责时，香港特别行政区政府有关部门须提供必要的便利和配合，对妨碍有关执行职务的行为依法予以制止并追究责任。</p><p style=\"text-align: left;\">第六章 &nbsp;附 &nbsp;则</p><p style=\"text-align: left;\">第六十二条 &nbsp;香港特别行政区本地法律规定与本法不一致的，适用本法规定。</p><p style=\"text-align: left;\">第六十三条 &nbsp;办理本法规定的危害国家安全犯罪案件的有关执法、司法机关及其人员或者办理其他危害国家安全犯罪案件的香港特别行政区执法、司法机关及其人员，应当对办案过程中知悉的国家秘密、商业秘密和个人隐私予以保密。</p><p style=\"text-align: left;\">担任辩护人或者诉讼代理人的律师应当保守在执业活动中知悉的国家秘密、商业秘密和个人隐私。</p><p style=\"text-align: left;\">配合办案的有关机构、组织和个人应当对案件有关情况予以保密。</p><p style=\"text-align: left;\">第六十四条 &nbsp;香港特别行政区适用本法时，本法规定的“有期徒刑”“无期徒刑”“没收财产”和“罚金”分别指“监禁”“终身监禁”“充公犯罪所得”和“罚款”，“拘役”参照适用香港特别行政区相关法律规定的“监禁”“入劳役中心”“入教导所”，“管制”参照适用香港特别行政区相关法律规定的“社会服务令”“入感化院”，“吊销执照或者营业许可证”指香港特别行政区相关法律规定的“取消注册或者注册豁免，或者取消牌照”。</p><p style=\"text-align: left;\">第六十五条 &nbsp;本法的解释权属于全国人民代表大会常务委员会。</p><p style=\"text-align: left;\">第六十六条 &nbsp;本法自公布之日起施行。</p><p style=\"text-align: left;\"> </p><p style=\"text-align: left;\"><br></p>', '本站编辑', '', '2023-04-11 21:37:50');
INSERT INTO `articles` VALUES (57, 9, '国家时事', '习近平在二十届中共中央政治局常委同中外记者见面时强调 始终坚持一切为了人民一切依靠人民 以中国式现代化全面推进中华民族伟大复兴', '<p style=\"text-align: left;\">新华社北京10月23日电 &nbsp;刚刚在中国共产党第二十届中央委员会第一次全体会议上当选的中共中央总书记习近平和中共中央政治局常委李强、赵乐际、王沪宁、蔡奇、丁薛祥、李希，23日中午在人民大会堂同采访中共二十大的中外记者亲切见面。</p><p style=\"text-align: left;\">人民大会堂金色大厅华灯璀璨、气氛热烈庄重。600多名中外记者来到这里，向全球报道新一届中共中央政治局常委的首次集体亮相。</p><p style=\"text-align: left;\">12时5分，习近平等在热烈的掌声中步入大厅，向在场的中外记者挥手致意。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202210/24085725cdoz.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">10月23日，刚刚在中国共产党第二十届中央委员会第一次全体会议上当选的中共中央总书记习近平和中共中央政治局常委李强、赵乐际、王沪宁、蔡奇、丁薛祥、李希在北京人民大会堂同中外记者亲切见面。这是习近平发表重要讲话。（新华社记者 李学仁/摄）</span></p><p style=\"text-align: left;\">在中共二十大新闻发言人孙业礼作简要介绍后，习近平发表了重要讲话。习近平首先表示，很高兴在这里同各位记者朋友见面。昨天，中国共产党第二十次全国代表大会已经胜利闭幕了。这是一次高举旗帜、凝聚力量、团结奋进的大会。国际社会对中共二十大高度关注。许多国家的政党发来了贺电贺信，其中很多是国家元首、政府首脑、政党和重要组织机构领导人发来的。在此，我谨代表中共中央，致以诚挚谢意。</p><p style=\"text-align: left;\">习近平向大家介绍了中共中央政治局常务委员会的其他成员后，代表新一届中共中央领导成员衷心感谢全党同志的信任，表示一定牢记党的性质和宗旨，牢记自己的使命和责任，恪尽职守、勤勉工作，决不辜负党和人民重托。</p><p style=\"text-align: center;\"><img src=\"https://qspfw.moe.gov.cn/u/cms/www/202210/24085803ztrk.jpg\" alt=\"\" data-href=\"\" style=\"width: auto;height: auto;\"></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">10月23日，刚刚在中国共产党第二十届中央委员会第一次全体会议上当选的中共中央总书记习近平和中共中央政治局常委李强、赵乐际、王沪宁、蔡奇、丁薛祥、李希在北京人民大会堂同中外记者亲切见面。（新华社记者 申宏/摄）</span></p><p style=\"text-align: left;\">习近平指出，经过全党全国各族人民共同努力，我们如期全面建成小康社会、实现了第一个百年奋斗目标。现在，我们正意气风发迈上全面建设社会主义现代化国家新征程，向第二个百年奋斗目标进军，以中国式现代化全面推进中华民族伟大复兴。</p><p style=\"text-align: left;\">习近平强调，新征程上，我们要始终保持昂扬奋进的精神状态。全面建设社会主义现代化国家寄托着中华民族的夙愿和期盼，凝结着中国人民的奋斗和汗水。中国式现代化是中国共产党和中国人民长期实践探索的成果，是一项伟大而艰巨的事业。惟其艰巨，所以伟大；惟其艰巨，更显荣光。为了这一事业，无数先辈筚路蓝缕、披荆斩棘，进行了艰苦卓绝的奋斗，我们心中永远铭记着他们的奉献和牺牲。我们要埋头苦干、担当作为，以更加强烈的历史主动精神推进马克思主义中国化时代化，不断谱写新时代中国特色社会主义新篇章，奋力实现中华民族伟大复兴的中国梦。</p><p style=\"text-align: left;\">习近平指出，新征程上，我们要始终坚持一切为了人民、一切依靠人民。一路走来，我们紧紧依靠人民交出了一份又一份载入史册的答卷。面向未来，我们仍然要依靠人民创造新的历史伟业。道阻且长，行则将至。前进道路上，无论是风高浪急还是惊涛骇浪，人民永远是我们最坚实的依托、最强大的底气。我们要始终与人民风雨同舟、与人民心心相印，想人民之所想，行人民之所嘱，不断把人民对美好生活的向往变为现实。</p><p style=\"text-align: left;\">习近平强调，新征程上，我们要始终推进党的自我革命。一个饱经沧桑而初心不改的党，才能基业常青；一个铸就辉煌仍勇于自我革命的党，才能无坚不摧。百年栉风沐雨、淬火成钢，特别是新时代10年革命性锻造，中国共产党更加坚强有力、更加充满活力。面对新征程上的新挑战新考验，我们必须高度警省，永远保持赶考的清醒和谨慎，驰而不息推进全面从严治党，使百年大党在自我革命中不断焕发蓬勃生机，始终成为中国人民最可靠、最坚强的主心骨。</p><p style=\"text-align: left;\">习近平指出，新征程上，我们要始终弘扬全人类共同价值。当今世界面临前所未有的挑战。我们历来主张，人类的前途命运应该由世界各国人民来把握和决定。只要共行天下大道，各国就能够和睦相处、合作共赢，携手创造世界的美好未来。我们将同各国人民一道，弘扬和平、发展、公平、正义、民主、自由的全人类共同价值，维护世界和平、促进世界发展，持续推动构建人类命运共同体。</p><p style=\"text-align: left;\">习近平强调，中国发展离不开世界，世界发展也需要中国。经过改革开放40多年不懈努力，我们创造了经济快速发展和社会长期稳定两大奇迹。现在，中国经济韧性强、潜力足、回旋余地广，长期向好的基本面不会改变。中国开放的大门只会越来越大。我们将坚定不移全面深化改革开放，坚定不移推动高质量发展，以自身发展为世界创造更多机遇。</p><p style=\"text-align: left;\">习近平指出，新征程是充满光荣和梦想的远征。蓝图已经绘就，号角已经吹响。我们要踔厉奋发、勇毅前行，努力创造更加灿烂的明天。</p><p style=\"text-align: left;\">习近平最后说，几天来，记者朋友们对大会进行了充分报道，向全世界传递中国声音、传播中国共产党的主张，使世界的目光注视中国。感谢你们的辛勤工作，大家辛苦了。欢迎大家多到中国各地走一走、看一看，客观真实向世界讲好中国故事，讲好中国共产党故事，讲好我们正在经历的新时代故事。</p><p style=\"text-align: left;\">12时30分，见面会结束。习近平等向中外记者挥手道别。全场再次响起热烈的掌声。</p><p style=\"text-align: left;\">中央广播电视总台和新华网进行了现场直播。</p><p style=\"text-align: left;\"><br></p>', '本站编辑', '', '2023-04-11 21:38:37');
INSERT INTO `articles` VALUES (58, 9, '国家时事', '去年公安机关侦破侵犯公民个人信息案件4900余起 “内鬼”监守自盗致侵害公民信息案多发', '<p style=\"text-align: right;\"><span style=\"color: rgb(69, 67, 67);\">法制网记者 李豪</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">考生报名后，立即接到各种卖答案信息；“请问是李××先生吗?”诈骗分子清楚地喊出了你的名字；买新房尚未签约，推销贷款业务的电话便接踵而至……近年来，侵犯公民个人信息违法犯罪活动猖獗，不仅严重侵害群众的隐私权，也易诱发电信网络诈骗、盗刷银行卡等下游犯罪，群众对此反响强烈。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">《法制日报》记者今天从公安部了解到，2017年以来，公安部组织全国公安机关深入推进打击整治网络侵犯公民个人信息犯罪专项行动，侦破了一批大要案件，有力打击了犯罪分子的嚣张气焰，有效肃清网络环境，切实保护公民合法权益。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">据统计，截至2017年12月20日，全国公安机关当年累计侦破侵犯公民个人信息案件4911起，抓获犯罪嫌疑人15463名，打掉涉案公司164个。</span></p><p style=\"text-align: left;\"><strong>抓获“内鬼”和黑客1220人</strong></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">近日，福建福州公安机关破获一起特大侵犯公民个人信息案，查获公民个人房产、征信报告、车辆、联系方式等信息超过千万条，抓获的19名犯罪嫌疑人，绝大多数是房产开发、销售、中介等内部人员。他们利用职务便利，非法收集、交换、出售公民个人信息，从中牟利。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">从已破获案件看，“内鬼”监守自盗和黑客攻击仍是公民个人信息泄露的主要渠道。当前，内部人员作案突出，非法获取公民个人信息新技术、新手段不断出现，涉案单位和人员反侦查意识日益增强，涉案单位和人员以及侵犯公民个人信息的黑色产业链条，拥有海量公民个人信息数据。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">过去的一年，公安机关严厉打击利用工作之便窃取、泄露公民个人信息的各部门、各行业内部人员以及入侵信息系统窃取公民个人信息的黑客，抓获各部门、各行业内部涉案人员831名，抓获涉案黑客389名。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">江苏南京侦破李某等人侵犯公民个人信息案，依法打击处理了某互联网企业金融风控部门经理和员工。吉林白山侦办的鲁某等人侵犯公民个人信息犯罪案，抓获连续3年伙同他人入侵某航空公司内部网络、窃取乘客信息并向诈骗团伙贩卖、个人非法获利600余万元的某航空公司客服人员鲁某。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">针对侵犯公民个人信息犯罪易发高发行业，各地公安机关层层追查，严厉打击处理涉案公司164家。其中，房产相关行业公司58家，互联网通讯类公司46家，金融商贸类公司41家，文化教育类公司20家。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">山东临沂打掉了贩卖公民个人信息的某新三板上市公司等11家公司，抓获博士后、博士、硕士等高学历犯罪嫌疑人14名。浙江杭州打掉利用为某省疾病预防控制中心建设网站之机窃取、贩卖儿童医疗信息、非法获利200余万元的微象科技有限公司。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">在打击侵犯公民个人信息犯罪过程中，公安机关注重全链条打击，包括购买、收售、交易、帮助建立平台等，深挖各类犯罪利益链条。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">浙江绍兴打掉全国最大的为窃取公民账号密码信息提供服务的人工智能打码平台“快啊”，冒充QQ好友诈骗案件发案率大幅下降。江苏徐州侦破“4·26”侵犯公民个人信息案，捣毁涉及全国30多个地市的买卖公民征信信息犯罪网络，抓获犯罪嫌疑人140余名，涉案金额200余万元。</span></p><p style=\"text-align: left;\"><strong>完善立法整治信息泄露</strong></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">2017年8月24日，山东省临沂市人民法院公开审理了被告人杜天禹侵犯公民个人信息一案。杜天禹通过植入木马等方式，非法侵入山东省2016年普通高等学校招生考试信息平台网站，窃取考生信息对外出售。陈文辉(另案处理)正是利用从杜天禹处购买来的信息实施电信诈骗，导致“徐玉玉受骗死亡案”发生。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">最终，杜天禹因犯侵犯公民个人信息罪，获刑6年并被处罚金6万元。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">公安部网络安全保卫局有关负责人说，党政机关和企事业单位公民个人信息安全防护缺位是公民个人信息泄露的重要原因之一。民政、教育、卫生、人社、通信、金融、快递等部门拥有海量公民个人信息，一些单位保护公民个人信息安全的职责没有完全落到实处，造成公民个人信息泄露事件时有发生。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">他进一步分析说，一些党政机关和企事业单位泄露公民个人信息主要原因有三；信息系统安全防护措施薄弱，极易遭到黑客入侵、窃取；内控安全制度不完善或不落实，内部工作人员为牟取不法利益，违规查询、非法窃取并贩卖公民个人信息多发；在进行政务和信息公开时，未能妥善处理与保护公民个人信息间的关系，造成信息泄露。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">据了解，随着去年网络安全法和办理侵犯公民个人信息刑事案件司法解释的相继实施，以及公安机关连续两年开展专项打击整治，国内大数据行业违法违规获取数据的行为有所收敛，但大数据的采集、流通、交易、应用等环节总体仍处于无章可循的状态，这也是导致公民个人信息泄露的重要原因之一。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">对于不法分子而言，利益驱使其持续作案。公安机关侦破的大量案件表明，犯罪嫌疑人大多是从最初几百几千条信息倒卖开始，之后主动在网上寻找资料来源，高价倒卖，数量很快上升至几万、几十万乃至上百万条。犯罪嫌疑人根据信息“含金量”进行标价，新开楼盘业主、新购车辆车主、新生儿家长、入学新生等信息价格最高。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">“我国行政处罚法、治安管理处罚法均没有明确规定侵犯公民个人信息应受的行政处罚措施，这为打击侵犯公民个人信息犯罪带来了不便。”公安部网络安全保卫局有关负责人说，严重侵犯公民个人信息行为纳入刑罚处罚，但是没有行政处罚的前置性程序，从民事责任直接跨度到刑事责任，跨度太大，出现只有刑法单打独斗的局面，不利于形成合力。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">据介绍，下一步，公安机关将继续严厉打击网络侵犯公民个人信息犯罪不放松，并会同相关部门，积极推动制定保护公民个人信息的专门法律，明确公民个人信息收集原则、程序，存储、使用和保护义务，以及不当使用、保护不力应承担的责任，争取在治安管理处罚法中增加侵犯公民个人信息违法行为的专门条款。</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(69, 67, 67);\">法制网北京1月9日讯</span></p><p style=\"text-align: left;\"><br></p>', '本站编辑', 'http://127.0.0.1:3000/article/7fC2Hrzhu9tLtuNZ8xo1vu7ADz01K8x47PAtXQQdbabxY1540388030356compressflag.jpg', '2023-04-11 21:39:21');

SET FOREIGN_KEY_CHECKS = 1;


