INSERT INTO `message` VALUES (1, '网站奔溃了', '网站奔溃了', '362429200008051932', '伍禧龙', '', '', '2023-04-02 22:36:53');
INSERT INTO `message` VALUES (2, '啊吧啊吧', '打球的青蛙打网球', '362429200008051935', '五五', '', '', '2023-04-02 22:36:57');
INSERT INTO `message` VALUES (3, '4324324', '43243243214', '362429200008051938', '恶趣味我去', '', '', '2023-04-02 22:37:01');
INSERT INTO `message` VALUES (9, '发射点发射点', '防守打法第三方', '362429200008051932', '伍禧龙', '', '', '2023-03-31 21:25:46');
INSERT INTO `message` VALUES (10, 'v但是v得瑟得瑟', '广东省格式', '362429200008051932', '伍禧龙', '', '', '2023-03-31 21:25:49');
INSERT INTO `message` VALUES (12, '111', '我去怎么搞的', '362429200008051932', '伍禧龙', '', '2278815788@qq.com', '2023-03-31 22:16:37');
INSERT INTO `message` VALUES (13, '123', '131312321', '362429200008051932', '天秀', '13311714408', '', '2023-03-31 22:19:27');
INSERT INTO `message` VALUES (17, '为建设社会主义文化强国奉献光和热', '“现在，许多文化经典作品已经进入中小学校园，成为诵读教材。”作为一名从事中国传统文化研究的学者，山东大学儒学高等研究院教授杨朝明代表讲述了自己参与传承、弘扬中华优秀传统文化的故事。\n\n　　杨朝明介绍，尼山世界文明论坛已连续举办了八届，有大批中外学者、国际友人来此交流，一起感受中华优秀传统文化的独特魅力。更让他欣喜的是，如今走出国门，也能深切感受到中华优秀传统文化对于世界的积极影响。', '362429200008051932', '伍禧龙', '', '2278815788@qq.com', '2023-04-03 23:29:36');
INSERT INTO `message` VALUES (18, '国家需求就是科研工作者努力的方向', '华中科技大学教授冯丹代表介绍，存储器和存储系统是数据的载体，关乎数字经济产业安全、国家安全。20多年来，冯丹见证并参与了我国存储技术和产品从无到有、从有到全、从弱到强的全过程，深深体悟到个人小我之于“国之大者”的人生价值，也深深体会到只有靠高水平科技自立自强才能赢得未来。', '362429200008051932', '伍禧龙', '13311714408', '', '2023-04-03 23:30:48');
INSERT INTO `message` VALUES (19, '聚焦职业教育建言献策', '“能够当选全国人大代表，我深感荣幸，这也充分体现了党和国家对青年劳动技能人才的关爱和重视。”谈起当选的过程，广东省机械技师学院技能竞赛与师资培训科教师杨登辉代表难掩内心激动。\n\n　　杨登辉是一名95后代表，曾获得第四十四届世界技能大赛冠军和全国青年岗位能手标兵称号。他初中毕业便从事技能学习，钻研数控铣技术。“几乎每天从早到晚都在学习，这种状态持续好几年”，夺冠的荣耀背后是无数个日夜的付出。如今，他', '362429200008051932', '伍禧龙', '13311714408', '', '2023-04-03 23:38:17');

SET FOREIGN_KEY_CHECKS = 1;


